INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Exemplo — Técnica de Ocultação nas Folhas", "Iruka faz com que algumas folhas apareçam ao seu redor. Se o oponente o atacar enquanto ele está oculto nas folhas, ele aparece atrás do mesmo e atira duas Fūma Shuriken.", null, null, null, "模範的　木ノ葉隠れの術もはんてき　このはがくれのじゅつ", "Mohanteki — Konohagakure no Jutsu", "Exemplo — Técnica de Ocultação nas Folhas", "Jutsu da Folha Oculta Exemplar", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Existência de Decapitação", "Mifune executa um ataque poderoso, rapidamente puxando a sua espada e cortando seu oponente. Seu oponente só sente o ataque quando Mifune embainha sua espada.", "Curto alcance", null, null, "居斬", "Ikiru", "Existência de Decapitação", "Decapitação do Residente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Exorcismo", "Após transformar seu corpo em papel, Konan paira no ar e começa a girar horizontalmente em direção do inimigo, infringindo dano. Suas asas servem como uma extensão do seu corpo, cortando o oponente um pouco mais longe de seu corpo.", "Curto alcance", null, null, "祓い", "Harai", "Exorcismo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Explosão Gelel", "Com um pedaço da Pedra de Gelel embutido na parte de trás de sua mão direita, Haido pode explodir objetos sem a necessidade de fazer contato físico com ele. Ele aprendeu tais poderes vendo o Livro de Gelel e felizmente conseguiu desenvolver o poder. Quando usado, Haido é capaz de causar uma pequena onda de choque à distância considerável.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Explosão Kunoichi", "Sakura lança uma shuriken no oponente, e Ino aproveita a distração para usar sua Técnica de Troca da Mente do Corpo, e, finalmente, Sakura acaba com o adversário com um soco poderoso.", "Todos os alcances", null, null, null, null, "Explosão Kunoichi", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Explosão Massiva do Deserto", "Enquanto em seu estado despertado e depois de executar o selo de mão necessário, Gaara cria um fluxo de vento e areia que protege-o de qualquer ataque.", "Curto alcance", null, "Tigre", "砂爆塊防さばくかたまりぼう", "Sabaku Katamari Bō", "Explosão Massiva do Deserto", "Pilha de Areia: Defesa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Explosão: Destruição da Montanha de Gelo", "Mizore usa seu Braço de Cabo Retrátil para lançar a mão de metal para agarrar o oponente, colocá-lo no ar, e lançá-lo em uma montanha de gelo que quebra e partes do seu gelo caem sobre o oponente.", "Ofensivo", null, "Médio alcance", "爆裂・氷山崩し", "Bakuretsu: Hyōzankuzushi", "Explosão: Destruição da Montanha de Gelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Explosão da Armadilha de Selamento", "Tsukino prepara uma armadilha de selamento no terreno que, quando pisada por um oponente, explode.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Explosão da Foice da Doninha", "Temari lança o oponente no ar, e Shikamaru chuta o oponente. Shikamaru em seguida, usa a sua Técnica de Imitação pela Sombra para paralisar o oponente. Então ele lança uma kunai que está acompanhada de um papel explosivo enquanto Temari usa a Técnica da Foice da Doninha para colocar o oponente num tufão e cortá-lo enquanto a kunai explode por causa do papel explosivo.", "Todos os alcances", null, null, null, null, "Explosão da Foice da Doninha", "Explosão de Vento da Foice", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Expressão Montanhosa", "O usuário ataca seu oponente com dois socos antes de agachar e erguer sua cabeça, dando uma cabeçada fatal no queixo do oponente, lançando-o para uma distância considerável.", "Curto alcance", null, null, "山顔", "Sangan", "Expressão Montanhosa", "Rosto das Montanhas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Extremas Ondas Sonoras Decapitadoras", "Zaku cria uma rajada enorme de vento cortante, ainda mais poderosa do que sua técnica Ondas Sonoras Decapitadoras, que é capaz de nivelar a área circundante, acabando com qualquer coisa em seu caminho.", "Curto a Médio alcance", "Rank C", "Juntar as Maos[2]", "斬空極波", "Zankūkyokuha", "Extremas Ondas Sonoras Decapitadoras", null, "Onda Supersônica Cortante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Exército de Cobras", "Depois do comando de Manda, cobras do tamanho de humanos saem do chão para dar o bote no oponente e derrubá-lo.", "Longo alcance", null, null, "軍陀ラ", "Gundara", "Exército de Cobras", "Gregário de Cobras", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Facada Infernal", "A Facada Infernal é a técnica mais forte do Terceiro Raikage, em que ele concentra uma quantidade considerável de chakra relâmpago apenas nas pontas dos dedos de uma única mão e em seguida, executa um movimento de empurrar. Os dedos juntos lhe permitem perfurar um alvo de uma maneira um pouco semelhante ao Chidori e ao Corte Relâmpago.", "Curto alcance", "Rank B", null, "地獄突きじごくずき", "Jigokuzuki", "Facada Infernal", null, "Ponta Infernal", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Facada da Agulha Negra", "Pain corre em direção do oponente e esfaqueia com a Manifestação de Receptores de Chakra, revertendo seus controles.", "Curto Alcance", null, null, "黒針突こくしんとう", "Kokushintō", "Facada da Agulha Negra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Facas do Chakra Devastador", "Kagura imerge várias facas no solo, antes de serem ligadas a fios de chakra, puxando-as com uma enorme força, que faz o oponente ir para o alto. Enquanto o inimigo está no ar, Kagura restringe os movimentos do oponente com os fios de chakra, o puxando para si, ao mesmo tempo em que puxa suas facas novamente, o perfurando. Quando o oponente se mostra incapacitado, ele cai no chão.", "Médio a Longo alcance", null, null, null, null, null, "Facas do Chakra Devastador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Faisão Caçador", "O Faisão Caçador é uma técnica de Liberação de Fogo utilizada por Momoshiki Ōtsutsuki.", "Curto a Médio alcance", null, null, "雉狩きじかり", "Kiji Kari", "Faisão Caçador", "Caça ao Faisão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Falcoaria", "A técnica consiste no ataque com uma kunai com as duas mãos em movimento descendente. A técnica pode ser combinada com uma sequência de socos e chutes no oponente, a fim de atordoá-lo.", "Curto alcance", null, null, "鷹狩", "Takagari", "Falcoaria", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Feixe de Luz da Destruição", "Este é um um feixe de luz especialmente desenvolvido para lidar com inimigos que possuem poderes regenerativos, matando e desfazendo as células a partir disso. Dessa forma, Delta mira em seus oponentes e dispara feixes de luz a partir de seus olhos, a fim de desintegrar tudo o que toca.", "Longo alcance", null, null, "破壊光線", "Hakai Kōsen", "Feixe de Luz da Destruição", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Agulhas de Aço da Flor Selvagem", "Usando um de seus pergaminhos menores, Tenten invoca várias esferas explosivas, que estão espalhados pelo campo de batalha e, em seguida, explodem depois de um tempo, disparando projéteis para fora ao mesmo tempo.", null, null, null, "操具・鋼針乱華そうぐ・こうしんらんか", "Sōgu: Kōshin Ranka", "Ferramentas Manipuladas: Agulhas de Aço da Flor Selvagem", "Ferramenta Ninja: Explosão das Agulhas de Flor", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Ataque de Mil Lâminas", "Esta técnica é muito semelhante à Ascensão dos Dragões Gêmeos, mas, nesta versão, Tenten só invoca inúmeras kunai de seus dois pergaminhos, que são ligados por linhas em seus dedos. Ela, então, joga todas as kunai ao mesmo tempo sobre o oponente.", "Curto alcance", null, null, "操具・襲千刃", "Sōgu: Shū Senjin", "Ferramentas Manipuladas: Ataque de Mil Lâminas", "Lâmina dos Mil Golpes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Ataques das Esferas Mortais", "Tenten puxa um pergaminho e gira em torno de si mesma. Várias bolas de metais pontiagudos irrompem o ataque de qualquer oponente próximo ou de desencorajá-la perto com ataques à distância.", "Curto alcance", null, null, "操具・死球撃", "Sōgu: Shikyū Geki", "Ferramentas Manipuladas: Ataques das Esferas Mortais", "Ferramenta Ninja: Golpe da Esfera Mortal", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Bola Gigantesca de Ferro", "Tenten atira cinco bolas de ferro contra o oponente, como distração e então desenrola o seu pergaminho gigante e invoca uma esfera metálica enorme, coberta de espinhos, assemelhando-se a ponta de uma clava ou à Jidanda. Manobrando-a arma com fios de aço conectados em suas mãos, ela bate a arma contra o inimigo distraído.", "Curto a Médio alcance", null, null, "操具・極大鉄球", "Sōgu: Kyokudai Tekkyū", "Ferramentas Manipuladas: Bola Gigantesca de Ferro", "Controle de Armas: Bola Gigantesca de Ferro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Cadeia do Desastre Celestial", "Uma versão muito mais forte da Ascensão dos Dragões Gêmeos. Tenten lança seu grande pergaminho ao céu, acima de seu oponente, e invoca dezenas de armas diferentes a partir dele, formando uma chuva de armas ninja, capaz de acertar o inimigo e seus arredores. As armas se movem em uma velocidade tão grande que aparentemente deixam manchas brancas no céu, que combinando com o grande alcance, a chuva de armas torna essa técnica muito difícil escapar.", "Todos os alcances", null, null, "操具・天鎖壊そうぐ・てんささい", "Sōgu: Tensasai", "Ferramentas Manipuladas: Cadeia do Desastre Celestial", null, null, "Controle de Arma: Tensasai");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Desastre do Aço Celestial", "Similar a Ferramentas Manipuladas: Cadeia do Desastre Celestial, Tenten joga e desenrola um grande pergaminho sobre o oponente, invocando incontáveis kunai que chovem sobre ele. Ela, então, invoca quatro grandes cadeias que acertam o oponente, forçando sua queda em direção ao solo.", "Todos os alcances", null, null, "操具・天鋼災", "Sōgu: Tenkōsai", "Ferramentas Manipuladas: Desastre do Aço Celestial", "Ferramenta Ninja: Desastre do Metal Celestial", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Desempenho do Ataque das Mil Lâminas", "Tenten corre em zigue-zague e, em seguida, pula pro alto e abre um pergaminho no ar, liberando uma variedade de lâminas, que são dispersadas no oponente.", "Todos os alcances", null, null, "操具・襲演千刃", "Sōgu: Shūen Senjin", "Ferramentas Manipuladas: Desempenho do Ataque das Mil Lâminas", "Ferramenta Ninja: Golpes das Mil Lâminas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Dez Mil Lâminas Selvagens Celestiais", "Tenten invoca várias armas ligadas a fios de aço até as pontas de seus dedos de um dos seus pergaminhos, direcionando-os para o seu oponente em uma formação corrente. Após a última arma atingir o oponente, Tenten usa os fios para fazer as armas voarem acima dele. Movendo os dedos para fazer as armas girarem em torno de seu oponente no ar, ela direciona as armas para baixo para acertar em cheio seu oponente.", "Curto a Médio alcance", null, null, "操具・天刃乱万", "Sōgu: Tenjin Ranman", "Ferramentas Manipuladas: Dez Mil Lâminas Selvagens Celestiais", "Ferramenta Ninja: Milhares de Lâminas do Caos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Dramatização da Andorinha Giratória", "Tenten bate repetidamente em seu oponente com um par de tonfas, em seguida, joga um pergaminho no ar para invocar um bō para continuar a bater no oponente e, por fim, derrubá-lo no chão. Quando o oponente está caído no chão, ela em seguida, invoca uma grande clava e esmaga-o com ela.", "Curto a Médio alcance", null, null, "操具・廻燕劇", "Sōgu: Kaien Geki", "Ferramentas Manipuladas: Dramatização da Andorinha Giratória", "Ferramenta Ninja: Show da Andorinha Giratória", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Esfera Explosiva", "Tenten libera múltiplas Esferas Explosivas do seu pergaminho, que explode quando entra em contato.", null, null, null, "操具・爆砕玉そうぐ・ばくさいだま", "Sōgu: Bakusaidama", "Ferramentas Manipuladas: Esfera Explosiva", "Ferramenta Ninja: Explosão da Esfera", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Grande Protuberância de Ferro", "Tenten abre um enorme pergaminho em círculo, então liberando uma bola de ferro gigante anexada em uma corrente que, em seguida, ela lança sobre seu oponente, esmagando-o. Após o esmagamento de seu oponente, a bola de metal volta para o seu pergaminho gigante.", "Curto a Médio alcance", null, null, "操具・大鉄塊そうご・だいてっかい", "Sōgu: Dai Tekkai", "Ferramentas Manipuladas: Grande Protuberância de Ferro", "Ferramenta Ninja: Gigante Protuberância de Ferro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Meteoro Anexado", "Tenten invoca uma manriki-gusari de um de seus pergaminhos e a usa para conter o adversário. Quando seu oponente está contido, ela usa o pergaminho para lançar uma barragem de armas nele e, finalmente, bate-as no chão com o manriki-gusari.", "Curto alcance", null, null, "操具・縛流星そうぐ・ばくりゅうせい", "Sōgu: Baku Ryūsei", "Ferramentas Manipuladas: Meteoro Anexado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Muro da Lâmina Flutuante", "Tenten abre dois dos seus vários pergaminhos e invoca uma enxurrada de armas de diferentes aspectos para cair sobre seu oponente, fazendo-o ir pro ar após o grande impacto. Ela, em seguida, envolve as pernas de seu oponente em correntes e puxá-o para baixo, lançando-o rapidamente pro chão.", "Curto alcance", null, null, "操具・浮刃壁", "Sōgu: Fujin Heki", "Ferramentas Manipuladas: Muro da Lâmina Flutuante", "Ferramenta Ninja: Muro da Lâmina Flutuante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Performance do Ataque de Mil Lâminas", "Tenten corre em volta do oponente abrindo um pergaminho no ar, liberando uma variedade de armas brancas que são então dirigidas a seu oponente.", null, null, null, "操具・襲演千刃", "Sōgu: Shūen Senjin", "Técnica das Ferramentas Manipuladas: Performance do Ataque de Mil Lâminas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferramentas Manipuladas: Pontapé da Lâmina", "Tenten invoca várias armas que automaticamente mostram-se estarem ligadas a fios de aço, movimentando-as de forma útil e inteligente. Ela, em seguida, vai pro alto, e gira para trás, disparando rapidamente várias armas ninja em seu oponente, assemelhando-se à tiros de metralhadora.", "Longo alcance", null, null, "操具・奏刃蹴", "Sōgu: Sōninshū", "Ferramentas Manipuladas: Pontapé da Lâmina", "Ferramenta Ninja: Pontapé da Lâmina", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ferrolho Relâmpago", "Killer B salta no ar com suas sete espadas e ataca o oponente enquanto revestido em chakra relâmpago.", "Curto alcance", null, null, "雷斗忍具菩流斗", "Raitoningu Baruto", "Luta da Ferramenta Ninja Relâmpago Fluindo Luta Rosa", "Ferrolho Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fiandeiro Violento", "Karin corre na direção de seu oponente; em seguida, ela espera um momento até que o mesmo fique com a guarda baixa, executando uma série de tapas atordoadores.", "Curto alcance", null, null, null, null, "Fiandeiro Violento", "Violenta Cabeça Giratória", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fios Crescentes", "Kagura atira várias facas ligadas a fios de chakra a partir do solo, como um gêiser.", "Médio a Longo alcance", null, null, null, null, null, "Fios Crescentes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fios de Chakra", "Fios de Chakra (チャクラの糸, Chakura no Ito) é uma técnica que normalmente é usada por marionetistas que lhes permite controlar suas marionetes em batalha.", "Curto a Médio alcance", null, null, "チャクラの糸", "Chakura no Ito", "Fios de Chakra", null, "Linhas de Chakra", null);
