INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fique Longe de Mim!", "Konohamaru salta sobre seu oponente, acertando-o com alguns socos e chutes.", "Curto alcance", null, null, "こっち来るなコレ!", "Kotchikurunakore!", "Fique Longe de Mim!", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash", "Um rápido ataque à distância, onde o usuário balança sua espada em um alvo após revesti-la em chakra, liberando um chakra crescente acentuado no arco que a lâmina foi balançada.", "Todos os alcances", null, null, "一閃", "Issen", "Flash", null, "Corte Diagonal", "Feixe de Lux");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash Amarelo", "Enquanto no Modo de Chakra do Nove-Caudas, o usuário é capaz de se mover a uma velocidade tremenda. A velocidade de Naruto ao usar essa técnica é tão grande que Killer B teve problemas para determinar se ele tinha usado a Técnica de Cintilação Corporal quando ataca Kisame Hoshigaki. Ele poderia até mesmo ultrapassar a velocidade máxima de A, bem como o relâmpago extremamente rápido do Terceiro Raikage antes que este tivesse qualquer chance de reagir.", null, null, null, "黄色い閃光きいろいせんこう", "Kiiroi Senkō", "Flash Amarelo", "Flash Amarelo da Folha[2]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash Relâmpago", "O usuário infunde chakra de relâmpago à uma shuriken, duplicando seu poder original de corte, o suficiente para cortar metais ou mutilar uma pessoa com relativa facilidade.", "Curto a Médio alcance", null, null, "雷閃", "Raisen", "Flash Relâmpago", "Centelha Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash Relâmpago do Combo do Leão", "Sasuke lança seu oponente no ar com sua espada de Kusanagi e pula em cima dele rapidamente, não dando chances de ataque ou defesa, e, em seguida, começa a socar e chutar seu oponente, ainda estando no ar. Depois de sua combinação, ele desce pro chão, logo depois de ter lançado seu oponente ferozmente pro chão, levantando seu Chidori para cima e eletrocutando o adversário que cai nele.", null, null, null, "雷光獅子連弾", "Raikō Shishi Rendan", "Flash Relâmpago do Combo do Leão", "Barragem de Leões Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash da Folha", "Rock Lee se move em alta velocidade fazendo com que várias folhas se espalhem ao redor do campo de batalha e em seguida desequilibra seu oponente e o leva para o ar, ele então chuta seu oponente no estômago e, em seguida, o chuta no rosto e, novamente, ele o chuta no estômago, jogando-o contra o chão e causando grandes danos.", "Curto alcance", null, null, "木の葉閃光", "Konoha Senkō", "Flash da Folha", "Luz da Folha Espiral", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash do Pé Rotativo", "Asuma acerta uma série de socos e ataques terminando com um chute giratório, fazendo com que o oponente seja lançado no ar.", "Curto alcance", null, null, "閃旋脚", "Sensenkyaku", "Flash do Pé Rotativo", "Flash do Chute Espiral", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flash do Telegrama", "Com sua equipe emprestando-lhe chakra, Akaboshi pode bater a mão no chão e causar um raio para se espalhar de sua mão para debaixo do oponente.", "Curto a Médio alcance", null, "Coelho + Rato + Cavalo + Carneiro simultâneamente", "電報石火", "Denpō Sekka", "Flash do Telegrama", null, null, "Denko Sekka");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flauta Demoníaca: Correntes do Som Fantasma", "Uma ilusão que depende do som da flauta demoníaca. Quem ouve será atacado pela sensação viva de que teve os seus membros presos com cordas resistentes, tornando-se incapaz de se mover livremente. Mesmo com a intervenção física de uma força externa, o corpo é roubado de toda mobilidade, e a própria consciência progressivamente desaparece. Ao mesmo tempo, eles são alimentados por alucinações mórbidas, o que torna também possível executá-las mentalmente. Dado que os meios de transmissão são baseados no som, que tem uma gama de eficácia, no topo do qual ele pode ser aplicado mesmo sem o conhecimento da posição do alvo, o que contribui para uma técnica muito conveniente.", "Todos os alcances", "Rank B", null, "魔笛・夢幻音鎖", "Mateki: Mugen Onsa", "Flauta Demoníaca: Correntes do Som Fantasma", null, "Flauta Demoníaca: Correntes Ilusórias de Som", "Flauta Demoníaca: Correntes da Fantasia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flauta Demoníaca: Guerreiros Ilusionados Manipulados por Melodia", "Usando sua Flauta Demoníaca, Tayuya controla seus três Doki à distância sem o uso de palavras. Ao lançar notas específicas, seus Doki agem e atacam de formas específicas. Tayuya também pode alterar a melodia se o oponente descobrir a sequência das notas.", null, "Rank B", null, "魔笛・幻武操曲まてき・げんぶそうきょく", "Mateki: Genbusō Kyoku", "Flauta Demoníaca: Guerreiros Ilusionádos Manipulados pela Melodia", null, "Flauta Demoníaca: Manipulação Musical dos Guerreiros Fantasma", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flauta Demoníaca: Notas Flutuantes", "Tocando sua Flauta Demoníaca, Tayuya cria várias notas flutuantes, de colorações azuladas, que ficam envoltos por uma onda de som transparente circular, e, então, lança-as contra seu oponente. Ao entrar em contato com o oponente, as notas explodem.", "Curto alcance", null, null, "魔笛・浮音まてき・ふおん", "Mateki: Fuon", "Flauta Demoníaca: Notas Flutuantes", "Flauta Demoníaca: Som Flutuante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flauta Demoníaca: Onda Espiritual Fantasma", "Usando sua Flauta Demoníaca, Tayuya invoca os espíritos materializados que ela armazena em seus Doki, sem a invocação dos Doki reais. Os espíritos perseguem seu oponente, drenando sua energia física e atordoando-o se o atingir.", "Curto a Médio alcance", null, null, "魔笛・幻霊波", "Mateki: Genreiha", "Flauta Demoníaca: Onda Espiritual Fantasma", "Flauta Demoníaca: Onda Fantasma", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flauta Demoníaca: Três Subordinados do Enterro da Melodia", "Enquanto em sua segunda fase do seu selo amaldiçoado, Tayuya aprisiona seu oponente em um genjutsu com sua flauta, e enquanto eles estão imobilizados, os Doki ataca seu oponente por baixo.", "Curto a Médio alcance", null, null, "魔笛・三従葬曲", "Mateki: Sanjū Sōkyoku", "Flauta Demoníaca: Três Subordinados do Enterro da Melodia", "Flauta Demoníaca: Trio Requiem", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flecha Flamejante da Incrível Habilidade", "Depois da modificação do corpo feita pelo Caminho Asura, esta técnica lança uma parte destacada de um braço, usando-o como uma arma de projétil. Com uma explosão de pequena escala, causada pelo chakra coletado na extremidade cortada do pulso, a porção da mão ganha poder de propulsão e voa em linha reta com uma força tremenda. Seu poder destrutivo é grande o suficiente para quebrar facilmente através mesmo da espessura de um escudo rochoso. A porção da mão é protegida com chakra, o que lhe permite manter-se incólume. O chakra no punho puxa a mão para trás como um ímã, recolocando-a novamente no corpo.", "Todos os alcances", "Rank –", null, "怪腕ノ火矢かいわんのひや", "Kaiwan no Hiya", "Flecha Flamejante da Incrível habilidade", "Canhão de Braço", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flecha de Indra", "A Flecha de Indra, de acordo com Sasuke, é seu ataque mais poderoso.", "Longo alcance", null, null, "インドラの矢インドラのや", "Indora no Ya", "Flecha de Indra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flechas Aladas Celestiais", "Ao espremer cada pingo de chakra contido em seu corpo, Kinshiki manifesta múltiplas armas de chakra que ele manipula telecineticamente próximas de si. Ao seu comando, uma torrente de armas é disparada em rápida velocidade rasgando tudo em seu caminho.", "Todos os alcances", null, null, "天羽々矢", "Amanohahaya", "Flechas Aladas Celestiais", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flor Celestial", "Kimimaro salta e cai contra o oponente com os picos de ossos que crescem em seu braço, finalizando com um ataque de sua broca de ossos.", "Curto alcance", null, null, "天花", "Tenka", "Flor Celestial", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flor Explosiva", "Ino joga um punhado de flores que são acompanhados de papéis explosivos, os quais ficam camuflados entre as pétalas, explodindo no momento do impacto contra o adversário.", "Curto a Médio alcance", null, null, "起爆花", "Kibaku Hana", "Flor Explosiva", "Flor Detonante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flor de Cerejeira Única", "Sakura é capaz de usar essa técnica graças ao seu preciso controle de chakra, que ela acumula em seu punho e o expulsa durante um soco. Com isso, ela pode desferir socos em pleno ar, liberando poderosas ondas de choque que podem destruir e repelir tudo o que estiver em seu caminho.", "Curto a Médio alcance", null, null, null, null, "Flor de Cerejeira Única", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flor de Teia de Aranha", "Uma rede adesiva especial lançada da boca de Kidōmaru. Estas redes, são criadas com o chakra, para manter suas presas e não deixá-las ir. As redes em si são muito pequenas, mas podem ser criadas em uma fração de segundo, por isso aumenta a taxa de captura por cuspir várias em uma linha. Esta é uma técnica útil contra vários inimigos. Qualquer um pego pelas duas redes em camadas são rebocados a uma parede ou árvore atrás deles.", "Curto a Médio alcance", null, null, "蜘蛛巣花", "Kumo Sōka", "Flor da Teia de Aranha", null, "Flores de Teia de Aranha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Flores Dançantes", "Kurenai cria diversas pétalas de flor de cerejeira ao desfazer seu clone, que gira ao seu redor, criando uma barreira de pétalas de cerejeira, oportunizando para Kurenai que golpeia o oponente diversas vezes até desmaiá-lo.", "Curto alcance", null, null, "花卉舞踊かきぶよう", "Kaki Buyō", "Flores Dançantes", "Dança da Flor", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fluxo Cortante do Relâmpago Sábio", "Hagoromo converte chakra em eletricidade, podendo criar um fluxo elétrico poderoso o suficiente para quebrar uma grande rocha quando infundido com a energia natural.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fluxo da Cachoeira de Areia Movediça", "Esta técnica cria uma enorme quantidade de areia na forma de uma onda, cobrindo e 'afogando' o oponente em um oceano de areia. A partir da areia que Gaara carrega em sua cabaça, ele mói as rochas e minerais no subsolo em um grande mar de areia e manipula-a como um tsunami que engolfa absolutamente todas as coisas em sua linha de visão. Este mar furioso causa uma série de danos por si só, mas pode-se tirar mais proveito da areia para um segundo e um terceiro assalto consecutivo. Gaara pode controlar a areia, permitindo-lhe atacar seu oponente, enquanto ele tenta fugir da onda. Esta técnica tem o efeito adicional de mudar a paisagem. Ela é geralmente seguida por uma técnica de esmagamento de área para matar o oponente preso.", "Todos os alcances", null, "Bater as Maos → Javali → Macaco → Rato → Macaco → Cavalo → Cão → Carneiro → Cavalo → Juntar as Mãos", "流砂瀑流りゅうさばくりゅう", "Ryūsa Bakuryū", "Fluxo da Cachoeira de Areia Movediça", "Tsunami de Areia", "Tormenta de Areia", "Tsunami de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fluxo da Palma de Madeira e Relâmpago", "Yamato ataca o oponente com a Liberação de Madeira: Técnica da Grande Floresta e em seguida Kakashi ataca a madeira com Corte Relâmpago que atinge o oponente.", null, null, null, "雷電木流掌らいでんもくりゅうしょう", "Raiden Mokuryūshō", "Fluxo da Palma de Madeira e Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fluxo da Parede de Areia", "Tirando proveito de sua areia, Gaara é capaz de criar três barragens simultâneas em seu oponente, e em seguida, mata-o ao criar três paredes flutuantes de areia que vão para esmagá-lo.", "Curto alcance", null, null, "流壁砂", "Ryūhekisa", "Fluxo da Parede de Areia", "Parede de Areia Fluída", null, null);
