INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fluxo de Tinta", "É um ninjutsu característico de Sai. Derramando sua tinta em vez de usá-la para desenhar, Sai pode criar uma grande quantidade de criaturas como um ninho de cobras para atacar o alvo. Se o oponente provar forte o suficiente para quebrar a liga se contido, eles podem reformar em torno do alvo, tornando inútil lutar.", "Curto a Médio alcance", null, null, "墨流し", "Sumi Nagashi", "Fluxo de Tinta", null, null, "Arte Ninja: Jato de Tinta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fogaréu do Gato de Chamas", "Yugito, enquanto totalmente transformada em Matatabi, dispara uma enorme bola de fogo espectral feita de chakra fogo, que explode ao contato. Estas explosões são muito destrutivas e podem reduzir seu entorno a escombros.", null, null, null, "猫炎咆火", "Byōen Hōka", "Fogaréu do Gato de Chamas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fogo Rápido", "Kusune usa Tsurukame e lança enormes quantidades de senbon.", "Médio Alcance", null, null, "連射", "Rensha", "Fogo Rápido", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fogos de Artificio Pretos", "Ao bater com seu Shakujō no estômago de seu oponente, Obito captura ele com suas Esferas da Busca da Verdade, prende-o em uma maior ao juntar todas, comprime essa esfera várias vezes e libera em uma explosão de energia escura detonando todo o conteúdo do seu interior.", "Curto alcance", null, null, "黒花火", "Kuro Hanabi", "Fogos de Artificio Pretos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Foguete Rasengan", "Mecha-Naruto usa seu Rasengan, que é de uma cor roxa clara, depois impulsiona-se em direção ao oponente. Quando o Rasengan atinge o oponente, Mecha-Naruto 'dispara' sua mão junto com o Rasengan, prejudicando ainda mais o oponente.", "Curto alcance", null, null, "ロケット螺旋丸", "Roketto Rasengan", "Foguete de Esfera Espiral", "Foguete Rasengan", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Forma Kaima", "Esta técnica, desenvolvida por Amachi por causa do interesse de Orochimaru em criar ninjas supremos que combatessem em baixo d'água, muda drasticamente a aparência do usuário, dando-lhes uma pele verde escamosa, barbatanas e guelras.", null, null, null, null, null, null, null, null, null);
