INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Asa da Garsa", "Após saltar no ar, o usuário ataca o inimigo em uma formação parecida com asas de garsa. Depois de estar em torno do inimigo, o usuário morde os membros do oponete tornando-o incapaz de se mover e fazendo este despencar até o chão.", null, null, null, "鶴翼の陣", "Kakuyoku no Jin", "Formação Asa da Garsa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Asa da Garsa (Oito Portões)", "Para realizar esta técnica, pelo menos seis ou mais usuários bem treinados em taijutsu dos oito portões devem abrir simultaneamente o sexto portão. Este ataque combinado é poderoso o suficiente para obliterar a metade de um enorme meteorito vindo da Lua.", "Todos os alcances", null, null, "鶴翼の陣かくよくのじん", "Kakuyoku no Jin", "Formação Asa da Garsa", null, null, null);
