INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação C", "A Formação C é uma tática de batalha utilizada por Itachi Uchiha e Jūzō Biwa, caso este último seja pego pelos ninjas de Kirigakure.", null, null, null, "フォーメーションC", "Fōmēshon C", "Formação C", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Circular", "O Hōen no Jin (方円の陣, Formação Circular) é uma manobra suplementar usada pelos ninkens de Kakashi, que envolve os cães em pé, formando um círculo. Eles também podem deixar um cão no meio para ser o foco de ataque do adversário, permitindo-lhes realizar outras técnicas.", null, null, null, "方円の陣ほうえんのじん", "Hōen no Jin", "Formação Circular", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Escura do Inferno", "O Quarteto do Som ativa seu segundo nível do selo amaldiçoado e cria uma formação de barreira de quatro vias que libera uma energia escura. Os quatro shinobi então selam o oponente dentro da formação de barreira, criando uma explosão em erupção de energia escura não deixando nenhum vestígio do inimigo.", "Médio alcance", null, null, "奈落冥陣ならくめいじん", "Naraku Meijin", "Formação Escura do Inferno", "Arte Ninja: Formação do Submundo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Ino–Shika–Chō", "Formação Ino–Shika–Chō são formações usadas pelos membros dos clãs Yamanaka, Nara e Akimichi projetadas devido à sua longa história de trabalho em conjunto.", null, null, null, "フォーメーション猪鹿蝶", "Fōmēshon Ino–Shika–Chō", "Formação Javali–Veado–Borboleta", null, null, "Formação Ino–Shika–Chō");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Jōseki de Konoha", "Izumo usa a Liberação de Água: Captura do Campo de Xarope de Amido para criar uma grande poça de água. Após isso, ele, ao lado de Asuma e Kotetsu, ocultam-se na água a fim de chegar perto de seu oponente. Os três então pulam para fora da água e acertam seu oponente por trás de suas costas, com Izumo e Kotetsu atacando com uma longa kunai e uma clava simultaneamente, seguidos por Asuma acertando o oponente com sua Lâmina de Chakra fortalecida pela Andorinha Voadora. Em seguida, Shikamaru utiliza sua Técnica do Puxão pela Sombra para puxar seus companheiros de equipe, permitindo a Izumo e Kotetsu atacar o oponente mais uma vez, criando um pilar de água, que endurece após o golpe, seguindo com Asuma, cortando através do pilar e do oponente.", "Todos os alcances", null, null, "木ノ葉定石の陣このはじょうせきのじん", "Konoha Jōseki no Jin", "Formação Tática da Folha", "Formação Padrão da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Manji", "Esta é uma formação muito básica que é usada para proteger um ponto central, para se defender contra ataques vindos de localizações múltiplas, ou para defender um grupo contra um ataque que pode vir de qualquer ângulo desconhecido. Seu nome deriva da forma de um manji, 卍, como a formação requer uma pessoa voltada para fora em todos os quatro pontos, onde o ninja fica de costas um para os outros. Cada pessoa é responsável por proteger suas costas a área em frente deles e para a esquerda, como o manji sugere. Esta formação tem um ponto cego: o centro.", "Curto alcance", null, null, "卍の陣まんじのじん", "Manji no Jin", "Formação Manji", null, null, "Formação de Batalha Manji");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação Uzumaki", "Naruto cria doze clones das sombras, cada um armado com uma kunai, formando um círculo defensivo, com Naruto no meio, tudo preparado para o inimigo atacar. Quando o inimigo se move, os clones de Naruto jogam a kunai no inimigo.", "Médio alcance", null, null, "うずまきの陣うずまきのじん", "Uzumaki no Jin", "Formação do Redemoinho", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação da Areia Extensa de Cegueira: Ruído", "O usuário espalha várias partículas de areia em uma área definida, impedindo a habilidade do inimigo de sensorear alvos. No entanto, esta técnica aparenta funcionar apenas em alvos que estão focados no usuário da técnica.", "Todos os alcances", null, null, "盲漠砂陣・濃偉砂", "Mōbaku Sajin: Noizu", "Formação da Areia Extensa de Cegueira: Grande Areia Espessa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação da Areia Polarizada: Camuflagem de Areia Quente", "O usuário infunde chakra colocando ambas as mãos no solo, fazendo com que partículas de areia polarizem, o que permite o dobramento da luz. As partículas partem na direção dos companheiros do usuário, cobrindo e tornando-os invisíveis e muito mais difíceis de detectar, com exceção de um shinobi tipo sensor. A técnica aparenta exigir uma grande quantidade de tempo para aperfeiçoá-la para poder acumular chakra.", "Curto a Médio alcance", null, "Cobra", "偏光砂陣・熱砂迷彩", "Henkō Sajin: Nessa Meisai", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação da Chama Uchiha", "Depois de manifestar pequenas chamas em cada dedo de uma das mãos, o usuário, em seguida, bate a palma da mão no chão, resultando na criação de uma barreira cilíndrica envolvendo um local de sua escolha, protegendo qualquer coisa contida dentro e queimando tudo o que entra em contato com o exterior da barreira.", "Longo alcance", "Rank B", null, "うちは火炎陣", "Uchiha Kaenjin", "Formação da Chama Uchiha", null, "Círculo de Fogo dos Uchiha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação da Shuriken Uchiha: Relâmpago da Primavera", "Sarada envia um fluxo de fogo que joga o oponente para o alto. Ela então salta para cima da nuvem de fumaça e, com seu Sharingan, desfere um ataque com várias shuriken que recocheteiam umas nas outras e acertam o alvo. Para finalizar, ela acumula chakra infundido com a natureza relâmpago em seu punho e usa o Impacto da Flor de Cerejeira em seu alvo, que o manda para o chão, causando grande destruição no impacto.", "Curto alcance", null, null, "うちは手裏剣陣・春雷うちはしゅりけんじん・しゅんらい", "Uchiha Shurikenjin: Shunrai", "Formação da Shuriken Uchiha: Relâmpago da Primavera", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação das Cordas de Luz", "Esta barreira é projetada para impedir que alguém saia de seus limites. Uma vez dentro de um determinado intervalo da pessoa, o usuário ergue a barreira sob os pés tornando qualquer pessoa dentro dos selos incapaz de escapar. Apesar de sua força, Naruto Uzumaki foi capaz de dominá-la facilmente com o seu Modo de Chakra do Nove-Caudas.", "Curto alcance", null, "Restringir: Selo do Confronto", "一糸灯陣いっしんとうじん", "Isshi Tōjin", "Formação das Cordas de Luz", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação das Múltiplas Cordas de Luz", "Ao ter mais de uma pessoa usando a Formação das Cordas de Luz, os efeitos da técnica são amplificadas, permitindo, assim, mesmo usuários mais fracos, capturar com sucesso um adversário mais forte se tiverem usuários suficientes.", "Curto alcance", null, null, "多重一糸灯陣たじゅういっしとうじん", "Tajū Isshi Tōjin", "Formação de Múltiplas Cordas de Luz", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação das Quatro Chamas Violeta", "Uma sólida e forte formação de barreira, realizada por quatro shinobi. Consiste em uma formação quadrada, os usuários estão coberto de todos os lados. Os rostos da barreira são feitas de chamas roxas. A barreira não permite a ninguém entrar ou sair enquanto o ninja está ileso. O corpo de alguém tocá-la será imediatamente envolvido em chamas. Além disso, não há nenhuma maneira de destruir a formação de dentro da barreira, uma vez que as quatro pessoas têm também uma barreira no interior protegendo-os. Sair é extremamente difícil.", "Médio alcance", "Rank B", "Cobra → Bater as maos", "四紫炎陣", "Shishienjin", "Acampamento de Batalha das Quatro Chamas Violeta", null, "Paredes das Chamas Púrpuras", "Arte Ninja: Formação Quatro Chamas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação de Areia: Shisa", "O usuário gera uma ou múltiplas criaturas em forma de leão para atacar ou dominar um alvo. Elas aparentemente se dispersam facilmente se forem atingidas forte o suficiente.", "Curto a Médio alcance", null, "Cobra → Cao → Carneiro", "砂陣・止威砂", "Sajin: Shīsā", "Formação de Areia: Autoridade da Areia Supressora", null, null, "Formação de Areia: Shisa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação de Avalanche de Pedras", "Vários shinobi entram em uma formação em que estão de pé sobre os ombros uns dos outros, e em seguida, avançam em direção ao inimigo, isolando os adversários para que eles não possam escapar.", "Curto alcance", null, null, "岩雪崩の陣", "Iwa Nadare no Jin", "Formação de Avalanche de Pedras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação de Barricada Canina", "Com a Formação de Barricada Canina (犬壁の陣, Inukabe no Jin), o usuário salta para dentro de um circulo de ar feito por outro usuário. Isto forma um bloqueio impedindo a visão dos oponentes, e também lhes permite manobrar no ar.", null, null, null, "犬壁の陣", "Inukabe no Jin", "Formação de Barricada Canina", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação de Dez Mil Cobras", "Com esta técnica, um incontável número de cobras invocadas rasteja para fora da boca de Orochimaru e caça o inimigo. Com tal número esmagador, um verdadeiro 'muro de cobras' é formado, fazendo defesa e evasão inefetivos, e além disso as cobras se tornam uma parede defensiva e podem bloquear praticamente qualquer ataque frontal. O inimigo não pode fazer nada, além de ser sepultado pelas cobras. Elas podem morder o inimigo com suas presas venenosas e abrir a boca para estender lâminas como a Kusanagi, tornando-as ainda mais mortais.", "Todos os alcances", "Rank B", null, "万蛇羅ノ陣", "Mandara no Jin", "Formação de Dez Mil Cobras Sedosas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Círculo Explosivo", "Hanzō executa um giro com a sua Kusarigama, liberando um círculo de papéis explosivos no processo, o qual, em seguida, explode no impacto com um alvo.", "Curto alcance", null, null, "爆円陣", "Bakuenjin", "Formação do Círculo Explodindo", "Circulo Explosivo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Eixo", "O usuário envia seus insetos para atacar o alvo em um movimento em espiral, a fim de parar o inimigo de retaliação.", "Curto a Médio alcance", null, null, "紡錘の陣ぼうすいのじん", "Bōsui no Jin", "Formação do Eixo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Furacão Inigualável", "Sasuke chuta o oponente. Ele, então, corre para trás e soca o adversário. Em seguida, ele salta no ar e executa um pontapé de machado. Ele, então, corre atrás do oponente novamente e varre para fora de seus pés, e vira-o para cima chutando o adversário. Ele, então, salta mais uma vez, desta vez por atrás do oponente, e dá um soco. Por fim, ele chuta o oponente nas costas com o calcanhar esquerdo fazendo o adversário cair no chão.", "Curto alcance", null, null, "無双疾風陣", "Musō Shippūjin", "Formação do Furacão Inigualável", "Formação do Furacão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Grande Vento Inigualável", "Enquanto em sua forma do selo amaldiçoado nível 2, Sasuke chuta para cima, lançando o oponente no ar. Ele, então, desliza atrás dele e executa outro chute lançando o adversário para bater no chão, em seguida, termina com um Chidori.", "Curto alcance", null, null, "無双豪風陣むそうごうふうじん", "Musō Gōfūjin", "Formação do Grande Vento Inigualável", "Formação do Ultra Vendaval", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Mecanismo de Oito Trigramas", "Uma técnica usada por Konohagakure para prender o inimigo em um labirinto de muros altos. O usuário bate um tambor gigante que faz com que inúmeras paredes aparecem ao redor da aldeia, encurralando o inimigo. Além disso, se tentar escapar escalando as paredes, armadilhas líquidas farão o trabalho da referida fuga.", null, null, null, "からくり八卦の陣", "Karakuri Hakke no Jin", "Formação do Mecanismo de Oito Trigramas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Quadrado Kagura", "Karin se envolve em uma esfera luminosa de tom rosado a fim de imobilizar seu inimigo ou de se defender. Sendo assim, ela abre uma brecha para lançar múltiplas senbon no oponente.", "Curto a Médio alcance", null, null, "神 楽 方陣", "Kagura Hōjin", "Formação Divina do Entretenimento Quadrado", "Barreira Kagura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação do Redemoinho do Pavão", "Esta é uma técnica que utiliza a especial Espadas Curtas Voadoras Crescentes da Vila Takumi, para criar um vórtice que afasta todos materiais que prendem o corpo do utilizador, libertando-o. Seimei usou esta técnica para libertar-se da areia de Gaara.", null, null, null, "孔雀旋風陣", "Kujaku Senpūjin", "Formação do Furação Kujaku", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação dos Oito Portões Liberados", "O usuário abre todos os Oito Portões simultaneamente, temporariamente ganhando poderes centenas de vezes mais forte do que no seu estado normal, mas morrerá por causa do dano causado ao seu corpo. Isto é foi visto quando Might Guy abriu todos os oito portões e luta com igualdade e gradualmente superando Madara Uchiha — o jinchūriki do Dez-Caudas — fazendo Madara comparar a sua luta contra Guy com a sua luta contra Hashirama Senju no Vale do Fim.", null, "Rank A", null, "八門遁甲の陣はちもんとんこうのじん", "Hachimon Tonkō no Jin", "Formação dos Oito Portões Liberados", "Formação Oito Portões Interiores", null, "Formação dos Oito Portões Internos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Formação dos Quatro Nevoeiros Pretos", "Um ninjutsu de barreira, realizado por todos do Quarteto do Som, usado durante a 'morte' temporária daqueles que forçam o avanço do seu selo amaldiçoado ao seu segundo nível. A técnica sela-os em um caixão enquanto o processo ocorre, e nem sequer permite que o Byakugan veja o interior. O Método de Selamento Escuro pode ser usado para uma camada adicional de proteção. O selo parece ser naturalmente deslocado quando o selo amaldiçoado avança para o nível dois.", null, "Rank B", null, "四黒霧陣しこくみじん", "Shikokumujin", "Formação dos Quatro Nevoeiros Pretos", null, "Muralha dos Quatro Nevoeiros Negros", "Formação das Quatro Névoas Negras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Forte Colapso da Árvore Gigante", "Yamato cria um grande pilar de madeira e Sakura dá um soco no pilar de madeira que cai em cima do oponente.", null, null, null, "巨木剛力落とし", "Kyoki Gōriki Otoshi", "Forte Colapso da Árvore Gigante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Frenesi", "Obito, tomado pelo delírio, segura no rosto do oponente e derruba-o violentamente contra o chão. Quando o inimigo está caído, Obito monta em cima de seu corpo e desfere dois poderosos socos em sequência e finaliza com um golpe descendente com as duas mãos juntas, como um golpe martelo.", "Curto alcance", null, null, "狂乱", "Kyōran", "Frenesi", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Frequência de Transmissão do Ruído de Vento", "Usando esta técnica, Fū é capaz de criar uma frequência de onda sonora que permite à ela se comunicar com seus aliados. Quando em uso, a frequência cria um som semelhante a uma tempestade.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fukuwajutsu", null, null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral Explosivo de Céu e Terra", "Obito torna-se intangível e corre em direção ao oponente, atravessando-o e deixando um rastro de papéis explosivos para trás dele, que detonam quando ele atravessa o inimigo.", "Curto a Médio alcance", null, null, "天地爆葬", "Tenchi Bakusō", "Funeral Explosivo de Céu e Terra", "Ataúde Explosiva de Céu e Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral Giratório", "Obito absorve seu oponente e lança-o no meio de múltiplos papéis explosivos, que o detona em seguida.", "Curto alcance", null, null, "転葬てんそう", "Tensō", "Funeral Giratório", "Enterro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral da Serpente Oculta na Sombra", "Aproveitando-se de sua forma esguia, o usuário enterra uma parte de seu corpo no solo e, em seguida, desfere um ataque surpresa ou captura o inimigo. Por não ter a forma esguia e super alongada, um usuário humano pode se alongar para o mesmo efeito, a fim de sepultar o oponente.", "Curto alcance", null, null, "潜影蛇葬せんえいじゃそう", "Sen'ei Jasō", "Funeral da Serpente Oculta na Sombra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral de Areia de Ferro", "O usuário envia sua Areia de Ferro até o inimigo para envolvê-lo e esmagá-lo em seguida, algo muito difícil de escapar. No entanto, se o oponente estiver preparado, ele pode usar clones a fim de evitar ser pego ou, se ele tiver resistência o suficiente, consegue quebrar o envólucro com sua força.", "Curto alcance", null, null, "砂鉄送葬さてつそうそう", "Satetsu Sōsō", "Funeral de Areia de Ferro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral do Deserto", "O Funeral do Deserto é uma técnica assinatura de Gaara, sendo também uma variação de suas várias outras versões destrutivas.", "Curto a Médio alcance", "Rank –", null, "砂瀑送葬さばくそうそう", "Sabaku Sōsō", "Funeral do Deserto", null, null, "Enterro de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral do Deserto Destrutivo", "Gaara cria um zigue-zague de areia sobre o oponente. O zigue-zague então forma o rosto de Shukaku que devora e joga o oponente á 200 metros de distância. Gaara então coloca a mão no chão e a areia faz um impacto com tanta força no chão que cria uma cratera com 200 metros de profundeza, levando o oponente para o nada.", "Todos os alcances", null, null, "滅砂瀑葬", "Messabakusō", "Funeral do Deserto Destrutivo", "Destruição da Cachoeira de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral do Deserto Gigante", "Gaara faz com que a areia suba do chão e circunde seu alvo, deixando-o no centro da técnica sem escapatórias. Gaara, então, faz com que essa areia suba ao céu e caia bruscamente contra o oponente, esmagando-o e se comprimindo no processo.", "Médio a Longo alcance", null, null, "巨大砂瀑送葬", "Kyodai Sabaku Sōsō", "Funeral do Deserto Gigante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral do Deserto Quente", "Gaara enquanto na forma de Shukaku ataca o oponente, fazendo-o ir para o ar, Gaara pega e joga o oponente de volta no chão esmagando-o ao mesmo tempo com areia.", "Curto alcance", null, null, "熱砂瀑葬", "Nessabakusō", "Funeral do Deserto Quente", "Enterro da Areia Quente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Técnica Riacho de Rocha Escaldante", "O usuário expele uma grande quantidade de lava da sua boca, que rapidamente se solidifica em múltiplos pedregulhos de rocha derretida que são disparados em direção ao alvo com uma força tremenda. Devido à sua composição, os pedregulhos podem derreter a maioria dos objetos com facilidade, e já que um número considerável é disperso em uma ampla área em rápida sucessão, eles também são difíceis de evitar.", "Médio alcance", null, "Cao → Boi → Coelho → Cobra", "熔遁・灼河流岩の術ようとん・しゃくがりゅうがんのじゅつ", "Yōton: Shakugaryūgan no Jutsu", "Liberação de Lava: Técnica Riacho de Rocha Escaldante", "Estilo Corrosão: Jutsu das Rochas Escaldantes", "Estilo da Lava: Técnica do Rio de Rochas Flamejantes", null);
