INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fūinjutsu Supressor de Chakra", "O usuário cria um selo nas costas de um alvo, representado pelo kanji 封 (Fū; literalmente significa: Selo) dentro de um círculo, a fim de suprimir seu chakra, evitando assim que ele seja roubado ou absorvido. No entanto, esta técnica deixa o receptáculo do selo incapaz de usar o próprio chakra, forçando-o a usar apenas técnicas que não necessitam do mesmo, tais como alguns taijutsu e kenjutsu.", "Curto alcance", null, null, null, null, null, null, null, null);
