INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral Imperial de Pó de Ouro", "Usando seu pó de ouro, Rasa cria envolve seu oponente em uma pequena duna do material. Logo então ele bate as mãos sobre o pó, esmagando seu oponente restringido.", "Todos os alcances", null, null, "砂金大葬さきんたいそう", "Sakin Taisō", "Funeral Imperial de Pó de Ouro", "Grande Sepultamento de Pó de Ouro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Funeral Trovejante: Banquete do Relâmpago", "O usuário cria várias descargas elétricas que cortam através do chão ou ar até acertar o oponente. Seus efeitos podem ser melhorados através do uso das espadas gêmeas.", "Médio a Longo alcance", null, "Coelho → Javali → Carneiro[1]", "雷葬・雷の宴らいそう・いかずちのうたげ", "Raisō: Ikazuchi no Utage", "Funeral Trovejante: Banquete do Relâmpago", null, null, "Enterro Relâmpago: Banquete do Trovão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Furacão Forte da Folha", "Um taijutsu que usa uma combinação de velocidade e poder. A essência desta técnica está concentrada em um chute giratório, com tal velocidade que uma única pessoa não pode acompanhar o movimento, esmagando o inimigo com uma força avassaladora.", "Curto alcance", "Rank B", null, "木ノ葉剛力旋風", "Konoha Gōriki Senpū", "Furacão Forte da Folha", null, "Poderoso Furacão da Folha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Furacão da Folha", "Um taijutsu onde se inicia uma sucessão de chutes altos e baixos. Ao ver o chute alto, o adversário se abaixa para se esquivar dele, aumentando, assim, as chances de o chute baixo acertá-lo. Então, originalmente, o chute alto não se passa de uma distração para abrir uma brecha.", "Curto alcance", "Rank D", null, "木ノ葉旋風このはせんぷう", "Konoha Senpū", "Furacão da Folha", null, "Furacões da Folha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Furacão da Lâmina de Vento", "Baki cria um furacão gigante usando a sua Lâmina de Vento que puxa o inimigo para o furacão, violentamente cortando ele.", "Curto Alcance", null, null, null, null, "Furacão da Lâmina de Vento", "Estilo Vento: Elevação do Dragão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Furacão de Takigakure", "Fū acerta seu alvo por cima com uma força poderosa. Sua força flui em torno de si mesma, se assemelhando a um furacão. Esta técnica foi mostrada sendo suficientemente forte para derrotar um gigante escorpião resistente.", "Curto alcance", null, null, "滝隠れの旋風", "Takigakure no Senpū", "Furacão de Takigakure", null, null, "Furacão da Cachoeira Oculta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Furacão de Trovoada", "O Grupo Magaki executa os selos de mão necessários para a técnica e estendem sua mão, revelando os kanji que compõem o nome da técnica em suas palmas e, a partir deles, emitem um chakra rosa, que toma a forma de duas linhas, que se conectam com o parceiro à direita e no topo com todas as outras, formando uma pirâmide que encurrala e imobiliza o oponente. Em seguida, eles lembram mais chakra e explodem a barreira, injuriando ou matando a pessoa dentro dela.", null, null, "Macaco → Passaro → Cachorro → Javali", "疾風迅雷", "Shippū Jinrai", "Furacão de Trovoada", null, null, "Shifū Jinrai");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Furacão do Pavão", "Utilizando as especiais Espadas Curtas Voadoras Crescentes da Vila Takumi, o usuário gira uma das armas do par para criar uma poderosa rajada de vento rodopiante.", "Curto a Médio alcance", null, null, "孔雀旋風", "Kujaku Senpū", "Furacão do Pavão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fúria", "Fúria (怒髪天, Dohatsuten) é uma técnica que pode criar uma enorme explosão, capaz de destruir uma vila inteira.", "Todos os alcances", null, "Javali → Cao → Cobra ou (Carneiro → Cavalo → Dragão → Passaro → Cobra)[1]", "怒髪天どはつてん", "Dohatsuten", "Fúria", null, null, "Jutsu da Fúria");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fúria Frenética", "Kinshiki manifesta uma clava de chakra com a qual ele golpeia seu oponente no estômago para atirá-lo para o alto e, em alta velocidade, ele o alcança em pleno ar e o golpeia com socos-ingleses, em seguida ele amarra com uma corrente para puxá-lo contra o chão e o puxa de volta para o alto antes de atirar um kamayari para perfurar seu alvo. Para finalizar, Kinshiki surge acima de seu oponente ainda no ar e desfere um poderoso golpe com uma marreta que o atira contra o chão, criando uma grande cratera no processo, então ele cai golpeando o solo com um machado, provocando um rasgo que corta o céu e a terra.", "Curto alcance", null, null, null, null, null, "Fúria Frenética", null, null);
