INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fórmula Explosiva da Sombra Oculta", "Shikamaru crava uma kunai especial no chão que, quando o oponente encosta nela, ele é jogado pra o alto. No ar, ele é atingido por uma série de outras kunai, com a ultima delas estando anexada com um papel explosivo.", null, null, null, "爆式影潜", "Bakushiki Eisen", "Fórmula Explosiva da Sombra Oculta", "Estilo Explosivo da Sombra Oculta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fórmula da Técnica de Espaço-Tempo: Virada do Submundo", "A técnica projetada por Orochimaru é utilizada ao liberar o ódio dentro dos selos amaldiçoados do Quarteto do Som reencarnado, para ser usado com o único propósito de vingança. O selo ativa ao redor do usuário, prendendo qualquer um dentro de seu alcance. As almas de ambos os alvos e os usuários são separadas de seus corpos e confinadas a uma barreira especial. Ninja sensor e até mesmo o Byakugan não pode sentir e ver através desta técnica, respectivamente. Os alvos são deixados em um estado semelhante à animação suspensa, ainda vivos, embora com grande dificuldade, grande parte de seu fluxo de chakra pára. Para restaurar as almas presas aos seus corpos, é necessário tocá-los com chakra. Inoichi Yamanaka compara a um cancelamento de genjutsu. Dentro da barreira, as almas daqueles presos ainda sentem-se como eles, e são capazes de usar todas as técnicas que eles conhecem. Mesmo que os usuários desta técnica sejam derrotados, ela não vai acabar. Como tal, é só uma questão de tempo antes que a vida daqueles que estão presos pela barreira termine, já que seus corpos físicos acabarão por morrer. No entanto, ele sacrifica a vida dos usuários de modo que os alvos podem ser presos e a maldade dos usuários não é bloqueada pela barreira para um ninja que pode perceber a má vontade pode localizar a barreira e quebrá-la, libertando as almas presas no interior, enquanto que o usuários desaparecem.", "Curto alcance", null, null, "時空術式・黄泉転身じくうじゅつしき・よみてんしん", "Jikūjutsu Shiki: Yomi Tenshin", "Técnica da Formula do Espaço-Tempo: Virada do Submundo", null, null, "Técnica da Formula de Transportação: Jutsu de Viagem ao Submundos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fórmula de Invocação", "A Fórmula de Invocação é um Ninjutsu Espaço-Tempo que permite teletransportar grandes grupos de pessoas através de longas distâncias em um curto período de tempo.", "Todos os alcances", null, null, "口寄せの式くちよせのしき", "Kuchiyose no Shiki", "Fórmula de Invocação", null, null, null);
