INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fênix Gentil dos Punhos de Leões Gêmeos Espirais", "Naruto, enquanto em seu Modo Besta com Cauda, transfere o seu chakra para Hinata, que lança um Oito Trigramas Palma de Vácuo no inimigo para desequilibrá-lo, antes de pegar na mão de Naruto. Ele transfere o seu chakra para ela novamente e eles decolam sob a forma de um pássaro, onde Hinata cria um Passo Gentil dos Punhos de Leões Gêmeos e Naruto forma um Rasengan dentro de suas mandíbulas acertando-o no adversário. O inimigo então é detonado na explosão de uma cúpula gigante, Naruto e Hinata aparecem depois.", "Todos os alcances", null, null, "柔鳳螺旋双獅拳", "Jūhō Rasen Sōshiken", "Fênix Gentil dos Punhos de Leões Gêmeos Espirais", "Rasen Punhos de Leões Gêmeos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Fênix Noturna", "Guy é coberto por chamas, em uma forma específica que se assemelha a uma Fênix. Quando o movimento atinge, o oponente entra em chamas.", "Curto alcance", null, null, "夜鳳凰", "Yoruhōō", "Fênix Noturna", "Yoruho'o", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garoa de Areia", "Após a coagulação juntando a areia em incontáveis ​​pedaços pequenos, Gaara usa-os para atacar um inimigo de todas as direções. Porque todos os grumos de areia são controlados por chakra, é possível iniciar um ataque, sem quaisquer ângulos mortos. Esta técnica é frequentemente usada como isca na batalha, distraindo um oponente, deixando-o vulnerável ​​a um ataque de baixo. Para que isso funcione, o inimigo é forçado primeiro a olhar para cima com a chuva de areia, simultaneamente trazendo seu campo de atividades sob controle do usuário. Em seguida, uma jogada ousada é feita a partir de debaixo dos pés. Para garantir que a chuva atinja seu alvo, pode ser combinado com o Caixão de Areia para imobilizar o alvo.", "Todos os alcances", "Rank B", null, "砂時雨すなしぐれ", "Suna Shigure", "Garoa de Areia", null, "Chuva de Areia", "Chuva de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garoa de Papel", "Depois de criar asas de papel, Konan dispara vários papéis contra o inimigo em alta velocidade. Devido a isso, os papéis podem criar uma pequena explosão ao colidir com outra técnica, suficiente para abafar mesmo a Liberação de Fogo: Projétil de Chamas de Jiraiya.", "Médio a Longo alcance", null, null, "紙時雨かみしぐれ", "Kami Shigure", "Garoa de Papel", "Chuva de Papel", null, null);
