INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra Veloz da Presa do Lobo", "Kiba e Akamaru rapidamente partem subitamente para o arredor do oponente e o corta antes de saltar para a frente em lados opostos para uma passagem combinada de mordidas e impactos.", null, null, null, "爪迅狼牙", "Sōjin Rōga", "Garra Veloz da Presa do Lobo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra da Nove-Caudas", "A Garra da Nove-Caudas é acessivel através da transformação física proporcionada pelo chakra de Kurama. O usuário manifesta garras afiadas com as quais ele pode desferir ataques cortantes, permitindo retalhar seus inimigos. Quando revestidas com chakra, as garras tem um poder ofensivo ainda mais letal, permitindo arrancar membros e destruir barreiras com relativa facilidade.", "Curto alcance", null, null, "九尾の爪", "Kyūbi no Tsume", "Garra da Nove-Caudas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra de Chakra do Gato", "Esta técnica emprega um tipo de fluxo de chakra exclusivo para os ninneko. Ele aumenta o alcance e melhora o poder de ataque das garras do gato a tal ponto que ele corta facilmente através de metais.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra de Ferro", "Este movimento implica simplesmente em apertar o rosto do alvo com a mão. A usa essa técnica quando precisa punir Killer B. Gyūki tem visto A fazer isso em B várias vezes ao ponto que o mesmo menciona que A daria a garra de ferro em B se ele ajudasse Naruto na fuga de Genbu.", "Curto alcance", null, null, "アイアンクロー", "Aian Kurō", "Garra de Ferro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra de Gato", "Enquanto em seu estado transformado, Yugito corta o oponente com um golpe poderoso e rápido, usando as unhas estendidas em suas mãos.", "Curto alcance", null, null, "猫爪", "Nekozume", "Garra de Gato", null, "Presas Felinas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Garra de Shukaku", "Após Gaara ter caído no sono e ter sido possuído totalmente por Shukaku, o monstro de areia é capaz de utilizar suas garras no solo, desferindo cortes no oponente e no solo, liberando um fluxo de areia que acompanha seu braço.", "Curto alcance", null, null, "守鶴の爪", "Shukaku no Tsume", "Garra de Shukaku", null, null, null);
