INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu Nebuloso", "Esta técnica lança uma ilusão sobre o inimigo que borra a visão do alvo de toda a área. Além disso, ela faz com que a vítima tenha a sensação de estar afundando em areia movediça, efetivamente imobilizando o alvo.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu Programado", "Com este genjutsu, o usuário é capaz de programar uma rota e um conjunto de ações na mente da vítima, tornando-a vulnerável e agindo tal qual uma marionete. Dessa forma, a vítima pode servir como um chamariz para atrair seus aliados para uma emboscada, bem como um lutador inesperado.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Aprisionamento da Árvore Assassina da Andorinha Voadora", "Trata-se de um ataque de duas vertentes em que Kurenai primeiro envolve o adversário em um genjutsu, aprisionando-o em árvores e então Asuma, usando as suas Lâminas de Chakra, o finaliza com a sua Andorinha Voadora.", "Curto Alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Colina de Flores", "O usuário convoca flores roxas ilusórias do chão na frente dele, o que causa dano nos inimigos com chance de paralisá-los.", "Curto alcance", null, null, null, null, "Técnica de Ilusão: Colina de Flores", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Deslizamento de Pedra", "Esta técnica cria uma ilusão em uma quantidade considerável de alvos, acreditando que várias explosões estão ocorrendo e a partir disto, causando destruições em massa. A maioria das vítimas pegas nesse genjutsu mostrou entrar em crise, desorientando-se e causando pânico por um período variante, dependendo do emocional de quem foi pego.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Fogo Desconhecido", "Este genjutsu é lançado apontando para o oponente e uma onda roxa desbotada é liberada do dedo que quando atinge o alvo, provoca-o a ver um anel de fogo azul em torno dele e, em seguida, vê-se inflamar com chamas azuis.", null, null, null, "幻術・不知火げんじゅつ・しらぬい", "Genjutsu: Shiranui", "Técnica de Ilusão: Fogo Desconhecido", "Genjutsu Shiranui", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Fuga de Pétala de Flor", "Quando presa dentro de um genjutsu ou deseja parecer presa, Kurenai usa essa técnica para dissolver seu corpo em um monte de pétalas de flores, que então consomem o corpo de seu inimigo.", "Curto alcance", null, "Tigre → Javali → Tigre → Cavalo → Boi", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Ketsuryūgan", "Trata-se de uma aplicação de genjutsu inespecífico por meio do uso do Ketsuryūgan. Por meio desta técnica, é possível manter um oponente preso numa ilusão, podendo causar-lhe danos reais, como asfixia. A força deste genjutsu é suficiente para manter um membro do clã Uchiha preso por um determinado tempo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Genjutsu: Miragem", "Kurenai se levanta em linha reta com a cabeça baixa e as mãos no peito. Ela então, pisa no chão, criando diversas flores de rosas que a cercam, e criando uma enorme área de névoa. O oponente fica então confuso quanto ao local onde Kurenai está. De repente, o oponente é perfurado para trás e ao tempo que o oponente vira-se para trás, Kurenai aparece na frente dele e lança o oponente no ar.", "Curto alcance", null, null, "幻術・蜃気楼げんじゅつ・しんきろう", "Genjutsu: Shinkirō", "Técnica Ilusória: Miragem", "Genjutsu: Miragem", null, null);
