INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Combo de Palma dos Hyūga", "Hanabi, Hinata e Neji iniciam desferindo uma série de golpes no adversário enquanto o levam para o alto e, antes que ele caia no chão, eles usam o Oito Trigramas Palma de Vácuo mandando-o para longe. Em seguida, Hiashi aparece para a surpresa dos outros três, e logo ele usa o Oito Trigramas Palma da Parede de Vácuo. Com isso, os quatro ativam seu Byakugan e utilizam um cooperativo Oito Trigramas Sessenta e Quatro Palmas. Neji e Hiashi usam o Oito Trigramas Palmas Giratórias do Céu com seu alvo entre eles, que é mandado para o alto, onde é novamente golpeado por Hinata e Hanabi. Logo os quatro concentram chakra em suas palmas e golpeiam o alvo simultaneamente, mandando-o voando por vários metros.", "Curto alcance", null, null, "日向大連掌ひゅうがだいれんしょう", "Hyūga Dairenshō", "Grande Combo de Palma dos Hyūga", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Céu Violento da Folha", "Rock Lee ataca o oponente e soca-o para o céu, uma vez nele, Lee realiza um ataque girando de cabeça para baixo e chuta seu oponente no chão. Finalmente, ele levanta elevando os braços e trazendo as pernas junto.", "Curto alcance", null, null, "木ノ葉大烈空", "Konoha Dairetsukū", "Grande Céu Violento da Folha", "Queimando o Céu da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Dança de Decapitação", "Kisame salta no ar e, enquanto desce em giros, corta o oponente com a Samehada.", "Curto alcance", null, null, "大舞斬りおおまいぎり", "Ōmaigiri", "Grande Dança de Decapitação", "Talho da Dança Gigante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Devorador de Areia", "Kaguya manipula o terreno de sua dimensão de deserto para criar uma onda de areia que avança em direção ao seu oponente.", "Curto a Médio alcance", null, null, null, null, null, "Grande Devorador de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Divisão da Lesma", "Por causa do corpo desossado de Katsuyu, ela tem a capacidade de dividir todo o seu corpo em lesmas menores, e reuni-lo à vontade. Esta técnica faz com que seja impossível capturar Katsuyu. Todos os ataques físicos sem corte também são anulados antes desta técnica, pois não importa o quão violento é o ataque, não será mais do que uma tentativa inútil. Qualquer inimigo que atacar só vai esgotar-se. Katsuyu pode dividir para outros fins, tais como canalizar chakra do seu invocador em um shinobi com uma das lesmas sobre eles para curar ferimentos.", null, null, null, "蛞蝓大分裂かつゆだいぶんれつ", "Katsuyu Daibunretsu", "Grande Divisão da Lesma", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Entrada do Ardente Espírito", "Guy primeiro abre sete dos Oito Portões, e então chuta o oponente para o alto. Ele então, vai na direção do seu oponente chutando o ar e acerta um poderoso soco de chakra para enviá-lo de volta para baixo.", "Curto alcance", null, null, "駆け上がる熱き魂", "Kake Agaru Atsuki Tamashii", "Grande Entrada do Ardente Espírito", "Precipitação do Espírito Ardente", null, null);
