INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Erupção do Rio Escaldante", "Rōshi agarra seu oponente e dá um grande salto, transformando-se em Son Gokū. Em seguida, ele coloca o seu adversário entre os punhos revestidos de lava, batendo-os no chão. Quando ele levanta os braços, o chão debaixo dele estoura.", "Curto alcance", null, null, "灼河大噴火しゃくかだいふんか", "Shakuka Daifunka", "Grande Erupção do Rio Escaldante", "Grande Erupção Ardente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Esfera de Fogo Espiral", "Naruto cria um Rasengan, e Sasuke em seguida, usa a Liberação de Fogo: Técnica da Grande Bola de Fogo para ele. Naruto então desata o Rasengan coberto de chamas para no oponente, que detona em uma explosão de fogo.", null, null, null, "螺旋豪火丸", "Rasen Gōkagan", "Grande Esfera de Fogo Espiral", "Rasengan de Tempestade de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Explosão da Bola Explosiva", "Konohamaru cria um clone da sombra que pula sobre o oponente, derrubando-o. Em seguida, Konohamaru pega duas bolas de papéis explosivos e lança-as em direção ao oponente, causando uma explosão ao entrar em contato.", "Curto alcance", null, null, "起爆玉大爆発", "Kibakutama Daibakuhatsu", "Grande Explosão da Bola Explosiva", "Grande Explosão da Esfera de Bomba", null, null);
