INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Flash da Folha", "Após distrair o oponente com diversos golpes, o usuário salta e desencadeia um poderoso chute lateral com objetivo de acertar a cabeça, lançando o adversário para longe.", "Curto alcance", null, null, "木ノ葉大閃光このはだいせんこう", "Konoha Daisenkō", "Grande Flash da Folha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Flutuação", "Ao balançar suas asas, Garuda é capaz de criar um enorme tornado de vento capaz de dilacerar vários oponentes de uma única vez.", "Médio a Longo alcance", null, null, "浮く空大うくそらだい", "Ukusoradai", "Grande Flutuação", "Grande Furacão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Funeral do Deserto", "A Grande Funeral do Deserto é um ninjutsu utilizado por Gaara. A técnica tem funções semelhantes ao seu Funeral do Deserto, exceto em uma escala muito maior.", "Todos os alcances", null, null, "砂瀑大葬さばくたいそう", "Sabaku Taisō", "Grande Funeral do Deserto", null, "Enterro na Cachoeira de Areia", "Enterro de Areia Gigante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Furacão da Folha", "Esta técnica é uma série de ataques de chutes, começando com um chute baixo, ligado em um chute de médio e chute alto e depois terminando com uma queda de calcanhar. Com a velocidade da rotação o usuário ganha flutuabilidade, o que eles usam para mudar para ataques gradualmente mais altos.", "Curto alcance", "Rank C", null, "木ノ葉大旋風", "Konoha Daisenpū", "Grande Furacão da Folha", null, "Grande Furacão da Folha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Limpeza", "O Dez-Caudas bate seu braço no chão e, em seguida, ele vem fazendo uma varredura no solo, levando junto o oponente.", "Todos os alcances", null, null, "巨拭", "Kyoshoku", "Grande Limpeza", "Grande Varredura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Martelo de Ferro", "Enquanto controla o poder de sua besta com cauda, o jinchūriki manifesta dois grandes braços criados a partir de si, seja um par de braços de chakra ou manifestando os braços em si, que ele une através das mãos e golpeia seu oponente com um movimento descendente.", "Curto alcance", null, null, "大鉄槌", "Daitettsui", "Grande Martelo de Ferro", "Martelo Gigante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Mordida de Kurama", "Naruto entra em seu Modo de Chakra do Nove-Caudas e manifesta a cabeça de Kurama, que ele usa para morder e agarrar objetos.", "Curto alcance", null, null, "九喇嘛大咬くらまだいこう", "Kurama Daikō", "Grande Mordida de Kurama", "Mordida de Kurama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Queda do Pontapé Giratório", "O usuário ataca novamente com um movimento conhecido do sumô japonês, salta no ar e acerta um fatal pontapé giratório em sua pré-finalização. Observando que o oponente se encontra ativo em batalha, ele se atira contra o oponente e termina com um outro pontapé giratório, em forma de 'estrelinha', que faz o oponente e quem mais estiver em volta ficar caído no chão atordoado.", "Curto alcance", null, null, "大回転浴びせ蹴りだいかいてんあびせけり", "Daikaiten Abisegeri", "Grande Queda do Pontapé Giratório", "Grande Pontapé Giratório", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Rotação Brutal", "Mitsuki estica ambos os braços em direção ao oponente para derrubá-lo. Logo após, ele salta e enterra um deles no solo, o qual ele emerge e enterra várias vezes indo em direção ao oponente, até que ele agarra-o pelos tornozelos. Com a captura surpresa bem-sucedida, ele puxa o oponente para baixo, enterrando-o e puxando para fora pelos mesmos buracos que seu braço passou, causando danos através do atrito com o solo e, em seguida, ele puxa para cima quando chega no último buraco, segurando-o no ar. Dessa forma, ele laceia seu braço elástico e rotaciona-o freneticamente agarrado ao inimigo, criando um turbilhão de vento. Para finalizar a combinação, ele puxa o adversário para baixo, contra o solo, causando um grande impacto e deixando vestígios para trás.", "Todos os alcances", null, null, "荒大旋あらだいせん", "Aradaisen", "Grande Rotação Brutal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Rugido", "Uma Besta com Cauda usa sua enorme reserva de chakra e transforma qualquer coisa a partir de um rugido ou uma onda em uma poderosa força de pressão e destruição. Quando Naruto estava no controle ao usar o poder de Kurama, ele demonstrou poderosas ondas de choque por rugido ou pelo movimento de seus braços. Quando enfurecido ou quando Kurama assumiu o controle de seu corpo, ele foi mostrado usando explosões com a onda poderosa de seus braços. A força bruta por trás de Naruto uma vez que ele entra na versão 2 era poderosa o suficiente para forçar o Shinra Tensei de Pain de volta para ele.", "Longo alcance", null, null, "大咆哮", "Daihokō", "Grande Rugido", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grande Tornado da Borboleta", "Chōji converte suas calorias em chakra e manifesta um grande par de asas de borboleta em suas costas. Com o bater de suas asas, ele é capaz de desencadear um vento de chakra na forma de um grande tornado, forte o suficiente para combater uma explosão.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grandes Círculos Gêmeos", "Após ter previamente guardado uma série de Fūma Shuriken em sua Dimensão do Kamui, Obito é capaz de abrir um portal e ejetá-las em alta velocidade em direção ao seu oponente.", "Curto a Médio alcance", null, null, "大双輪", "Daisōrin", "Grandes Círculos Gêmeos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Grandeza do Mal", "Orochimaru primeiro soca o adversário na cara e bate-o de volta. Ele, então, envolve o adversário com inúmeras cobras que cria um domo ao redor dele e prende-o. Em seguida, ele salta e apunhala-o com sua Espada de Kusanagi finalizando-o.", null, null, null, "邪傑じゃけつ", "Jaketsu", "Grandeza do Mal", "Gigante do Mal", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Granizo de Areia", "Sendo uma versão em escala mais destrutiva da Barragem da Garoa de Areia, o procedimento para utilizar a técnica é semelhante. Gaara reúne sua areia em grandes aglomerações, formando rochas maciças, e então as deixa cair do céu para atingir seus adversários. Ela abrange uma grande área, o que a torna uma técnica difícil de se esquivar. Como a sua versão em menor escala, a técnica pode distrair o inimigo com o ataque aéreo, deixando-os expostas a um ataque oriundo vindo de baixo.", "Longo alcance", null, null, "砂霰", "Suna Arare", "Granizo de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Granizo de Areia: Selamento da Prisão Restritora de Areia", "Gaara usa o Granizo de Areia contra seu oponente, atacando-o com uma chuva incessável de areia e, em seguida, cria uma Prisão Restritora de Areia, que restringe seus movimentos. Com o inimigo preso num mar de areia, Gaara completa sua técnica selando-o.", "Todos os alcances", null, null, "砂霰・砂縛牢封印すなあられ・さばくろうふういん", "Suna Arare: Sabakurō Fūin", "Granizo de Areia: Selamento da Prisão Restritora de Areia", null, null, null);
