INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Horizontal de Relâmpago Violento", "Coberto pelo Modo de Chakra de Liberação de Relâmpago, A executa um poderoso golpe horizontal contra o adversário, de tal forma a ser poderoso o suficiente para rachar os ossos da caixa torácica do Susanoo tanto de Sasuke Uchiha como de Madara.", "Curto alcance", null, null, "雷虐水平らいぎゃくすいへい", "Raigyaku Suihei", "Horizontal de Relâmpago Violento", null, "Lâmina Transgressora do Relâmapgo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Horror do 'Amanhecer'", "Esta é uma combinação única dos membros da Akatsuki, onde cada um deles usa uma de suas especialidades.", "Todos os alcances", null, null, "戦慄の'暁'せんりつの「あかつき」", "Senritsu no 'Akatsuki'", "Horror do 'Amanhecer'", "Horror da 'Akatsuki'", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Hyūga: Grande Céu Giratório", "Uma técnica colaborativa utilizada por dois membros do Clã Hyūga. Um executa a técnica Proteção de Oito Trigramas Sessenta e Quatro Palmas e o outro usa Oito Trigramas Palmas Giratórias do Céu em torno dele. Isso cria uma enorme esfera com tanto poder ofensivo quanto defensivo, causando grande dano ao adversário.", "Curto alcance", null, null, "日向・大回天ひゅうが・だいかいてん", "Hyūga: Daikaiten", "Hyūga: Grande Céu Giratório", "Hyūga: Grande Rotação", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Iaidō", "O termo Iaidō (居合道) refere-se a um estilo específico de kenjutsu que se preocupa com os movimentos suaves e controlados de sacar a espada da bainha, golpeando ou cortando um adversário, retirando o sangue da lâmina, e, em seguida, colocando a espada na bainha. O movimento linear e força aplicada para desenhar a espada de seu resultado resulta em cortes muito mais rápidos do que os realizados com a espada já desenhada.", "Curto alcance", null, null, "居合道いあいどう", "Iaidō", "Caminho de Iai", "Battōjutsu[1]", "Iai", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Causada pelas Mariposas Venenosas", "Fuen primeiro usa um veneno que é fino e pequeno o suficiente para ser levado no ar. Seu alvo irá inalar o veneno e começará a ver as coisas que Fuen escolhe, como cavernas, mariposas, ou aliados do alvo tentando matá-lo. Quando seu alvo está preso em seu genjutsu, Fuen ataca e mata seu alvo. Sakura Haruno foi capaz de criar um antídoto para a técnica que lhe permitiu derrotar Fuen muito facilmente. É ideal para espaços confinados.", "Longo Alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Aprisionamento da Árvore Assassina", "Este é um dos genjutsu transmitidos desde os tempos antigos em Konohagakure. Primeiro o usuário desaparece da vista de todos como uma névoa, a fim de se aproximar do alvo sem ser detectado. Uma vez perto o suficiente, o alvo será, então, completamente privado de sua mobilidade, vendo a miragem de uma árvore crescendo rápido e enrolando-se ao redor dele. Tendo em conta que o alvo permanece consciente, esta técnica se mostra extremamente eficiente para a coleta de informações. Uma vez que a vítima fica imóvel, o usuário pode atacar o inimigo, geralmente depois de emergir a partir do tronco da árvore.", "Curto alcance", "Rank B", "Cao → Cobra → Macaco → Boi → Tigre", "魔幻・樹縛殺", "Magen: Jubaku Satsu", "Ilusão Demoníaca: Aprisionamento da Árvore Assassina", "Ilusão do Demônio: Aprisionamento da Árvore Assassina", "Ilusão Demoníaca: Galhos da Restrição Fatal", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Confronto do Canto de Sapo", "Ilusão Demoníaca: Confronto do Canto de Sapo é um genjutsu à base de som transmitido do Monte Myōboku. Porque a aprendizagem requer muitos anos de treinamento, apenas os Dois Grandes Sábios Sapos, Fukasaku e Shima, têm as habilidades para realizar isso.", "Todos os alcances", null, null, "魔幻・蝦蟇臨唱まげん・がまりんしょう", "Magen: Gama Rinshō", "Ilusão Demoníaca: Confronto do Canto de Sapo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Destruição da Árvore Aprisionadora", "Depois de utilizar a Ilusão Demoníaca: Aprisionamento da Árvore Assassina, a árvore que prende o oponente é perfurada por várias kunai.", "Curto alcance", null, null, "魔幻・樹縛滅殺まげん・じゅばくめっさつ", "Magen: Jubaku Messatsu", "Ilusão Demoníaca: Destruição da Árvore Aprisionadora", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Encantamento de Persuasão", "Trata-se um genjutsu muito forte que permite o usuário a colocar um oponente sob uma ilusão usando apenas seu discurso. Enquanto encontra-se sob os efeitos do genjutsu, o alvo pode ser facilmente influenciado pela sua vontade e autosegurança. A ilusão não pode ser desfeita, ainda que a pessoa perceba que que o genjutsu está sob efeito, porém fortes conexões emocionais, como o amor, podem ajudar a dispersar a técnica.", "Curto a Médio alcance", null, null, "魔幻・蠱惑の甘言", "Magen: Kowaku no Kangen", "Ilusão Demoníaca: Encantamento de Persuasão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Estrutura Fumegante de Vários Andares", "Um genjutsu de longo alcance (広範囲幻術, Kōhani Genjutsu), em que o Gengetsu Hōzuki, em conjunto com a sua invocação, é capaz de manifestar miragens realistas usando a névoa exalada pelo molusco. Como é impossível para observadores distinguir a coisa real, através de métodos normais, esta propriedade faz com que a técnica seja ideal para esconder a localização real de um indivíduo, resultando em confusão entre a oposição como seus ataques são ineficazes e tentam determinar a verdadeira posição do par. No entanto, a fim de manter as ilusões, o molusco deve continuamente exsudar a névoa de seus sifões, durante toda a duração da técnica.", "Todos os alcances", "Rank B", null, "魔幻・気蒸の楼閣まげん・きじょうのろうかく", "Magen: Kijō no Rōkaku", "Ilusão Demoníaca: Estrutura Fumegante de Vários Andares", null, "Genjutsu de Miragem", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Explosão do Aprisionamento da Árvore Assassina", "Kurenai usa a Ilusão Demoníaca: Aprisionamento da Árvore Assassina para aprisionar o seu alvo que, em seguida, explode.", "Longo alcance", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Flor da Névoa", "Kurenai prende o oponente em um botão de flor, e detona-o em uma explosão florida.", "Curto alcance", null, "Cobra → Selo de mao especifico da técnica", "魔幻・花霞", "Magen: Hanagasumi", "Ilusão Demoníaca: Flor da Névoa", "Ilusão Demoníaca: Flor da Neblina", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Miragem do Corvo", "Lançando corvos reais contra o inimigo, este genjutsu intercepta a vítima em uma poderosa ilusão, projetando uma torrente de corvos e deixando o adversário à mercê do usuário, permitindo uma conversa forçada ou uma distração para um ataque.", "Curto alcance", null, null, "魔幻・蜃鴉まげん・しんからす", "Magen: Shinkarasu", "Ilusão Demoníaca: Corvo Amêijoa", "Ilusão Demoníaca: Miragem de Corvos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Mudança do Espelho do Céu e da Terra", "O usuário decifra um genjutsu que foi aplicado sobre ele e lança de volta uma técnica com efeitos idênticos no oponente. É uma forma de inversão de ilusão, mas para ler instantaneamente através de uma técnica e lançá-la de volta, o poder do Sharingan é necessário. Como genjutsu é inicialmente usado para confundir o inimigo, essa técnica, que envia-o de volta ao seu conjurador, causa uma grande quantidade de dano mental, mais do que qualquer outra coisa.", "Curto alcance", null, null, "魔幻・鏡天地転まげん・きょうてんちてん", "Magen: Kyō Tenchi-ten", "Ilusão Demoníaca: Mudança do Espelho do Céu e da Terra", null, "Ilusão Demoníaca: Inversão do Céu e Terra", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Paralisia de Pregos", "O usuário, enquanto no Modo Tsukuyomi, invoca grandes picos brancos para prender o oponente dentro da barreira.", "Curto a Médio alcance", null, null, null, null, "Ilusão Demoníaca: Paralisia de Pregos", "Ilusão do Demônio: Prego Paralisador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Sombra Voadora", "Kurenai lança o adversário sob seu genjutsu, fazendo com que o adversário fique confuso. Asuma então ataca-o com sua Andorinha Voadora, derrubando o adversário para fora.", "Curto alcance", null, null, "魔幻・飛影まげん・ひえい", "Magen: Hiei", "Ilusão Demoníaca: Sombra Voadora", "Ilusão do Demônio: Sombra Voadora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Técnica da Loucura do Espírito Lutador", "Genshō Ryūdōin cria uma ilusão pilares roxos que têm a aparência de um rosto no meio e usa-os alternadamente para infringir danos aos inimigos. Esta técnica foi forte o suficiente para deixar os 11 de Konoha inconscientes, com exceção de Naruto que Genshō não atacou, bem como absorver seu chakra.", "Curto alcance", null, null, "魔幻・狂気精神戦闘機の術まげん・きょうきせいしんせんとうきのじゅつ", "Magen: Kyōki Seishin Sentōki no Jutsu", "Ilusão Demoníaca: Técnica da Loucura do Espírito Lutador", "Ilusão do Mal: Jutsu da Loucura do Espírito Lutador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Técnica da Visão do Inferno", "Este é um genjutsu que revela os medos que habitam dentro do coração das pessoas. Todo mundo tem uma imagem da única coisa que não quer nunca ver. Este genjutsu é uma técnica que chama para fora essa tal imagem a partir de dentro do coração e o modifica para a realidade. Primeiro, um círculo imaginário de folhas vai girar ao redor e envolver o alvo, fazendo-o cair logo depois. Após um curto período, a ilusão começará. Este faz com que a ilusão seja muito convincente, uma vez que o usuário provavelmente for movido antes a ilusão se ajusta dentro e se a imagem mental é horrível, o choque será grande nesse sentido.", "Curto alcance", "Rank D", "Cobra → Rato", "魔幻・奈落見の術まげん・ならくみのじゅつ", "Magen: Narakumi no Jutsu", "Ilusão Demoníaca: Técnica da Visão do Inferno", "Ilusão do Mal: Jutsu da Miragem da Morte", null, "Jutsu de Ilusão: Controle Mental");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Técnica de Estacas de Agrilhoar", "Primeiramente, esta técnica requer a utilização do Genjutsu: Sharingan. O adversário é pego no mundo ilusório criado pelo usuário, e a vítima é atormentada com a sensação de ter estacas impulsionadas através de seus membros, tirando a liberdade do seu corpo. Ao mesmo tempo, a dor física que acompanha a ilusão revela que a maior utilidade desta técnica é a tortura.", "Curto alcance", "Rank –", null, "魔幻・枷杭の術", "Magen: Kasegui no Jutsu", "Ilusão Demoníaca: Tranca de Estacas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Técnica do Arredor Falso", "Esta técnica é um genjutsu que permite enganar os outros para persuadir o adversário, fazendo-o acreditar que está em outro local, impedindo-o de ver certos aspectos do cenário em que está, criando brechas para armadilhas que nem sequer poderão ser vistas. A principal característica desta técnica é que ela pode ser lançada sobre uma superfície extensa, portanto, tudo e qualquer coisa que entrar na área de efeito da ilusão cairá sobre a técnica. Dito isso, ela pode não funcionar tão bem em pessoas que são bem versadas ​​ou possuem outras formas hábeis em genjutsu.", "Todos os alcances", "Rank C", null, "魔幻・此処非の術まげん・ここにあらずのじゅつ", "Magen: Kokoni Arazu no Jutsu", "Ilusão Demoníaca: Técnica do Arredor Falso", null, null, "Genjutsu: Lugar Falso");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Técnica do Arredor Falso Duplo", "Esta técnica coloca uma ilusão dentro de outra ilusão criada pelo usuário. Quando o alvo dissipa a primeira ilusão, eles não vão perceber que a segunda está no lugar.", null, null, null, "魔幻・二此処非の術", "Magen: Nijū Kokoni Arazu no Jutsu", "Ilusão Demoníaca: Técnica do Arredor Falso Duplo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão Demoníaca: Técnica do Inferno Decrescente", "Após os selos de mão necessários, o usuário cria a ilusão de uma imensa bola de fogo caindo do céu em uma área designada. Os alvos envolvidos na ilusão literalmente sentem a temperatura quente da bola se aproximando.", "Médio alcance", null, "Javali → Cao → Passaro → Macaco → Carneiro", "魔幻・地獄業火の術まげん・じごくごうかのじゅつ", "Magen: Jigoku Gōka no Jutsu", "Ilusão Demoníaca: Técnica do Inferno Decrescente", null, null, "Ilusão Demoníaca: Jutsu de Fogo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ilusão do Aprisionamento na Corda de Feijão", "Uma técnica comum entre os membros do clã Kurama, o usuário faz-se desaparecer, enquanto o alvo vê várias vinhas de feijão que crescem a partir do chão. Estas vinhas prendem o corpo da vítima, levantando-a no ar. Em seguida, uma vagem de feijão eleva-se e se abre para revelar o usuário, pronto para atacar. No entanto, um usuário qualificado de genjutsu como Kurenai pode reverter os efeitos para o usuário.", "Curto alcance", null, "Cachorro → Coelho → Cobra → Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Imagem do Gato Escondido na Areia", "Imagem do Gato Escondido na Areia é um técnica sensorial que utiliza matéria, como a areia de Gaara.", "Todos os alcances", null, null, "潜砂絵猫", "Sensa Kaibyō", "Imagem do Gato Escondido na Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto Dilacerante da Flor de Cerejeira", "Sakura soca o chão para desencadear uma onda de chakra, que persegue e explode junto ao oponente.", "Curto a Médio alcance", null, null, "桜花裂衝おうかれっしょう", "Ōka Resshō", "Impacto Dilacerante da Flor de Cerejeira", "Explosão da Flor de Cerejeira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto Vazio", "Utilizando a Técnica do Tamanho Múltiplo Parcial, o usuário bate no chão com seus punhos aumentados, fazendo com que os destroços sejam lançados para o ar. Rapidamente então ele bate nos escombros que ainda estão no ar e lança-os contra o alvo, esmagando o que tocar. A técnica foi poderosa o suficiente para fazer a Areia de Ferro de Shinki escorrer enquanto na forma de capa, mas não foi capaz de penetrar suas defesas. No entanto, um oponente rápido o suficiente é capaz de escapar dos projéteis.", null, null, null, "からのドーン", "Kara no Dōn", "Boom", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto Verde", "Naruto realiza Furacão da Folha para desabilitar o adversário, então varrê-o com os seus pés usando o Vendaval da Folha. Depois disso, ele lança o oponente para o céu e usa o Rasengan. Naruto só pode usar esta técnica com o terno de Guy.", "Curto alcance", null, null, "碧い衝撃", "Aoi Shōgeki", "Impacto Verde", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto da Armadura de Rocha", "Esta é uma técnica de força sobre-humana que usa a durabilidade da Armadura de Rocha.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto da Flor de Cerejeira", "Esta é uma técnica de força sobre-humana que usa de fundamentos médicos, tais como a capacidade de concentração e controle de chakra minucioso, para ser realizada.", "Curto alcance", "Rank C", null, "桜花衝おうかしょう", "Ōkashō", "Impacto da Flor de Cerejeira", null, "Ataque Terrestre da Hercúlea", "Colisão da Flor de Cerejeira");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto do Fluxo de Água", "Kisame converte seu chakra em água e, em seguida, expele-a para frente. Com a água em torno de si, ele a manipula para bater e derrubar o oponente, enfraquecendo-o ou afogando-o.", "Curto alcance", null, null, "水流衝", "Suiryūshō", "Impacto do Fluxo de Água", "Colisão do Fluxo de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impacto do Rochedo Celestial", "O usuário, utilizando-se de sua força sobre-humana soca o chão e o destrói por completo. Em meio à destruição causada por seu soco, ele puxa um enorme fragmento e lança com força contra seu oponente.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impermanência de Coisas Mundanas", "O Grupo Magaki executa os selos de mão necessários e emite seu chakra para o céu, criando um losango de chakra sólido com imensa pressão interna, o que a faz explodir e lançar diversas lascas que perfuram o inimigo. De acordo com Yamato, a técnica é muito formidável.", null, null, "Cavalo + Cobra + Tigre + Cobra Simultaneamente", "諸行無常", "Shogyō Mujō", "Impermanência de Coisas Mundanas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impulso da Flor", "Kimimaro, enquanto em seu selo amaldiçoado nível dois, corre em direção a seu alvo e golpeia-o com os ossos de suas costas. Essa técnica foi poderosa o suficiente para atravessar uma das defesas de Gaara.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impulso do Pilar de Água", "Tobirama usa sua maestria na Liberação de Água para materializar um pequeno corpo de água para levantar-se debaixo do oponente e empurrá-lo para o ar.", "Curto alcance", null, null, "水柱突", "Suichūtotsu", "Impulso do Pilar de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Impulso do Punho Certeiro", "O usuário corre contra o oponente e ataca-o com um soco reforçado com chakra, enviando-o para longe. A potência do soco é tão grande que foi capaz de causar sequelas graves em Shin Uchiha ao ponto da necessidade de um transplante de órgãos.", "Curto alcance", null, null, "正拳突きせいけんつき", "Seikentsuki", "Impulso do Punho Certeiro", "Impulso Reto", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Inclinação da Flor", "Kurenai ataca o oponente com um golpe de cotovelo, seguido com dois chutes e uma ataque com uma kunai antes de terminar com um chute por baixo arrebatador que gera um anel de pétalas de flor de cerejeira em torno dela.", "Curto alcance", null, null, "枝垂れ華", "Shidarehana", "Inclinação da Flor", "Flor Desbotada", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Incêndio da Chama do Macaco", "Son Gokū ou Rōshi transformado, expele chamas verdes no adversário.", "Curto alcance", null, null, "猿炎放火", "En'en Hōka", "Incêndio da Chama do Macaco", "Lançamento da Chama do Macaco", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Infinita Dança Selvagem", "Depois de abrir alguns dos Oito Portões, Lee desestabiliza o oponente e ataca-o muitas vezes na velocidade da luz, movendo-se tão rápido que parece que há três Lee atacando ao mesmo tempo. Para terminar, Lee soca o inimigo no céu três vezes, lançando-o de volta para o chão.", "Curto alcance", null, null, "無限乱舞", "Mugen Ranbu", "Infinita Dança Selvagem", "Lótus da Dança da Infinidade", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ingestão de Chakra", "Através do contato físico, Yoroi é capaz de absorver o chakra de uma vítima. Sua habilidade é ligeira de modo a lhe possibilitar o roubo de uma grande quantidade de chakra de Sasuke em poucos segundos.", "Curto alcance", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Inscrição Absoluta", "Com sua espada, o usuário rapidamente corta tudo em torno de si.", "Curto alcance", null, null, "絶銘", "Zetsumei", "Inscrição Absoluta", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Inseto Explosivo", "O usuário cobre o inimigo com uma cortina de fumaça para dispersar larvas que serão inaladas, elas vão crescer dentro do corpo e virar uma aranha parasita que explode, matando seu hospedeiro. O crescimento do inseto depende da quantidade de chakra usada, se o hospedeiro usar uma enorme quantidade de chakra pode acelerar o processo de crescimento. O inseto pode, contudo, ser removido por meio de cirurgia.", "Todos os alcances", null, null, "爆裂虫ばくれつちゅう", "Bakuretsuchū", "Inseto Explosivo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Inseto Parasita Gigante — Mordida de Inseto", "Na sequência de um membro do clã Aburame colocar um número de kidaichū na pele de um inimigo, esses insetos enterram-se imediatamente no corpo da vítima e começam a devorar sua carne e chakra. Este processo faz com que os insetos a cresçam rapidamente em tamanho, até que o corpo do hospedeiro é completamente corroído de dentro para fora.", "Curto alcance", "Rank –", null, "寄大蟲　虫食いきだいちゅう　むしくい", "Kidaichū — Mushikui", "Inseto Parasita Gigante — Mordida de Inseto", null, "Grande Inseto Parasita Devorador Voraz", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Miméticos: Rastejamento", "Shino convoca seus kikaichū em sua volta para engolfar o oponente em uma pequena barreira que implode nele.", "Todos os alcances", null, null, "擬態蟲・這", "Gitaichū: Sha", "Insetos Miméticos: Rastejamento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Parasitas Destrutivos: Eclipse", "Shino manipula seus kikaichū para formar um pilar gigante que desmorona em forma de onda que engole seu oponente. Os insetos em seguida, criam um redemoinho que arrasta o oponente para abaixo do solo.", "Todos os alcances", null, null, "寄壊蟲・蝕きかいちゅう・しょく", "Kikaichū: Shoku", "Insetos Parasitas Destrutivos: Eclipse", "Insetos Parasitas: Digerir", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Parasitas Destrutivos: Enxame", "Shino libera vários kikaichū atrás de seu oponente, que começam se envolver em torno deles. Ao mesmo tempo, Shino envia mais insetos em um padrão espiral. Todos os insetos fazem um enxame no oponente, danificando-o e, em seguida, dispersando.", "Curto alcance", null, null, "寄壊蟲・簇きかいちゅう・むら", "Kikaichū: Mura", "Insetos Parasitas Destrutivos: Enxame", "Insetos Parasitas: Enxame", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Parasitas Destrutivos: Formas", "O usuário envia seus insetos para criar variadas formas que podem ser usadas para exemplificar algo.", "Curto a Médio alcance", null, "Selo de Confronto", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Parasitas Destrutivos: Granizo", "Shino libera milhares de insetos de seu corpo no ar. Em seu comando, os insetos atacam o oponente em cinco ondas. Os insetos fundem-se se transformando em uma grande nuvem que chove sobre o oponente, e, em seguida, os insetos formam um inseto gigante como uma esfera de meteoros desaba sobre o oponente.", "Curto alcance", null, null, "寄壊蟲・霰", "Kikaichū: Arare", "Insetos Parasitas Destrutivos: Granizo", "Insetos Parasitas: Tempestade de Granizo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Parasitas Destrutivos: Lança", "Os kikaichū de Shino combinam em duas formas de lança, como que em espiral em direção ao oponente e leva-o para o ar, onde brutalmente banqueteiam-se com seu chakra.", "Curto alcance", null, null, "寄壊蟲・槍きかいちゅう・そう", "Kikaichū: Sō", "Insetos Parasitas Destrutivos: Lança", "Insetos Parasitas: Lança", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Insetos Parasitas Destrutivos: Oco", "Shino manipula os kikaichū para formar uma grande nuvem no ar que desce sobre o oponente. Depois que os insetos estão no terreno em torno do oponente, os enxame de insetos cai em cima dele, e rapidamente entra em erupção em uma explosão de insetos.", "Médio alcance", null, null, "寄壊蟲・虚", "Kikaichū: Utsuro", "Insetos Parasitas Destrutivos: Oco", "Insetos Parasitas: Decepção", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Instante", "O usuário rapidamente aparece na frente de seu adversário e corta-o, movendo-se para trás dele. O adversário posteriormente entra em colapso quando o usuário termina, dando tempo de embainhar a lâmina novamente. Este movimento é forte o suficiente para matar um alvo, enquanto está no selo amaldiçoado nível 2.", "Curto alcance", null, null, "刹那", "Setsuna", "Instante", "Lâmina Instantânea", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Instrução de Punho Gentil", "Hanabi atinge seu oponente com vários movimentos do Punho Gentil, fazendo-o cair no chão. Hanabi em seguida, vai em direção a seu pai, Hiashi, que lhe pergunta como ela conseguiu. Hiashi percebe então que o oponente começa a levantar-se, e com uma velocidade incrível desfere um forte golpe, com seu punho no inimigo.", "Curto a Médio alcance", null, null, "柔拳指南じゅうけんしなん", "Jūken Shinan", "Instrução de Punho Gentil", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Intenção Assassina", "A Intenção Assassina é simplesmente o ato do usuário exalar uma pura intenção de matar, e afetando seu oponente, eles mesmos, e outros ao seu redor, a ponto de paralisar o oponente de terror. Quando a intenção é particularmente forte, ele pode fazer as vítimas terem visões de sua própria morte. Isto pode confundir o método com um genjutsu, apesar de ser apenas uma habilidade geral.", null, null, null, "殺気さっき", "Sakki", "Intenção Assassina", "Intenção de Matar", null, "Intenção Mortífera");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Inukai Takeru no Mikoto", "Inukai Takeru no Mikoto permite personificar o chakra em cães selvagens que emergem a partir do terreno circundante. Eles perseguem seu alvo implacavelmente e, quando o alcançam, o oprimem com suas fortes mandíbulas, restringindo e esmagando-o; sua velocidade de rastreamento foi grande ao ponto de capturar Sasuke Uchiha.", "Todos os alcances", null, null, "犬飼健命いぬかいたけるのみこと", "Inukai Takeru no Mikoto", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Inundação das Ondas Explosivas de Areia", "Gaara aprisiona o oponente em um vórtice de areia, que, em seguida, tem seu tamanho reduzido. Com o oponente contido e um vácuo no deserto, Gaara fecha a mão para esmagá-lo. A areia da qual prendia o oponente retorna ao tamanho original, criando, assim, ondas no deserto.", "Todos os alcance", null, null, "濤砂瀑氾とうさばくはん", "Tōsabakuhan", "Inundação das Ondas Explosivas de Areia", "Onda de Inundação da Cascata de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Investida Aérea de Múltiplos Clones do Seis Caminhos", "No ar, Naruto cria alguns clones que formam o Rasengan e são lançados como projéteis para chocarem-se contra o oponente em uma velocidade absurda, jogando-o no chão com extrema compulsão.", "Médio alcance", null, null, null, null, null, "Investida Aérea de Múltiplos Clones do Seis Caminhos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Bastão Adamantino", "Aproveitando o corpo transformado em bastão de Enma, Hiruzen empunha-o e desfere uma sucessão de golpes rápidos e poderosos contra o alvo. A técnica foi mostrada sendo forte o suficiente para cortar até mesmo a Liberação de Madeira de Hashirama e as raízes da Árvore Divina.", null, null, null, "口寄せ・金剛如意くちよせ・こんごうにょい", "Kuchiyose: Kongōnyoi", "Invocação: Bastão Adamantino", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Boilergama", "O usuário invoca um gigante sapo fumegante. O imenso tamanho do sapo é um perigo para qualquer um abaixo da área de invocação.", "Curto alcance", null, null, "口寄せ・蒸気蝦蟇", "Kuchiyose: Boira Gama", "Invocação: Sapo Fumegante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Chamas da Montanha Flamejante", "Koji desenha um círculo ritualístico no chão abaixo de seu alvo e, ao juntar as mãos, ele conjura chamas da distante Montanha Flamejante para incinerá-lo. Uma vez que são chamas naturais e não envolvem chakra em sua composição, é impossível absorvê-las com técnicas absorvedoras de chakra.", "Todos os alcances", null, "Juntar as Maos", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Câmara de Tortura", "Essa técnica é utilizada por Ibiki que invoca uma câmara de tortura de ferro em torno de si mesmo e do oponente. Primeiramente, os braços e as pernas do inimigo são capturados por quatro correntes e, então, as paredes da gaiola aparecem planas no chão, rapidamente dobrando-se como uma caixa juntas. Finalmente, uma grande estátua cai em cima da gaiola, que é então fortemente presa em correntes.", "Curto alcance", null, "Javali → Cao → Passaro → Macaco → Carneiro", "口寄せ・拷問部屋", "Kuchiyose: Gōmon Heya", "Invocação: Câmara de Tortura", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Dança da Decapitação Rápida", "Temari invoca Kamatari, uma doninha de um olho só segurando uma foice gigante com outras foices menores ao redor dela, ao colocar sangue em seu Leque Dobradiço Gigante como pagamento do contrato e, em seguida, girando-o. O leque chicoteia um vendaval poderoso e impulsiona Kamatari — que cavalga no vento, cortando através de tudo o que toca. Os efeitos da técnica se estendem a várias centenas de metros, arrastando e dilacerando árvores, pedras e outras formas de detritos, que, em seguida, caem sobre o inimigo indefeso. É altamente eficaz quando cercado por todos os lados, quando a posição do inimigo é relativamente desconhecida, ou quando este está escondido atrás de um escudo.", "Todos os alcances", "Rank B", null, "口寄せ・斬り斬り舞くちよせ・きりきりまい", "Kuchiyose: Kirikiri Mai", "Invocação: Dança da Decapitação Rápida", null, "Invocação: Dança da Navalha", "Invocação: Dança da Espada");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Dança de Colapso do Leque", "Temari invoca Kamatari e, em seguida, Kamatari gira com sua kama e corta o oponente para afastá-lo, cortando depois em pedaços tudo o que estiver pelo caminho em um combo, podendo causar uma avalanche para esmagar o inimigo.", null, null, null, "口寄せ・扇舞降ろし", "Kuchiyose: Senmai Oroshi", "Invocação: Queda da Dança do Leque", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Espada Gigante", "Ao bater a mão no chão, o selo de invocação aparece e invoca uma espada gigante para usar em batalha.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Estátua Demoníaca do Caminho Exterior", "Invocação: Estátua Demoníaca do Caminho Exterior, é uma técnica concedida pelo portador do Rinnegan.", "Todos os alcances", "Rank –", null, "口寄せ・外道魔像", "Kuchiyose: Gedō Mazō", "Invocação: Estátua Demoníaca do Caminho Exterior", null, "Jutsu de Invocação: Estátua do Demônio do Inferno", "Jutsu de Invocação: Estátua Gedo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Fūma Shuriken", "Ao executar o selo de mão necessário, o usuário invoca uma Fūma Shuriken para usar em batalha.", "Curto alcance", null, "Juntar as Maos[1] ou Selo do Confronto[2]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Ibuse", "Hanzō invoca Ibuse, que cria uma nuvem de veneno na área geral a sua frente, envenenando seu oponente.", "Curto alcance", null, null, "口寄せ・イブセ", "Kuchiyose: Ibuse", "Invocação: Ibuse", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Inferno de Rashōmon", "Sakon e Ukon separam-se e usam uma técnica de invocação combinada para invocar o Rashōmon no chão. O portão demoníaco abre-se e engole o oponente em outra dimensão.", "Curto a Médio alcance", null, null, "口寄せ・羅生門奈落", "Kuchiyose: Rashōmon Naraku", "Invocação: Inferno de Rashōmon", "Invocação: Abismo de Rashōmon", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Kunai", "Ao abrir uma de suas mãos, Sakon invoca uma kunai para usar em batalha.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Liberação de Terra: Técnica da Presa Rastreadora", "Um ninjutsu de rastreamento ofensivo de alta qualidade, onde é invocado ninken, para que eles possam perseguir o alvo pelo subsolo e atacá-lo simultaneamente com as suas presas.", "Todos os alcances", "Rank B", "Tigre → Cobra → Dragao → Cão", "口寄せ・土遁・追牙の術くちよせ・どとん・ついがのじゅつ", "Kuchiyose: Doton: Tsuiga no Jutsu", "Invocação: Liberação de Terra: Técnica da Presa Rastreadora", null, null, "Invocação: Estilo Terra: Jutsu Perseguição de Garras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Marionete", "Esse fūinjutsu é utilizado pelos marionetistas a fim de trasportar suas marionetes dentro de pergaminhos e, quando necessário, convocá-las para a batalha.", null, null, null, "口寄せ・傀儡くちよせ・くぐつ", "Kuchiyose: Kugutsu", "Invocação: Marionete", "Drama dos Marionetes[3]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Portão da Água de Chuva", "Trata-se de uma forma da Técnica de Invocação, trazendo um único portão resistente o suficiente para suportar um ataque de taijutsu de Rock Lee. No entanto, um papel explosivo vindo por trás é capaz de destruir o portão.", "Curto alcance", null, "Javali → Cao → Passaro → Macaco → Bater a mão no solo", "口寄せ・雨水門", "Kuchiyose: Usuimon", "Invocação: Portão da Água de Chuva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Prisão da Boca do Sapo", "Uma técnica de invocação peculiar que invoca apenas um órgão: o esôfago de um grande, sapo que cospe fogo do Monte Myōboku: o Alojamento de Pedra (妙木山・岩宿, Myōbokuzan: Iwayado), prendendo o inimigo em seu estômago. Se ativado dentro de um edifício ou de uma caverna, este truque pode iludir o inimigo sem ele perceber. Neste interior, até o ponto de apoio não é confiável, o inimigo encontra-se em um local apertado, tendo dificuldade em executar suas habilidades habituais e sendo incapaz de ativá-las corretamente. O interior do esôfago é altamente flexível e adesivo, e não permite que o oponente se movimente livremente. Com o tempo, as paredes de carne se fecham e envolvem o inimigo, forçando-o a ir para dentro do estômago, onde será digerido.", "Todos os alcances", "Rank A", "Serpente → Javali → Rato → Dragao → Javali → Dragão → Tigre → Ambas as palmas das mãos no chão", "口寄せ・蝦蟇口縛り", "Kuchiyose: Gamaguchi Shibari", "Invocação: Prisão da Boca do Sapo", null, "Invocação: Aprisionamento da Boca Bratáquia", "Arte Ninja: Armadilha da Boca de Sapo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Rashōmon", "Bem fechado, este portão ameaçador quando invocado fornece uma tremenda defesa para seu invocador e é capaz de interceptar completamente o ataque físico de um oponente. De acordo com Sakon e Ukon, esta técnica apresenta uma defesa final e o fato de que Kiba Inuzuka e Akamaru conseguiram amassar não era pouca coisa.", null, "Rank B", "Cobra", "口寄せ・羅生門くちよせ・らしょうもん", "Kuchiyose: Rashōmon", "Invocação: Rashōmon", null, "Invocação dos Portões do Castelo", "Jutsu de Invocação: Rashomon");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Rashōmon Quíntuplo", "Com um já ostentando enormes propriedades defensivas, com esta técnica o usuário invoca cinco portões Rashōmon para bloquear um ataque de entrada. A defesa é bastante formidável, e com cinco portões, o usuário também é capaz de mudar a trajetória de um ataque tão poderoso quanto uma Bola da Besta com Cauda equipada com uma Espada do Susanoo. As qualidades defensivas desta técnica são ainda mais reforçadas pela falta de selos de mão necessários para realizá-la.", null, "Rank A", null, "口寄せ・五重羅生門くちよせ・ごじゅうらしょうもん", "Kuchiyose: Gojū Rashōmon", "Invocação: Rashōmon Quíntuplo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Rashōmon Triplo", "O Invocação: Rashōmon Triplo é uma atualização para o padrão Invocação: Rashōmon, que — como o próprio nome sugere — não invoca um, mas três ameaçadores Rashōmon. A técnica exige dois selos de invocação para ser concluída, e estes portões têm a mesma função como o único Rashōmon, mas com o triplo da defesa. O primeiro portão leva o ataque de frente, o segundo reduz o poder do ataque e o terceiro dispersa a pressão, fazendo com que todos os ataques que entram em contato com eles percam seu poder antes que eles possam ser eficazes. As capacidades defensivas desta técnica são ainda mais impulsionadas pela falta de selos de mão necessários para realizar esta técnica.", null, "Rank A", null, "口寄せ・三重羅生門くちよせ・さんじゅうらしょうもん", "Kuchiyose: Sanjū Rashōmon", "Invocação: Triplo Rashōmon", null, null, "Rashōmon Triplo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Reencarnação do Mundo Impuro", "A Invocação: Reencarnação do Mundo Impuro é uma técnica proibida em que, utilizando-se uma pessoa viva como um recipiente, a alma de uma pessoa falecida podem ser invocada de volta ao mundo dos vivos e ligada a este. O recipiente, então, toma a forma que a pessoa teve em vida, reencarnando-o para cumprir as ordens de seu invocador.", null, "Rank S", "Ativar: Tigre → Cobra → Cao → Dragão → Juntar as Mãos,Controlar: Carneiro ou Selo do Confronto[2],Reforcar: Selo do Confronto[3],Cancelar: Rato → Boi → Macaco → Tigre → Dragao → Javali", "口寄せ・穢土転生", "Kuchiyose: Edo Tensei", "Invocação: Reencarnação do Mundo Impuro", "Jutsu Proibido: Volta dos Mortos", "Invocação: Reencarnação nas Terras Impuras", "Jutsu de Invocação: Reencarnação Impura");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Seis Caminhos de Pain", "É uma versão divergente da Técnica de Invocação, que permite ao usuário a invocação de outros corpos do Seis Caminhos de Pain. Ao contrário das outras invocações, esta técnica não requer nenhum contrato ou possui limitações, uma vez que o corpo que executa a invocação é conectado através de um corpo principal.", null, null, "Bater as Maos", "口寄せ・ペイン六道くちよせ・ペインりくどう", "Kuchiyose: Pein Rikudō", "Invocação: Seis Caminhos de Pain", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Serpente de Duas Cabeças", "Ichi invoca uma serpente de duas cabeças para atacar seus inimigos.", "Curto a Médio alcance", null, null, "口寄せ・双頭蛇くちよせ・そうとうじゃ", "Kuchiyose: Sōtōja", "Invocação: Serpente de Duas Cabeças", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Tora Giratória", "Rin invoca uma tora de madeira, que gira enquanto atinge o oponente.", "Curto alcance", null, null, "口寄せ・丸太転がし", "Kuchiyose: Maruta Korogashi", "Invocação: Tora Giratória", "Invocação: Troncos Rolantes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Transformação da Lâmina Relâmpago", "Esta técnica é um especial da invocação de armas ninja que permite ao usuário selar armas como espadas ninja ou kunai em um pergaminho ou em suas roupas, com antecedência, permitindo que elas sejam invocadas quando necessário. As armas ninja podem ser invocadas em um instante apenas tocando nas 'marcas' do selamento, que podem ser marcadas em qualquer lugar. A maior vantagem desta técnica é a de que o tempo entre a tomada de uma arma ninja e o tempo de ficar na posição certa é reduzido. Além disso, quando uma arma como uma Fūma Shuriken é invocada, o tempo necessário para jogá-la pode ser ainda mais reduzido, por ter preparado a arma em sua forma totalmente aberta antecipadamente.", "Curto alcance", "Rank B", null, "口寄せ・雷光剣化くちよせ・らいこうけんか", "Kuchiyose: Raikō Kenka", "Invocação: Transformação da Lâmina Relâmpago", "Transformação da Espada Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Triste Eletricidade Escura", "Ibiki invoca um gato gigante de ferro, que aparece abaixo do oponente no subsolo, dividido ao meio. A estátua fecha ao redor do alvo e é então envolta por correntes, que arrastam-o de volta para o chão.", "Curto alcance", null, "Javali → Cao → Passaro → Tigre", "口寄せ・哀暗冥電", "Kuchiyose: Aian Meiden", "Invocação: Triste Eletricidade Escura", null, null, "Invocação: Dama de Ferro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Técnica da Loja de Sapo", "Esta técnica invoca um Sapo Loja (見世蝦蟇, Mise Gama) do Monte Myōboku. Este sapo tem a capacidade de se transformar em um edifício e com o seu chakra, o usuário dá ao edifício uma concepção que eles imaginavam. O edifício terá tudo que o usuário imaginar enquanto faz a técnica. O alvo é, então, atraído para dentro, por exemplo, com a promessa de bebidas baratas. Uma vez que o alvo entrou na loja, o sapo muda de volta para sua forma original pequeno, prendendo o alvo dentro do estômago do sapo. A partir do momento em que o selo de mão de cancelamento é realizado, leva apenas alguns segundos para que ela retorne à sua forma original, ou seja, há pouca chance de escapar.", "Todos os alcances", "Rank B", null, "口寄せ・蝦蟇見世の術", "Kuchiyose: Gama Mise no Jutsu", "Invocação: Técnica da Loja de Sapo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação: Técnica do Cesto de Compra Destruidor", "Um mergulho destrutivo que tira o máximo proveito da massa corporal de um sapo gigante. A força da queda varia dependendo do utilizador. Por exemplo, no caso de alguém como Jiraiya — um dos Sannin, a invocação pode ser executada em um instante logo acima do alvo, acrescentando ainda a grande força gravitacional de várias centenas de metros de altura, mergulhando ao peso próprio do sapo.", "Todos os alcances", "Rank B", null, "口寄せ・屋台崩しの術", "Kuchiyose: Yatai Kuzushi no Jutsu", "Invocação: Técnica do Cesto de Compra Destruidor", "Invocação: Jutsu de Trazer a Casa Abaixo", "Invocação: Técnica da Casa Caiu", "Arte Ninja Invocação: Jutsu de Trazer a Casa Abaixo");
