INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação de Enma: Rodopio do Raio", "Hiruzen invoca o Rei Macaco: Enma para cortar o oponente com suas garras até mandá-lo ao ar. Ele então se lança nele e se transforma em um bastão que Hiruzen usa para atacar o oponente no chão.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação de Árvore Espontânea", "Através das células de Hashirama em seu braço direito, Danzō pode criar uma grande árvore a fim de defender-se de ataques inimigos.", "Curto alcance", null, null, "自発木口寄せ", "Jihatsu Ki Kuchiyose", "Invocação de Árvore Espontânea", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Invocação do Rinnegan", "Uma habilidade utilizada através do Caminho Animal, que permite ao usuário invocar animais com cópias de seus olhos Rinnegan e vários piercings em seus corpos. Como os Seis Caminhos de Pain, os animais invocados estão visualmente ligados ao usuário para oferecer um amplo campo de visão.", null, "Rank –", "Juntar as Maos", "口寄せ輪廻眼くちよせりんえがん", "Kuchiyose Rinnegan", "Invocação do Olho de Saṃsāra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Início do Balanço Giratório", "Naruto salta no ar, e logo após, ele cria um clone da sombra, e os dois se revezam acertando golpes no oponente, a dinâmica da afiação mantêm-no ar até o último golpe.", "Curto alcance", null, null, "独楽まわし", "Koma Mawashi", "Início do Balanço Giratório", "Pião", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ioiô da Bala Humana", "Esta é uma técnica base para a 'Formação E' (フォーメーションE, Fōmēshon Ī) usada pelo trio Ino–Shika–Chō.", "Curto a Médio alcance", "Rank –", null, "肉弾ヨーヨーにくだんヨーヨー", "Nikudan Yōyō", "Ioiô da Bala Humana", null, "Ioiô de Carne", "Ioiô de Massa Humana");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Izanagi", "Izanagi é um genjutsu que é lançado sobre o próprio usuário, é um dos mais poderosos genjutsu, pois, assim como seu homólogo, o Izanami, ele quebra as barreiras entre ilusão e realidade.", "Curto alcance", "Rank –", "Coelho → Javali → Carneiro", "イザナギ", "Izanagi", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Izanami", "Izanami é um dos supremos dōjutsu do clã Uchiha. É um genjutsu que afeta o alvo através de sensações físicas compartilhadas entre eles e o usuário. Tal como o seu homólogo, em troca da habilidade temporária que concede ao usuário, o Sharingan com que o Izanami é lançado torna-cego e perde a sua luz para sempre. De acordo com Itachi, esta técnica e o Izanagi formam um par, mas, ao contrário deste último, que é uma técnica capaz de alterar o destino, ao invés Izanami é o que 'decide-o'.", "Curto alcance", "Rank –", null, "イザナミ", "Izanami", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jateamento do Cristal de Gelo", "Haku primeiro prende os pés do oponente no chão através do congelamento de gelo sobre eles. Ele então usa um selo de mão específico que faz com que os pedaços de gelo nos pés do oponente se transformem em um grande pedaço, congelando o alvo completamente. Passando rapidamente para trás, ele atira várias senbon no oponente congelado. O pedaço de gelo é parcialmente rachado, e depois quebra para fora, danificando o oponente mais uma vez. Especificamente os cristais são capazes de se formar diretamente no corpo do inimigo de acordo com a vontade de Haku, tornando a escapatória desse jutsu praticamente impossível.", "Médio alcance", null, "Selos de mao especificos da técnica", "爆砕氷晶ばくさいひょうしょう", "Bakusai Hyōshō", "Jateamento do Cristal de Gelo", "Explosão de Cristais de Gelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jibakugan", "Este genjutsu traz emoções subconscientes negativas ou ódio da vítima. O usuário pode tirar vantagem desses sentimentos e controlar as ações da vítima. Esta técnica pode ser cancelada ao cheirar o Perfume da Flor da Lua Nova, usando o Sharingan, ou ao matar o usuário. Aqueles que são controlados pelo Jibakugan tem várias marcas estranhas sob seus olhos e na testa. Para que esta técnica seja eficaz, o contato visual direto é necessário, a não ser que o poder do usuário seja suficiente para usá-lo sem a necessidade de contato visual direto.", null, null, "Tigre", "自縛眼じばくがん", "Jibakugan", "Olho da Auto-Sujeição", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jiongu", "Jiongu é um kinjutsu secreto de Takigakure que transforma o corpo do usuário em algo similar ao corpo de uma boneca de pano, constituído por milhares de fios pretos.", null, "Rank –", null, "地怨虞じおんぐ", "Jiongu", "Medo da Terra em Crise", "Terra Rancorosa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jiongu: Disparo Final", "Kakuzu prepara as máscaras de fogo, relâmpago e vento anexadas a seu corpo para combinar seus ataques e, em seguida, criar uma poderosa explosão a fim de dizimar o inimigo.", null, null, null, "地怨虞・最終射撃ジオング・さいしゅうしゃげき", "Jiongu: Saishū Shageki", "Medo da Terra em Crise: Tiro Final", "Terra Rancorosa: Tiro Final", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jiongu: Ruptura Fixa", "Kakuzu, enquanto em sua forma monstruosa do Jiongu, salta no ar e manipula os fios pretos de modo a transformá-los em estacas grossas e altamente afiadas para perfurar rapidamente o inimigo contra o chão. Esta técnica pode ser usada contra vários alvos de uma só vez, com um impulso forte que levanta poeira.", "Curto alcance", null, null, "地怨虞・一点突破ジオング・いってんとっぱ", "Jiongu: Itten Toppa", "Medo da Terra em Crise: Ruptura Fixa", "Terra Rancorosa: Ruptura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jogada Final", "Shikamaru obtém o oponente pego com a Técnica da Costura pelas Sombras enquanto Asuma estende o chakra em sua Andorinha Voadora e corta o oponente.", "Curto alcance", null, null, "最後の一手さいごのいって", "Saigo no Itte", "Jogada Final", "Último Recurso", null, null);
