INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Juinjutsu da Família Principal Hyūga", "Este selo é marcado em todos os membros da família secundária do Clã Hyūga por membros da família principal. Seu principal objetivo é selar a capacidade de seu Byakugan quando os membros da família secundária morrem, impedindo um inimigo de conhecer os segredos do Byakugan. Seu objetivo secundário é o de controlar os membros da família secundária, uma vez que pode ser ativado à vontade por um membro da família principal, com um selo de mão único conhecido por eles, para destruir as células do cérebro do portador do selo.", null, "Rank A", null, "日向宗家の呪印術ひゅうがそうけのじゅいんじゅつ", "Hyūga Sōke no Juinjutsu", "Selo Amaldiçoado da Família Principal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Juinjutsu de Kido", "O usuário coloca um selo amaldiçoado em seu alvo para impedir que ele revele segredos que o envolvam. O selo é projetado de forma que a vítima exploda caso divulgue alguma informação confidencial.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Juinjutsu de Orochimaru", "Os Juinjutsu de Orochimaru criam selos amaldiçoados criados e utilizados por Orochimaru.", "Curto alcance", "Rank A", null, "大蛇丸の呪印術", "Orochimaru no Juinjutsu", "Técnica do Selo Amaldiçoado de Orochimaru", null, null, "Marca da Maldição");
