INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Jujutsu do Recipiente de Energia", "O Tenseigan de Hamura era protegido por um jujutsu de nome desconhecido do clã Ōtsutsuki, que impedia aqueles que não tivessem o 'chakra não diluído de Hamura' de destruí-lo. Aqueles que tentassem tocar o tesouro teriam seu chakra completamente drenado, levando-o à morte.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Julgamento de Shikamaru", "Shikamaru lança várias kunai com papéis explosivos que cercam o alvo, usando então suas sombras para puxar os fios com papéis explosivos em torno do alvo. Com o alvo restrito, Shikamaru lança sua tantō no local marcado desencadeando um desmoronamento do local previamente cavado e deixando o oponente suspenso. Ao jogar algum objeto em chamas contra os papéis explosivos, eles explodem todo o corpo do inimigo, separando-o em vários pedaços.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kagutsuchi: Chibaku Tensei", "O Kagutsuchi: Chibaku Tensei é uma técnica exclusiva do Mangekyō Sharingan e Rinnegan Supremo de Sasuke.", "Todos os alcances", null, "Juntar as Maos", "加具土命・地爆天星", "Kagutsuchi: Chibaku Tensei", "Ferramenta Adicionada do Senhor da Terra: Estrela da Devastação", "Controle de Chamas: Devastação Planetária", null, null);
