INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Kamui", "Kamui é um poderoso dōjutsu do Mangekyō Sharingan cujo cria um exclusivo e específico tipo de ninjutsu de espaço-tempo. A técnica permite que o usuário alcance dois feitos muito distintos, porém intimamente associados — teletransporte e intangibilidade.", "Todos os Alcances", "Rank –", null, "神威かむい", "Kamui", "Autoridade dos Deuses", "Poder Divino, Ninjutsu de Transporte", "Poder Divino", null);
