INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Konoha: Milhares de Mãos de Hokage Verdadeiras", "Em seu Modo Sábio, Hashirama utiliza sua gigantesca estátua de madeira. Os outros cinco Hokage canalizam seu chakra na estátua, fazendo-a resplandecer com poder. Hashirama separa a estátua de suas incontáveis mãos, e aterra uma sequência de seis socos aprimorados com chakra, rapidamente movendo a estátua para a frente do oponente após cada ataque para acertar o próximo. O último soco é um uppercut, lançando o oponente para o céu, e criando um incêndio na forma do kanji para fogo.", "Curto alcance", null, null, "木ノ葉・真数火影千手このは・しんすうほかげせんじゅ", "Konoha: Shinsū Hokage Senju", "Folha de Árvore: Milhares de Mãos de Sombras do Fogo Verdadeiras", "Folha Oculta: Kan'on dos Verdadeiros 1000 Braços de Hokage", null, null);
