INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lacuna de Sanguessuga", "Enquanto em seu estado transformado, Utakata pode escorrer um líquido extremamente alcalino, que queima ao entrar em contato. Killer B enquanto em sua plena forma de Oito-Caudas comentou que a substância era forte o suficiente para queimar sua pele. No anime, quando ele usou esta técnica em sua forma da Versão 2, o manto de chakra de Utakata ficou azul.", "Curto alcance", null, null, "蛭間", "Hiruma", "Lacuna de Sanguessuga", null, "Sanguessuga Diurna", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lance Giratório", "Utilizando seu bō especial, Yagura prende seu oponente na ponta do bastão, realiza o movimento de translação e finaliza lançando-o para trás.", "Curto alcance", null, null, "旋投", "Sentō", "Lance Giratório", "Lance Rotativo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lance do Moinho de Vento", "Sakura agarra o oponente pelo torso, pula com ele e vai para baixo, batendo ele no chão, e ainda segurando ele, joga ele atrás dela.", "Curto alcance", null, null, "風車投げ", "Fūshanage", "Lance do Moinho de Vento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança Afiada de Chidori", "O Chidori original ganhou seu poder de moldar em uma forma suficiente para uma descarga elétrica e usá-la juntamente com o próprio 'impulso' do usuário. Esta técnica utiliza a transformação da forma para transformar o Chidori original em uma lança ou forma de lança, adaptando-o para ataques de médio alcance com um alcance máximo de cerca de cinco metros. Aumentar o seu alcance significa que há menos de um risco para o usuário. Tem também a vantagem de ser capaz de ser usado para ataques surpresa e captura.", "Curto a Médio alcance", "Rank A", null, "千鳥鋭槍", "Chidori Eisō", "Lança Afiada dos Mil Pássaros", "Lança Laser de Chidori", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança Celestial Invertida", "Kinshiki golpeia seu oponente com uma clava de chakra, atirando-o para longe. Em seguida, ele avança até seu alvo em alta velocidade e, com um par de socos-ingleses, ele desfere um poderoso golpe carregado de chakra.", "Curto alcance", null, null, null, null, null, "Lança Celestial Invertida", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança Poderosa das Árvores", "Hashirama forma o selo de mão necessário e cria raízes e ramos que emergem do chão e avançam em direção ao oponente, entrelaçando uns com os outros antes de perfurar o adversário.", "Curto alcance", null, "Carneiro ou Cobra[1]", "剛槍樹", "Gōsōju", "Lança Poderosa das Árvores", "Lança da Floresta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança da Nuvem de Pó de Ouro: Impulso Externo", "Rasa desencadeia uma série de projéteis constituídos de pó de ouro a fim de atacar o oponente em rápida sucessão. O ataque pode ser feito diretamente no contra o oponente — seja no ar ou no chão – ou como um ataque surpresa.", "Curto a Médio alcance", null, null, "砂金雲槍・突き飛ばしさきんくもやり・つきとばし", "Sakin Kumoyari: Tsukitobashi", "Lança da Nuvem de Pó de Ouro: Impulso Externo", "Lança da Nuvem de Pó de Ouro: Mandar Voando", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança de Areia de Ferro", "O usuário usa sua areia de ferro para formar uma grande e longa lança para perfurar o inimigo. Seu poder é tão grande que poderia facilmente perfurar o solo e destruir uma área significativa da área também.", "Curto alcance", null, null, "砂鉄槍さてつそう", "Satetsusō", "Lança de Areia de Ferro", "Lança de Ferro da Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança de Cabelo Afiada: Poder", "A Lança de Cabelo Afiada: Poder permite o usuário estender e endurecer seu cabelo, ao ponto der tão afiado quanto uma lança, para perfurar o corpo do oponente. É útil contra múltiplos inimigos pela capacidade de formar vários ramos que permitem atacar simultaneamente.", "Curto alcance", null, null, "鋭毛槍・威", "Eikesō: I", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança de Fogo", "Após performar o selo de mão necessário, o usuário gera fogo que se estende na forma de uma lança flamejante na direção do oponente, permitindo-o perfurá-lo e queimá-lo na região perfurada. Enquanto o a lança estava posicionada no inimigo, a técnica apenas o paralisou, no entanto, ela mostrou-se extremamente letal no momento que Benga a desativou. Com força suficiente, a técnica é capaz de facilmente dissipar um clone.", "Curto alcance", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lança do Ataque do Chifre Brilhante", "Chōmei ataca seu oponente com seu chifre bifurcado. O ataque é tão forte que a vítima é deixada incapacitada pela força do vento, é incapaz de reagir ou de se defender.", "Curto alcance", null, null, "戟討耀角", "Gekitō Yōkaku", "Lança do Ataque do Chifre Brilhante", "Lança da Borda Impressionante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lançamento Desordenado de Shuriken", "O usuário dispara inúmeras shuriken em direção ao inimigo. A quantidade lançada chega a ser grande a ponto de impossibilitar o desvio e/ou impedimento do movimento, a menos que faça segundos antes de ser acertado. As shuriken não seguem o mesmo padrão, esvaindo-se para todos os ângulos à sua frente.", "Curto a Médio alcance", null, null, "手裏剣乱れ撃ちしゅりけんみだれうち", "Shuriken Midare Uchi", "Lançamento Desordenado de Shuriken", "Dança da Shuriken Selvagem (手裏剣乱舞, Shuriken Ranbu)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lançamento de Equipamentos Ninja!", "Konohamaru se agacha e começa a lançar diversos tipos de equipamentos ninja no ar. Os equipamentos podem ser ofensivos, como itens explosivos e veneno; ou suplementares, como kunai e shuriken.", "Curto alcance", null, null, "忍具ぽいぽい!にんぐぽいぽい！", "Ningu Poipoi!", "Lançamento de Equipamentos Ninja!", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lançamento de Penas", "Satori lança suas penas em direção aos oponentes em alta velocidade, capazes de perfurar chão de pedra e madeira aplicada ao chakra.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lançamento do Leque", "Balançando seu Leque Dobradiço Gigante contra o oponente e para cima, o usuário gera um redemoinho que levanta o oponente, causando diversos danos.", "Curto a Médio alcance", null, null, "投扇", "Tōsen", "Lançamento do Leque", "Lançamento do Ventilador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lanças Gêmeas de Pó de Ouro", "Rasa desfere uma onda de espinhos crescentes de pó de ouro.", "Curto a Médio alcance", null, null, "砂金双槍さきんそうそう", "Sakin Sōsō", "Lanças Gêmeas de Pó de Ouro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lanças de Gelo do Assassinato Certeiro", "Aproveitando-se das partículas de gelo na atmosfera, o usuário as condensa em espinhos gigantes, de forma instantânea, com capacidade de perfurar através de um casebre. Haku pode utilizar esta técnica involuntariamente, defendendo-se da multidão que foram em seu encontro.", "Curto a Médio alcance", null, null, "必殺氷槍ひっさつひょうそう", "Hissatsu Hyōsō", "Lanças de Gelo do Assassinato Certeiro", "Quebra da Lança de Gelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lariat", "O Lariat é um famoso movimento de taijutsu usado pelo Raikage e seu parceiro.", "Curto alcance", "Rank A", null, "雷犂熱刀ラリアット", "Rariatto", "Arado Elétrico com Lâmina Quente", "Laço", "Lâminas Incandescentes do Arado Trovejante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Laço do Clone", "Naruto cria dois clones e após uma curta combinação, eles entrelaçam as suas pernas e fazem um giro, esmagando seu oponente ao cair sobre ele.", "Curto alcance", null, null, "分身投げ縄", "Bunshin Nagenawa", "Laço do Clone", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Leitura Visual da Mente", "Usando essa técnica, o usuário olha para um alvo e tece um sinal com seu olho, coletando informações sem que ele perceba. O usuário ainda é capaz de executar esta neles mesmos para restaurar ou remover informações em sua própria mente. Isto pode também ser usado em um alvo que está atualmente lendo a mente do utilizador.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Leque Voador", "Com um balanço de seu Leque Dobradiço Gigante, Temari cria uma forte corrente de vento capaz de fazer seu leque voar, permitindo com que ela percorra longas distâncias. De acordo com a intensidade das correntes de vento, Temari pode ganhar velocidade e altitude quase que instantaneamente.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lesma Trituradora", "Tsunade invoca uma miniatura de Katsuyu e joga ela no ar, em seguida ficando gigante, esmagando o oponente, bem similar com a Invocação: Técnica do Cesto de Compra Destruidor.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Letras de Areia", "Letras de Areia é uma técnica que envolve a manipulação de areia para utiliza-la como um método de comunicação.", "Todos os alcances", null, null, "砂文字", "Suna Moji", "Letras de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Aço: Armadura Impenetrável", "Esta técnica permite ao usuário transformar seu corpo em aço negro, fazendo daquela parte invulnerável. Hiruko afirma que as lâminas não o afetam totalmente devido a essa habilidade. Hiruko utiliza-a para contrariar a tantō de Sai, fazendo com que a pressão a partisse ao meio no momento do impacto.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Aço: Pilar de Aço", "Após performar os selos de mão necessários, o usuário ergue um pilar de aço afiado a partir de um quadrado do chão para empalar o alvo à sua frente.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Calor: Assassinato de Vapor Extremo", null, "Curto alcance", null, null, "灼遁・過蒸殺しゃくとん・かじょうさつ", "Shakuton: Kajōsatsu", "Liberação de Calor: Assassinato de Vapor Extremo", null, "Estilo do Calor: Morte por Vapor", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Calor: Auréola do Furacão Negro em Espiral", "Esta técnica combina a Liberação de Vento: Rasenshuriken de Naruto Uzumaki com a Liberação de Chama: Kagutsuchi de Sasuke Uchiha. Por causa da razão do vento para a chama ser perfeitamente equilibradas, as chamas são bastante reforçadas, fazendo com que tudo o que é atingido por esta técnica seja envolto em um turbilhão de inextinguíveis chamas negras do Amaterasu.", "Curto alcance", null, null, "灼遁・光輪疾風黒旋丸", "Shakuton: Kōrin Shippū Kuro Rasen", "Liberação de Calor: Auréola do Furacão Negro em Espiral", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Calor: Auréola do Furacão de Flecha Negra de Estilo Zero", "Esta técnica combina a Liberação de Vento: Rasenshuriken de Naruto Uzumaki com a Liberação de Chama: Kagutsuchi de Sasuke Uchiha juntamente com suas variantes. Por causa da razão do vento para a chama ser perfeitamente equilibrada, as chamas são bastante reforçadas, fazendo com que tudo o que é atingido por esta técnica seja envolto em um turbilhão de inextinguíveis chamas negras do Amaterasu.", "Todos os alcances", null, null, "灼遁・光輪疾風漆黒矢零式しゃくとん・こうりんしっぷうしっこくやれいしき", "Shakuton: Kōrin Shippū Shikkokuya Reishiki", "Liberação de Calor: Auréola do Furacão de Flecha Negra de Estilo Zero", null, "Técnica Escaldante do Vendaval Destruidor da Flecha Negra", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Calor: Técnica do Alargamento de Incineração", "Pakura combina várias de suas esferas flamejantes para criar uma única massa, que depois explode violentamente com força e poder suficiente para incinerar uma grande área em torno dela. Quando isto acontece, o vapor pode ser visto a partir da área emitida.", "Todos os alcances", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Honoikazuchi", "A Liberação de Chama: Honoikazuchi é uma técnica utilizada por Sasuke Uchiha através do poder do seu Mangekyō Sharingan.", "Curto alcance", null, null, "炎遁・火雷えんとん・ほのいかづち", "Enton: Honoikazuchi", "Liberação de Chama: Chama Relâmpago", null, null, "Estilo Inferno: Honoikazuchi");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Kagutsuchi", "A Liberação de Chama: Kagutsuchi é uma técnica que consiste na aplicação da Transformação da Forma nas chamas negras do Amaterasu, que permite ao usuário manipulá-las à vontade. Sasuke é capaz de criar o Amaterasu a partir de seu olho esquerdo, enquanto manipula as chamas pelo seu olho direito; ao contrário de Indra Ōtsutsuki, que controla suas chamas pelo olho esquerdo. Depois de entrar em sua forma final, o Susanoo de Sasuke ganhou uma esfera de chamas negras, que serve como uma fonte alternativa de chamas para manipular com esta técnica. Esta técnica também permite ao usuário apagar essas chamas, bem como manipular as chamas já existentes do Amaterasu no campo de batalha, gastando uma quantidade de chakra menor.", "Curto a Médio alcance", "Rank –", null, "炎遁・加具土命えんとん・カグツチ", "Enton: Kagutsuchi", "Liberação de Chama: Ferramenta Adicionada do Senhor da Terra", "Ferramenta de Fogo Adicionada do Senhor da Terra (火之迦具土, Hinokagutsuchi)[2]", "Estilo das Chamas: O Deus do Fogo", "Estilo Inferno: Controle de Chamas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Kagutsuchi — Chama Voadora", "Sasuke manipula suas chamas negras para criar uma espada que ele empunha em sua mão. Ao balançá-la, ele pode desencadear uma onda de espinhos que perfuram e incineram seu alvo.", "Curto a Médio alcance", null, null, "炎遁・加具土命 飛炎えんとん・カグツチ ひえん", "Enton: Kagutsuchi — Hien", "Liberação de Chama: Ferramenta Adicionada do Senhor da Terra — Chama Voadora", "Estilo Inferno: Controle de Chamas Saltitantes", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Kagutsuchi — Passo do Céu", "Sasuke ataca o oponente com uma espada infundida de chakra relâmpago, jogando-o para cima, e em seguida, Sasuke cria vários picos de Amaterasu que entram em erupção a partir do solo, atingindo-o no ar e queimando o oponente até a morte.", "Curto alcance", null, null, "炎遁・加具土命 天の段えんとん・カグツチ てんのだん", "Enton: Kagutsuchi Ten no Dan", "Liberação de Chama: Ferramenta Adicionada do Senhor da Terra — Passo do Céu", "Estilo Inferno: Controle de Chama da Etapa do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Kagutsuchi — Porco-Espinho", "Sasuke foca as chamas do Amaterasu em seu oponente, transformando em vários espinhos, prejudicando seu oponente e jogando-o para trás.", "Curto alcance", null, null, "炎遁・加具土命　豪猪えんとん・カグツチ　やまあらし", "Enton: Kagutsuchi — Yamārashi", "Liberação de Chama: Ferramenta Adicionada do Senhor da Terra - Porco-espinho", "Estilo Inferno: Controle de Chamas do Ouriço", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Rasenshuriken", "Após utilizar o Traje Majestoso: Susanoo, Sasuke combina as suas chamas negras com o Rasenshuriken de Naruto, e então a técnica é lançada contra o oponente, causando uma explosão após a ocorrência do impacto.", "Todos os alcances", null, null, "炎遁・螺旋手裏剣えんとん・らせんしゅりけん", "Enton: Rasenshuriken", "Liberação de Chama: Rasenshuriken", "Estilo Inferno: Rasen Shuriken", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Susanoo Ōdachi", "Sasuke, enquanto em seu Susanoo reforçado por Jūgo com senjutsu, acerta um poderoso golpe com sua Espada de Kagutsuchi no oponente.", "Curto a Médio alcance", null, null, "炎遁・須佐能乎大太刀", "Enton: Susanoo Ōdachi", "Liberação de Chama: Grande Espada Daquele com a Habilidade de Ajudar por Todos os Meios", "Estilo Inferno: Susano'o Odachi", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Susanoo Kagutsuchi", "Uma aplicação de Kagutsuchi através do Susanoo pelo qual as chamas eternas do Amaterasu são formadas em armas do orbe negro segurado no braço direito secundário do Susanoo de Sasuke Uchiha.", "Todos os alcances", null, null, "炎遁・須佐能乎の加具土命えんとん・すさのカグツチ", "Enton: Susanoo Kagutsuchi", "Liberação de Chama: Ferramenta Adicionada do Senhor da Terra com a capacidade de ajudar por todos os meios", "Estilo Inferno: Controle de Chamas de Susano'o", "Estilo das Chamas: Susanoo Deus do Fogo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Técnica da Grande Bola de Fogo", "Sasuke lança uma bola de fogo envolvida em chamas negras, para aumentar significativamente seu poder destrutivo.", "Curto alcance", null, "Selo do Confronto", "炎遁・豪火球の術えんとん・ごうかきゅうのじゅつ", "Enton: Gōkakyū no Jutsu", "Liberação de Chama: Técnica da Grande Bola de Fogo", "Estilo Inferno: Jutsu Bola de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Ultra-Bola da Besta com Cauda", "Essa é uma técnica utilizada por Naruto e Sasuke após unir Modo de Chakra da Besta com Cauda com o Susanoo.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Chama: Yasaka Magatama", "Utilizando a esfera de chamas negras na terceira mão de seu Susanoo, o usuário é capaz de produzir rapidamente uma série de projéteis magatama, que são compostos inteiramente das chamas. Com o impacto, as vítimas são forçosamente jogadas ao chão e rapidamente incineradas.", "Curto a Médio alcance", null, null, "炎遁・八坂ノ勾玉えんとん・やさかのまがたま", "Enton: Yasaka no Magatama", "Liberação de Chama: Oito Inclinações da Joia Curva", "Estilo Inferno: Yasaka Magatama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Acampamento da Parede de Cristal", "Guren é capaz de libertar uma onda de chakra molecular de cristal, transformando-a em uma cadeia de cristal quase impenetrável, sendo capaz de regenerar-se a cada ataque. Este cristal contêm uma resistência semelhante ao Liberação de Cristal: Técnica do Labirinto de Cristal de Jade. Somente os Insetos Avançados de Shino foram capazes de debilitar a imensa resistência, potência e regeneração da cadeia de cristal, após isso, Kakashi finaliza a devastação da cadeia de cristal utilizando o Corte Relâmpago.", null, null, "Cobra → Selo de mao especifico da técnica → Cão → Selo de mão específico da técnica → Selo de mão específico da técnica", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Agulhas de Cristal", "Guren é capaz de formar agulhas de cristais rosados completamente brilhantes, sendo todas afiadas e longas. Após formá-las, Guren lança-as na direção do inimigo de modo totalmente veloz, empalando ou aprisionando o adversário com grande rapidez e precisão, deixando-o com sérios ferimentos.", "Todos os alcances", null, null, "晶遁・結晶針しょうとん・けっしょうはり", "Shōton: Kesshōhari", "Liberação de Cristal: Agulhas de Cristal", null, null, "Estilo Cristal: Agulhas de Cristal de Jade");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Colunas Hexagonais de Cristais de Jade", "Esta técnica foi utilizada após o surgimento de Isobu, tornando a artimanha de Yūkimaru ainda mais avançada, fazendo Isobu ouvir o chamado de Yūkimaru, no entanto, com o aprimoramento dos medicamentos de Kabuto. Guren é capaz de formar cinco colunas cristalinas gigantes, brilhantes e rosadas em torno de si.", null, null, "Rato → Cobra → Carneiro → Cao → Tigre → Bater as Mãos", "晶遁・翠晶六角柱しょうとん・すいしょうろっかくちゅう", "Shōton: Suishō Rokkakuchū", "Liberação de Cristal: Colunas Hexagonais de Cristais de Jade", null, null, "Estilo Cristal: Colunas Hexagonais de Cristais de Jade");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Corda da Glória", "Após a utilização dos Pontos de Pressão de Dano e Morte, Guren encerra-se em uma prisma de cristal. Ao formar a prisma, ela fica imóvel sobre o céu, tornando-se totalmente resistente a várias técnicas, sendo capaz de suportar uma barragem de Rasengan contínua, somente desfazendo-se ao final da utilização de sua técnica. Além disso, a técnica é capaz de ser ofensiva, focando o chakra em explosões escaldantes completamente poderosas, lançadas por meio da ponta da prisma de modo extremamente veloz, gerando linhas brancas ao céu e atacando o inimigo com raios totalmente potentes.", "Todos os alcance", null, null, "晶遁・一糸光明", "Shōton: Isshi Kōmyō", "Liberação de Cristal: Corda da Glória", null, null, "Estilo Cristal: Seta de Luz");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Dança Selvagem da Shuriken", "Guren envolve-se em partículas de cristal, condensando-as em numerosas shuriken que então chovem sobre o oponente.", "Todos os alcances", null, "Carneiro", "晶遁・手裏剣乱舞しょうとん・しゅりけんらんぶ", "Shōton: Shuriken Ranbu", "Liberação de Cristal: Dança Selvagem da Shuriken", null, null, "Estilo Cristal: Dança Selvagem da Shuriken");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Destruição do Dragão de Cristal em Queda", "O usuário cristaliza um material na área, e, em seguida, transforma-o em um dragão. Ele pode ser usado como um meio para viajar ou para atacar um alvo. Vários dragões também podem ser feitos de uma só vez e manipulados.", "Todos os alcances", null, "Javali → Carneiro", "晶遁・破晶降龍しょうとん・はしょうこうりゅう", "Shōton: Hashō Kōryū", "Liberação de Cristal: Destruição do Dragão de Cristal em Queda", null, null, "Estilo Cristal: Estouro do Dragão de Cristal em Queda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Espelho de Cristal de Jade", "Guren cria um grande espelho na forma de um floco de neve, o que ela pode usar para executar a Técnica do Clone de Cristal de Jade.", null, null, "Carneiro → Cao → Macaco → Mãos Abertas", "晶遁・翠晶鏡", "Shōton: Suishō Kyō", "Liberação de Cristal: Espelho de Cristal de Jade", null, null, "Estilo Cristal: Espelho de Cristal de Jade");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Espinhos Crescentes de Cristal", "Guren cria cristais crescentes como espinhos que ela pode manipular para crescer continuamente na direção de seu alvo.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Formação da Oitava Parede de Cristal de Jade", "Com esta técnica Guren envolve uma imensa área em cristais, sendo assim, ela é capaz de cristalizar todos os adversários em uma abóbada de cristal imensurável. Além disso, a técnica é totalmente resistente, deixando os inimigos imóveis, assim, a técnica torna-se uma cadeia de cristal completamente poderosa a ser utilizada para capturar o Sanbi. Infelizmente a técnica não teve potencia suficiente para deixá-lo quieto, desfazendo-se logo em seguida.", "Todos os alcances", null, "Rato → Cao → Cavalo → Carneiro → Javali → Dragão → Rato → Carneiro → Javali", "晶遁・翠晶壁八の陣しょうとん・すいしょうほけはちのじん", "Shōton: Suishōheki Hachi no Jin", "Liberação de Cristal: Formação da Oitava Parede de Cristal de Jade", null, null, "Estilo Cristal: Paredes de Cristal da Oitava Formação");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Fruta Carmesim", "Com esta técnica Guren forma uma abóbada de cristal para envolver um alvo. A abóbada é altamente resistente, geralmente sendo utilizada para amparar um companheiro ou interceptar o adversário, sendo assim, ela se torna completamente eficaz na defensiva, tanto interna, quanto externa.", "Curto Alcance", null, "Carneiro", "晶遁・紅の果実", "Shōton: Kurenai no Kajitsu", "Liberação de Cristal: Fruta Carmesim", null, null, "Frutificar Estilo Cristal: Fruta Carmesim");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Lança de Cristal", "Esta técnica permite Guren criar cristal como uma lança grande e afiada ao redor de seu antebraço, que ela pode usar para empalar seu alvo.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Lâmina de Cristal de Jade", "Com esta técnica, Guren pode formar rapidamente uma lâmina de cristal em seu braço, e usá-la de uma forma que lembra uma tantō. As lâminas podem ser formadas em ambos os braços, se necessário.", "Curto Alcance", null, "Cavalo → Boi → Javali → Cobra", "晶遁・翠晶刀", "Shōton: Suishōtō", "Liberação de Cristal: Lâmina de Cristal de Jade", null, null, "Lâmina de Cristal de Jade");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Onda da Prisão de Cristal", "Esta é a versão avançada da Liberação de Cristal: Prisão Pentagonal de Cristal, em que Guren pode envolver múltiplos adversários em cristal, como mostrado ao capturar os Kikaichū de Shino Aburame. Geralmente as técnicas de prisão de Guren formam-se no solo, no entanto, esta técnica é capaz de aprisionar os inimigos em pleno ar.", "Longo Alcance", null, "Selos de mao especificos da técnica", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Prisão Pentagonal de Cristal", "Guren é capaz de envolver múltiplos adversários em cristal, deixando os inimigos totalmente imóveis. No momento em que o cristal é quebrado, os corpos das vítimas se desfazem, somente sobrando mínimos cristais ao solo. Após os Kikaichū de Shino serem examinados quando atingidos pela técnica, foi demonstrado que eles estavam com um padrão completamente congelados.", null, null, null, "晶遁・結晶五角牢", "Shōton: Kesshō Gokakurō", "Liberação de Cristal: Prisão Pentagonal de Cristal", null, null, "Estilo Cristal: Prisão de Cristal");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Prisão do Cristal de Jade", "Guren é capaz de envolver o adversário em cristais, semelhante ao Liberação de Cristal: Prisão Pentagonal de Cristal, porém esta técnica não é capaz de aprisionar mais que uma única vítima. No momento em que o cristal é quebrado, o corpo do inimigo se desfaz, somente sobrando mínimos cristais ao solo. Porém, esta técnica não teve sucesso ao aprisionar Hinata que foi capaz de formar uma camada de Chakra em volta de seu corpo quando foi atingida pela técnica, ignorando a desintegração de si mesma.", "Curto alcance", null, "Carneiro", "晶遁・翠晶牢の術しょうとん・すいしょうろうのじゅつ", "Shōton: Suishō Rō no Jutsu", "Liberação de Cristal: Prisão do Cristal de Jade", null, null, "Estilo Cristal: Prisão do Cristal de Jade");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Roda de Cristal", "Guren forma uma roda em volta dela, semelhante a um anel de cristal, sendo capaz de manter-se sobre a água e aumentar incrivelmente sua velocidade, utilizando-a para se aproximar de seus adversários.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Técnica do Cruzamento de Deuses", "Guren é capaz de formar cristais completamente afiados e resistentes somente tocando o solo, gerando um amplo caminho de cristal, capturando e cortando vários adversários de forma simultânea, deixando os inimigos totalmente feridos.", "Médio a Longo Alcance", null, "Cavalo → Macaco → Carneiro → Javali", "晶遁・御神渡りの術しょうとん・おみわたりのじゅつ", "Shōton: Omiwatari no Jutsu", "Liberação de Cristal: Técnica do Cruzamento de Deuses", null, null, "Estilo Cristal: Técnica do Caminho Divino");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Cristal: Técnica do Labirinto de Cristal de Jade", "Primeiramente, Guren forma múltiplas flores cristalinas, após formá-las, Guren é capaz de fazê-las nascer e modelarem um imenso labirinto de cristais vermelhos. Esta técnica deixa todos inimigos aprisionados, confusos, isso é feito pela luz que atinge os cristais avermelhados e refletem as imagens dos adversários, detalhes e caminhos que são aprisionados na técnica, intimidando-os, semelhante a um espelho. Além disso, a técnica bloqueia até os usuários do Byakugan, isto acontece pela completa reflexão do campo.", "Todos os alcances", null, "Javali → Macaco → Cavalo → Cao → Carneiro", "晶遁・翠晶迷宮の術しょうとん・すいしょうめいきゅうのじゅつ", "Shōton: Suishō Meikyū no Jutsu", "Liberação de Cristal: Técnica do Labirinto de Cristal de Jade", null, null, "Estilo Cristal: Labirinto de Cristal de Jade");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Dinheiro: Técnica de Ajuda", "A Liberação de Dinheiro: Técnica de Ajuda é uma artimanha inventada por Kunihisa em estímulo das técnicas ninjas de Naruto.", "Curto alcance", null, null, "金遁・たすけての術", "Kinton: Tasukete no Jutsu", "Liberação de Dinheiro: Técnica de Ajuda", null, null, "Estilo Dinheiro: Salve-me!");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Dinheiro: Técnica do Clone de Dinheiro", "A Liberação de Dinheiro: Técnica do Clone de Dinheiro é uma técnica ilusória inventada por Kunihisa, baseada na Técnica dos Clones das Sombras de Naruto Uzumaki.", "Curto alcance", null, "Selo de Clonagem", "金遁・金分身の術", "Kinton: Kin Bunshin no Jutsu", "Liberação de Dinheiro: Técnica do Clone de Dinheiro", null, null, "Estilo Dinheiro: Clone de Dinheiro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Escuridão: Boca Devoradora", "Essa é uma técnica de Liberação de Escuridão usada por Hiruko, que é capaz de engolir o chakra de qualquer pessoa nas proximidades. Com isso ele é capaz de adquirir sua afinidade elemental e manipula-la livremente. Também é possível absorver e adquirir múltiplos ninjutsu simultaneamente.", "Todos os alcances", null, "Rato → Coelho → Passaro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Escuridão: Boca Inaladora", "Hiruko usa esta técnica através da Técnica Quimera, a fim de extrair forçadamente a energia física e espiritual de outro ninja e absorvê-lo através do quadrado superior da marca em sua mão esquerda, mesmo a uma distância considerável. Além disso, parece que ele é capaz de remover completamente o chakra da vítima, podendo causar a morte.", "Todos os alcances", null, "Cavalo", "冥遁・吸穴孔めいとん・きゅうけつこう", "Meiton: Kyūketsukō", "Liberação de Escuridão: Boca Inaladora", null, null, "Estilo Sombra: Buraco Negro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Escuridão: Julgamento", "Hiruko converte o chakra absorvido através do jutsu Liberação de Escuridão: Boca Inaladora em uma energia fantasmagórica semelhante a chamas azuis-claras, jogando de volta em seus inimigos com a força ampliada para jogá-los longe. A energia é expelida através do quadrado inferior da marca na mão esquerda de Hiruko.", null, null, null, "冥遁・邪自滅斗めいとん・じゃっじめんと", "Meiton: Jajimento", "Liberação de Escuridão: Julgamento", null, null, "Estilo Sombra: Julgamento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Escuridão: Onda Ceifadora", "Hiruko libera uma onda de escuridão a partir do seu corpo, que se expande para todas as direções, absorvendo o chakra de todos em que ela tocar. Com isso ele foi capaz de dissipar centenas de clones das sombras.", "Todos os alcances", null, "Cavalo", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Explosão: Palma Explosiva", "O usuário cria uma pequena explosão em sua palma. Se o ângulo for devidamente cronometrado, ela pode ser usada para enviar para trás kunai jogadas pelo inimigo. Gari também usou esta técnica para cortar o pano de ligação que Maki utilizou para limitar Pakura.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Explosão: Punho de Mina Terrestre", "Depois de estabelecer contato direto com seu oponente através de um simples soco, o usuário utiliza a Liberação de Explosão para, posteriormente, gerar uma explosão enorme do ponto inicial de contato físico. Esta explosão é poderosa o suficiente para obliterar completamente um adversário muito maior do que o usuário. Devido à direcionalidade da explosão, o usuário minimiza qualquer repercussão potencial, como ser atingido por detritos resultantes e até mesmo recuar parece ser completamente desnecessário.", "Curto alcance", null, null, "爆遁・地雷拳ばくとん・じらいけん", "Bakuton: Jiraiken", "Liberação de Explosão: Punho de Mina Terrestre", null, "Estilo da Explosão: Punho de Minas Explosivas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fervura: Força Inigualável", "Liberação de Fervura: Força Inigualável é um estilo de luta baseado em ninjutsu de Liberação de Fervura, usado por ambos Han e o Cinco-Caudas, Kokuō.", "Curto alcance", null, null, "沸遁・怪力無双ふっとん・かいりきむそう", "Futton: Kairiki Musō", "Liberação de Fervura: Força Inigualável", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fervura: Técnica da Lótus Pecaminosa", "Mei manipula seu chakra para criar vapor a ser lançado a partir do solo que, em seguida, faz com que ele crie explosões corrosivas em diferentes intervalos. A corrosividade da técnica apenas pode causar algum dano grave para o corpo do alvo, mas não possui propriedades ácidas totais que pode derretê-lo instantaneamente. Ela usa essa técnica depois que ela ouve mal Ao.", "Todos os alcances", null, null, null, null, "Liberação de Fervura: Técnica da Lótus Pecaminosa", "Estilo Vapor: Jutsu da Lótus Pecaminosa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fervura: Técnica da Névoa Qualificada", "O usuário cria uma nuvem de névoa que ele libera de sua boca. Esta névoa tem propriedades extremamente corrosivas, capazes de derreter até os ossos do Susanoo de Sasuke Uchiha. A técnica tem uma desvantagem inerente que os seus efeitos são indiscriminados, limitando o seu uso em áreas que não contêm aliados. No caso em que a névoa escapa da área de contenção, o utilizador pode ajustar o nível de pH para níveis mais seguros da névoa.", "Curto a Médio alcance", "Rank –", "Cavalo", "沸遁・巧霧の術ふっとん・こうむのじゅつ", "Futton: Kōmu no Jutsu", "Liberação de Fervura: Técnica da Névoa Qualificada", null, "Estilo da Ebulição: Técnica da Névoa Engenhosa", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fervura: Técnica do Corpo de Vapor", "O usuário é capaz de desfazer seu corpo em vapor para se mover sem ser percebido e ficar invulnerável a ataques. Conforme sua vontade, ele pode reunir o vapor para reconstituir seu corpo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Aranha da Terra Carmesim", "O usuário cospe fogo que envolve o inimigo e, em seguida, se transforma em uma aranha que ataca o inimigo. Esta técnica eficaz intercepta e ataca um adversário, ao mesmo tempo, e pode ser usada para forçá-los a outro ataque.", "Curto a Médio alcance", null, "Tigre → Cavalo → Rato → Cao → Tigre", "火遁・紅地蜘蛛かとん・べにじぐも", "Katon: Benijigumo", "Liberação de Fogo: Aranha da Terra Carmesim", null, null, "Estilo Fogo: Aranha Vermelha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Bestas Ardentes", "O usuário é capaz um lançar um poderoso fogo ardente que assume a forma de um cachorro e um macaco gigantes. As bestas avançam em direção aos seus oponentes e podem atacar múltiplos alvos simultaneamente.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Bombardeio do Tigre de Chamas", "Jakō Ryūdōin invoca um tigre feito de chamas a partir do pergaminho em suas costas, causando enorme dano para a equipe do jogador. Além disso, tem a habilidade de anexar ao oponente e explodir com muito impacto. Esta técnica é semelhante à Liberação de Fogo: Técnica do Grande Dragão de Fogo e ao Ataque do Dragão Explosivo, exceto pelo fato de usar um tigre ao invés de um dragão. É também um pouco semelhante a um papel explosivo, mas muito mais forte.", "Médio alcance", null, null, "火遁・炎虎爆殺", "Katon: Enko Bakusatsu", "Liberação de Fogo: Bombardeio do Tigre de Chamas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Campo da Cortina em Extinção", "Jiraiya pisa no chão, criando uma rachadura que vai até o seu oponente. Ele, em seguida, lança fogo na rachadura, fervendo a terra abaixo de seu oponente e batendo as mãos, criando uma explosão grande e ardente, que finaliza com seu oponente.", "Médio alcance", null, null, "火遁・消し幕が原かとん・けしまくらはら", "Katon: Keshimakuga Hara", "Liberação de Fogo: Campo da Cortina em Extinção", "Estilo Fogo: Ataque de Desaparecimento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Captura por Chamas", "Transformando seu chakra em fogo, o usuário cria um fluxo de chamas na forma de um chicote que pode ser usado para bater em um oponente e lançá-lo longe.", "Curto a Médio alcance", null, null, "火遁・捕獲炎", "Katon: Hokaku En", "Liberação de Fogo: Captura das Chamas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Chama Alta", "Trata-se de uma técnica de Liberação de Fogo utilizada por Ino para gerar uma chama super alta e grelhar os ingredientes das Pílulas de Ração Militar.", "Curto alcance", null, "Juntar as Maos → Selo Desconhecido → Selo Desconhecido", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Dança Selvagem da Onda Explosiva", "É uma técnica poderosa que combina o poder do Mangekyō Sharingan e a Liberação de Fogo.", "Curto a Médio alcance", "Rank B", "Tigre", "火遁・爆風乱舞", "Katon: Bakufū Ranbu", "Liberação de Fogo: Dança Selvagem da Onda Explosiva", "Estilo Fogo: Tempestade Explosiva", "Estilo do Fogo: Vendaval Dançante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Execução de Fogo", "Esta técnica cria um fluxo de fogo que pode ser manipulado em diversas formas (até agora, anéis de fogo que correm sobre o solo ou um círculo de fogo voando através do ar) antes de atingir o alvo. Ele também pode ser usado em combinação com a Liberação de Vento: Vento Divino para criar um tornado de chamas, ou com a Liberação de Vento: Vento Divino das Montanhas para liberar um inferno de chamas maciço.", "Médio alcance", null, "Tigre", "火遁・火走り", "Katon: Hibashiri", "Liberação de Fogo: Fogo em Execução", null, null, "Estilo Fogo Arte Ninja: Corrida de Fogo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Flor da Fênix Sábia do Prego Carmesim", "O usuário libera uma enxurrada de shuriken com uma mão, e, ao mesmo tempo, exala fogo infundido com chakra nelas, a fim de aumentar o potencial de destruição das armas, tornando-as capazes de causar queimaduras graves em contato direto com o alvo, ou qualquer outro objeto preso dentro de sua trajetória. Esta técnica pode servir como um ataque duplo: o dano causado pelas armas mais o dano causado pelas chamas.", "Curto a Médio alcance", null, "Rato → Tigre → Cao → Boi → Coelho → Tigre → Macaco", "火遁・鳳仙花爪紅かとん・ほうせんかつまべに", "Katon: Hōsenka Tsumabeni", "Liberação de Fogo: Flor da Fênix Sábia do Prego Carmesim", null, "Estilo do Fogo: Flores de Fogo", "Estilo Fogo: Arte da Flor de Fênix");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Formação de Chama Explosiva", null, "Curto à Médio alcance", null, "Tigre → Rato → Passaro → Cobra", "火遁・起爆炎陣かとん・きばくえんじん", "Katon: Kibaku Enjin", "Liberação de Fogo: Formação de Chama Explodindo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Aniquilação do Fogo", "Uma técnica onde chakra amassado dentro do corpo é convertida em fogo, e depois expulso da boca e moldado em uma parede maciça de chamas intensas, que abrange uma ampla gama. Isso faz com que a técnica seja extremamente difícil de evitar ou conter, com isso exigindo que os esforços combinados de vários usuários de Liberação de Água, a fim de extinguir as chamas.", "Médio a Longo alcance", null, "Cavalo", "火遁・豪火滅却かとん・ごうかめっきゃく", "Katon: Gōka Mekkyaku", "Liberação de Fogo: Grande Aniquilação do Fogo", "Estilo Fogo: Grande Inferno", "Estilo do Fogo: Aniquilação por Fogo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Bala de Chama", "Esta técnica é uma versão senjutsu alterada da Liberação de Fogo: Projétil de Chamas. O usuário preenche sua cavidade bucal com óleo, criado dentro do corpo com o chakra do Senjutsu. Este óleo é, então, expelido, e ao mesmo tempo inflama, o que cria uma chama gigante, equivalente a múltiplos marcadores da chama. Essa chama pode completamente cortar a rota de fuga diante de um inimigo encurralado em um corredor ou sala, ou pode queimar completamente a respectiva rota de fuga.", "Curto a Médio alcance", "Rank B", null, "火遁・大炎弾かとん・だいえんだん", "Katon: Dai Endan", "Liberação de Fogo: Grande Bala de Chama", null, "Estilo do Fogo: Grande Bola de Fogo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Bola de Chamas", "Itachi exala uma esfera de fogo do tamanho do meteoro que leva o inimigo para longe e causa uma enorme explosão com o impacto. O impacto faz com que o solo ao redor Itachi quebre enquanto ele permanece imperturbável. Isso é muito parecido com a Liberação de Fogo: Técnica da Grande Bola de Fogo, mas em uma versão maior e mais destrutiva.", "Todos os alcances", null, "Cavalo → Tigre", "火遁・豪焔球", "Katon: Gōenkyū", "Liberação de Fogo: Grande Bola de Chamas", "Estilo Fogo: Bola de Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Destruição de Fogo", "Uma técnica onde chakra é amassado dentro do corpo do usuário e convertido em fogo, depois expelido pela boca em um fluxo enorme de chamas intensas que podem destruir uma vasta área em chamas, engolindo o alvo em um verdadeiro mar de fogo. Se for utilizado em cima de um terreno combustível denso, tal como a floresta, a área confinada pode aumentar muito a possibilidade de incineração do alvo.", "Todos os alcances", null, "Rato → Boi → Cao → Cavalo → Macaco → Javali → Tigre", "火遁・豪火滅失かとん・ごうかめっしつ", "Katon: Gōka Messhitsu", "Liberação de Fogo: Grande Destruição de Fogo", null, "Estilo do Fogo: Devastação por Fogo", "Estilo Fogo: Destruidor Majestoso");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Flor de Chama", "O usuário molda seu chakra em fogo e, em seguida, dispara uma infinidade de bolas de fogo contra o inimigo. As bolas de fogos causam severos danos no terreno, bem como destruir abrigos de Liberação de Terra ou dominar técnicas de Liberação de Água.", "Longo alcance", null, null, "火遁・豪炎華かとん・ごうえんか", "Katon: Gōenka", "Liberação de Fogo: Grande Flor de Chama", "Estilo Fogo: Flor de Chama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Rasengan em Chamas", "Jiraiya cria um Rasengan normal, e, em seguida, cobre com chakra fogo, a fim de aumentar o poder de destruição. Ao atingir o oponente, as chamas aumentam e criam um redemoinho de fogo, para consumir o corpo do oponente por completo.", "Curto alcance", null, null, "火遁・豪炎螺旋丸かとん・ごうえんらせんがん", "Katon: Gōen Rasengan", "Liberação de Fogo: Grande Esfera Espiral em Chamas", "Fogo do Inferno: Rasengan", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Grande Redemoinho de Fogo", "Madara abana seu leque de guerra e expele chamas de sua boca nos ventos, criando uma barragem de redemoinhos flamejantes.", "Todos os alcances", null, null, "火遁・豪火旋風", "Katon: Gōka Senpū", "Liberação de Fogo: Grande Redemoinho de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Incêndio da Chama Explosiva", "Após liberar pólvora no ar, Boro expele fogo a partir de sua boca, causando uma explosão nas proximidades. Isso pode ser especialmente útil quando o usuário está sendo atacado por vários oponentes e em múltiplas direções.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Lanterna Demônio", "Esta técnica forma vários fantasmas de fogo para atacar o inimigo em conjunto. Os fantasmas atacam rapidamente, então é pouco provável uma escapatória.", null, null, "Tigre", "火遁・鬼灯籠かとん・おにどうろう", "Katon: Onidōrō", "Liberação de Fogo: Lanterna Demônio", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Lança de Chamas", "Após performar os selos de mão necessários, o usuário infunde sua lança com chakra de natureza fogo, aumentando sua letalidade.", "Curto alcance", null, null, null, null, "Liberação de Fogo: Lança de Chamas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Liberação de Vento: Onda Selvagem de Chamas Ventiladas", "Ao usar suas máscaras de fogo e vento, Kakuzu combina seus ataques para desencadear uma poderosa explosão de fogo alimentada por fortes rajadas de vento. O ataque é poderoso o suficiente para devastar toda uma região e impedir que uma técnica de água interfere no processo.", "Todos os alcances", null, null, "火遁・風遁・炎風の乱波かとん・ふうとん・えんぷうのらっぱ", "Katon: Fūton: Enpū no Rappa", "Liberação de Fogo: Liberação de Vento: Onda Selvagem de Chamas Ventiladas", "Estilo Fogo Estilo Vento: Inferno Áspero de Ondas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Limite das Chamas Assassinas", "Após ter o chakra amassado dentro de seu corpo e convertido em fogo, Obito salta uma quantidade considerável de altura e vomita uma grande esfera de chamas em direção ao adversário.", "Curto alcance", null, null, "火遁・炎殺界", "Katon: Ensakkai", "Liberação de Fogo: Limite das Chamas Assassinas", "Estilo Fogo: Barreira de Chamas Assassinas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Meteoros Flamejantes", "O usuário cria uma legião de pequenas bolas de fogo que são disparadas contra o inimigo.", "Curto a Médio Alcance", null, null, "火遁・焔流星", "Katon: Homuranagase", "Liberação de Fogo: Meteoros Flamejantes", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Máscara do Aprisionamento nas Chamas", "Depois de relativa distância, Kakuzu desencadeia uma saraivada de bolas de fogo para explodir enquanto em contato com o inimigo.", "Longo alcance", null, null, "火遁・獄炎の面かとん・ごくえんのめん", "Katon: Gokuen no Men", "Liberação de Fogo: Máscara do Aprisionamento nas Chamas", "Estilo Fogo: Máscara das Chamas Aprisionadoras", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Névoa de Água", "A Névoa de Água é uma das várias técnicas presentes no arsenal de Kakashi Hatake, o Sexto Hokage.", "Todos os alcances", null, null, null, null, "Liberação de Fogo: Névoa de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Pederneira de Yagura", "O usuário aciona um isqueiro e produz uma pequena chama que ele expande e manipula em uma série de formas, como uma parede, um pilar, ou até mesmo uma grande criatura animalesca.", "Médio alcance", null, null, "火遁・火打ち矢倉", "Katon: Hiuchi Yagura", "Liberação de Fogo: Pederneira de Yagura", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Pilha Ardente de Cinzas", "O usuário expele um fluxo de pólvora infundido com chakra de sua boca, que circunda a região. Como a pólvora é composta inteiramente de cinzas, ela permanece no ar ao redor da vítima como uma nuvem, que pode ser usada como uma cortina de fumaça. Depois de cercar o inimigo com as cinzas, o utilizador pode inflamá-la com um isqueiro colocado sobre os dentes previamente para criar uma faísca, o que resulta em uma violenta explosão, queimando o inimigo. No entanto, essa técnica exige sentidos aguçados para ler as correntes de ar, discernimento para ler os movimentos do inimigo, experiência táctica para não envolver também os próprios aliados nas chamas, e uma atenção especial para o momento da ignição.", "Médio alcance", "Rank B", "Cobra → Rato → Cobra → Tigre", "火遁・灰積焼かとん・はいせきしょう", "Katon: Haisekishō", "Liberação de Fogo: Pilha Ardente de Cinzas", "Estilo Fogo: Cinza Ardente", "Estilo do Fogo: Campo das Cinzas Flamejantes", "Estilo Fogo: Cinzas Abrasadoras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Prisão Celestial", "A Liberação de Fogo: Prisão Celestial é uma técnica de aprisionamento desenvolvida por um conhecido clã de Kusagakure. Essa essa técnica é utilizada nos prisioneiros de Castelo Hōzuki.", "Curto alcance", null, "Selo do Confronto", "火遁・天牢かとん・てんろう", "Katon: Tenrō", "Liberação de Fogo: Prisão Celestial", null, null, "Estilo Fogo: Prisão Celestial");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Prisão Gigantesca Celestial de Fogo", "Uma versão mais forte da Liberação de Fogo: Prisão Celestial que pode ser usada em alvos maiores.", "Curto", null, null, "火遁・豪火天牢かとん・ごうかてんろう", "Katon: Gōka Tenrō", "Liberação de Fogo: Prisão Gigantesca Celestial de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Projétil Voador de Chamas", "Enquanto em sua forma nível dois do selo amaldiçoado, Sasuke exala uma versão mais forte, contudo, mais curta da Liberação de Fogo: Técnica da Grande Bola de Fogo no oponente.", "Curto alcance", null, null, "火遁・飛炎弾", "Katon: Hiendan", "Liberação de Fogo: Projétil Voador de Chamas", "Estilo Fogo: Projétil Voador de Chamas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Projétil de Chama do Dragão de Fogo", "O usuário amassa seu chakra em chamas, que são manipuladas na aparência de um dragão e direcionadas para atacar um alvo a partir de múltiplas direções. As investidas são focadas, rapidamente reduzindo o alvo em cinzas ao tocar. Devido ao poder desta técnica, bem como a habilidade necessária para controlar as chamas, pouquíssimos ninjas foram capazes de performá-la, sendo o Terceiro Hokage um deles.", "Curto a Médio alcance", "Rank B", "Carneiro → Cavalo → Cobra → Dragao → Rato → Boi → Tigre", "火遁・火龍炎弾", "Katon: Karyū Endan", "Liberação de Fogo: Projétil de Chama do Dragão de Fogo", "Estilo Fogo: Bomba do Dragão Flamejante", "Estilo do Fogo: Dragão Flamejante", "Estilo Fogo: Bomba do Dragão Flamejante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Projétil de Chamas", "Esta técnica é executada pela reunião de óleo que é criado pelo chakra na boca do usuário, cuspindo-o fora, e inflamando-o. Essa técnica simplesmente dispara um míssil de fogo no inimigo. Se a quantidade de óleo que é preparado é grande, o tempo que leva para invocar a técnica é reduzido. Isso permite que o inimigo seja atingido inconscientemente, permitindo que o míssil de fogo contendo óleo o queime completamente. Também é possível disparar uma série de mísseis de chamas dividindo o óleo, ou um contínuo lança-chamas. Se esta técnica é usada depois de cobrir o inimigo com óleo utilizando o Projétil do Óleo de Sapo, ela tem o poder de ferir ou matar. Isso também mantém baixo o consumo de chakra.", "Curto a Médio alcance", "Rank C", "Cobra → Carneiro → Macaco → Javali → Cavalo → Tigre", "火遁・炎弾かとん・えんだん", "Katon: Endan", "Liberação de Fogo: Míssil Flamejante", null, "Estilo do Fogo: Bola de Fogo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Projétil de Chamas do Óleo de Sapo", "Esta é uma técnica de konbijutsu de duas vias. Gamabunta usa a técnica Projétil do Óleo de Sapo para atirar um jato de óleo de sua boca. Seu invocador inflama então o óleo. Jiraiya usa a Liberação de Fogo: Projétil de Chamas para este fim, enquanto que Naruto, não conhecendo ninjutsu de Liberação de Fogo, optou por usar papéis explosivos em seu lugar. O óleo, agindo como um acelerador, fornece mais combustível para o fogo em um efeito como um lança-chamas, e criando uma conflagração de poder muito maior e magnitude que o ninjutsu original de fogo usado em si poderia ter.", "Todos os alcances", "Rank B", "Cobra → Carneiro → Macaco → Javali → Cavalo → Tigre", "火遁・蝦蟇油炎弾かとん・がまゆえんだん", "Katon: Gamayu Endan", "Liberação de Fogo: Projétil de Chamas do Óleo de Sapo", "Bombas Flamejantes do Sapo[2]", null, "Estilo Fogo: Bomba da Chama de Sapo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Projétil do Dragão de Fogo", "Após moldar chakra fogo dentro de seu corpo, o usuário exala um sopro de chamas da sua boca. Com objetivo de mais efetividade na técnica, o usuário pode usar em combinação com a Liberação de Terra: Projétil do Dragão de Terra para inflamar os projéteis disparados, tornando o dano mais devastador.", "Curto alcance", null, "Tigre", "火遁・火龍弾", "Katon: Karyūdan", "Liberação de Fogo: Projétil do Dragão de Fogo", null, null, "Estilo Fogo: Chama da Bomba do Dragão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Raposa de Fogo", "O usuário manifesta dez bolas de fogo em um círculo à sua frente e as lança com os movimentos das mãos.", "Curto a Médio alcance", null, "Serpente → Carneiro → Macaco → Javali → Passaro → Tigre", "火遁・狐火", "Katon: Kitsunebi", "Liberação de Fogo: Raposa de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Redemoinho de Chamas", "O fogo entra em erupção em torno do corpo do usuário em uma forma em espiral, que é então lançado com as mãos em direção ao alvo. Uma vez que a técnica atinge o alvo, o adversário é enviado para o ar de forma em espiral e seu corpo queima em cinzas.", "Curto à Médio alcance", null, "Macaco → Passaro → Cao → Carneiro", "火遁・火炎旋風", "Katon: Kaen Senpū", "Liberação de Fogo: Redemoinho de Chamas", null, null, "Estilo Fogo: Furação de Chamas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Ruído da Chama do Dragão", "Enquanto no Modo Despertar, Madara cria um único dragão de fogo para explodir assim que entrar em contato com o inimigo. Na explosão, três pequenas chamas são criadas em diferentes ângulos, para, em seguida, dar origem a um vórtice gigante de fogo e engolir o inimigo.", "Curto a Médio alcance", null, null, "火遁・龍焔業歌かとん・りゅうえんごうか", "Katon: Ryūen Gōka", "Liberação de Fogo: Ruído da Chama do Dragão", "Estilo Fogo: Jutsu da Canção da Chama do Dragão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Tela de Vendaval Flamejante", "O usuário cospe um tornado de fogo da sua boca, que mostrou-se poderoso o suficiente para fazer seus alvos queimarem como papel. Quando em contato com grandes volumes de água, a técnica cria uma densa nuvem de vapor.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Cerâmica Queimada", "O usuário cria chamas dentro de um forno. As chamas podem também ser poderosas o suficiente para queimar em um forno gigante criado pelo Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares, cozinhando os adversários até que eles sejam totalmente derrotados. Esta técnica também é usada em conjunto com a Liberação de Terra: Queda da Terra Pegajosa para endurecer a lama que cobre o corpo do inimigo, posteriormente imobilizando-o.", "Todos os alcances", null, "Rato → Dragao → Javali ou Cavalo → Tigre[1]", "火遁・素焼きの術かとん・すやきのじゅつ", "Katon: Suyaki no Jutsu", "Liberação de Fogo: Técnica da Cerâmica Queimada", null, null, "Estilo Fogo: Jutsu do Biscoito Queimado");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Dança da Chama Mistica", "Depois de realizar os selos necessários, o usuário é capaz de criar e soprar um gás inflamável a partir de seus pulmões. Uma vez que ele entra em contato com fogo ou faísca, o gás se inflama em uma grande bola de fogo. Kagerō usa esta técnica, enquanto estava disfarçado de Kabuto Yakushi.", "Curto alcance", null, "Cobra → Carneiro → Macaco → Tigre", "火遁・霞炎舞の術", "Katon: Kasumi Enbu no Jutsu", "Liberação de Fogo: Técnica da Dança da Chama Mistica", null, null, "Estilo Fogo: Técnica da Dança da Chama Mistica");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Fênix Sábia de Fogo", "Esta técnica cria uma saraivada de bolas de fogo variando em tamanho pequeno até médio, que são lançadas em direções imprevisíveis contra o oponente. Além disso, as chamas são controladas uma a uma, com chakra, sendo que evitar todas elas é bastante difícil. As shuriken também podem ser escondidas dentro das chamas, criando um ataque surpresa secundário inesperado. Como as chamas, a shuriken pode ser controlada com chakra. Para a maior parte, no entanto, esta técnica é utilizada como uma distração.", "Curto alcance", "Rank C", "Rato → Tigre → Cao → Boi → Coelho → Tigre", "火遁・鳳仙火の術かとん・ほうせんかのじゅつ", "Katon: Hōsenka no Jutsu", "Liberação de Fogo: Técnica da Fênix Sábia de Fogo", "Estilo Fogo: Jutsu da Fênix de Fogo", "Estilo do Fogo: Técnica das Flores de Fogo", "Estilo Fogo: Jutsu Flor de Fênix");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Fênix Sábia de Fogo — Prego Carmesim", "Através de seu Susanoo, Shisui lança algumas fūma shuriken infundidas em fogo em seu oponente.", "Todos os alcances", null, null, "火遁・鳳仙火の術　爪紅かとん・ほうせんかのじゅつ　つまべに", "Katon: Hōsenka no Jutsu — Tsumabeni", "Liberação de Fogo: Técnica da Fênix Sábia de Fogo — Prego Carmesim", "Estilo Fogo: Jutsu Flor da Fênix: Garra Escarlate", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Grande Bola de Fogo", null, "Curto alcance", "Rank C", "Tigre[2] ou Cobra → Carneiro → Macaco → Javali → Cavalo → Tigre", "火遁・豪火球の術かとん・ごうかきゅうのじゅつ", "Katon: Gōkakyū no Jutsu", "Liberação de Fogo: Técnica da Grande Bola de Fogo", null, null, "Estilo Fogo: Jutsu Bola de Fogo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Grande Bola de Fogo — Chama Rotatória", "Tobi abana uma rajada de vento no oponente com seu gunbai, formando um turbilhão de chamas com sua Liberação de Fogo: Técnica da Grande Bola de Fogo.", "Curto alcance", null, "Tigre", "火遁・豪火球の術　旋炎かとん・ごうかきゅうのじゅつ　せんえん", "Katon: Gōkakyū no Jutsu — Sen'en", "Liberação de Fogo: Técnica da Grande Bola de Fogo — Chama Rotatória", "Estilo Fogo: Jutsu da Bola de Fogo de Chama e Vento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Grande Bola de Fogo: Onibi", "Itachi cria um clone da sombra, que ataca com uma enxurrada de shuriken, enquanto o Itachi real termina a técnica concluindo com uma bola de fogo.", "Curto alcance", null, null, "火遁・豪火球の術・鬼火かとん・ごうかきゅうのじゅつ・おにび", "Katon: Gōkakyū no Jutsu: Onibi", "Liberação de Fogo: Técnica da Grande Bola de Fogo: Onibi", "Estilo Fogo: Jutsu Bola de Fogo: Fogo Negro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica da Grande Chama", "O usuário amassa chakra em seu estômago e expele um grande fluxo de chamas de sua boca que se alarga progressivamente com o alcance. As chamas são quentes o suficiente para evaporar rapidamente grandes quantidades de água dentro de um período relativamente curto de tempo.", "Todos os alcances", null, null, "火遁・豪炎の術", "Katon: Gōen no Jutsu", "Liberação de Fogo: Técnica da Grande Chama", null, "Estilo do Fogo: Técnica das Grandes Chamas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica de Ocultação em Pó e Cinza", "O usuário expele fumaça quente com infusão de chakra que queima quando em contato. A técnica também pode ser usada como um poderoso dissuasor de ataques, bem como uma cortina de fumaça altamente eficaz, também pode ser usada para maneiras de distração para fugas.", "Todos os alcances", null, null, "火遁・灰塵隠れの術かとん・はいじんがくれのじゅつ", "Katon: Haijingakure no Jutsu", "Liberação de Fogo: Técnica de Ocultação nas Cinzas", null, "Estilo do Fogo: Técnica da Cortina de Cinzas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica de Ocultação no Fogo", "Usando esta técnica, o usuário pode esconder-se dentro de uma chama. Ao deixar a ocultação, chamas queimam, revelando o usuário. Um membro do Grupo Nokizaru fez uso desta técnica durante uma missão para recuperar um pergaminho roubado por um shinobi de Kumogakure.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica do Dragão de Fogo", "O usuário cospe fogo, ao longo de um cabo, fio ou qualquer outro tipo de objeto longo, que corre para a frente em linha reta para pegar o inimigo em chamas. Uma linha de inimigos pode ser usada como condutor também. As chamas são destinadas ao alvo e atacam a parte superior do corpo do inimigo como uma arma de projétil.", "Médio alcance", "Rank C", "Cobra → Dragao → Coelho → Tigre", "火遁・龍火の術", "Katon: Ryūka no Jutsu", "Liberação de Fogo: Técnica do Dragão de Fogo", null, null, "Estilo Fogo: Jutsu Chama do Dragão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica do Grande Dragão de Fogo", "O usuário comprime uma grande quantidade de chakra construído dentro de seu corpo e moldando-o em uma bola de fogo em forma de cabeça de dragão. O usuário então manipula habilmente o grande fogo, e ataca seu oponente. Mesmo fora do alcance de ataque, sua potência e confiabilidade são estressantes​​. As chamas têm uma alta temperatura, como elas podem criar um movimento ascendente das correntes de ar que por sua vez podem dar origem a nuvens de trovão. Um mestre dessa técnica pode disparar várias chamas em sucessão e quem for pego pela técnica poderia ser potencialmente reduzido a cinzas.", "Todos os alcances", "Rank B", "Tigre", "火遁・豪龍火の術", "Katon: Gōryūka no Jutsu", "Liberação de Fogo: Técnica do Grande Dragão de Fogo", "Estilo Fogo: Jutsu do Dragão de Fogo", "Estilo do Fogo: Técnica do Grandioso Dragão de Fogo", "Estilo Fogo: Jutsu do Dragão de Chama");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica do Projétil de Fogo", "Depois de amassar chakra no estômago, o usuário o expele sob a forma de uma poderosa corrente de fogo até o alvo pretendido.", "Todos os alcances", null, "Passaro → Javali → Tigre[1]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Técnica do Rugido do Dragão de Chamas", "Tal como acontece com a Liberação de Fogo: Técnica do Grande Dragão de Fogo, o usuário comprime uma grande quantidade de chakra moldada e, em seguida, expele em uma série de bolas de fogo com forma de dragão. É possível controlar a direção e a intensidade do ataque, tornando inevitável uma fuga.", "Todos os alcances", null, "Tigre", "火遁・龍炎放歌の術かとん・りゅうえんほうかのじゅつ", "Katon: Ryūen Hōka no Jutsu", "Liberação de Fogo: Técnica do Rugido do Dragão de Chamas", "Estilo Fogo: Jutsu do Dragão de Chama Cantando Ruídos", "Estilo do Fogo: Canção Lança-Chamas do Dragão", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Fogo: Zukokku", "A máscara de Liberação de Fogo de Kakuzu dispara uma pequena bola de fogo que explode em contato com qualquer superfície. O poder explosivo faz com que as chamas generalizem por todo o local. Combinada com a máscara de vento, esta técnica compõe uma das mais poderosas combinações de Kakuzu.", "Todos os alcances", "Rank B", "Rato → Cao → Tigre", "火遁・頭刻苦かとん・ズコック", "Katon: Zukokku", "Liberação de Fogo: Máscara do Sofrimento", null, "Estilo do Fogo: Máscara do Sofrimento", "Estilo Fogo: Enxaqueca Cauterizante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Andorinha da Tempestade de Neve", "Esta técnica cria um conjunto de agulhas de gelo em forma de pequenas andorinhas formadas à partir de gelo pré-existente, em seguida são lançadas no adversário. Estas agulhas podem mudar de direção em pleno ar e podem mutilar o adversário com suas asas afiadas. Esta técnica pode ser eliminada por meio de técnicas baseadas em Fogo.", "Curto a Médio alcance", null, "Tigre", "氷遁・ツバメ吹雪ひょうとん・つばめふぶき", "Hyōton: Tsubame Fubuki", "Liberação de Gelo: Andorinha da Tempestade de Neve", null, null, "Estilo Gelo: Nevasca Tsubame");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Andorinha de Sincelo", "O usuário dispara lanças de gelo para perfurar seu alvo. Uma vez atingido, o alva ficará congelado por um período fixo de tempo e não será capaz de se mover.", "Médio alcance", null, null, "氷遁・氷柱燕ひょうとん・つららつばめ", "Hyōton: Tsurara Tsubame", "Liberação de Gelo: Andorinha de Sincelo", "Estilo Gelo: Andorinha Sincelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Baleia Branca de Um Chifre", "Esta técnica permite que o usuário crie uma baleia monstruosa com um longo chifre narval de preexistência de gelo para atacar seu oponente. O utilizador pode usar o tamanho da invocação para destruir barreiras. O grande volume desta técnica é capaz de criar uma grande destruição em cima de uma aterragem. No entanto, devido ao seu tamanho e falta de velocidade, esta técnica é utilizada mais como uma distração, ou para bloquear movimentos de um oponente ao invés de ataque.", "Todos os alcances", null, "Tigre → Passaro → Macaco → Cavalo → Cobra → Cao", "氷遁・一角白鯨ひょうとん・いっかくはくげい", "Hyōton: Ikkaku Hakugei", "Liberação de Gelo: Baleia Branca de Um Chifre", null, null, "Estilo Gelo: Jutsu da Baleia Branca");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Captura no Campo Congelado", "Depois de realizar o selo de mão necessário para a técnica, Ryōgi congela o solo, de forma instantânea, de modo a congelar os pés do inimigo e imobilizá-lo.", "Médio alcance", null, "Selo do Confronto", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Combo Sincelo", "Haku desfere uma série de golpes com senbon, seguido de alguns chutes, antes de retomar a cortar o adversário. Ele então chuta o oponente para longe, gerando várias agulhas grandes de gelo no ar no intuito de acertar o oponente.", "Curto alcance", null, null, "氷遁・氷柱連弾ひょうとん・つらられんだん", "Hyōton: Tsurara Rendan", "Liberação de Gelo: Combo Sincelo", "Estilo Gelo: Barragem de Sincelo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Correntes Consecutivas de Gelo Térreo", "O usuário, por meio de contato físico ou remotamente, faz com que partículas de gelo se formem no corpo do alvo. Se usado em um ser vivo, o alvo congelará até a morte, a menos que ele seja capaz de moldar chakra. Revestindo-se com chakra e dedicando toda a sua energia para se manter quente, o alvo consegue evitar a morte; se o alvo usar seu chakra para executar uma técnica, ele começará a congelar rapidamente novamente. Sua capacidade em neutralização está a par com a Liberação de Fogo: Prisão Celestial.", "Curto a Médio alcance", null, null, "氷遁・地鎖連氷ひょうとん・じされんひょう", "Hyōton: Jisarenhyō", "Liberação de Gelo: Correntes Consecutivas de Gelo Térreo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Desintegração do Pico de Gelo", "Haku dispara uma série de senbon, seguido de alguns chutes, antes de cortar o oponente no alto. Em seguida, ele chuta o oponente novamente, antes de criar gelo para atacar o oponente e arrastá-lo para longe. Antes de seu oponente cair no chão, Haku coloca a mão no chão, gerando múltiplas agulhas longas e finas de gelo para espetar o oponente.", "Curto a Médio alcance", null, null, "氷遁・氷嶽崩ひょうとん・ひょうがくほう", "Hyōton: Hyōgakuhō", "Liberação de Gelo: Desintegração do Pico de Gelo", "Estilo Gelo: Iceberg Esmagador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Dez Mil Pétalas de Gelo", "Ryōgi lança uma barragem de dardos de gelo. A intensidade do frio é tão grande, que se uma pessoa tentar bloqueá-los, isso causará danos severos à referida pessoa.", "Curto e Médio alcance", null, "Tigre", "氷遁・万華氷ひょうとん・まんげひょう", "Hyōton: Mangehyō", "Liberação de Gelo: Dez Mil Pétalas de Gelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Domo de Aicebergue", "Haku pode rapidamente criar uma cúpula de gelo em torno de seus aliados e de si mesmo para agir como uma defesa. É forte o suficiente para suportar o ataque de vários papéis explosivos ao mesmo tempo, mantendo-se praticamente intacta.", "Curto alcance", null, null, "氷遁・氷岩堂無ひょうとん・ひょうがんどうむ", "Hyōton: Hyōgan Dōmu", "Liberação de Gelo: Câmara Nula de Aicebergue", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Martelo de Forja Quebra-Gelo", "O usuário envolve seu antebraço com gelo altamente resistente como o aço, de modo a aumentar tanto seu poder de ataque, como, alternativamente, usar para bloquear ataques físicos potentes.", "Curto alcance", null, null, "氷遁・砕氷槌ひょうとん・さいひょうずち", "Hyōton: Saihyōzuchi", "Liberação de Gelo: Martelo de Força Quebra-Gelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Nevasca do Dragão Negro", "Uma técnica de Liberação de Gelo formada a partir de gelo já existente. Depois de formar o selo de mão necessário Dotō que estica o braço para mandar um dragão semelhante ao éter negro para atacar seu oponente. Como ele voa pelo ar ele começa a girar. Quando atinge o alvo usa seu movimento para lançar o oponente para o alto.", "Longo alcance", null, "Passaro → Cobra → Macaco → Cavalo → Cao", "氷遁・黒龍暴風雪ひょうとん・こくりゅうぼうふうせつ", "Hyōton: Kokuryū Bōfūsetsu", "Liberação de Gelo: Nevasca do Dragão Negro", null, null, "Estilo Gelo: Nevasca do Dragão Negro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Nevasca dos Dragões Gêmeos", "Uma versão avançada da Liberação de Gelo: Nevasca do Dragão Negro em que Dotō libera dois dragões de neve negra que se fundem em um tornado maciço. Esta técnica manipula o gelo existente em vez de criar gelo esta técnica incapacita o oponete o deixando congelado. seu tornado consegue alcançar uma gigantesca area devastando tudo pelo caminho.", "Longo alcance", null, "Cobra → Rato → Dragao → Macaco → Tigre → Cão", "氷遁・双龍暴風雪ひょうとん・そうりゅうぼうふうせつ", "Hyōton: Sōryū Bōfūsetsu", "Liberação de Gelo: Nevasca dos Dragões Gêmeos", null, null, "Estilo Gelo: Nevasca dos Dragões Gêmeos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Parede de Cristal", "O usuário envolve os inimigos em uma cúpula de gelo. Depois, uma vez que os inimigos olham para os reflexos de si mesmos, os mesmos são atraídos pela ilusão, vendo seus aliados como inimigos.", "Curto e Médio alcance", null, "Tigre", "氷遁・水晶壁ひょうとん・すいしょうへき", "Hyōton: Suishōheki", "Liberação de Gelo: Parede de Cristal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Rasgante Dragão Feroz Tigre", "Esta técnica permite ao usuário manipular gelo pré-existente e criar um tigre gigante, exigindo, portanto, uma fonte próxima de gelo para usar esta técnica. Ela é fria o suficiente para congelar todas as técnicas baseadas em água quando entra em contato as mesmas.", "Longo alcance", null, "Tigre → Javali → Cao", "氷遁・破龍猛虎ひょうとん・はりゅうもうこ", "Hyōton: Haryū Mōko", "Liberação de Gelo: Rasgante Dragão Feroz Tigre", null, null, "Estilo Gelo: Dragão Contra Tigre");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Redemoinho de Vento", "Mirando sua mão contra o adversário, o usuário dispara vários blocos de gelo que, em seguida, fragmentam-se para originar um redomoinho gelado de modo a ofuscar seu campo de visão.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Gelo: Técnica da Avalanche da Presa de Lobo", "Esta técnica permite que Nadare crie uma avalanche quando perto de uma montanha e transforma a neve em uma matilha de lobos que atacam o adversário. Esta técnica é especialmente fraca contra técnicas baseadas em Liberação de Relâmpago, como visto quando os lobos dissolveram-se completamente quando atacados pelo Corte Relâmpago de Kakashi Hatake.", "Todos os alcances", null, "Rato → Coelho → Cao", "氷遁・狼牙雪崩の術ひょうとん・ろうがなだれのじゅつ", "Hyōton: Rōga Nadare no Jutsu", "Liberação de Gelo: Técnica do Avalanche da Presa de Lobo", null, null, "Estilo Gelo: Jutsu Avalanche de Lobo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lama: Boneco de Lama", "Ameyuki é capaz de criar um boneco feito de lama, e utilizá-lo para diversas finalidades.", null, null, null, "泥遁・泥人形", "Deiton: Doro Ningyō", "Liberação de Lama: Boneco de Lama", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lama: Chão de Lama", "Ameyuki é capaz destruir a área circundante ao converter o terreno em lama.", "Todos os alcances", null, null, "泥遁・地盤泥化でいとん・じばんでいか", "Deiton: Jiban Deika", "Liberação de Lama: Chão de Lama", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lama: Parede de Barro", "Uma técnica usada por Ameyuki, utilizando tonjutsu de Liberação de Lama. Ao colocar as mãos no chão, o usuário o transforma em lama que entra em erupção como uma parede e cai rapidamente sobre o inimigo em uma onda de água barrenta.", null, null, null, "泥遁・泥水壁", "Deiton: Deisuiheki", "Liberação de Lama: Parede de Barro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lama: Vazio Profundo de Lama", "Ameyuki é capaz de criar um lamaçal que engole os pés do inimigo, deixando apenas seus braços para fora.", null, null, null, "泥遁・泥底無", "Deiton: Deiteimu", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Bola de Borracha", "O usuário expele de sua boca uma grande bola de lava, que rapidamente se solidifica em uma bola esférica e oca, que pode ser usada para proteger uma pessoa, botando ela dentro da bola que é cercada por um material semelhante a borracha. As propriedades dessa substância é que ela pode tanto absorver ou dissipar atrás de um golpe quando atingido, o que a deforma ou a faz saltar pelo impacto.", "Curto alcance", null, "Passaro", "熔遁・護謨玉ようとん・ゴムだま", "Yōton: Gomudama", "Liberação de Lava: Bola de Borracha", null, "Estilo da Lava: Esfera Protetora de Borracha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Bolas de Borracha", "Após moldar seu chakra, o usuário vomita várias bolas de borracha de tamanhos variados, que flutuam de acordo com o movimento de suas mãos, garantindo um certo controle sobre elas. Ao golpear as bolas de borracha, o usuário é capaz de atira-las contra seu alvo. Uma vez que as bolas estão infundidas com chakra, um único impacto pode ser forte o suficiente para causar pressão nos ossos do alvo.", "Todos os alcances", null, "Tigre", "熔遁・護謨鞠ようとん・ゴムまり", "Yōton: Gomumari", "Liberação de Lava: Bolas de Borracha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Chute Escaldante", "Rōshi cobre o pé de lava e desfere um pontapé em seu oponente, jogando-o para cima.", "Curto alcance", null, null, "熔遁・灼蹴ようとん・しゃくしゅう", "Yōton: Shakushū", "Liberação de Lava: Chute Escaldante", "Estilo Corrosão: Pontapé Ardente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Corda de Borracha", "O usuário cria uma corda com lava convertida num material semelhante a borracha. Ela pode ser usada para rapidamente retirar um aliado da linha de ataque, sendo mais eficiente quando combinada com uma distração.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Defesa de Borracha", "O utilizador cria um tubo curto com lava convertido em um material semelhante a borracha que pode ser utilizado para proteger contra ataques de inimigos.", "Curto alcance", null, null, null, null, "Liberação de Lava: Defesa de Borracha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Desintegração da Grande Flor Escaldante", "O usuário infunde seu punho com chakra de Liberação de Lava, aumentando em várias vezes o poder destrutivo do seu soco, com o efeito adicional de queimar seu oponente no impacto.", "Curto alcance", null, null, "熔遁・灼花大崩ようとん・しゃくかたいほう", "Yōton: Shakuka Taihō", "Liberação de Lava: Desintegração da Grande Flor Escaldante", "Estilo Corrosão: Destruição da Flor de Fogo", null, null);
