INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Explosão Marca-Estrela", "Graças à sua linhagem sanguínea, a Liberação de Lava, Boro é capaz de manipular uma substancia ácida altamente corrosiva que ele pode utilizar de diferentes maneiras: ao performar o selo do pássaro, ele é capaz de desencadear uma enorme erupção que jorra a partir do chão, inundando toda a região, enquanto, ao executar o selo do tigre, ele é capaz de expelir o ácido a partir de sua boca em direção ao seu alvo; ou ainda, ele é capaz de lançar diversos projéteis antes de executar qualquer selo manual a fim de um ataque rápido.", "Todos os alcances", null, "Passaro ou Tigre", "溶遁・烙星破", "Yōton: Rakuseiha", "Liberação de Lava: Explosão Marca-Estrela", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Fluxo Escaldante do Pico da Rocha", "Rōshi cria círculos de lava que irrompem em rocha derretida para o adversário cair dentro.", "Todos os alcances", null, null, "熔遁・灼流峰岩ようとん・しゃくりゅうほうがん", "Yōton: Shakuryūhōgan", "Elemento Lava: Fluxo Escaldante do Pico da Rocha", "Estilo Corrosão: Pico Fundido", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Parede de Borracha", "O usuário cria uma torrente enorme de lava do chão na frente dele, que forma então uma parede que possui durabilidade imensa devido à sua consistência de borracha que permite que o material se deforme quando atacado, o que significa que tanto pode absorver e dissipar a força por trás de um golpe em vez de simplesmente impedir isso. Esta técnica pode também ser utilizada para proporcionar um reforço para uma barreira pré-existente.", "Curto a Médio alcance", null, null, "熔遁・護謨壁ようとん・ゴムへき", "Yōton: Gomuheki", "Liberação de Lava: Parede de Borracha", null, "Estilo da Lava: Parede Protetora de Borracha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Punho Blindado Escaldante", "Rōshi reveste-se em lava e soca o adversário. Mesmo que o soco não consiga se conectar, o intenso calor da lava ainda pode queimar o seu oponente se ele estiver perto o suficiente.", "Curto alcance", null, null, "熔遁・灼鎧甲拳ようとん・しゃくがいこうけん", "Yōton: Shakugaikōken", "Liberação de Lava: Punho Blindado Escaldante", "Estilo Corrosão: Palma da Armadura Incandescente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Punho de Fluxo Escaldante", "Rōshi salta no ar, cobre o punho com lava e fornece um soco em seu oponente, fazendo com que ele caia rapidamente no chão.", "Curto alcance", null, null, "熔遁・灼流拳ようとん・しゃくりゅうけん", "Yōton: Shakuryūken", "Liberação de Lava: Punho de Fluxo Escaldante", "Estilo Corrosão: Palma Fundida", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Técnica da Aparição Fusionada", "Mei libera uma grande quantidade de lava de sua boca, que se espalha em um fluxo grande e fino, voando através do ar. Uma vez que é disparado para cima, forma de arco que cai sobre os alvos, impedindo que os indivíduos tenham a intenção de pular para cima para evitar o ataque. Uma grande quantidade de fumaça é criada depois, pelos efeitos da lava, por isso, caso os alvos ainda estejam vivos, um ataque seguido pode ser realizado enquanto a sua visão permanece obscura.", "Curto a Médio alcance", "Rank –", "Cao → Javali → Tigre → Boi → Rato → Passaro", "溶遁・溶怪の術ようとん・ようかいのじゅつ", "Yōton: Yōkai no Jutsu", "Liberação de Lava: Técnica da Aparição Fusionada", "Estilo Lava: Jutsu de Monstro da Lava", "Estilo da Lava: Técnica da Assombração de Lava", "Estilo Lava: Jutsu da Lava Fusionada");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Técnica de Congelamento de Cal", "Essa técnica faz uso da kekkei genkai de Liberação de Lava, onde o usuário expele uma grande quantidade de cal viva de sua boca. Esta habilidade também pode ser manipulada de modo para restringir os movimentos do alvo pretendido, tal como depois da explosão inicial, os restos da substância podem atuar de um modo semelhante ao cimento de secagem rápida. No entanto, a técnica requer água para desencadear a reação necessária para fazer a substância endurecer. Isto pode ser facilmente conseguido através da utilização de uma técnica de Liberação de Água, que também pode servir para aumentar a área coberta pela substância, aumentando ainda mais a gama potencial da técnica.", "Médio alcance", null, "Coelho → Boi → Cao → Cobra", "熔遁・石灰凝の術ようとん・ようかいのじゅつ", "Yōton: Sekkaigyō no Jutsu", "Liberação de Lava: Técnica de Congelamento de Cal", null, "Estilo da Lava: Técnica da Calcificação", "Estilo Corrosão: Jutsu de Congelamento de Cal");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Lava: Técnica do Selo da Pedra de Cinzas", "O usuário envolve seu alvo com uma pedra de cinzas circundante, incapacitando bastante os seus movimentos. Aparentemente, concentrando-se em seu destino, o usuário pode melhorar as propriedades de ligação da técnica.", "Curto a Médio alcance", null, "Expelir: Tigre,Restringir: Rato", "熔遁・灰石封の術ようとん・かいせきふうのじゅつ", "Yōton: Kaisekifū no Jutsu", "Liberação de Lava: Técnica do Selo da Pedra de Cinzas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Aceno Adamantino da Árvore", "Hashirama usa essa técnica para fazer algumas gavinhas grossas de madeira cresçam ao seu redor. Isso pode ser usado para se defender contra ataques de curta distância ou para atacar um oponente quando eles estão perto.", "Curto alcance", null, null, "木遁・金剛招木", "Mokuton: Kongō Shōboku", "Liberação de Madeira: Aceno Vajra da Árvore", "Estilo Madeira: Chamado da Floresta Adamantina", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Advento de um Mundo de Árvores Florescentes", "Aproveitando-se das propriedades inatas da Liberação de Madeira que permite ao usuário forçar árvores a crescer em qualquer superfície em um instante, o usuário cria uma densa floresta de árvores floridas. Durante a formação da floresta, o usuário pode usar o ramo para conter qualquer alvo(s) que tentam atacar o usuário antes da formação da floresta estar completa. O pólen produzido por estas flores é então liberado para a atmosfera e, quando inalado, torna qualquer alvo atingido inconsciente. O usuário também pode controlar onde o pólen se espalha de modo que não afete-o, nem os seus companheiros. O pólen também é capaz de penetrar as defesas do Susanoo.", "Todos os alcances", "Rank –", "Cobra", "木遁・花樹界降臨もくとん・かじゅかいこうりん", "Mokuton: Kajukai Kōrin", "Liberação de Madeira: Advento de um Mundo de Árvores Florescentes", null, "Estilo da Madeira: Advento da Floresta Florida", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Anel de Espinhos de Madeira", "O usuário cria um semicírculo de madeira em torno de um alvo capturado, e então forma picos dentro do anel, apontando para o alvo, impedindo-o de fazer qualquer movimento brusco.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Barreira Vegetal", "Um ninjutsu desconhecido utilizado por Moegi em Boruto: Naruto Next Generations.", null, null, null, "木遁・草木結界", "Mokuton: Sōmoku Kekkai", "Liberação de Madeira: Barreira Vegetal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Bloqueio da Parede de Madeira", "Um movimento extremamente versátil, esta técnica usa chakra para ativar as raízes no chão e, usando o usuário da técnica como um ponto de referência, fazendo pilares de madeira aparecerem da esquerda e da direita. Os pilares de madeira une-se em uma forma de cúpula com o usuário no centro. Este processo pode ser usado para capturar o inimigo em um ataque de dupla face. Além disso, no caso de um confronto com o inimigo a uma curta distância, o inimigo pode ser trancado dentro da cúpula, definindo a situação para uma luta um-contra-um. Esta cúpula também pode entrar em colapso ou ser queimada com o inimigo dentro para infligir danos. Além disso, ela funciona como um abrigo de fácil contra ataques de longa distância do inimigo, como shuriken e kunai.", "Curto alcance", "Rank –", "Rato → Cao → Tigre", "木遁・木錠壁", "Mokuton: Mokujōheki", "Liberação de Madeira: Bloqueio da Parede de Madeira", null, null, "Estilo Madeira: Jutsu Domo de Madeira");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Cabeças de Cães", "Uma técnica concebida para ser utilizada para absorver chakra dos Bijū. O usuário produz o kanji 'sentar' (座, suwaru) em sua palma da mão e cria uma série de pilares de madeira, que são gravados com o que se assemelham a rostos de cães de foo e têm pavios acesos em cima deles. Quando o usuário bate a palma da mão no chão, as estátuas de madeira percorrem em direção ao alvo e absorvem seu chakra.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Captura Completa", "Yamato aprisiona seu oponente em uma cúpula de madeira, e cria várias vigas de madeira que a compele, destruindo a cúpula e atingindo o oponente várias vezes simultaneamente.", "Curto alcance", null, null, "木遁・完拿", "Mokuton: Kanna", "Liberação de Madeira: Captura Completa", "Estilo Madeira: Captura Total", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Desintegração da Floresta", "O usuário emerge árvores a partir do chão, que crescem rapidamente sob o oponente, apunhalando-o e jogando-o para longe. No entanto, enquanto no Modo Sábio, Hashirama inicia a combinação com um soco brutal e finaliza da mesma forma, mas sem encostar no chão.", "Curto alcance", null, "Cobra", "木遁・樹林大崩もくとん・じゅりんたいほう", "Mokuton: Jurin Taihō", "Liberação de Madeira: Desintegração da Floresta", "Liberação de Madeira: Destruição da Floresta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Enterro Glorioso pela Contenção na Árvore", "Depois de preparar o terreno amassando-o e umedecendo-o, o usuário invoca uma árvore em torno de seu oponente a fim de prendê-lo enquanto ele afunda na lama criada. Depois disso, com o inimigo entre as raízes da árvore, o usuário é capaz de esmagá-lo.", "Curto a Médio alcance", null, "Cobra → Rato → Dragao → Cão → Tigre", "木遁・樹縛栄葬", "Mokuton: Jubaku Eisō", "Liberação de Madeira: Enterro Glorioso pela Contenção na Árvore", null, null, "Estilo Madeira: Enterro do Ligamento da Árvore");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Escudo de Madeira", "A fim de auxilia-lo em batalhes de curto alcance, Yamato usa sua Liberação de Madeira para criar um escudo sobre sua mão. O escudo mostrou-se resistente o suficiente para defletir ataques de armas perfuro-cortantes sem que sua integridade seja comprometida.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Lança da Grande Árvore", "Obito levanta várias grossas raízes do solo, e as usa para esmagar seus adversários.", "Curto a Médio alcance", null, null, "木遁・大槍樹", "Mokuton: Daisōju", "Liberação de Madeira: Lança da Grande Árvore", "Estilo Madeira: Lança da Grande Árvore", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Manipulação da Árvore Divina", "Trata-se de uma técnica de Liberação de Madeira utilizada para manipular o tronco e as raízes da Árvore Divina, permitindo que seu usuário molde-a de acordo com a sua necessidade.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Martelo de Madeira", "Yamato cria vários ramos de madeira que emergem do chão, dando forma a um martelo. Em seguida, o oponente é brutalmente apanhado por este.", "Curto a Médio alcance", null, null, "木遁・木槌", "Mokuton: Kiduchi", "Liberação de Madeira: Martelo de Madeira", "Estilo Madeira: Marreta Amadeirada", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Nascimento Turbulento da Grande Árvore", "Hashirama emerge pequenas raízes do chão e as usa como trampolim, voando em direção ao oponente. Logo após, Hashirama pousa com um soco no oponente e bate-o no chão, criando pequenas árvores desordenadas que crescem a partir do inimigo e lançam-o para longe.", "Curto alcance", null, null, "木遁・大樹轟誕", "Mokuton: Taiju Gōtan", "Liberação de Madeira: Nascimento Turbulento da Grande Árvore", "Estilo Madeira: Grande Árvore", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Natividade de um Mar de Árvores", "Depois de formar os selos necessários, Yamato é capaz de transformar uma área em uma vasta floresta em questão de momentos com árvores completas e até mesmo a grama cresce em qualquer tipo de terreno que ele esteja. Esta técnica é muito semelhante a técnica de assinatura de Hashirama Senju, embora numa escala ligeiramente menor.", "Curto a Médio alcance", null, "Cobra", "木遁・樹海降誕もくとん・じゅかいこうたん", "Mokuton: Jukai Kōtan", "Liberação de Madeira: Natividade de um Mar de Árvores", "Jutsu Oculto do Estilo Madeira: Surgimento da Floresta Escura", null, "Estilo Madeira: Surgimento da Floresta Escura");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Natividade de Árvores", "Yamato emerge sua Liberação de Madeira de cima e golpeia o oponente com grande impacto.", "Todos os alcances", null, null, "木遁・樹木降誕", "Mokuton: Jumoku Kōtan", "Liberação de Madeira: Natividade de Árvores", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Parede do Mundo de Árvores", "O usuário cria inúmeras raízes de madeira que crescem em alta velocidade a partir do solo. As raízes se entrelaçam para formar uma grande parede, com um impressionante poder defensivo. Yamato também foi capaz de manipular a forma do crescimento das raízes, formando um domo.", "Curto alcance", null, "Passaro → Coelho → Cobra", "木遁・樹界壁", "Mokuton: Jukai Heki", "Liberação de Madeira: Mundo da Parede de Árvores", "Estilo Madeira: Parede de Madeira", null, "Estilo Madeira: Barreira da Parede de Árvore");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Pilar Esmagador", "Yamato cria um clone de madeira, e em seguida aprisiona seu oponente em uma prisão de madeira. Logo, ele transforma seu clone em um pilar gigante que, por fim, esmaga o oponente.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Prego Cipreste", "Yamato empala o oponente com a Liberação de Madeira: Técnica da Grande Floresta e martela a madeira com um martelo de madeira gigante que esmaga o oponente em pedaços.", null, null, null, "木遁・椹釘", "Mokuton: Sawara Kugi", "Liberação de Madeira: Prego Cipreste", "Estilo Madeira: Prego Cipreste", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Prisão Medieval", "Yamato usa uma prisão de madeira para suprimir o chakra dos alvos com sua Liberação de Madeira. Há também dois apêndices semelhantes a velas em cada extremidade do dispositivo semelhante à técnica usada durante o treinamento de Naruto.", "Curto alcance", null, "Cobra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Revolta do Inferno", "Obito perfura seu oponente com suas estacas de madeira, desfere um soco em suas costas, seguido de um chute que o manda para o ar. Ainda no ar, Obito salta em direção ao seu adversário e lhe dá uma joelhada, atravessando seu corpo em seguida, e finaliza convocando enormes raízes subterrâneas que o esmagam.", "Todos os alcances", null, null, "木遁・地獄ノ乱もくとん・じごくのらん", "Mokuton: Jigoku no Ran", "Liberação de Madeira: Revolta do Inferno", "Estilo Madeira: Caos Infernal", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Toda a Criação", "Hashirama provoca múltiplas gavinhas de madeira para levantar-se do chão, tecendo em uma parede de madeira em torno dos membros dos oponentes, vinculando-os no lugar. Ele então concentra seu chakra em seu punho e depois ataca o adversário, quebrando-os livre da parede de madeira tecida.", "Curto alcance", null, null, "木遁・森羅万象もくとん・しんらばんしょう", "Mokuton: Shinra Banshō", "Liberação de Madeira: Todas as Florestas de Cobertura e Dez Mil Coisas", "Estilo Madeira: Universo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Todos Cavacos do Céu", "Hashirama bate seu punho contra o chão, criando duas vigas de madeira que lançam o oponente para o ar. Em seguida, Hashirama se posiciona abaixo do oponente e, enquanto o último cai, o Hokage soca brutalmente o adversário nas costas, causando grande impacto.", "Curto alcance", null, null, "木遁・一天木端もくとん・いってんこば", "Mokuton: Itten Koba", "Liberação de Madeira: Todos Cavacos do Céu", "Estilo Madeira: Todas as Lascas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Transformação", "Yamato realiza os selos de mão necessários, e em seguida, surge várias vigas de madeira que cobre todo o seu corpo com uma nuvem de fumaça ou chakra, revelando a transformação. Uma vez que a técnica é feita, a transformação é realmente perfeita. Ela atua mais como uma fantasia do que uma transformação, como ela é capaz de deixar isso para trás, se necessário. Ela também demonstra a capacidade de ajustar a sua voz.", "Curto alcance", null, "Passaro → Cao → Cobra", "木遁・変化", "Mokuton: Henge", "Liberação de Madeira: Transformação", null, null, "Transformação Estilo Madeira");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica Cortante", "Uma técnica versátil, onde o usuário pode produzir estacas afiadas de madeira de seu corpo para tanto moldar armas de curto alcance semelhantes a uma espada improvisada, ou então usá-las como projéteis de longo alcance, onde o crescimento dos ramos podem ser acionados remotamente, espetando o inimigo como um resultado.", "Todos os alcances", "Rank –", "Carneiro", "木遁・挿し木の術もくとん・さしきのじゅつ", "Mokuton: Sashiki no Jutsu", "Liberação de Madeira: Técnica de Estaquia", "Estilo Madeira: Lança de Carregamento", "Estilo da Madeira: Técnica das Estacas", "Estilo Madeira: Técnica do Ramo Perfurante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica Hōbi", "Uma ténica de vedação utilizada por Hashirama Senju, que consiste em criar uma defesa capaz de neutralizar ataques poderosos, como uma Bola da Besta com Cauda.", "Curto alcance", null, null, "木遁・榜排の術", "Mokuton: Hōbi no Jutsu", "Liberação de Madeira: Técnica Hōbi", null, "Estilo da Madeira: Técnica da Rejeição", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica Hotei", "Depois de realizar os selos de mão necessários, o usuário cria uma série de mãos de madeira gigantes que explodem para cima de debaixo da terra, que circundam e, em seguida, tentam conter um inimigo escolhido. O tamanho de cada uma destas mãos é em torno do tamanho de uma besta com cauda. Hashirama é mostrado sendo capaz de manifestar duas mãos apenas com força de vontade, sem o uso de selos de mão.", "Médio a Longo alcance", "Rank –", "Juntar as Maos → Selo especifico da técnica", "木遁・皆布袋の術もくとん・ほうていのじゅつ", "Mokuton: Hotei no Jutsu", "Liberação de Madeira: Técnica do Saco de Pano", "Jutsu do Monge Risonho", "Estilo da Madeira: Técnica do Deus da Sorte", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica da Casa de Quatro Pilares", "Ao enviar chakra no solo, as raízes das plantas são convertidas em matérias-primas. Esta técnica permite que um edifício seja construído, forçando o crescimento acelerado. Assim como com o Liberação de Madeira: Técnica da Prisão de Quatro Pilares, o tamanho e a forma do edifício são tomados a partir da imaginação do usuário ou memórias. Ao colocar marcas ao redor da casa, o usuário cria uma barreira, tornando impossível para o inimigo detectá-lo e permitindo que o usuário 'acampe' em paz.", "Curto alcance", "Rank –", "Cobra → Rato → Boi → Carneiro → Cobra", "木遁・四柱家の術もくとん・しちゅかのじゅつ", "Mokuton: Shichūka no Jutsu", "Liberação de Madeira: Técnica da Casa de Quatro Pilares", null, null, "Estilo Madeira: Jutsu Casa de Quatro Pilares");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica da Grande Floresta", "Uma técnica que muda o próprio braço em grandes árvores. Usando chakra, os tecidos se transformam em árvores no nível celular. Em seguida, ativando as árvores, estimulando-as a crescer rapidamente, elas alongam em alta velocidade e forquilham em vários ramos. É possível capturar o inimigo com esses ramos e, ao mesmo tempo, se a pessoa muda as extremidades em estacas afiadas, transformá-as em incontáveis armas afiadas perfurantes semelhantes a lança.", "Todos os alcances", "Rank –", "Tigre → Cavalo → Tigre → Cobra", "木遁・大樹林の術もくとん・だいじゅりんおじゅつ", "Mokuton: Daijurin no Jutsu", "Liberação de Madeira: Técnica da Grande Floresta", null, null, "Jutsu da Floresta Gigante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica da Ligação Sufocante", "Ao transformar seu braço em uma árvore, o usuário pode criar vários ramos, que vai crescer rapidamente em torno do alvo, imobilizando-o. Esta técnica é forte o suficiente para conter uma Lula Gigante.", "Curto a Médio alcance", null, "Cobra", "木遁・黙殺縛りの術もくとん・もくさつしばりのじゅつ", "Mokuton: Mokusatsu Shibari no Jutsu", "Liberação de Madeira: Técnica da Encadernação Sufocante", null, "Estilo da Madeira: Técnica do Laço Estrangulador", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica da Ponte de Dois Pilares", "Yamato ergue duas árvores a partir do solo e cria uma ponte de madeira entre elas. Se necessário, o usuário pode estender o comprimento da ponte executando o selo cobra.", "Longo alcance", null, "Ativar: Javali → Rato → Cachorro → Tigre → Cobra[1],Alongar: Cobra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica da Prisão de Pilares Circulares", "Yamato cria pilares de madeira em uma formação circular para prender e esmagar o oponente.", null, null, null, "木遁・周柱牢の術", "Mokuton: Shūchūrō no Jutsu", "Liberação de Madeira: Técnica da Prisão de Pilares Circulares", "Liberação de Madeira: Técnica de Prisão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica da Prisão de Quatro Pilares", "O usuário converte chakra em madeira sob a terra e o faz crescer rapidamente, enquanto forma-o em uma prisão de madeira. O tamanho da prisão baseia-se na imaginação do utilizador. Desde uma gaiola de inseto até uma grande prisão, pode-se transformá-la em tudo o que quiser, regulando a quantidade de chakra usado. A madeira propriamente dita é tratada com um revestimento de chakra, tornando extremamente difícil a sua destruição. Durante a Guerra, ela tem a grande capacidade de deter prisioneiros, mas ela também pode ser usada como uma sanção para discutir com subordinados.", "Curto alcance", "Rank –", "Carneiro → Boi → Javali → Cobra", "木遁・四柱牢の術", "Mokuton: Shichūrō no Jutsu", "Liberação de Madeira: Técnica da Prisão de Quatro Pilares", null, null, "Prisão de Quatro Colunas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica das Casas de Pilar em Série", "Uma versão em maior escala da Liberação de Madeira: Técnica da Casa de Quatro Pilares, onde em vez de criar uma única casa, Yamato cria várias casas de uma só vez. Devido ao grande volume de estruturas criadas com esta técnica, parece esgotá-lo rapidamente.", null, null, "Cao → Carneiro → Cobra", "木遁・連柱家の術", "Mokuton: Renchūka no Jutsu", "Liberação: Técnica das Casas de Pilar em Série", null, "Estilo Madeira: Técnica dos Pilares em Série", "Estilo Madeira: Jutsu da Fileira de Casas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica do Dragão de Madeira", "O usuário cria um dragão de madeira gigante, que ele pode usar para uma variedade de propósitos, incluindo restringir um alvo, ou para fins ofensivos. O dragão que o usuário cria pode variar em termos de aparência. Embora em ambos os casos, criou um dragão serpentino, dragão chinês, sendo que o primeiro tinha um nariz como tronco curto, enquanto os outros dois mais tarde criados não. Ele também tem a capacidade de absorver chakra do seu alvo, lentamente, tornando-o incapaz de usar técnicas. No anime, Hashirama foi capaz de fazer com que o dragão detonasse em seu alvo, resultando em uma grande explosão.", "Todos os alcances", "Rank –", "Cobra → Carneiro → Coelho → Cao → Selo especifico da técnica ou Cobra", "木遁・木龍の術もくとん・もくりゅうのじゅつ", "Mokuton: Mokuryū no Jutsu", "Liberação de Madeira: Técnica do Dragão de Madeira", "Estilo Madeira: Jutsu do Dragão de Madeira", "Estilo da Madeira: Técnica do Dragão de Madeira", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica do Humano de Madeira", "Depois de formar os selos de mão necessários, o usuário cria uma criatura enorme como estátua de madeira. Esta criatura humanoide com um corpo cheio e uma face semelhante a oni é geralmente criado com um dragão de madeira envolto em torno de seu torso, e é geralmente usada em batalha como um avatar. Esta criatura é poderosa o suficiente para pegar uma Bola da Besta com Cauda descompactada disparada pelo Nove-Caudas e confrontar contra os semelhantes do Susanoo perfeito de Madara Uchiha. Se o usuário não tem tempo suficiente para manifestar todo o corpo, ele pode manifestar primeiro um membro ou dois, a ganhar tempo suficiente para o corpo inteiro se formar.", "Curto a Médio alcance", "Rank –", "Cobra", "木遁・木人の術もくとん・もくじんのじゅつ", "Mokuton: Mokujin no Jutsu", "Liberação de Madeira: Técnica Humano de Madeira", "Estilo Madeira: Jutsu do Homem de Madeira", "Estilo da Madeira: Técnica do Homem de Madeira", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica do Verdor", "Um ninjutsu desconhecido utilizado por Moegi em Boruto: Naruto Next Generations.", null, null, null, "木遁・緑色の術", "Mokuton: Ryokushoku no Jutsu", "Liberação de Madeira: Técnica do Verdor", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Técnica dos Grilhões de Madeira", "Yamato cria um pilar de madeira em espiral que faz o oponente ser levantado em alta velocidade, e em seguida, ele ataca e causa vários danos ao oponente, além de jogá-lo a vários pés de distância.", "Curto alcance", null, null, "木遁・木桎梏の術", "Mokuton: Kishikkoku no Jutsu", "Liberação de Madeira: Técnica dos Grilhões de Madeira", "Estilo Madeira: Jutsu dos Grilhões de Madeira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Vigas", "Yamato cria várias vigas de madeira que crescem a partir do solo e atingem o oponente.", "Curto a Médio alcance", null, null, "木遁・梁木", "Mokuton: Yanagi", "Liberação de Madeira: Vigas", "Liberação de Madeira: Colunas de Árvores", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Madeira: Árvore Voadora", "Primeiramente, Yamato corre até o inimigo e chuta-o, lançando-o rapidamente para o alto, assim ele toma vantagem da inconsciência do inimigo e invoca uma imensa coluna de madeira, em seguida, a coluna acerta o inimigo. E finalmente, ele realiza um selo e salta na frente da coluna, e assim ele revela um feixe fino e extenso em sua mão, logo o feixe atinge o inimigo e o força contra a coluna, e o feixe consegue estilhaçar a coluna e acerta o inimigo até esmagá-lo no chão.", "Curto alcance", null, null, "木遁・飛乃木もくとん・ひのき", "Mokuton: Hinoki", "Liberação de Madeira: Árvore Voadora", "Estilo Madeira: Árvore Voadora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Magnetismo: Foco da Abelha das Lâminas Gêmeas", null, "Médio alcance", null, null, "磁遁・省蜂の双刃じとん・じゃくほうのそうじん", "Jiton: Jakuhō no Sōjin", "Liberação de Magnetismo: Foco da Abelha das Lâminas Gêmeas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Magnetismo: Funeral Imperial de Pó de Ouro", "Rasa faz com que uma quantidade considerável de Pó de Ouro para entrar em erupção debaixo da superfície, colocando ambas as palmas das mãos no chão em frente a ele, antes de manipular a substância, resultando em uma onda de proporções das marés. A quantidade de pó produzido por esta habilidade é comparável à da areia utilizada durante o Fluxo da Cachoeira de Areia Movediça de Gaara, mas como o ouro é o mais pesado dos dois, o primeiro era capaz de anular o último quando colidiram.", "Médio a Longo alcance", "Rank –", "Carneiro", "磁遁・砂金大葬じとん・さきんたいそう", "Jiton: Sakin Taisō", "Liberação de Magnetismo: Funeral Imperial de Pó de Ouro", "Fluxo da Cachoeira de Pó de Ouro (流砂金瀑流, Ryūsakin Bakuryū)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Magnetismo: Grande Funeral de Pó de Ouro", "Rasa convoca seu pó de ouro no entorno do oponente e aprisiona-o sob uma duna espiral feita do material e, após formar o selo de mão necessário, a duna é comprimida, esmagando quem está dentro.", "Longo alcance", null, "Esmagar: Selo desconhecido[Nota 1]", "磁遁・砂金大葬送じとん・さきんたいそうそう", "Jiton: Sakin Taisōsō", "Liberação de Magnetismo: Grande Funeral de Pó de Ouro", "Estilo Magnético: Remessa do Funeral Imperial de Pó de Ouro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Magnetismo: Técnica do Deus do Relâmpago", "Usando seu chakra magnético, o usuário absorve massa metálica em torno de si, expandindo a sua aparência escalar e adquirindo a forma de um gigante. Resumidamente, trata-se de uma técnica que cria uma imensa forma humanoide de Areia de Ferro.", "Todos os alcances", null, null, "磁遁・雷神我の術じとん・らいじんがのじゅつ", "Jiton: Raijinga no Jutsu", "Liberação de Magnetismo: Técnica do Deus do Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Poeira: Técnica de Divisão do Desprendimento do Mundo Primitivo", "Mū divide-se, com uma de suas metades jogando seu oponente no ar, desaparecendo de vista em seguida. Essa metade reaparece depois, e as duas disparam uma Liberação de Poeira, pegando o oponente entre suas técnicas.", "Curto a Médio alcance", null, "Cobra → Dragao → Cão", "塵遁・分裂原界剥離の術", "Jinton: Bunretsu Genkai Hakuri no Jutsu", "Liberação de Poeira: Técnica de Divisão do Desprendimento do Mundo Primitivo", "Estilo Partícula: Jutsu de Desmontagem da Fissão Atômica, Estilo Partícula: Jutsu de Fissão da Decomposição Atômica", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Poeira: Técnica do Desprendimento do Mundo Primitivo", "O usuário aplica a Liberação de Poeira para criar uma estrutura com uma esfera localizada em seu centro entre as palmas das suas mãos. A estrutura se expande rapidamente em tamanho quando é impulsionada em direção ao seu alvo, o prendendo. A esfera, em seguida, parece explodir com uma tremenda quantidade de força, enquanto as paredes exteriores da estrutura conseguem restringir o tamanho do raio da explosão, resultando na destruição de tudo o que estiver dentro dele à medida que são pulverizados em partículas diminutas de pó. A técnica não se limita a apenas afetar a matéria física, mas também pode afetar as defesas de chakra.", "Todos os alcances", "Rank –", "Rato → Urso → Cobra", "塵遁・原界剥離の術じんとん・げんかいはくりのじゅつ", "Jinton: Genkai Hakuri no Jutsu", "Liberação de Poeira: Técnica do Desprendimento do Mundo Primitivo", null, "Estilo do Pó: Técnica do Desprendimento Atômico", "Estilo Partícula: Jutsu de Deslocamento Atômico");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Porco: Fogos de Artifícios Enlouquecidos", "Shizune rodeia seu oponente com sua névoa venenosa, e em seguida, salta para o alto, atirando várias senbon que perfuram-o. Para finalizar, Tonton finaliza o oponente lançando oito grandes bombas no mesmo, que está atualmente preso na névoa venenosa. Depois de jogar as oito bombas, ocorre uma enorme explosão que devasta a área onde o oponente estava.", "Longo alcance", null, null, "豚遁・乱れ花火とんとん・みだれはなび", "Tonton: Midare Hanabi", "Liberação de Porco: Fogos de Artifícios Enlouquecidos", "Estilo Porco: Loucos Fogos de Artifícios", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Porco: Tonton", "Tonton é jogado no oponente por Shizune, que, em seguida, ataca constantemente o oponente em várias direções. Tonton então gira ferozmente para cima sob o seu oponente, deixando-o no ar, que faz com que um acidente uma vez que o oponente bater no chão.", "Curto alcance", null, null, "豚遁・トントン", "Tonton: Tonton", "Liberação de Porco: Tonton", "Estilo Porco: Tonton", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Alicerce de Relâmpago Ilusório", "Usando seu corpo como alicerce, C emana uma luz intensa capaz de cegar seus inimigos. Uma vez pego, o inimigo alucina no mesmo instante.", "Todos os alcances", null, "Carneiro → Cavalo → Cobra → Tigre", "雷遁・雷幻雷光柱らいとん・らいげんらいこうちゅう", "Raiton: Raigen Raikōchū", "Liberação de Relâmpago: Alicerce de Relâmpago Ilusório", null, "Colunas de Raios da Ilusão", "Ilusão Relâmpago: Flash do Pilar");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares", "Uma versão mais potente da Liberação de Relâmpago: Quatro Pilares Vinculados, na qual o usuário cria dezesseis pilares gigantes, que, em seguida, formam uma estrutura imensa como um forno para interceptar o inimigo. É usada após a Liberação de Terra: Queda da Terra Pegajosa para cobrir o inimigo de lama, e é seguida pela Liberação de Fogo: Técnica da Cerâmica Queimada, criando chamas no interior do forno para assar e endurecer a lama, imobilizando o inimigo.", "Curto a Médio alcance", null, "Rato → Macaco → Cavalo → Tigre", "雷遁・十六柱しばり", "Raiton: Jūrokuchū Shibari", "Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Assassinato Eletromagnético", "Esta técnica permite que o usuário crie uma onda de eletricidade a partir de suas mãos. O usuário pode variar seu poder a partir de uma pequena onda de choque em um adversário à uma poderosa corrente de relâmpagos capaz de rasgar através da rocha sólida. Também é utilizada em conjunto com a Liberação de Água.", "Curto alcance", null, "Javali → Carneiro → Cobra → Cavalo → Dragao", "雷遁・磁場死", "Raiton: Jibashi", "Liberação de Relâmpago: Assassinato Eletromagnético", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Bastão do Relâmpago Pulverizador", "O usuário canaliza os raios em seu porrete, aumentando a força dos ataques lançados.", "Curto alcance 0-5m", null, null, "雷遁・雷砕棒", "Raiton: Kanasaibō", "Liberação de Relâmpago: Bastão do Relâmpago Pulverizador", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Chuva de Raios", "Por intermédio de alguma fonte de raios, seja natural ou artificial, o usuário manipula e libera poderosas descargas elétricas em forma de raios que caem sobre as proximidades, eletrocutando e paralisando aqueles que são atingidos.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Descarga Elétrica", "O usuário acumula seu chakra e o libera na forma de uma potente descarga elétrica que envolve todo o seu corpo por um curto período de tempo, eletrocutando quem faz contato com ele.", "Curto alcance", null, "Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Duplo Dez Mil Volts", "Trata-se de uma técnica colaborativa da qual dois usuários com chakra relâmpago lançam suas técnicas em sincronia a fim de eletrocutar o mesmo alvo. O poder combinado das técnicas desencadeia uma corrente elétrica de voltagem ainda mais elevada e poder.", "Curto alcance", null, null, "雷遁・二重万ボルト", "Raiton: Nijūman Boruto", "Liberação de Relâmpago: Duplo Dez Mil Volts", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Elevação Inversa", "Darui desfere uma série de ataques com sua Espada Cutelo, infundida com relâmpago, que joga seu oponente para trás, finalizando ao lançar uma rajada de relâmpago através do chão com sua espada, jogando seu adversário no ar.", "Curto a Médio alcance", null, null, "雷遁・逆揚げらいとん・ぎゃくあげ", "Raiton: Gyakuage", "Liberação de Relâmpago: Elevação Inversa", "Estilo Relâmpago: Elevação Inversa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Flash do Terremoto Relâmpago", "O usuário libera uma descarga de relâmpago roxa na palma de sua mão, a qual carrega até seu oponente e desfere um golpe. Esta técnica é semelhante ao Corrente de Chidori, pois ambos têm o efeito de alongamento para efetuar algum golpe.", "Curto alcance", null, null, "雷遁・雷震閃", "Raiton: Raishinsen", "Liberação de Relâmpago: Flash do Terremoto Relâmpago", "Estilo Relâmpago: Flash Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Flecha Trovejante", "Trata-se de um ninjutsu de relâmpago que é usado para disparar um raio em forma de flecha, eletrocutado seu alvo. Ao combiná-lo com um ninjutsu de água, Boruto obteve maior eficácia no uso.", "Curto a Médio alcance", null, "Carneiro", "雷遁・迅雷箭らいとん・じんらいせん」", "Raiton: Jinraisen", "Liberação de Relâmpago: Flecha Trovejante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Gian", "A máscara de Liberação de Relâmpago de Kakuzu emite feixes de relâmpago a partir de sua boca para eletrocutar e perfurar o inimigo. Um único feixe pode partir rochas ou fragmentá-las por completo. A quantidade também pode ser ajustada, a fim de obter maior sucesso, mas, em compensação, os feixes ficam devagar, ainda que capazes de desferir sérios danos ao inimigo.", "Todos os alcances", "Rank B", "Cobra", "雷遁・偽暗らいとん・ギアン", "Raiton: Gian", "Liberação de Relâmpago: Falsa Escuridão", null, "Estilo do Relâmpago: A Desconfiança das Trevas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Ligação do Trovão", "Esta técnica permite que Kitane crie uma parede de três lados com eletricidade para conter seu adversário. Kitane para criar os pontos de condução, deve ter colocado três varas em cada ponta da barreira, assim criando os pontos. Além disso, uma vez que o inimigo está dentro da barreira, se entrar em contato com a parede, ele irá ser eletrocutado. A única maneira existente de anular esta técnica, é tendo um companheiro de equipe que ataque um dos lados da parede da barreira com um ataque poderoso, assim então, anulando todos os outros lados da barreira.", "Curto alcance", null, "Boi → Cavalo → Dragao", "雷遁・雷縛りらいとん・かみなりしばり", "Raiton: Kaminari Shibari", "Liberação de Relâmpago: Ligação do Trovão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Lâmina Elétrica", "Uma técnica em que o usuário infunde chakra de natureza relâmpago em uma arma de arremesso, como uma kunai ou shuriken, para atordoar seu alvo com um choque ao perfurá-lo. Embora seja considerada uma técnica de baixo nível, é muito versátil, uma vez que a arma pode ser utilizada em combates corpo a corpo ou até mesmo arremessada sem que seus efeitos sejam perdidos.", "Curto a Médio alcance", null, "Kunai: Tigre → Cao[1],Shuriken: Tigre[2]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Manipulação da Cobra Elétrica", "O usuário cria uma descarga elétrica que pode ser lançada em forma de uma cobra ou pode cobrir seu corpo para eletrocutar em quem ele encostar. Essa técnica pode ser potencializada por um ambiente aquático. Uma vez usada num ambiente propenso a uma carga elétrica maior, o usuário também parece ficar vulnerável a receber danos.", "Curto a Médio alcance", null, "Bater as maos → Macaco ou (Carneiro espelhado)[1]", "雷遁・蛇御雷らいとん・へびみかずち", "Raiton: Hebi Mikazuchi", "Liberação de Relâmpago: Manipulação da Cobra Elétrica", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Mergulho Violento do Relâmpago", "Darui canaliza chakra de relâmpago através de sua Espada Cutelo e coloca-a no chão, paralisando seu oponente, e em seguida ele ataca seu adversário, enviando-o para longe.", "Curto alcance", null, null, "雷遁・雷激浸らいとん・らいげきしん", "Raiton: Raigekishin", "Liberação de Relâmpago: Mergulho Violento do Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Onda de Inspiração", "Esta é uma técnica de fusão (融合術, Yūgōjutsu) onde o usuário emprega Liberação de Água e Liberação de Relâmpago. Depois de criar um fonte de água, ele então forma os selos de Liberação de Relâmpago, misturando assim um relâmpago à água. Se esta técnica atinge um inimigo, ele instantaneamente recebe um choque elétrico.", "Médio alcance", null, "Coelho → Cao → Javali → Carneiro → Rato", "雷遁・感激波", "Raiton: Kangekiha", "Liberação de Relâmpago: Onda de Inspiração", null, "Estilo do Relâmpago: Onda Chocante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Overdrive", "Mediante ao uso de chakra de natureza relâmpago, o usuário é capaz de aumentar suas sinapses neurais para acelerar a comunicação de seu cérebro com os músculos, aumentando a velocidade de ataque e reação efetivamente. A técnica também concede ao usuário capacidades cortantes com ataques de mãos nuas.", null, null, null, "雷遁・オーバードライブらいとん・おおばあどらいぶ", "Raiton: Ōbādoraibu", "Liberação de Relâmpago: Ultra-impulso", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Pantera Negra", "Depois de gerar um Relâmpago Negro de seu corpo que toma a forma de uma pantera, o usuário pode manipulá-lo para afetar múltiplas vítimas de uma só vez eletrocutando-as. Tal como várias outras técnicas de Liberação de Relâmpago, a potência desta habilidade pode ser aumentada através da aplicação de água.", "Curto a Médio alcance", "Rank B", "Carneiro", "雷遁・黒斑差", "Raiton: Kuropansa", "Liberação de Relâmpago: Diferença Manchada de Preto", null, "Estilo do Relâmpago: Contraste da Pantera Negra", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Para-Raios", "Depois de entrar em contato físico com o oponente, o usuário levanta o braço no ar, gerando um raio poderoso de energia elétrica que percorre seu corpo no corpo do oponente.", "Curto alcance", null, null, "雷遁・避雷針", "Raiton: Hiraishin", "Liberação de Relâmpago: Para-Raios", null, null, "Estilo Relâmpago: Faísca da Agulha Elétrica");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Portão de Trovão", "Usando as Kiba, o usuário primeiro enterra as espadas no chão e, em seguida, envia uma carga elétrica para o céu que retorna como um grande relâmpago, dizimando a área imediatamente ao redor do usuário, queimando tudo e transformando em cinzas.", "Todos os alcances", null, null, "雷遁・雷門", "Raiton: Sandāgēto", "Liberação de Relâmpago: Portão de Trovão", null, null, "Estilo Relâmpago: Portão de Trovão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Presa de Relâmpago", "Usando Kiba, o usuário envia raios de luz para eletrocutar seus inimigos.", "Todos os alcances", null, null, "雷遁・雷牙", "Raiton: Raiga", "Liberação de Relâmpago: Presa de Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Punho Trovejante", "Concentrando relâmpago em seu punho, o usuário desfere uma descarga elétrica no alvo.", "Curto alcance", null, null, "雷遁・迅雷拳", "Raiton: Jinraiken", "Liberação de Relâmpago: Punho Trovejante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Quatro Pilares Vinculados", "O usuário invoca quatro grande pilares de pedra em torno do inimigo para, em seguida, eletrocutá-lo, de modo a desestabilizar o inimigo por um longo período de tempo.", "Todos os alcances", null, "Carneiro → Javali → Cao → Passaro → Lebre", "雷遁・四柱しばりらいとん・しちゅうしばり", "Raiton: Shichū Shibari", "Liberação de Relâmpago: Quatro Pilares Vinculados", null, "Estilo do Relâmpago: Prisão dos Quatro Pilares", "Estilo Relâmpago: Armadilha dos Quatro Pilares");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Raio", "O usuário estende ambos os braços, liberando uma alta descarga através de ambas as palmas das mãos, tendo como alvo vários adversários ao mesmo tempo. É possível aumentar a potência utilizando esta técnica, em combinação com uma fonte de água.", "Todos os alcances", null, "Tigre → Cobra → Tigre → Passaro → Tigre → Carneiro → Tigre", "雷遁・サンダーボルト", "Raiton: Sandāboruto", "Liberação de Relâmpago: Raio", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Rastreamento de Presa da Besta Relâmpago", "Ao aplicar a transformação de forma em seu Corte Relâmpago, Kakashi consegue moldar um lobo feito de relâmpago que persegue os seus alvos.", "Todos os alcances", "Rank C", null, "雷遁・雷獣追牙らいとん・らいじゅうついが", "Raiton: Raijū Tsuiga", "Liberação de Relâmpago: Rastreamento de Presa da Besta Relâmpago", "Técnica da Besta Corredora Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Relâmpago Explosivo", "Usando as espadas Kiba, o usuário cobre seu corpo com uma poderosa carga elétrica capaz de eletrocutar o oponente com um mero toque. Uma vez eletrificado o usuário pode disparar a carga elétrica de qualquer parte de seu corpo.", "Todos os alcances", null, null, "雷遁・爆雷らいとん・ばくらい", "Raiton: Bakurai", "Liberação de Relâmpago: Relâmpago Explosivo", null, null, "Estilo Relâmpago: Carga Profunda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Relâmpago Roxo", "Esta é uma técnica original de Kakashi Hatake, criada para suprir a falta de seu Corte Relâmpago, o qual incapacitou-lhe de utilizar com segurança após perder o Sharingan.", "Curto a Médio alcance", null, null, "雷遁・紫電らいとん・しでん", "Raiton: Shiden", "Liberação de Relâmpago: Relâmpago Roxo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Sinal de Fogo Relâmpago", "Depois de formar os selos de mão necessários, o usuário aponta as mãos para uma certa direção e dispara uma descarga elétrica, que então explode em uma forma específica. Quando as mãos são levantadas para o céu, esta técnica pode ser usada como um sinalizador.", "Longo alcance", null, "Dragao", "雷遁・電光狼煙", "Raiton: Denkō Noroshi", "Liberação de Relâmpago: Sinal de Fogo Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Teia de Aranha", "Depois de amassar o chakra no corpo e convertê-lo para relâmpago, o usuário coloca sua mão no chão que cria uma onda de energia que se origina dele e se estende por fora em torno do usuário criando uma teia de eletricidade em torno dele. Qualquer um preso dentro desta teia é eletrocutado por efeito do raio.", "Curto alcance", null, null, "雷遁・網蜘蛛", "Raiton: Amigumo", "Liberação de Relâmpago: Teia de Aranha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Tesouras de Tigre", "Semelhante a uma armadilha de urso, esta técnica é, no entanto, muito maior e repleta de relâmpagos. A técnica é capaz de materializar-se a partir do solo e bloquear o alvo com sua lâmina de fluxo de raio.", "Curto alcance", null, null, "雷遁・虎鋏", "Raiton: Torabasami", "Liberação de Relâmpago: Tesouras de Tigre", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Tiro da Bomba Relâmpago", "Indra flui chakra através de seu corpo e o converte em eletricidade, então ele o dispara na forma de um projétil em direção ao seu oponente.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Trovoada", "Vários usuários se reúnem em torno de um pergaminho com o kanji de relâmpago nele, e recolhem seu chakra. O símbolo começa brilhar, e o chakra é enviado para o céu, as nuvens de tempestade, então desencadeiam um grande raio.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Trovão", "Com o movimento de seu pulso, o usuário é capaz de disparar inúmeras pequenas faíscas de relâmpagos em direção a um alvo.", "Longo alcance", null, null, "雷遁・散雫らいとん・さんだ", "Raiton: Sanda", "Liberação de Relâmpago: Trovão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Técnica Clone das Sombras", "Esta técnica permite ao usuário fazer um clone da sombra infundido com relâmpago. Por ser um clone de sombra, é capaz de executar técnicas e interagir com o ambiente devido a ele ter substância física; e também distribui uniformemente o chakra do usuário para cada clone. Se o clone é ferido, ele voltará ao seu estado de relâmpago natural, ao mesmo tempo eletrocutando tudo o que é tocado.", null, "Rank A", null, "雷遁・影分身", "Raiton: Kage Bunshin no Jutsu", "Liberação de Relâmpago: Técnica Clone das Sombras", null, "Clone das Sombras do Estilo do Relâmpago", "Estilo Relâmpago: Jutsu Clone da Sombra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Técnica da Lâmina Relâmpago", "É uma técnica original criada por Indra Ōtsutsuki, que envolve a transformação da natureza de Liberação de Relâmpago.", "Curto alcance", null, "Selo especifico da técnica → Boi → Coelho → Macaco", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Relâmpago: Técnica do Projétil de Relâmpago", "Depois de amassar chakra no estômago, o usuário o expele sob a forma de uma poderosa corrente de relâmpago até o alvo pretendido.", "Todos os alcances", null, "Cobra → Porco → Macaco[1]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Tempestade Esotérica: Tempestade do Dragão Demoníaco", "Hiruko usa a Liberação de Tempestade, adquirida através da Técnica Quimera, para produzir uma enorme nuvem tempestuosa com capacidade de absorção de chakra infinita, que cresce em tamanho, força e ferocidade. A quantidade de chakra recebida também aumenta conforme a nuvem. Aparentemente é possível canalizar a energia absorvida para dentro de si, ampliando o limite da técnica.", "Todos os alcances", null, "Rato → Dragao → Coelho → Tigre", "嵐遁奥義・嵐鬼龍らんとんおうぎ・らんきりゅう", "Ranton Ōgi: Rankiryū", "Liberação de Tempestade Esotérica: Tempestade do Dragão Demoníaco", null, null, "Estilo Vendaval: Dragão do Vendaval");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Tempestade: Caçada Negra", "Empregando o chakra tempestade em seu Relâmpago Negro, a fim de deixar a técnica mais fluida, Darui envia várias panteras negras que são guiadas em direção ao oponente, infligindo dano conforme passam. As panteras, em seguida, mesclam-se em uma única pantera gigante atrás do inimigo, que morde-o, comprime-se e expande-se rapidamente em uma explosão.", "Curto a Médio alcance", null, "Carneiro", "嵐遁・漆黒斑纏遇らんとん・ぶらっく はんちんぐ", "Ranton: Burakku Hantingu", "Liberação de Tempestade: Deleite do Jato Envolvido de Mancha Negra", "Estilo Vendaval: Caçada Negra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Tempestade: Circo de Laser", "Esta técnica cria vários feixes e os atira contra o inimigo. Primeiro, uma auréola de energia luminosa circunda as mãos do utilizador, que então são envolvidas por essa mesma luz. Com isso, vigas de energia destrutiva são disparadas contra o inimigo. O usuário é capaz de manipular a direção destes feixes, fazendo-os perseguir os inimigos de forma tão precisa que eles conseguem desviar de até mesmo reféns que estão estreitamente próximos do alvo. Caso deseje, o utilizado também pode aumentar o número de vigas que são disparadas.", "Todos os alcances", null, "Tigre → Cao → Cobra → Dragão", "嵐遁・励挫鎖苛素らんとん・れいざあさあかす", "Ranton: Reizā Sākasu", "Liberação de Tempestade: Circo de Laser", "Estilo Tempestade: Circo de Laser", "Estilo da Tempestade: Laser Circus", "Estilo Vendaval: Circo de Laser");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Tempestade: Onda Interna da Nuvem Tempestuosa", "Hiruko usa a Liberação de Tempestade para gerar um grosso anel de nuvens tempestuosas carregadas ao seu redor, criando uma proteção ofensiva e defensiva. Ele pode, então, usar essas nuvens para disparar poderosas rajadas de raios em seus inimigos, e como o perímetro é eletrificado; pode prevenir eficazmente os seus inimigos de aproximarem-se.", "Curto", null, "Selo especifico da técnica → Javali → Cavalo", "嵐遁・雷雲腔波らんとん・らいうんくうは", "Ranton: Raiunkūha", "Liberação de Tempestade: Onda Interna da Nuvem Tempestuosa", null, null, "Estilo Vendaval: Onda Nula da Nuvem Tempestuosa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Abertura de Terra da Nascente de Escavação", "Com esta técnica, o usuário pode trazer alguma coisa que está no subterrâneo profundo para a superfície, lançando os alvos no ar com grande força e velocidade e resultando na aparência e tamanho semelhantes a um vulcão em erupção. Ele pode forçar um grande número de soldados escondidos no subsolo a emergirem de uma só vez quando usada colaborativamente.", "Longo alcance", null, null, "土遁・開土昇掘", "Doton: Kaido Shōkutsu", "Liberação de Terra: Abertura de Terra da Nascente de Escavação", null, "Estilo da Terra: Brotamento de Rochas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Armadura de Rocha da Aranha Demoníaca", "Devido à Prescrição de Fortalecimento: Injeção de Chakra, Gitai cria uma versão melhorada de sua Liberação de Terra: Técnica de Endurecimento. Ao contrário da versão anterior, o usuário cria uma armadura de rocha que envolve seu corpo com os olhos demoníacos, alguns braços estendidos e uma cor mais escura. A aparência do utilizador se assemelha a uma aranha de rocha.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Bolinho Mausoléu de Terra", "Com o controle de chakra e força sobre-humana, Jirōbō eleva a própria superfície da Terra. O pedaço de terra é gigantesco e o poder destrutivo é óbvio. Mas até mesmo o impacto visual pode ser o suficiente para fazer inimigos perderem seus espíritos de combate e fugirem. A bola da terra é, então, atirada em seu oponente, achatando tudo em seu caminho.", "Curto a Médio alcance", "Rank B", null, "土遁・土陵団子どとん・どりょうだんご", "Doton: Doryō Dango", "Liberação de Terra: Bolinho Mausoléu de Terra", "Estilo Terra: Esfera de Sepulturas", "Estilo da Terra: Biscoito de Terra Sepulcral", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Caixão de Pedra Basiliar", "Esta técnica permite ao usuário controlar múltiplas seções de rocha e movê-las em torno de seu oponente. O usuário, então, forma duas seções gigantescas que esmagam o inimigo.", "Médio a Longo alcance", null, "Selo especifico da técnica", "土遁・岩盤柩", "Doton: Ganban Kyū", "Liberação de Terra: Caixão de Pedra Basiliar", null, null, "Estilo Terra: Caixão de Pedra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Canhão da Montanha de Rocha Eruptiva", "Jirōbō enquanto em sua forma do selo amaldiçoado nível 2 com a sua força sobre-humana puxa para cima a partir do solo um pedaço gigantesco de terra e joga-o no ar na frente do oponente, onde ele ataca com um soco poderoso quebrando a rocha e fazendo com que os fragmentos bombardeiem o oponente.", "Curto alcance", null, null, "土遁・噴岩砲岱どとん・ふんがんほうたい", "Doton: Fungan Hōtai", "Liberação de Terra: Canhão da Montanha de Rocha Eruptiva", "Estilo Terra: Jorrada da Bola de Canhão da Montanha de Rocha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Cascalho", "Depois de performar o selo de mão necessário, o usuário rapidamente move a terra embaixo do oponente em camadas a fim de circundá-lo, deixando-o completamente imóvel. Conforme a necessidade, o usuário pode executar outro selo de mão a fim de libertar o prisioneiro.", "Curto a Médio alcance", null, "Ativar: Selo especifico da técnica,Desativar: Cao", "土遁・細れ石", "Doton: Sazareishi", "Liberação de Terra: Cascalho", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Clone das Sombras", "Esta técnica cria um clone das sombras do usuário feito de barro. Desde que é feito de lama, ele pode continuar a reformar-se e moldar-se de volta à sua forma original. Uma vez revertido para a lama, o clone pode servir como uma restrição poderosa que é capaz de travar completamente os movimentos do oponente.", null, null, null, "土遁・影分身", "Doton: Kage Bunshin", "Liberação de Terra: Clone das Sombras", null, null, "Clone das Sombras do Estilo de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Colapso de Rochas", "De um ponto de altitude, os usuários fazem formações de rochas se separarem, caindo sobre seus alvos para esmagá-los.", "Curto a Médio alcance", null, "Bater palmas no solo", "土遁・岩石崩しどとん・がんせきくずし", "Doton: Ganseki Kuzushi", "Liberação de Terra: Colapso de Rochas", null, null, "Estilo Terra: Colapso de Rochas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Corredor de Terra", "Esta técnica faz a terra na área circundante erguer-se, criando uma enorme caverna e aprisionando múltiplos alvos. O usuário pode deixar uma abertura para permitir ataques, a fim de derrubar os adversários ali presos, ou fechá-la completamente para impedir a fuga.", null, null, "Cobra", "土遁・土回廊", "Doton: Tsuchi Kairō", "Liberação de Terra: Corredor de Terra", null, null, "Estilo Terra: Corredor Terroso");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Corte da Presa de Broca", "Aproveitando-se de uma manobra surpresa, o usuário, em um momento de distração do oponente, executa um soco vindo do chão. O usuário também pode utilizar uma kunai ao invés do soco para aumentar a fatalidade da técnica.", null, null, null, "土遁・裂穿牙どとん・れっせんが", "Doton: Ressenga", "Liberação de Terra: Corte da Presa de Broca", "Estilo Terra: Rasgo da Presa de Broca", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Cúpula de Rocha", "O usuário rodeia-se com terra feita a partir da proximidade, de modo a fabricar uma formação de rochas, que é capaz de proteger tanto o a si mesmo como outros, em estreita proximidade, se desejado. Fazendo assim, aparentemente deixa o terreno circundante inalterado, mesmo quando a quantidade de terra usada é suficiente para formar uma cúpula completa em torno de vários indivíduos, como visto quando Ittan utilizou esta técnica.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Dança da Areia", "O usuário após realizar os selos de mão necessários ele dirige suas mãos e bate as palmas no chão, fazendo com que a terra se mova como uma onda e derramando tudo acima. Esta técnica é forte o suficiente para mudar o cenário.", null, null, null, "土遁・砂踊り", "Doton: Sunaodori", "Elemento Terra: Dança da Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Deslocamento Subterrâneo", "O usuário controla a permeabilidade do local para tornar possível um deslocamento subterrâneo e possibilitar um ataque em alta velocidade contra o inimigo de cima para baixo. Uma vez que o solo é uma das últimas opções a serem pensadas para um ataque, o usuário pode realizar seu ataque surpresa com facilidade.", "Curto a Médio alcance", "Rank C", null, "土遁・土中潜航どとん・どちゅうせんこう", "Doton: Dochū Senkō", "Liberação de Terra: Deslocamento Subterrâneo", null, null, "Estilo Terra: Jutsu do Tubarão Subterrâneo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Destruição de Caverna", "Esta técnica permite ao usuário destruir um local rochoso, especialmente cavernas, a fim de matar todos os inimigos em seu interior sem chance de escapatória. Os ninjas de Iwagakure se orgulham desta técnica, considerando os muitos massacres na Terceira Guerra Mundial Ninja.", "Todos os alcances", "Rank B", "Tigre → Rato → Cobra", "土遁・岩宿崩しどとん・いわやどくずし", "Doton: Iwayado Kuzushi", "Liberação de Terra: Destruição de Caverna", "Estilo Terra: Destruidor de Rochas", "Estilo da Terra: Destruição de Rochas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Divisão do Fluxo de Terra", "Ao fluir chakra concentrado a partir de uma das palmas para as Veias do Dragão (龍脈, Ryūmyaku) que flui no subsolo, pode rasgar além da terra, criando grandes abismos. O comprimento, largura, direção e curva do abismo são todos definidos pelo o usuário. Manipular as Veias do Dragão requer um controle de chakra muito bom, mas se alguém tem tal habilidade, ele podem usar essa técnica para dividir o espaço entre o inimigo e sua própria equipe em dois, destruindo campos inimigos ou defesas, e até mesmo para atacar.", "Todos os alcances", "Rank B", "Coelho → Javali → Rato → Cavalo → Tigre → Cobra", "土遁・土流割どとん・どりゅうかつ", "Doton: Doryūkatsu", "Liberação de Terra: Divisão do Fluxo de Terra", null, "Estilo da Terra: Divisão de Pedras", "Estilo Terra: Rachador de Planeta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Domu", "Kakuzu faz com que flua chakra terra por seus braços ou, se preferível, por todo seu corpo, para endurecer a área ao limite máximo, como um diamante. Além disso, o chakra terra torna a região do corpo de Kakuzu escura. Todos os ataques físicos se tornam inúteis, ao contrário dos ataques de Kakuzu, que, em vez disso, recebem um aumento drástico de dano. Uma das poucas vulnerabilidades desta técnica é o chakra relâmpago.", null, "Rank B", "Cobra", "土遁・土矛どとん・ドム", "Doton: Domu", "Liberação de Terra: Braço de Terra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Dragão de Terra e Pedra", "O usuário cria um dragão feito a partir do solo para atacar o adversário.", "Longo alcance", null, "Cavalo → Cao → Passaro → Boi → Cobra", "土遁・土石龍", "Doton: Dosekiryū", "Liberação de Terra: Dragão de Terra e Pedra", null, null, "Estilo Terra: Dragão de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Drone", "Após performar o selo de mão da técnica, o usuário age como um ponto gravitacional para as rochas, que levitam, e, então, às suas ordens, são lançadas em alta velocidade contra o inimigo, sem chances de escapatória.", "Curto alcance", null, "Serpente", "土遁・土呂怨どとん・ドローン", "Doton: Dorōn", "Liberação de Terra: Drone", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Esporo de Lama", "Esta técnica permite ao usuário invocar um rio de lama capaz de atacar o oponente. Esta técnica pode ter água adicionada para acelerar o fluxo e aumentar a sua potência.", "Longo alcance", null, null, "土遁・泥胞子", "Doton: Doro Hōshi", "Liberação de Terra: Esporo de Lama", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Explosão do Solo", "O usuário cria um deslizamento de terra que se move em direção ao alvo em um ritmo rápido.", "Médio alcance", null, null, "土遁・土中爆", "Doton: Dochūbaku", "Liberação de Terra: Explosão do Solo", null, null, "Estilo Terra: Jutsu do Desmoronamento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Fissura", "Depois de serem formado os selos de mão, uma grande fissura, abrangendo uma distância considerável é formada. As vítimas desprevenidas, então, caem nas profundezas da terra.", "Curto a Médio alcance", null, "Cao → Cobra → Cavalo → Tigre → Cobra → Cão → Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Fluxo de Terra do Grande Rio", "Depois de formar o selo de mão necessário, o usuário transforma o chão debaixo de seu oponente em um rio de lama que lança-o para longe do usuário. A lama criada por esta técnica pode também ser utilizada como um meio para a Liberação de Terra: Projétil do Dragão de Terra.", "Curto a Médio alcance", null, null, "土遁・土流大河", "Doton: Doryū Taiga", "Liberação de Terra: Fluxo de Terra do Grande Rio", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Fuga da Terra", "O usuário bate a mão no chão e rochas gigantes emergem entalhando o oponente por baixo, criando uma trilha montanhosa. As rochas também podem ser usadas como uma plataforma para saltar.", "Curto a Médio alcance", null, "Carneiro → Passaro → Cobra → bater no chao[1]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Grande Movimentação do Núcleo da Terra", null, "Todos os alcances", null, "Lebre → Carneiro → Cobra → Coelho → Carneiro → Cobra", "土遁・大地動核どとん・だいちどうかく", "Doton: Daichidōkaku", "Liberação de Terra: Grande Movimentação do Núcleo da Terra", null, "Estilo da Terra: Deslocamento do Núcleo Terrestre", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Grande Rachadura", "O usuário é capaz de criar enormes rachaduras no chão para engolir seu alvos para o subsolo, então ele fecha a rachadura, esmagando-os. Essa técnica também pode ser utilizada para evacuar uma enchente.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Imprensa Lítica", "Muitos usuários da Liberação de Terra criam cubos de pedra sólida em conjunto que formam quatro paredes, prendendo o que está lá dentro. Depois de formar o selo de mão: cobra, duas partes das pedras avançam um para o outro com a intenção de acertar o adversário que está dentro.", "Médio alcance", null, "Cobra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Lamaçal", "Mū cria um lamaçal em forma de disco que ele lança no oponente, afastando o alvo com o lamaçal criado.", "Curto a Médio alcance", null, null, "土遁・泥沼", "Doton: Doronuma", "Liberação de Terra: Lamaçal", "Estilo Terra: Lamaçal", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Lança do Pilar de Rocha", "Esta técnica permite que o usuário crie alguns pilares de pedra que se projetam a partir do solo para perfurar o alvo. Os pilares de pedra são capazes de passar por quase qualquer obstáculo para chegar ao seu alvo.", "Todos os Alcances", null, null, "土遁・岩柱槍どとん・がんちゅうそう", "Doton: Ganchūsō", "Liberação de Terra: Lança do Pilar de Rocha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Lanças do Fluxo de Terra", "Esta técnica permite ao usuário condensar lama ou pedra do chão e moldá-la em espinhos que se projetam a partir do solo, a fim de espetar o alvo. Os espinhos não podem penetrar alvos de maior densidade.", "Curto a Médio alcance", null, "Passaro", "土遁・土流槍どとん・どりゅうそう", "Doton: Doryūsō", "Liberação de Terra: Fluxo das Lanças de Terra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Lobos de Lama", "Uma técnica de armadilha que é ativada quando o inimigo entra em contato com o chakra do usuário. Ao fazer isso, a lama pré-existente se transformará em lobos que o atacarão. Ao serem danificados, os lobos podem simplesmente se reconstruírem a partir do terreno, mantendo o inimigo ocupado enquanto o usuário foge. A única desvantagem dessa técnica é que requer um tempo para ser preparada e pode permitir que o oponente o acompanhe.", "Longo alcance", null, null, null, null, "Liberação de Terra: Lobos de Lama", null, null, "Elemento Terra: Lobos de Lama");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Movimentação do Núcleo da Terra", "Esta técnica garante ao usuário a capacidade de aumentar ou diminuir o solo na área circundante, permitindo-lhe criar buracos gigantes ou elevações no solo. O tamanho da área afetada, assim como a profundidade que é abaixada ou levantada também é acima do usuário. Com sincronismo preciso, essa técnica pode ser usada para evitar ataques de entrada, bem como pôr em perigo os oponentes, movendo-os para o caminho de um ataque iminente reduzindo seu tempo de reação, bem como tirá-los fora de equilíbrio, tornando ainda mais difícil para evitar o ataque. Além de ser movido abaixo ou acima do solo — em relação à superfície circundante — o terreno não é alterado como a vida vegetal e as pessoas mantêm suas mesmas posições quando esta técnica é utilizada.", "Todos os alcances", null, null, "土遁・地動核どとん・ちどうかく", "Doton: Chidōkaku", "Liberação de Terra: Movimentação do Núcleo da Terra", null, "Estilo da Terra: Núcleo Móvel (Submersão)", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Muralha do Estilo Terra", "Esta técnica faz uma muralha gigantesca subir debaixo dos pés do usuário, por meio da formulação do solo e aumentando a quantidade de terra com o chakra. Dependendo do que o usuário imagina, a forma da muralha pode ser qualquer coisa, desde nível do solo para uma íngreme montanha em forma de agulha. Se o usuário aumentar o volume de terra muito, ele pode ser forçado a manter o consumo de chakra baixo.", "Curto a Médio alcance", "Rank B", "Tigre", "土遁・土流城壁どとん・どりゅうじょうへき", "Doton: Doryū Jōheki", "Liberação de Terra: Muralha do Estilo Terra", null, "Estilo Terra: Muralha de Pedra", "Estilo Terra: Muralha de Defesa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Mutabilidade", "Esta técnica é uma combinação de Técnica de Invocação e de uma técnica de terra. O usuário invoca uma grande quantidade de rochas de uma garganta gigante para serem utilizadas para outras técnicas. O usuário pode canalizar seu chakra na terra para alterar a formação, o auxiliando em ataques furtivos. Como alternativa, através da oferta de uma doação de sangue, eles podem invocar uma rocha no design de um dos desenhos de Fuen.", "Todos os alcances", null, "Javali → Macaco → Passaro → Macaco → Carneiro", "土遁・有為転変", "Doton: Uitenpen", "Liberação de Terra: Mutabilidade", null, null, "Estilo Terra: Impermanência");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Múltiplas Paredes do Estilo Terra", "Assim como na técnica parente, esta cria uma parede de terra reforçada para ser usada como um mecanismo defensivo. No entanto, utilizando uma fonte pré-existente de terra, esta permite ao usuário criar múltiplas paredes de terra, fortificando ainda mais suas defesas, assim, mesmo que as paredes de fora sejam destruídas, as de dentro ainda permanecem intactas. Ela ainda foi vista sendo usada como uma barreira circular para capturar algum ser gigante. Quando a técnica é usada em conjunto, ela cobre uma área muito maior.", "Curto a Médio alcance", null, null, "土遁・多重土流壁", "Doton: Tajū Doryūheki", "Liberação de Terra: Múltiplas Paredes do Estilo Terra", null, null, "Estilo Terra: Múltiplas Paredes de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Onda de Fluxo de Terra", "O usuário cria e monta em uma onda de terra que pode se ramificar para atacar à várias distâncias. O usuário também pode usá-la para manipular habilmente o terreno à sua volta e de seu oponente.", "Médio a Longo alcance", null, "Rato → Cobra → Tigre", "土遁・土流波どとん・どりゅうは", "Doton: Doryūha", "Liberação de Terra: Onda de Fluxo de Terra", null, null, "Elemento Terra: Onda de Fluxo de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Parede do Estilo-Terra de Dez Mil Ri", "Este é um konbijutsu em que diversos usuários de Liberação de Terra simultaneamente performam os selos de mão e então expelem um fluxo de lama de suas bocas, que se solidifica em múltiplas paredes densas de terra. Estas crescem a partir do chão e o conjunto forma uma parede defensiva resistente.", "Curto a Médio alcance", null, "Cachorro → Cobra", "土遁・万里土流壁", "Doton: Banri Doryūheki", "Liberação de Terra: Parede de Estilo-Terra de Dez Mil Ri", null, "Estilo da Terra: Grande Muralha de Terra", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Parede do Estilo Terra", "A técnica consiste no usuário criar uma parede sólida de terra como uma forma de defesa.", "Curto alcance", "Rank B", "Completo: Tigre → Coelho → Javali → Cao,Reduzido: Passaro[2]", "土遁・土流壁どとん・どりゅうへき", "Doton: Doryūheki", "Liberação de Terra: Parede do Estilo Terra", null, "Estilo da Terra: Parede de Pedra", "Estilo Terra: Parede de Lama");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Prisão do Punho de Ferro", "O usuário cria uma arena circular cercada por muros altos que emergem da terra. O campo é então completamente mudado, tornando-se uma trincheira larga com o solo exposto, não deixando nenhum traço da vegetação original. Se os muros forem danificados, eles desaparecem em um sopro de fumaça, porém o terreno permanece alterado.", null, null, "Carneiro → Cao → Coelho → Rato", "土遁・鉄拳牢", "Doton: Tekkenrō", "Liberação de Terra: Prisão do Punho de Ferro", null, null, "Estilo Terra: Prisão de Ferro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Prisão em Lamaçal", "O usuário manipula a terra existente no local, deslocando-a em volta do corpo de seu inimigo, imobilizando-o e aprisionando-o. Pode ser usada apenas em um membro do corpo ou numa metade inteira do corpo da vítima.", "Curto alcance", null, "Cobra", "土遁・泥地獄どとん・どろじごく", "Doton: Dorojigoku", "Liberação de Terra: Prisão em Lamaçal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Projétil do Dragão de Terra", "Quando usado com a Liberação de Terra: Fluxo de Terra do Grande Rio, esta técnica pode criar uma cabeça semelhante à de dragão, que dispara bolas de lama em direção ao oponente. Isso também pode ser combinado com uma técnica de fogo para disparar rochas quentes e abrasadoras contra o inimigo.", "Longo alcance", null, "Carneiro → Cavalo → Dragao", "土遁・土龍弾", "Doton: Doryūdan", "Liberação de Terra: Projétil do Dragão de Terra", null, null, "Estilo Terra: Bomba do Dragão de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Pântano Sem Fundo", "Performando o selo de mão correspondente, o usuário transforma o solo abaixo do alvo em lama, permitindo-o capturá-lo e deixá-lo a mercê de seus atos.", "Curto alcance", null, "Cobra", "土遁・底なし沼­", "Doton: Sokonashi Numa", "Liberação de Terra: Pântano Sem Fundo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Pântano do Submundo", "Ao alterar a superfície de um objeto embaixo de um inimigo em lama e criar um pântano, o usuário pode afundar o seu inimigo na lama. A lama com chakra infundido enlaça o corpo do inimigo, tornando-se quase impossível para recuperar suas forças e escapar do pântano. O tamanho e a profundidade do pântano criado depende da habilidade do usuário e da quantidade de chakra usado, mas se o usuário é um especialista nesta técnica, ele sempre será capaz de fazer um pântano de porte médio, que é extremamente eficaz contra um grande número de inimigos ou ao ter que enfrentar criaturas gigantescas.", "Todos os alcances", "Rank A", "Javali → Tigre", "土遁・黄泉沼", "Doton: Yomi Numa", "Liberação de Terra: Pântano do Submundo", null, "Estilo da Terra: Pântano Infernal", "Estilo Terra: Pântano Escuro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Queda da Terra Pegajosa", "O usuário cria uma abertura no céu, que faz escorrer no oponente uma grande quantidade de lama, cobrindo-o. Durante a Invasão de Pain na segunda parte do Anime, esta técnica é seguida pela Liberação de Relâmpago: Aprisionamento dos Dezesseis Pilares para criar um forno gigante ao redor do oponente, e pela Liberação de Fogo: Técnica da Cerâmica Queimada para acender o forno, endurecendo a lama e imobilizando o inimigo.", "Curto a Médio alcance", null, null, "土遁・粘土落としどとん・ねんつちおとし", "Doton: Nentsuchi Otoshi", "Liberação de Terra: Queda da Terra Pegajosa", null, null, "Estilo Terra: Queda de Lama");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Retorno da Costa da Terra", "Depois de bater no chão com as mãos, Jirōbō cria uma grande parede de terra em frente a ele. A defesa não é perfeita, porém, um forte impacto ou perfuração, como o Tanque da Bala Humana Espinhenta pode perfurar a parede. Além disso, uma vez que esta técnica só protege contra ataques frontais, o inimigo pode facilmente atacar do lado ou até mesmo de cima.", "Curto alcance", "Rank C", null, "土遁・土陸返し", "Doton: Doroku Gaeshi", "Liberação de Terra: Retorno da Margem de Terra", "Estilo Terra: Escudo de Terra", "Estilo da Terra: A Outra Face do Solo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Seção do Bastão de Rocha", "Esta técnica permite que o usuário tome de um pequeno pedaço de rocha e transforme-o em forma de um bastão. O usuário pode, então, usá-lo como uma arma ou jogá-lo com precisão em seu oponente. Uma variação desta técnica permite ao usuário criar vários bastões e lançá-lo no alvo, porém com menos precisão.", "Curto alcance", null, null, "土遁・岩節棍どとん・がんせつこん", "Doton: Gansetsukon", "Liberação de Terra: Seção do Bastão de Rocha", null, null, "Estilo Terra: Lanças de Pedras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Shuriken de Rocha", "Ōnoki lança um conjunto de rochas que rodopiam entre si, criando o formato e o movimento de shuriken, acertando o oponente em cheio.", "Curto alcance", null, null, "土遁・岩石手裏剣", "Doton: Ganseki Shuriken", "Liberação de Terra: Shuriken de Rocha", "Estilo Terra: Shuriken de Rocha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Solo de Pedra Basiliar", "Depois de formar os selos necessários, o usuário cria duas paredes de terra, que prende seu adversário entre elas, esmagando-o.", "Médio alcance", null, "Cobra", "土遁・大地岩床", "Doton: Daichi Ganshō", "Liberação de Terra: Solo de Pedra Basiliar", null, null, "Estilo Terra: Prisão de Terra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Tampa Caída", "O usuário cria no ar uma tampa gigante de barro, em forma de javali, que cai sobre o alvo, prendendo-o.", "Todos os alcances", null, "Cobra", "土遁・おとし蓋", "Doton: Otoshibuta", "Liberação de Terra: Tampa Caída", null, "Estilo da Terra: Tampa Gigante", "Estilo Terra: Buta Futa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Terra Devoradora", "Esta técnica permite que o usuário controle pedras, rochas e o solo na área para atacar o inimigo, formando várias mandíbulas enormes emitidas da terra que engolem e esmagam o inimigo.", "Todos os alcances", null, "Carneiro → Cavalo → Tigre → Coelho → Cobra", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Terremoto", "Um poder proveniente do Genryū de Terra, que é capaz de desencadear um poderoso terremoto que desequilibra seus alvos e os põe contra o chão. Kuroma obteve acesso a essa técnica após assimilar os poderes do dragão.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Tiro do Bambu de Terra e Pedra", "Monga condensa o solo e a rocha na área fazendo brotar na forma de quatro grandes picos que atacam o adversário de ângulos diferentes. Devido esta técnica atacar pelos quatro ângulos, é improvável que o adversário saia ileso do ataque.", "Curto a Médio alcance", null, null, "土遁・土石筍", "Doton: Dosekidake", "Liberação de Terra: Tiro do Bambu de Terra e Pedra", null, null, "Estilo Terra: Tiro pelo Bambu");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Tonelada", "Ao fincar seu jō em uma superfície rochosa, o usuário pode coletar pedras em torno do mesmo, transformando-o em um martelo. Adicionalmente, para aumentar o peso e a força dos golpes dessa ferramenta, o usuário pode também batê-lo no chão, criando uma longa onda de choque.", "Curto a Médio alcance", null, null, "土遁・瓦岩棍", "Doton: Gagankon", "Liberação de Terra: Tonelada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Torneamento da Palma Rasgadora de Terra", "Esta técnica faz com que o solo ao redor do inimigo o envolva em espiral, enterrando-o vivo ou esmagando-o. Esta técnica é capaz de causar muito dano à área. Esta técnica é muito mais prejudicial se usado em zonas montanhosas e rochosas, ou dentro de uma caverna.", "Todos os alcances", "Rank C", "Javali → Boi → Selo especifico da técnica", "土遁・裂土転掌", "Doton: Retsudo Tenshō", "Liberação de Terra: Torneamento da Palma Rasgadora de Terra", null, "Estilo da Terra: A Mão que Quebra Montanhas", "Estilo Terra: Batida Terremoto");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Torrente de Lama", "O usuário bate no chão fazendo com que a terra se levante por debaixo deles, que libera uma onda de sujeira.", "Curto a Médio alcance", null, "Cao → Carneiro → Javali", "土遁・土石流", "Doton: Dosekiryū", "Liberação de Terra: Torrente de Lama", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica Sanduíche", "O usuário cria duas formações rochosas enormes que fecham-se sobre o adversário dos dois lados opostos, com uma força tremenda, quebrando tudo apanhado no meio. Ambas as formações são capazes de superar até mesmo a Estátua Demoníaca do Caminho Exterior em tamanho, e cada uma tem o símbolo de Iwagakure inscrito em cima dela. Kitsuchi afirmou que esta foi a sua técnica mais forte e permanente, como prova do potencial destrutivo desta habilidade e sua dificuldade.", "Longo alcance", "Rank B", "Tigre → Carneiro → Rato → Batida de Maos no Chão", "土遁・山土の術", "Doton: Sando no Jutsu", "Liberação de Terra: Montanha de Terra", "Estilo Terra: Jutsu Sanduíche", "Estilo da Terra: Técnica da Prensa Montanhosa", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Estalactite", "O usuário bate suas mãos e cria duas grandes pedras que, em seguida, são lançadas contra o seu oponente.", "Curto alcance", null, null, "土遁・岩垂氷の術どとん・いわだれこおりのじゅつ", "Doton: Iwadare Kōri no Jutsu", "Liberação de Terra: Técnica da Estalactite", "Estilo Terra: Jutsu da Estalactite", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Onda de Terra", "O usuário bate com a palma da mão no chão abaixo de seus pés, fazendo-o se mover, semelhante às ondas. É mencionado que esta técnica exige uma quantidade considerável de chakra.", "Curto alcance", null, "Cobra", "土遁・土波の術", "Doton: Tsuchi Nami no Jutsu", "Liberação de Terra: Técnica da Onda de Terra", null, null, "Estilo Terra: Onda de Barro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Prisão do Pilar de Rocha", "Esta técnica cria uma quantidade suficiente de pilares de rocha sob o solo. Eles podem então serem controlados para cercar o adversário e criar uma cela semelhante a bambus de pedras. Essa técnica também mostrou-se útil para golpear um oponente e lançá-lo para longe.", "Curto a Médio alcance", null, "Rato → Cavalo → Cao → Cobra", "土遁・岩柱牢の術", "Doton: Ganchūrō no Jutsu", "Liberação de Terra: Técnica da Prisão do Pilar de Rocha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Projeção do Peixe Subterrâneo", "Esta técnica é usada para se esconder e atacar silenciosamente, como peixes subaquáticos, e de repente aparecer na superfície do solo. Além disso, é ainda mais eficiente quando usada em uma área movimentada em que, se o alvo perceber alguma coisa, seu ataque pode ser evitado pelas massas nas proximidades, ou com distrações como uma Técnica de Clonagem para chamar a atenção do inimigo. Como tal, é uma grande estratégia para a guerrilha, e um resort provável, usado muitas vezes para coisas como assassinato.", null, "Rank C", null, "土遁・土中映魚の術", "Doton: Dochū Eigyo no Jutsu", "Liberação de Terra: Técnica da Projeção do Peixe Subterrâneo", null, null, "Estilo Terra: Projeção do Peixe Subterrâneo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Prática do Tijolo", "O usuário cria uma solida parede de terra como uma forma de defesa, manipulando o solo pré-existente para formar a parede. A terra então instantaneamente ascende e toma forma.", "Todos os alcances", null, "Javali → Tigre → Cobra → Cao → Rato → Boi → Cavalo → Coelho → Tigre → Javali → Cobra", "土遁・練瓦の術", "Doton: Renga no Jutsu", "Liberação de Terra: Técnica da Prática do Tijolo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Redução de Peso na Rocha", "Ao contrário de seu homólogo, a Liberação de Terra: Técnica do Aumento de Peso na Rocha, esta técnica diminui o peso à vontade do usuário, permitindo a manipulação deste corpo com facilidade e maior velocidade comparada com a original, no entanto, como consequência, a força exercida por esse corpo também é diminuída.", "Curto alcance", "Rank B", null, "土遁・軽重岩の術どとん・けいじゅうがんのじゅつ", "Doton: Keijūgan no Jutsu", "Liberação de Terra: Técnica da Redução de Peso na Rocha", "Estilo Terra: Jutsu do Pedregulho Leve", "Estilo da Terra: Técnica da Redução de Peso", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica da Super Redução de Peso na Rocha", "Esta técnica é uma versão aprimorada da Liberação de Terra: Técnica da Redução de Peso na Rocha para o usuário diminui drasticamente o peso de seu alvo, mas em um grau muito maior do que sua versão inicial. O usuário pode usar esta técnica para levantar enormes objetos, estes quais jamais não poderiam ser levantados, como um meteorito ou uma ilha inteira.", "Curto alcance", null, null, "土遁・超軽重岩の術どとん・ちょうけいじゅうがんのじゅつ", "Doton: Chōkeijūgan no Jutsu", "Liberação de Terra: Técnica da Super Redução de Peso na Rocha", "Estilo Terra: Jutsu do Pedregulho Super Leve", "Estilo da Terra: Técnica da Super-Redução de Peso", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica de Endurecimento", "Ao utilizar chakra baseado em terra, o usuário cria uma armadura de rocha que cobre seu corpo, tornando-o muito mais resistente à ataques. A armadura também pode ser usada para criar armas extensíveis e contundentes para ataques mais poderosos.", "Curto a Médio alcance", null, null, "土遁・硬化の術どとん・こうかのじゅつ", "Doton: Kōka no Jutsu", "Liberação de Terra: Técnica de Endurecimento", "Estilo Terra: Jutsu de Endurecimento[1]", null, "Estilo Terra: Jutsu de Petrificação");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica de Endurecimento Múltiplo", "Ao utilizar chakra baseado em terra, o usuário cria um escudo rochoso baseado em sua armadura de rocha que cobre seu corpo, protegendo seus companheiros e a si mesmo de ataques, bem como aumenta a chance de um acerto crítico.", "Curto a Médio alcance", null, null, "土遁・多重硬化の術どとん・たじゅうこうかのじゅつ", "Doton: Tajū Kōka no Jutsu", "Liberação de Terra: Técnica de Endurecimento Múltiplo", "Estilo Terra: Técnica de Endurecimento Múltiplo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica de Esconder-se Como uma Toupeira", "Essa técnica muda a terra em areia fina, canalizando chakra nela, permitindo ao usuário cavar através dela como uma toupeira. Este efeito vai ao redor do corpo (não apenas nas mãos), tornando-se grande o suficiente para uma pessoa poder percorrer. O usuário pode localizar seu inimigo, apesar de estar no subsolo, ao sentir as forças magnéticas. Ele também pode sentir o que está acontecendo na superfície e usar essa informação para lançar um ataque surpresa contra o inimigo. Ele também pode se esconder no fundo da terra, escapando a uma profundidade onde o inimigo não pode alcançar. Parece também que, após sair da escavação, o solo pode ser retornado para o seu estado original, sem deixar qualquer vestígio de onde o usuário tenha entrado na terra.", null, "Rank C", null, "土遁・土竜隠れの術どとん・もぐらがくれのじゅつ", "Doton: Moguragakure no Jutsu", "Liberação de Terra: Técnica de Esconder-se Como uma Toupeira", null, "Estilo Terra: Esconderijo da Toupeira", "Estilo Terra: Jutsu do Esconderijo da Toupeira");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica de Ocultação nas Pedras", "Esta técnica permite que o usuário misture-se em rochas e em seguida, entre e saia de outras rochas sem ser detectado.", null, null, null, "土遁・岩隠れの術", "Doton: Iwagakure no Jutsu", "Liberação de Terra: Técnica de Ocultação nas Pedras", null, null, "Estilo Terra: Jutsu do Esconderijo nas Pedras");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica de Prisão da Formiga-Leão", "Esta técnica faz com que o chão e tudo sobre e em torno dele seja sugado para o meio de um grande poço, muito parecido com o método de uma formiga-leão usado para capturar presas. Após ser arrastado para o centro, a vítima será então sugada para subterrâneo. A vítima, dependendo da profundidade que o usuário faz afundar, vai ser presa e/ou sufocada. O alcance do buraco é determinado pela quantidade de chakra que é colocado na técnica.", "Médio a Longo alcance", null, "Carneiro ou Selo especifico da técnica[1]", "土遁・蟻地獄の術", "Doton: Arijigoku no Jutsu", "Liberação de Terra: Técnica de Prisão da Formiga-Leão", null, null, "Estilo Terra: Jutsu Formiga-Leão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica de Túneis", "A técnica permite que o usuário crie túneis no solo. O túnel de grandes dimensões que é criado permite que o usuário transporte os outros sem detecção.", null, null, "Carneiro → Cao → Passaro → Rato", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Anel de Rocha Flutuante", "O usuário usa a terra para criar cubos grandes de rochas flutuantes para atirar no oponente. Os cubos de pedra giram em torno do usuário, duplicando como uma proteção contra outros ataques. O usuário também pode ajustar o tamanho das rochas, podendo afiá-las no processo.", "Curto a Médio alcance", null, null, "土遁・浮輪岩の術", "Doton: Ukiwagan no Jutsu", "Liberação de Terra: Técnica do Anel de Rocha Flutuante", "Estilo Terra: Jutsu da Rocha Flutuante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Aumento de Peso na Rocha", "Esta técnica permite ao usuário aumentar o peso de tudo que tocar. Quando Ōnoki usou esta técnica em um clone de Zetsu Branco, alguns sintomas semelhantes a petrificação começaram a ser apresentados, para, em seguida, endurecer completamente.", "Curto alcance", "Rank B", "Cobra", "土遁・加重岩の術どとん・かじゅうがんのじゅつ", "Doton: Kajūgan no Jutsu", "Liberação de Terra: Técnica do Aumento de Peso na Rocha", null, "Estilo da Terra: Técnica da Rocha Pesante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Carimbo de Rocha Explosiva", "Gitai, ao estender um de seus braços, esmaga o alvo com uma rocha enorme que começa a ser anexada instantaneamente à pele do inimigo. Depois de anexada, as partículas começam a brilhar intensamente, e explode, matando-o.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Corpo de Lama", "O usuário pode transformar seu corpo em lama. Nesta forma, o utilizador pode deslocar-se e controlar a lama a um alto grau, até mais do que a massa do seu corpo. Gozu usou esta habilidade para bloquear o Chidori de Kakashi, formando em uma parede de lama na frente de Guren e depois salvou-a formando a lama em uma mão improvisada e empurrou-a para longe da batalha.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Duplo Suicídio por Decapitação", null, "Curto alcance", "Rank D", null, "土遁・心中斬首の術", "Doton: Shinjū Zanshu no Jutsu", "Liberação de Terra: Técnica do Duplo Suicídio por Decapitação", null, null, "Estilo Terra: Jutsu do Caçador de Cabeças");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Golem", "Esta técnica permite ao usuário a criação duma humanoide grande, composta de rochas, depois de expelir o material a partir da boca ou usando uma fonte pré-existente próxima. Dependendo do usuário, o golem pode ser formado instantaneamente.", "Todos os alcances", null, "Selo de Confronto", "土遁・剛隷武の術どとん・ごうれむのじゅつ", "Doton: Gōremu no Jutsu", "Liberação de Terra: Técnica do Forte Servo Guerreiro", "Estilo Terra: Jutsu do Golem de Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Injetor da Terra", "Esta é uma técnica usada pelos shinobi de Iwagakure que soltam pedaços de rochas de suas bocas. Depois que eles são disparados, os pedaços se expandem em se tornam enormes rochas. Orochimaru afirmou que, quando é utilizada de forma colaborativa, esta técnica pode destruir uma montanha.", "Curto a Médio alcance", null, null, "土遁・岩鉄砲の術", "Doton: Iwadeppō no Jutsu", "Liberação de Terra: Técnica do Injetor da Terra", "Estilo Terra: Jutsu da Pistola de Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Martelo de Rocha", "Usando suas habilidades em Liberação de Terra, Ōnoki cria uma pedra enorme para martelar seu oponente.", "Curto alcance", null, null, "土遁・岩槌の術", "Doton: Iwa Tsuchi no Jutsu", "Liberação de Terra: Técnica do Martelo de Rocha", "Estilo Terra: Jutsu do Martelo de Rocha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Projétil de Rocha", "A Liberação de Terra: Técnica do Projétil de Rocha é uma técnica de Liberação de Terra em que o usuário cria uma grande rocha e atira em direção ao seu alvo. A rocha também pode ser jogada para o alto e explodida, a fim de atingir múltiplos alvos com os seus detritos.", "Longo alcance", null, null, "土遁・弾岩の術どとん・だんがんのじゅつ", "Doton: Dangan no Jutsu", "Liberação de Terra: Técnica do Projétil de Rocha", "Estilo Terra: Jutsu da Bala de Rocha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Projétil de Terra", "O usuário amassa chakra de natureza terra no estômago, então ele o expele sob a forma de uma poderosa corrente de rochas e poeira até o alvo pretendido.", "Todos os alcances", null, "Cao → Cavalo → Cobra[1]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Punho de Rocha", "A técnica consiste em cobrir o antebraço do usuário com rochas, aumentando o poder ofensivo do mesmo. Ao dar um potente soco endurecido com as rochas, ele pode proteger-se do contato direto com seu alvo. Se necessário, o peso da rocha pode ser aumentado para reforçar ainda mais o poder destrutivo do impacto.", "Curto alcance", null, "Cobra → Selo de Confronto", "土遁・拳岩の術どとん・けんがんのじゅつ", "Doton: Kengan no Jutsu", "Liberação de Terra: Técnica do Punho de Rocha", null, "Estilo da Terra: Técnica do Punho Rochoso", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Punho de Rocha: Queda", "Cobrindo seu braço com rochas, o usuário pode dar um soco endurecido com as rochas verticalmente, derrubando o oponente.", "Curto alcance", null, null, "土遁・拳岩の術・落どとん・けんがんのじゅつ・らく", "Doton: Kengan no Jutsu: Raku", "Liberação de Terra: Técnica do Punho de Rocha: Queda", "Estilo Terra: Jutsu do Punho Rochoso: Queda", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Super Aumento de Peso na Rocha", "Uma versão aprimorada da Liberação de Terra: Técnica do Aumento de Peso na Rocha em que o peso é drasticamente aumentado, em uma escala muito superior comparada com a versão original. Vantajosamente, esta técnica permite o aumento do peso das técnicas do próprio usuário, a fim de esmagar o inimigo ou destruir alguma barreira com um ataque físico.", "Curto alcance", null, null, "土遁・超加重岩の術どとん・ちょうかじゅうがんのじゅつ", "Doton: Chōkajūgan no Jutsu", "Liberação de Terra: Técnica do Super Aumento de Peso na Rocha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Super Punho de Rocha", "Cobrindo seu braço com rochas, o usuário pode dar um potente soco endurecido com as rochas, contra seu oponente no solo. Se necessário, o peso da rocha pode ser aumentado para aumentar ainda mais o poder destrutivo do impacto.", "Curto alcance", null, null, "土遁・超拳岩の術どとん・ちょうけんがんのじゅつ", "Doton: Chō Kengan no Jutsu", "Liberação de Terra: Técnica do Super Punho de Rocha", "Estilo Terra: Jutsu do Super Punho Rochoso", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Terra: Técnica do Tatame de Rocha", "O usuário manipula diversas placas de rocha que atacam severamente seu oponente, seja sequencialmente em tiros, de forma condensada ou ainda rotacionando-as verticalmente para um ataque mais efetivo.", "Curto alcance", null, null, "土遁・岩畳の術", "Doton: Iwadatami no Jutsu", "Liberação de Terra: Técnica do Tatame de Rocha", "Estilo Terra: Técnica do Tatame de Rocha", null, null);
