INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Velocidade: Voo Sem Sombra", "Hiruko usou esta técnica para se mover a velocidades muito altas, atingindo rapidamente os seus oponentes em vários ângulos, evitando qualquer tipo de contra-ataque. Esta técnica é muito eficaz contra usuários de taijutsu que usam golpes de curto alcance com conceito baseado em força.", null, null, "Javali", "迅遁・無影翔じんとん・むかげしょう", "Jinton: Mueishō", "Liberação de Velocidade: Voo Sem Sombra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Atsugai", "A máscara de Liberação de Vento de Kakuzu comprime uma grande quantidade de vento e, em seguida, a libera em direção ao inimigo. Devido à compressão, quando a técnica é liberada, a pressão dela gera uma explosão que, além de ir contra o inimigo, devasta tudo em contato. Combinada com a máscara de fogo, esta técnica compõe uma das mais poderosas combinações de Kakuzu.", "Todos os alcances", "Rank B", "Carneiro", "風遁・圧害ふうとん・アツガイ", "Fūton: Atsugai", "Liberação de Vento: Pressão Nociva", null, "Estilo do Vento: Pressão Nociva", "Estilo Vento: Pressão Nociva");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Bala de Ar Perfuradora", "Para usar esta técnica, Shukaku respira fundo e, em seguida, bate no seu estômago para aplicar pressão externa, usando para atirar uma poderosa bola de ar altamente comprimida de sua boca. A bola de ar expelida é forte o suficiente para escavar o solo e nivelar uma floresta inteira. Devido à grande quantidade de chakra comprimido nela, ela explode no momento em que atinge o seu alvo, causando uma quantidade enorme de danos, bem como o nivelamento absolutamente tudo no seu caminho. Shukaku é capaz de disparar múltiplas destas em rápida sucessão, tendo um certo nível de controle sobre o tamanho das balas.", "Todos os alcances", null, null, "風遁・練空弾", "Fūton: Renkūdan", "Liberação de Vento: Bala de Ar Perfuradora", null, "Estilo do Vento: Esfera de Vento", "Estilo Vento: Bala de Ar");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Balas de Ar", "Esta técnica atira múltiplas balas de ar na vítima com velocidade intensa. As balas também têm grande poder, o suficiente para subjugar todo um grupo de shinobi.", "Todos os alcances", null, "Passaro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Barreira Protetora de Vento Forte", "Temari manuseia o Leque Dobradiço Gigante para criar uma rápida e forte corrente de vento para afastar seus inimigos nas proximidades.", "Curto alcance", null, null, "風遁・豪風防壁ふうとん・ごうふうぼうへき", "Fūton: Gōfū Bōheki", "Liberação de Vento: Barreira Protetora de Vento Forte", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Bola de Vento Espiral", "Esta técnica permite que o usuário respire chakra infundido com vento na palma da sua mão, moldando-o em uma pequena bola espiral de ar. Em seguida, o usuário irá dispará-la em seu oponente. Esta técnica parece ser muito rápida e poderosa, pois é capaz de esmagar através da espessura de uma rocha. Elas podem ser lançadas em rápida sucessão.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Bola de Vácuo", "O usuário toma uma respiração profunda e, em seguida, exala uma esfera de vácuo que é disparada em rápida velocidade contra seu alvo. O impacto da esfera causa uma poderosa explosão de vento forte o suficiente para despedaçar a cúpula de madeira de Yamato.", "Curto a Médio alcance", null, null, "風遁・真空玉ふうとん・しんくうだま", "Fūton: Shinkūdama", "Liberação de Vento: Bola de Vácuo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Corrente da Bandeira", "Por meio da oscilação da sua bandeira, o usuário pode mudar o fluxo e a forma das correntes de ar à sua volta e pode usá-la para vários propósitos ofensivos contra a ameaça inimiga, como romper várias defesas e cortar através de roupas.", "Curto a Médio Alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Dança Selvagem da Corrente de Ar", "O usuário cria e controla correntes de ar a partir das palmas de suas mãos. A técnica é capaz de chicotear facilmente o pó solto na área sendo realizada para criar uma tempestade de poeira controlável.", "Todos os alcances", null, "Coelho → Passaro → Rato → Dragao → Pássaro[2]", "風遁・気流乱舞", "Fūton: Kiryū Ranbu", "Liberação de Vento: Dança Selvagem da Corrente de Ar", null, "Estilo do Vento: Dança das Correntes de Ar", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Dança da Dispersão de Flor", "Uma técnica que permite que o usuário convoque um ciclone de pétalas para atacar seu oponente. O usuário pode permanecer parado em um local para usar a técnica, pois ele ou ela pode dirigi-lo em qualquer lugar que desejar. Portanto, o usuário pode aumentar a direção e poder da técnica ao máximo, como um último recurso.", "Longo Alcance", null, "Tigre → Coelho → Dragao", "風遁・花散舞", "Fūton: Hanachiri Mai", "Liberação de Vento: Dança da Dispersão das Flores", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Destruição Aberta", "Kakuzu, enquanto em sua forma de longo alcance, libera uma bala vento pressurizado para o centro do campo de batalha que cria um turbilhão que atrai seu adversário para ele.", "Longo alcance", null, null, null, null, null, "Estilo Vento: Destruição Aberta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Dispersão de Balas de Areia", "Depois de Gaara fazer inúmeras balas de areia, Shukaku expele uma rajada de vento gigante de seu estômago para aumentar a força das balas. Este ataque é poderoso o suficiente para perfurar a carne com facilidade. Um benefício adicional deste ataque é que Gaara pode usar qualquer uma das areias deixadas no sangue de seu alvo para imobilizá-lo, deixando-o vulnerável ​​a ataques seguintes.", "Todos os alcances", null, "Carneiro", "風遁・砂散弾ふうとん・ささんだん", "Fūton: Sasandan", "Liberação de Vento: Dispersão de Balas de Areia", null, "Estilo do Vento: Projéteis de Areia", null);
