INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Esfera de Vácuo", "Depois de promover uma inalação profunda, o usuário exala em várias pequenas explosões de chakra de vento, de tal maneira com que as dispersam numa extensa área, dificultando qualquer saída sem resultar em algum dano. Devido às propriedades desta técnica, as explosões expelidas são capazes de perfurar e, potencialmente, atravessar a carne de um oponente enquanto se colidem.", "Todos os alcances", "Rank B", "Rato → Coelho → Cachorro", "風遁・真空玉", "Fūton: Shinkūgyoku", "Liberação de Vento: Esfera de Vácuo", null, "Estilo do Vento: Esfera de Vácuo", "Estilo Vento: Balas de Vácuo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Espada do Conde do Vento", "Com apenas um movimento manual, o usuário dá origem a uma onda de vento fina comprimida em sua mão. Essa técnica é forte o suficiente para cortar um aço ou um clone da sombra com facilidade.", "Curto alcance", null, null, "風遁・風伯刃ふうとん・ふうはくじん", "Fūton: Fūhakujin", "Liberação de Vento: Espada do Conde do Vento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Estouro de Ar Comprimido", "Uma versão inversa da Liberação de Vento: Lâmina de Vácuo, em que o usuário compressa ar no interior de sua arma, que, caso seja destruída, irá fazer com que seus fragmentos estourem perto de seu adversário, derrubando-o.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Fogos de Artifício Intermitentes", "O usuário lança um punhado de shuriken no ar como se poderia jogar flores. Com um balanço de seu Leque Dobradiço Gigante, o usuário, em seguida, junta as shuriken no vento, fazendo-as cair no chão e voltar como uma mola em várias sequências.", "Curto a Médio alcance", null, null, "風遁・閃光華美", "Fūton: Senkō Hanabi", "Liberação de Vento: Fogos de Artifício Intermitentes", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Chama Explosiva", "Kakashi desfere alguns ataques no oponente, em seguida usa a Liberação de Fogo: Projétil de Chama do Dragão de Fogo, incinerando-o. Logo, ele salta para trás e rapidamente expele uma onda de vento de sua boca, aumentando significativamente as chamas ao redor do oponente e causando uma enorme explosão.", "Curto alcance", null, null, "風遁・大爆炎", "Fūton: Daibakuen", "Liberação de Vento: Grande Chama Explosiva", "Estilo Vento: Grande Chama Explosiva", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Destruição", "A Liberação de Vento: Grande Destruição é um ninjutsu de Liberação de Vento relativamente simples que permite criar uma súbita rajada de vento em escala variável, dependendo da maestria do usuário.", "Curto a Médio alcance", "Rank C", "Tigre → Boi → Cao → Coelho → Cobra", "風遁・大突破ふうとん・だいとっぱ", "Fūton: Daitoppa", "Liberação de Vento: Grande Destruição", null, null, "Estilo Vento: Grande Destruição");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Esfera de Vácuo", "Esta técnica é uma versão mais forte da Liberação de Vento: Esfera de Vácuo, onde o usuário escolhe comprimir todo o ar inalado anteriormente em uma única esfera, causando um grande esmagamento de chakra vento que, depois, cospe de sua boca.", "Todos os alcances", "Rank B", "Tigre → Cao", "風遁・真空大玉ふうとん・しんくうたいぎょく", "Fūton: Shinkū Taigyoku", "Elemento Vento: Grande Esfera de Vácuo", null, "Estilo do Vento: Grande Esfera de Vácuo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Lançamento da Rede", "Da mesma forma que sua técnica parente, o usuário utiliza seu Leque Dobradiço Gigante para criar uma infinidade de lâminas de vento extremamente afiadas para dilacerar o seu oponente.", "Médio a Longo alcance", null, null, "風遁・豪掛け網ふうとん・おうかけあみ", "Fūton: Ōkakeami", "Liberação de Vento: Grande Lançamento da Rede", null, "Estilo do Vento: Grande Rede", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Sucção", "Esta habilidade consiste em sugar, de forma rápida e potente, qualquer objeto pequeno e leve — geralmente guloseimas, como salgadinhos ou doces — ou líquidos, direto à boca. Embora inofensiva, esta habilidade não possui um limite de sucção, e, portanto, os membros do clã Akimichi podem usar em combinação com suas técnicas, como Chōji Akimichi, que é capaz de sugar água o suficiente para usar uma variante do Tanque da Bala Humana.", "Curto alcance", null, "Juntar as Maos", "風遁・大吸引ふうとん・だいきゅういん", "Fūton: Daikyūin", "Liberação de Vento: Grande Sucção", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Tarefa do Dragão", "O usuário balança seu Leque Dobradiço Gigante pro alto, criando um tornado gigante que desce sobre seu alvo. Esta técnica pode ser configurada como uma finta, pois tem uma ativação retardada. A técnica modifica temporariamente as condições meteorológicas e os ataques saem do céu sobre a pessoa. As nuvens da tempestade que criam o ataque se dissipam imediatamente após o tornado começar quando o furacão continua em um estado altamente concentrado.", "Todos os alcances", null, null, "風遁・龍ノオオシゴト", "Fūton: Tatsu no Ōshigoto", "Liberação de Vento: Grande Tarefa do Dragão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grande Tornado Cortante", "Temari manuseia seu Leque Dobradiço Gigante e cria um tornado, que lança seu oponente para longe e corta-o gravemente, deixando-o caído no chão após o tornado desaparecer.", "Médio alcance", null, null, null, null, "Liberação de Vento: Grande Tornado Cortante", "Grande Parede do Redemoinho", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Grito Estridente", "Ao gritar, o usuário é capaz de provocar um intenso som agudo poderoso o suficiente para demolir uma casa.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Lançamento da Rede", "Usando o Leque Dobradiço Gigante, Temari, cria várias correntes estreitas de vento que forma uma grande rede de corte. Os ventos gerados a partir desta técnica são extremamente afiados e poderosos o suficiente para infligir ferimentos superficiais no Terceiro Raikage, cujo corpo foi reconhecido por sua durabilidade tremenda.", "Todos os Alcances", null, null, "風遁・掛け網ふうとん・かけあみ", "Fūton: Kakeami", "Liberação de Vento: Lançamento da Rede", "Estilo Vento: Arrastão[2]", "Estilo do Vento: Rede de Captura", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Lâmina de Vácuo", "O usuário exala vento infundido com chakra em uma arma, a fim de aumentar a sua agudeza, alcance e letalidade. Por exemplo, o usuário pode infundir em uma kunai para se assemelhar a uma cimitarra improvisada ou, como visto no anime, infundir uma shuriken para aumentar seu alcance e poder de corte.", "Curto a Médio alcance", null, null, "風遁・真空刃ふうとん・しんくうじん", "Fūton: Shinkūjin", "Liberação de Vento: Lâmina de Vácuo", "Estilo Vento: Lâmina de Vácuo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Máscara do Vendaval", "Kakuzu, enquanto em sua forma de fios pretos, cria um projétil de vento para repelir qualquer ataque ou inimigo em uma área considerável.", "Longo alcance", null, null, "風遁・豪風の面ふうとん・ごうふうのめん", "Fūton: Gōfū no Men", "Liberação de Vento: Máscara do Vendaval", "Estilo Vento: Máscara do Vendaval", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Nuvem de Areia Infinita — Grande Destruição", "Uma técnica usada por Gaara na sua forma de miniatura de Shukaku. Ele primeiro aspira o ar com a boca em seu rosto e em todo seu corpo, e sopra para fora junto com nuvens de areia revestidas de chakra. Esta poderosa rajada abrange um grande ângulo de um fluxo suficiente para derrubar as árvores e danificá-las com a areia. Essa técnica é extremamente eficaz, mesmo contra vários inimigos.", "Todos os alcances", null, null, "風遁・無限砂塵　大突破ふうとん・むげんさじん　だいとっぱ", "Fūton: Mugen Sajin — Daitoppa", "Liberação de Vento: Nuvem de Areia Infinita — Grande Destruição", "Estilo Vento: Infinita Devastação da Tempestade de Areia", "Estilo do Vento: Devastação Infinita da Areia", "Estilo Vento: Tempestade Devastadora de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Onda de Vácuo", "A Liberação de Vento: Onda de Vácuo é uma técnica fundamental de Liberação de Vento e a especialidade de Danzō Shimura.", "Curto a Médio alcance", "Rank B", null, "風遁・真空波", "Fūton: Shinkūha", "Liberação de Vento: Onda de Vácuo", null, "Estilo do Vento: Onda de Vácuo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Palma Vendaval", null, "Curto a Médio alcance", "Rank C", "Bater as Maos Juntas", "風遁・烈風掌ふとん・れっぷうしょう", "Fūton: Reppūshō", "Liberação de Vento: Palma Vendaval", null, "Estilo do Vento: Palma da Rajada de Vento", "Estilo Vento: Palma Vendaval");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Parede Protetiva de Areia Aérea", "Através da manipulação da areia juntamente com seu chakra de vento, Gaara forma múltiplos pilares gigantes de areia que atuam como escudos através da elevação de uma grande porção de areia do solo.", "Médio a Longo alcance", null, null, "風遁・空砂防壁ふうとん・くうさぼうへき", "Fūton: Kūsa Bōheki", "Liberação de Vento: Muro de Proteção de Areia Aérea", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Projétil de Chamas do Óleo de Sapo", "Esta é uma técnica de konbijutsu tripla entre Naruto, Gamatatsu e Gamakichi. É uma expansão do Liberação de Vento: Projétil do Óleo de Sapo. Depois de Naruto reforçar o Projétil do Óleo de Sapo de Gamatatsu com seu chakra Vento, Gamakichi usa sua Liberação de Fogo: Projétil de Chamas para inflamar o óleo. O vento não só melhora a cobertura do óleo e a distância, mas também adiciona o poder à chama. Usando esta técnica eles foram capazes de derrubar o Três-Caudas.", "Todos os alcances", null, "Carneiro", "風遁・蝦蟇油炎弾ふうとん・がまゆえんだん", "Fūton: Gamayu Endan", "Liberação de Vento: Projétil de Chamas do Óleo de Sapo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Projétil do Óleo de Sapo", "Esta técnica é uma versão melhorada do Projétil do Óleo de Sapo infundida com chakra da Liberação de Vento, semelhante em princípio à Liberação de Água: Revólver de Água. É parecida com a Liberação de Vento: Revólver de Sapo de Naruto e Gamatatsu, porém é usado óleo de sapo em vez de água. Naruto molda seu chakra de Liberação de Vento, enquanto Gamatatsu molda o seu próprio chakra no óleo em sua boca. No comando Gamatatsu vai expelir o óleo de sua boca, e combiná-lo com o chakra de vento que é canalizado de Naruto para o corpo de Gamatatsu, criando um fluxo poderoso e rápido de óleo de sapo, que se espalha para cobrir uma área muito maior, mas mantêm uma poderosa força devido ao chakra de vento.", "Todos os alcances", null, "Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Punho do Redemoinho", "A Liberação de Vento: Punho do Redemoinho é um ninjutsu desenvolvido por Mirai Sarutobi, baseando-se na manipulação de vento da Técnica da Foice da Doninha de Temari e na forma do Rasengan.", "Curto a Médio alcance", null, null, "風遁・旋風拳ふうとん・せんぷうけん", "Fūton: Senpūken", "Liberação de Vento: Punho do Redemoinho", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Rasengan", "A Liberação de Vento: Rasengan é uma técnica original criada por Naruto Uzumaki, ampliando o conceito original do Rasengan: adicionando um elemento de mistura de chakra com o Rasengan, enfatizando a combinação das mais altas formas de Transformação da Forma e Transformação da Natureza.", "Curto alcance", "Rank A", null, "風遁・螺旋丸ふうとん・らせんがん", "Fūton: Rasengan", "Liberação de Vento: Esfera Espiral", null, "Estilo do Vento: Rasengan", "Estilo Vento: Rasengan");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Rasengan da Lâmina do Dragão", "Naruto utiliza a Liberação de Vento: Rasengan, que, quando utilizado em conjunto com a respectiva participação da Lâmina do Dragão, aprimora a técnica, causando um dragão de vento que gira em torno de Naruto, danificando o ambiente ao seu redor.", "Curto alcance", null, null, "風遁・龍刃螺旋丸", "Fūton: Ryūjin Rasengan", "Liberação de Vento: Rasengan da Lâmina do Dragão", "Estilo Vento: Rasengan da Lâmina do Dragão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Rasenshuriken", "A Liberação de Vento: Shuriken Espiral (風遁・螺旋手裏剣, Fūton: Rasenshuriken) foi criada por Naruto Uzumaki após ele ter dominado a Liberação de Vento: Rasengan.", "Todos os alcances", "Rank S", null, "風遁・螺旋手裏剣", "Fūton: Rasenshuriken", "Liberação de Vento: Shuriken Espiral", null, "Estilo do Vento: Rasenshuriken", "Estilo Vento: Rasen Shuriken");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Rasenshuriken Repetido", "Enquanto no Modo Besta com Cauda ou no Modo Sábio, Naruto é capaz de formar dois Rasenshuriken e empunhar os dois ao mesmo tempo. Seu poder de corte é tão alto que, usando um deles como uma arma branca, Naruto foi capaz de cortar através das caudas extremamente duráveis do Dez-Caudas com facilidade.", "Todos os alcances", null, null, "風遁・螺旋連手裏剣ふうとん・らせんれんしゅりけん", "Fūton: Rasenrenshuriken", "Liberação de Vento: Shuriken Espiral Repetida", "Estilo Vento: Rasen Shuriken Repetido", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Redemoinho da Nuvem de Areia", "O usuário cria uma tempestade de areia em forma de cúpula que rodeia o adversário e impede que ele escape. Durante a execução da técnica, as partículas de areia usadas colidem umas com as outras, criando assim ondas eletromagnéticas que colocam a pessoa presa sob um interminável genjutsu formado por seres sem forma que atacam com kunai. Enquanto o alvo está ocupado se defendendo dessas ilusões, ele fica vulnerável à ataques de fora.", "Curto alcance", null, "Passaro → Macaco → Javali → Dragao", "風遁・砂塵旋風ふうとん・さきんせんぷう", "Fūton: Sajin Senpū", "Liberação de Vento: Redemoinho da Nuvem de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Revólver de Sapo", "A Liberação de Vento: Revólver de Sapo é uma técnica de duas vias de konbijutsu entre Naruto e Gamatatsu. Ela é uma versão da Liberação de Água: Revólver de Água infundida com a Liberação de Vento.", "Todos os alcances", null, null, "風遁・蝦蟇鉄砲ふうとん・がまてっぽう", "Fūton: Gama Teppō", "Liberação de Vento: Pistola de Água do Sapo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Ruptura", "Depois de concentrar chakra em seu estômago, o usuário expele como uma rajada de vento. Esta técnica foi poderosa o suficiente para soprar tudo que estava no alcance imediato do usuário. Esta técnica pode também ser usada para propósitos suplementares como parar a queda de alguém que ele testemunha. Quando Mitsuki utilizou novamente esta técnica, ele criou um vórtex de vento logo abaixo de Boruto Uzumaki.", "Curto a Médio alcance", null, "Juntar as Maos → Cão → Cavalo → Passaro", "風遁・突破", "Fūton: Toppa", "Liberação de Vento: Ruptura", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Shuriken Rotativa", "Esta técnica infunde chakra da natureza vento em armas brancas. O usuário pode, em seguida, manipular as lâminas de forma em que elas girem e ataquem seu alvo precisamente.", "Curto a Médio alcance", null, null, "風遁・回天手裏剣", "Fūton: Kaiten Shuriken", "Liberação de Vento: Shuriken Rotativa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Série de Ondas de Vácuo", "O usuário toma uma respiração profunda e exala várias lâminas de vento em ângulos diferentes, por mover a cabeça rapidamente em várias direções. A potência desta técnica pode ser dramaticamente melhorada quando utilizada em conjunto com os efeitos de uma extrema sucção, tais como as geradas pelo Baku, onde o ataque aumentado provou ser eficaz o suficiente para cortar através das defesas de um Susanoo completo.", "Curto a Médio alcance", null, "Rato → Cobra → Cavalo → Cobra → Cao", "風遁・真空連波", "Fūton: Shinkū Renpa", "Elemento Vento: Série de Ondas de Vácuo", null, "Estilo do Vento: Sequência de Vácuos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Tornado Shakujō", "Ao girar sua shakujō acima de sua cabeça, Kazuma produz um grande tornado em torno de si antes de lançá-lo contra sua vítima. O tornado é poderoso o suficiente para facilmente repelir a Palma Vendaval Dilacerante da Besta.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Tornado Violento", "Com o movimento de suas mãos, o usuário desencadeia um poderoso vendaval que toma a forma de um tornado que avança em direção ao seu alvo, podendo causar danos maciços à área circundante.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Tufão Transitório", "A Liberação de Vento: Tufão Transitório é uma poderosa técnica de Liberação de Vento utilizada por Yagura exclusivamente no anime.", "Todos os alcances", null, null, "風遁・台風一過", "Fūton: Taifūikka", "Liberação de Vento: Tufão Transitório", null, null, "Estilo Vento: Tufão Passageiro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Técnica da Grande Foice da Doninha", "Temari usa seu leque gigante para criar uma versão mais poderosa e em maior escala de sua Técnica da Grande Foice da Doninha, liberando correntes de ar pesado que se chocam em conjunto para criar muitas bolsas de vácuo para cortar seus alvos.", "Médio a Longo alcance", null, null, "風遁・大鎌いたちの術", "Fūton: Ōkamaitachi no Jutsu", "Liberação de Vento: Técnica da Grande Foice da Doninha", null, null, "Jutsu do Ciclone Ceifador");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Técnica da Nuvem de Poeira", "Esta técnica cria uma corrente de vento que contém partículas de poeira em alta velocidade, capazes de dizimar qualquer coisa que encostar nela em apenas uma questão de momentos. A técnica concluída é direcionada verticalmente através das mãos sobrepostas que são colocadas na frente da boca do usuário após a formação dos selos de mão necessários.", "Todos os alcances", null, "Cavalo → Macaco → Passaro", "風遁・風塵の術", "Fūton: Fūjin no Jutsu", "Liberação de Vento: Técnica da Nuvem de Poeira", null, "Estilo do Vento: Técnica da Poeira ao Vento", "Estilo Vento: Jutsu da Tempestade de Poeira");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Técnica da Onda de Ar", "Com o movimento de um único braço, o usuário libera uma onda de vento capaz de repelir ataques inimigos. Com isso Kaguya foi capaz de se defender de centenas de flechas. Hagoromo foi capaz de dilacerar uma das raízes da Árvore Divina com essa técnica.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Técnica do Projétil de Vento", "Depois de amassar chakra no estômago, o usuário o expele sob a forma de uma poderosa corrente de vento até o alvo pretendido, ou servindo de suporte para outras técnicas ou uma rápida locomoção. No anime, foi mostrado que também pode ser usado para criar uma nuvem de poeira, escondendo a localização e os movimentos do usuário.", "Todos os alcances", null, "Carneiro → Coelho → Cao[1]", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Técnica do Vento Cortador", "Usando seu chakra ou uma ferramenta de ninja, como um leque da guerra para manipular ou então criar uma fonte externa de vento, o usuário pode criar uma única, ou várias lâminas de vento, que são capazes de cortar através de seu alvo com facilidade. Temari e um grupo de usuários tessenjutsu de Sunagakure também usaram esta técnica como um meio de combater uma poderosa torrente de entrada de vento.", "Médio alcance", null, "Tigre → Coelho → Cao → Carneiro → Dragão", "風遁・風切りの術", "Fūton: Kazekiri no Jutsu", "Liberação de Vento: Técnica do Vento Cortador", null, "Estilo do Vento: Técnica do Vento Cortante", "Estilo Vento: Arte da Lâmina de Rajada");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Ultra-Grande Bola Rasenshuriken", "Liberação de Vento: Ultra-Grande Bola Rasenshuriken é uma versão mais avançada da Liberação de Vento: Rasenshuriken.", "Todos os alcances", null, null, "風遁・超大玉螺旋手裏剣ふうとん・ちょうおうだまらせんしゅりけん", "Fūton: Chōōdama Rasenshuriken", "Liberação de Vento: Bola Ultra-Grande Shuriken Espiral", null, "Estilo do Vento: Chōōdama Rasenshuriken", "Estilo Vento: Rasenshuriken Massivo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Vendaval da Montanha Verdejante", "Utilizando chakra baseado em vento, o usuário pode infundir suas armas brancas com ele a partir de uma certa distância e manipulá-las.", "Médio alcance", null, "Cobra", "風遁・翠嵐烈風", "Fūton: Suiran Reppū", "Liberação de Vento: Vendaval da Montanha Verdejante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Vendaval em Múltiplas Camadas", "O usuário cria uma parede de múltiplas camadas de vácuo, que são empilhadas uma em cima da outra, como um mil-folhas. Esta técnica pode ser utilizada para proporcionar suporte adicional para outras técnicas defensivas.", "Curto alcance", null, null, "風遁・八重疾風", "Fūton: Yae Hayate", "Liberação de Vento: Vendaval em Múltiplas Camadas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Vento Divino", "O usuário solta uma rajada de vento que forma vários pequenos tornados. Combinando com a Liberação de Fogo: Execução de Fogo que forma um círculo flamejante ao nível do solo, é possível criar um enorme furacão de chamas.", "Curto a Médio alcance", null, null, "風遁・神風", "Fūton: Kamikaze", "Liberação de Vento: Vento Divino", null, null, "Arte Ninja: Estilo Vento: Vento Divino");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Vento Divino das Montanhas", "Esta técnica cria um vórtice de vento para explodir em um alvo. Ela pode ser usada em um combo com a Liberação de Fogo: Execução de Fogo e Liberação de Terra: Corredor de Terra para lançar um inferno maciço nos oponentes, enquanto eles estão presos dentro do espaço confinado da terra.", "Médio alcance", null, "Tigre → Cobra → Carneiro", "風遁・神颪", "Fūton: Kami Oroshi", "Liberação de Vento: Vento Divino das Montanhas", null, null, "Estilo Vento: Baixa Corrente Divina");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento: Violentas Ondas do Redemoinho de Vento", "Dançarina dos Ventos usa seu Leque Dobradiço Gigante e envia uma onda de vento na direção do adversário, lançando-o longe.", "Curto a Médio alcance", null, null, "風遁・烈波旋風ふうとん・れつはせんぷう", "Fūton: Retsuha Senpū", "Liberação de Vento: Violentas Ondas do Redemoinho de Vento", "Estilo Vento: Redemoinho Violento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Vento e Relâmpago: Tufão Relâmpago Espiral", "Naruto faz um Rasengan de natureza vento e Sasuke acrescenta seu Chidori na esfera, após o qual os dois empurram a técnica no oponente.", "Curto alcance", null, null, "風遁雷遁・颶風雷旋丸", "Fūton Raiton: Gufū Raisengan", "Liberação de Vento e Relâmpago: Tufão Relâmpago Espiral", "Estilo Vento e Relâmpago: Furacão Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Yin: Chibaku Tenrai", "Madara lança várias esferas negras para o céu a fim de criar um Chibaku Tensei com o oponente sendo o centro gravitacional. Em seguida, ele infunde ao satélite relâmpagos roxos, que alimentam gradativamente a energia até explodir e resultar em uma grande explosão.", "Longo alcance", null, null, "陰遁・地爆天雷いんとん・ちばくてんらい", "Inton: Chibaku Tenrai", "Liberação de Yin: Relâmpago da Devastação", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Yin: Relâmpago Inverso", "Usando a Liberação de Yin para criar forma do nada, o usuário concentra uma massa de relâmpagos nas palmas de suas mãos e eletrocuta o inimigo.", "Curto a Médio alcance", null, null, "陰遁・転雷いんとん・てんらい", "Inton: Tenrai", "Liberação de Yin: Relâmpago Inverso", "Estilo Sombra: Relâmpago Rotatório", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água Medicinal: Louva-a-Deus de Água", "O usuário pode formar grandes garras de louva-a-deus feitas de água nos seus braços, estendendo o alcance de seus ataques. Também pode-se aplicar fluxo de chakra pelas garras de forma semelhante ao Bisturi de Chakra, aumentando a capacidade de perfuração dos ataques e assim aumentando também a força de penetração.", "Curto alcance", null, "Carneiro → Cavalo → Passaro → Carneiro", "医療水遁・ミズカマキリ", "Iryō Suiton: Mizukamakiri", "Liberação de Água Medicinal: Louva-a-deus de Água", null, null, "Estilo Médico da Água: Escorpião de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água Medicinal: Medusa", "Por meio da aplicação de uma água-viva para a forma de uma pessoa com antecedência, a água-viva pode armazenar e espalhar chakra pelo corpo de seu hospedeiro para permitir uma rápida recuperação de um incidente recente. Essa técnica aparenta drenar a água-viva completamente, já que pode ser usada uma única vez.", "Curto alcance", null, null, "盲漠砂陣・ミズクラゲ", "Iryō Suiton: Mizukurage", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água Medicinal: Mosquito de Água", "O usuário cria mosquitos de água, os quais podem ser usados para a extração de toxinas em uma parte específica do corpo do paciente. A total extensão desta técnica não foi vista, mas é aparentemente muito dolorosa quando aplicada.", "Curto alcance", null, "Tigre", "医療水遁・水蚊", "Iryō Suiton: Suika", "Liberação de Água Medicinal: Mosquito de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Agulhas de Chuva", "Ao usar a chuva existente ao seu redor, o usuário infunde seu chakra nas gotas de chuva individuais, afiando-as em agulhas ultrafinas para cair e perfurar seus alvos. Por causa de sua natureza minúscula, é difícil para uma pessoa discernir a verdadeira natureza dessa técnica sem conhecimento prévio.", "Todos os Alcances", null, "Tigre → Cobra → Tigre → Cobra → Lebre", "水遁・雨降針", "Suiton: Amefuribari", "Liberação de Água: Agulhas de Chuva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Armas de Água", "Através de suas mãos, o usuário é capaz de reunir água e converte-la em várias armas. Essas armas podem ser usadas tanto em combate de curto alcance, quando de longo alcance.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Ataque de Água do Céu Frio", "O usuário dispara uma numerosa quantidade de grandes dardos de gelo de sua boca, em alta velocidade que podem facilmente quebrar pedras.", "Curto a Médio alcance", null, "Boi → Cavalo → Carneiro", "水遁・冽空水撃", "Suiton: Rekkū Suigeki", "Liberação de Água: Ataque de Água do Céu Frio", null, null, "Estilo Água: Jutsu dos Dardos de Gelo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Bacia de Água", "O usuário emite um jato contínuo de água de sua mão para atacar o inimigo e interromper o ataque. Devido à natureza ser de água, esta técnica funciona particularmente bem contra ataques baseados em fumaça. No anime, essa técnica também foi usada para regar uma plantação.", "Curto alcance", null, "Cobra → Dragao[2]", "水遁・水鉢", "Suiton: Suihachi", "Liberação de Água: Bacia de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Bala Torrencial", "Utilizando a Liberação de Água, o usuário cria um poderoso turbilhão de água em volta do seu oponente apenas com um simples balanço de seu braço. Ele então dissipa a técnica com outro gesto de seu braço.", "Médio alcance", null, null, "水遁・激流弾", "Suiton: Gekiryūdan", null, "Estilo Água: Corredeiras Furiosas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Bala de Respingo", "Um ninjutsu desconhecido utilizado por Boruto em Boruto: Naruto Next Generations.", null, null, null, "水遁・飛沫弾すいとん・ひまつだん", "Suiton: Himatsudan", "Liberação de Água: Bala de Respingo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Barragem de Granizo", "Esta técnica congela água em doze pedaços irregulares de granizo.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Besta de Água", "O usuário manipula a água e a transforma em um lobo gigante de coloração amarela. A besta pode lançar poderosas correntes de água de sua boca, ou varrer a área com suas garras para deixar uma poderosa torrente de água para lançar os inimigos para longe.", "Todos os alcance", null, "Cao → Cavalo → Macaco → Carneiro", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Bloqueio Tormentoso", "Esta técnica permite que o usuário invoque uma enorme quantidade de água do céu, batendo-a sobre o oponente. Se necessário, o usuário pode cancelar a técnica em um instante.", "Todos os alcances", null, null, "水遁・波乱万衝", "Suiton: Haran Banshō", "Liberação de Água: Bloqueio Tormentoso", "Estilo Água: Turbulência da Tempestade", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Boca da Serpente", "O usuário gera uma coluna em espiral de água que toma a forma de uma serpente com uma mandíbula escancarada. Esta serpente pode torcer e acompanhar os seus alvos e engoli-los. Depois de ter engolido eles, ela se transforma em um rio que transporta os alvos para longe.", "Longo alcance", null, "Macaco → Rato → Tigre → Dragao → Cavalo → Dragão", "水遁・蛇の口", "Suiton: Ja no Kuchi", "Liberação de Água: Boca da Serpente", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Captura do Campo de Xarope de Amido", "Trata-se de um ninjutsu de Liberação de Água desenvolvido por Izumo Kamizuki.", "Todos os alcances", "Rank C", "Carneiro → Tigre", "水遁・水飴拿原", "Suiton: Mizuame Nabara", "Liberação de Água: Campo de Captura de Xarope de Amido", null, null, "Estilo Água: Armadilha de Calda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Cerco de Pilares", "O usuário cria quatro pilares de água que, em seguida, circulam em torno do usuário como uma barreira protetora.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Chicote de Água", "O usuário cria um chicote feito de água que pode envolver o oponente. O usuário também pode canalizar chakra relâmpago para dar choque em qualquer um envolto nele. Esta técnica parece ser conhecida pelos caçadores ninjas de Kirigakure.", "Curto a Médio alcance", null, "Rato → Cao → Boi", "水遁・水鞭すいとん・すいべん", "Suiton: Suiben", "Liberação de Água: Chicote de Água", null, null, "Estilo Água: Jutsu do Chicote de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Chicote do Dragão de Água", "Esta técnica cria uma esfera de água a partir da qual muitos chicotes afiados de água emergem, que podem ser direcionados para empalar um alvo ou alvos múltiplos. Os chicotes também podem mudar de direção em meados de trajetória, inclinando-se para procurar os alvos.", "Todos os alcances", null, "Tigre", "水遁·水龍鞭", "Suiton: Suiryūben", "Liberação de Água: Chicote do Dragão de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Chicote do Fluxo de Água", "O usuário molda o chakra em sua boca, colocando-o sobre a palma de sua mão em um fluxo de água. Em seguida, o fluxo de água se torna mais compresso e toma forma de um chicote, permitindo o usuário realizar vários golpes consecutivos no oponente.", "Curto alcance", null, null, "水遁・流水鞭すいとん・りゅうすいべん", "Suiton: Ryūsuiben", "Liberação de Água: Chicote do Fluxo de Água", "Estilo Água: Chicote de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Choque do Pilar de Água", "O usuário salta e crava sua espada no chão, fazendo com que a água exploda do solo para cima e em torno dele, criando um pilar de água ascendente que repele os inimigos.", "Curto alcance", null, null, "水遁・水震柱すいとん・すいしんしゅう", "Suiton: Suishinshū", "Liberação de Água: Choque do Pilar de Água", "Estilo Água: Choque do Pilar de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Chuva de Granizo", "O usuário molda seu chakra em incontáveis balas de água e as dispara em direção ao seu oponente.", "Todos os alcances", "C", "Carneiro", "水遁・雨あられ", "Suiton: Amearare", "Liberação de Água: Chuva de Granizo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Chuvisco", "O usuário condensa a umidade de água no ar e gera múltiplos projéteis de água que atira no oponente com muita força. Eles também podem atirar uma barragem pequenas de água diretamente do balanço do braço.", "Curto alcance", null, null, "水遁・時雨すいとん・しぐれ", "Suiton: Shigure", "Liberação de Água: Chuvisco", "Estilo Água: Chuveiro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Cinco Tubarões Famintos", "O usuário coloca a mão sobre uma superfície de água e, a partir da pontas de seus dedos, chakra é liberado para a água, dando-lhe forma de cinco tubarões ferozes. Os tubarões isolam sua vítima e criam um violento fluxo para impedir seus movimentos. Em seguida, eles a devoram com seus dentes extremamente afiados. Os tubarões têm capacidade de regeneração por meio da água.", "Longo alcance", "Rank B", null, "水遁・五食鮫すいとん・ごしょくざめ", "Suiton: Goshokuzame", "Liberação de Água: Cinco Tubarões Famintos", null, "Estilo da Água: Tubarões das Cinco Mordidas", "Estilo Água: Cinco Tubarões da Fome");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Colisão da Onda de Água", "Esta técnica permite ao usuário fazer um vórtice em espiral de água. O vórtice então prossegue para explodir a partir do topo, na forma de uma onda. O usuário também pode controlar a direção que a onda vai com movimentos da mão.", "Curto a Médio alcance", null, "Maos para Frente → Mãos Juntas ou (Confronto)[1]", "水遁・水衝波", "Suiton: Suishōha", "Liberação de Água: Colisão da Onda de Água", null, null, "Estilo Água: Onda Chocante de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Convergência da Água dos Céus", "Esta técnica permite ao usuário controlar a água e umidade em torno de si mesmo. Esta técnica pode ser usada para bloquear ataques, afogar adversários, e moldar a água em vários ataques, permitindo que o usuário use em conjunto com a Técnica de Invocação para que a invocação à base de água possa combater em áreas que normalmente precisam de água para se movimentar ou mesmo prender o alvo na mesma água ou manobrar na água para permitir a invocação a perseguir o alvo. Com isso, ainda é capaz de extrair e utilizar águas subterrâneas que é muito eficaz a maioria das áreas, mas inútil em áreas rochosas.", "Curto a Médio alcance", null, "Tigre → Cao → Macaco → Cobra → Javali → Tigre", "水遁・水天彷彿すいとん・すいてんほうふつ", "Suiton: Suiten Hōfutsu", "Liberação de Água: Convergência da Água dos Céus", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Corte Destrutivo do Tubarão Oculto", "Kisame cria uma lagoa com tubarões perto de seu oponente para capturá-lo. À considerar que o oponente se cansará, os tubarões o devorará dentro da lagoa.", "Curto alcance", null, null, "水遁・潜鮫削滅すいとん・せんこうさくめつ", "Suiton: Senkō Sakumetsu", "Liberação de Água: Corte Destrutivo do Tubarão Oculto", "Estilo Água: Ataque do Tubarão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Disparo da Boca", "Uma técnica onde o usuário cospe uma única gota em forma de senbon de água em alta velocidade, que é difícil de evitar. Quando a gota atinge qualquer coisa, ela estoura com força e fere o alvo. Embora a técnica não tem a força de comprometer gravemente alguém, ela pode ser usada para enfraquecer os inimigos enquanto eles estão distraídos.", "Curto alcance", "C", null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Escudo de Água", "O usuário lança uma bola de água que se transforma em um escudo de água que derruba o oponente quando encosta nele. Técnicas que são lançadas contra o escudo de água também são repelidas.", "Curto alcance", null, null, "水遁・水盾", "Suiton: Suijun", "Liberação de Água: Escudo de Água", "Estilo Água: Escudo de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Espigões de Água", "O usuário produz uma barragem de grandes espigões capazes de perfurar através de um alvo com facilidade. Esta técnica pode ser realizada de longe através de qualquer fonte de água existente, mesmo que seja névoa, para que assim o usuário o faça chover em seu inimigo ou emergir do chão.", "Curto a Médio alcance", null, "Tigre → Cao → Serpente", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Formação da Parede de Água", "Esta técnica defensiva cria uma parede de água ao redor do usuário. Ataques inimigos são completamente interceptados por uma cerca de água soprada para fora da boca, e é disparado para baixo com tremenda força. A água é soprada para fora na forma de um círculo em torno do usuário, e faz uma defesa, sem aberturas. É também possível para o utilizador controlar a quantidade de água e duração à vontade. A força da parede e mudança de resistência seguindo a quantidade de chakra derramado para dentro da água, assim um shinobi habilidoso será capaz de construir uma resistente. Além disso, já que o campo de visão é mantido mesmo enquanto a defesa ​​pode facilmente ir para o próximo passo, é uma grande vantagem. A parede também pode ser formada a partir de uma fonte de água pré-existente.", "Curto alcance", "Rank B", "Tigre → Cobra → Rato → Cobra → Tigre", "水遁・水陣壁すいとん・すいじんへき", "Suiton: Suijinheki", "Liberação de Água: Formação da Parede de Água", null, "Estilo da Água: Parede de Água", "Estilo Água: Parede de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Formação do Pilar de Água", "Uma poderosa técnica de defesa onde o chakra é concentrado dentro do corpo do usuário e é convertido em água, em seguida, expelido a partir da boca em uma camada densa, um torrente curvo, criando efetivamente uma barreira circular quase instantânea, que pode ser utilizada para interceptar ataques de entrada.", "Curto a Médio alcance", null, "Tigre[2]", "水遁・水陣柱", "Suiton: Suijinchū", "Liberação de Água: Formação do Pilar de Água", null, "Estilo da Água: Pilar Protetor de Água", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Golpe da Lâmina de Água", "Tobirama empunha uma kunai cobre ela com seu chakra de água e finaliza o oponente com um rápido e poderoso corte, reforçado com água.", "Curto alcance", null, null, "水遁・水刃撃", "Suiton: Suijingeki", "Liberação de Água: Golpe da Lâmina de Água", "Estilo Água: Lâmina de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Grande Broca-d'água", "Kisame remove as ataduras de Samehada, agora combinada com água, recebendo aparência similar a de uma broca. Em seguida, Samehada vai a encontro do oponente e perfura seu corpo.", "Curto alcance", null, null, "水遁・大水穿すいとん・だいすいせん", "Suiton: Daisuisen", "Liberação de Água: Grande Broca-d'água", "Estilo Água: Grande Broca-d'água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Grande Fluxo da Cachoeira", "Ao infundir água pré-existente com chakra, o usuário é capaz de criar um redemoinho de tamanhos diferenciados, a fim de afogar seu adversário.", "Médio a Longo alcance", null, "Javali → Cao → Passaro → Macaco → Carneiro", "水遁・大瀑流すいとん・だいばくりゅう", "Suiton: Daibakuryū", "Liberação de Água: Grande Fluxo da Cachoeira", null, null, "Estilo Água: Jutsu Corrente Furiosa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Grande Onda de Choque Explosiva de Água", "Esta técnica é uma versão em maior escala da Liberação de Água: Onda de Choque Explosiva de Água na qual o usuário expele uma quantidade de água ainda maior, a fim de cobrir uma área inteira com ondas inevitáveis. Então, a água toma forma de uma esfera gigante, ao invés de fluir livremente como na técnica original. A partir desta, Kisame pode utilizar outra técnica de água.", "Longo alcance", null, "Carneiro", "水遁・大爆水衝波すいとん・だいばくすいしょうは", "Suiton: Dai Bakusui Shōha", "Liberação de Água: Grande Onda de Choque Explosiva de Água", null, "Estilo da Água: Grande Onda de Impacto Explosiva", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Grande Projétil", "Após entrar em sua Forma Kaima, o usuário é capaz de atacar seu adversário com uma forte explosão de água corrente, a qual é cuspida de sua boca.", null, null, null, "水遁・大砲弾", "Suiton: Taihōdan", "Liberação de Água: Grande Projétil", null, null, "Estilo Água: Jutsu Bola Grande de Canhão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Grande Projétil de Massa de Água", "Isobu, ou Yagura enquanto em sua forma, lança bolas de água de tamanhos variados. As bolas de água também são poderosas o suficiente para destruir todo o lado de um penhasco.", "Todos os alcances", null, null, "荒海飛沫", "Suiton: Daikaisuidan", "Liberação de Água: Grande Projétil de Massa de Água", "Estilo Água: Grande Massa de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Grande Tiro de Espingarda", "A Liberação de Água: Grande Tiro de Espingarda é uma versão baseada e mais aprimorada da Liberação de Água: Tiro da Arma.", "Todos os alcances", null, null, "水遁・大鉄砲玉", "Suiton: Dai Teppōdama", "Liberação de Água: Grande Tiro de Espingarda", "Estilo Água: Projétil da Bala Gigante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Granizo-d'água", "O usuário estica seus braços e junta as mãos, mirando contra um alvo, e, em seguida, inúmeras balas de água são expelidas em alta velocidade, de modo a ser capaz de derrubar árvores grandes e resistentes.", "Curto a Médio alcance", null, "Cobra → Tigre → Boi → Tigre → Cachorro", "水遁・水あられすいとん・みずあられ", "Suiton: Mizu Arare", "Liberação de Água: Granizo-d'água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Lança da Água Corrente", "O usuário cria um afiado fluxo de água que segue os movimentos de seu braço. Os movimentos variam entre rodopios e ataques diretos, gerando a crescente lâmina de água que é usada para atacar o oponente.", "Curto alcance", null, null, "水遁・流水槍", "Suiton: Ryūsuisō", "Liberação de Água: Lança da Água Corrente", "Estilo Água: Corrente de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Lança de Gotículas", "Usando suas habilidades com a Liberação de Água, o usuário cria uma fonte de água, liberando múltiplos fluxos perfuradores, que podem ser usados para atacar seu oponente de várias direções ou e em diversos formatos.", "Curto alcance", null, null, null, null, "Liberação de Água: Lança de Gotículas", "Estilo Água: Lança de Gotículas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Lâmina do Redemoinho de Água Endurecida", "O usuário faz a água explodir sob o oponente, lançando-o no ar. Em seguida, o usuário salta por cima dele e cria um grande redemoinho em torno de seu braço, atingindo o oponente com ele criando uma grande explosão de água.", "Curto alcance", null, null, "水遁・硬渦水刃", "Suiton: Kōka Suijin", "Liberação de Água: Lâmina do Redemoinho de Água Endurecida", "Estilo Água: Broca de Água Endurecida", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Lâmina do Tubarão de Água", "Kisame envolve Samehada em água e, em seguida, cria um tubarão com o balançar de Samehada, que voa em direção ao oponente para explodir em contato.", "Todos os alcances", null, null, "水遁・水鮫刃すいとん・すいこうじん", "Suiton: Suikōjin", "Liberação de Água: Lâmina do Tubarão da Água", "Estilo Água: Lâmina do Tubarão de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Mil Tubarões de Alimentação", "Esta é uma versão muito maior da Liberação de Água: Cinco Tubarões Famintos na qual o usuário cria mil tubarões a partir de uma vasta fonte de água. Os tubarões caem como chuva para atacar o inimigo.", "Curto a Médio alcance", "Rank B", null, "水遁・千食鮫すいとん・せんしょくこう", "Suiton: Senshokukō", "Liberação de Água: Mil Tubarões de Alimentação", null, "Estilo da Água: Mil Tubarões Vorazes", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Mordida Explosiva do Dragão de Água", "Tobirama cria um largo e raso redemoinho de água no solo, lançando o oponente no ar e precipitando pequenas massas de água que atingem o inimigo. Assim que a água precipita completamente, ela forma uma nuvem no céu e rapidamente converte-se em uma massa de água, que libera um enorme dragão de água, o qual ataca o oponente brutalmente com sua boca, como uma mordida, esmagando-o contra o chão e causando um enorme impacto.", null, null, "Passaro → Cao → Selo especifico da técnica → Selo específico da técnica", "水遁・水龍咬爆", "Suiton: Suiryū Kōbaku", "Liberação de Água: Mordida Explosiva do Dragão de Água", "Estilo Água: Mordida Explosiva do Dragão de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Método de Ataque com Pilar de Água", "Depois de jogar o oponente no ar, Yagura cria quatro pilares de água e acerta-o através deles com muita força, ao ponto de criar uma explosão de água. Para lançar o oponente para o alto, Yagura pode enganchá-lo com seu bō.", "Longo alcance", null, null, "水遁・水柱法撃", "Suiton: Suichū Hōgeki", "Liberação de Água: Método de Ataque com Pilar de Água", "Estilo Água: Barragem do Pilar de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Onda Selvagem de Bolha", "O usuário cospe para fora uma grande quantidade de bolhas semelhante à espuma que cobre o solo e reduz a sua fricção, limitando consideravelmente os movimentos dos oponentes. As bolhas são cuspidas com força violenta que é capaz de retirar o efeito da maioria dos ataques, como o óleo e fogo. Esta técnica também pode ser usada como uma distração eficaz.", "Todos os alcances", "Rank C", null, "水遁・泡沫乱波すいとん・ほうまつらっぱ", "Suiton: Hōmatsu Rappa", "Liberação de Água: Onda Selvagem de Bolha", "Estilo água: Ondas Dispersas de Espuma", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Onda de Choque Explosiva de Água", "O usuário cospe um grande volume de água a partir de sua boca para formar uma grande onda abaixo de seus pés. Uma vez sobre a onda, o usuário pode se movimentar em alta velocidade para atacar o inimigo, também impulsionado pela pressão da onda. Em razão da quantia de água então existente, o local se torna propício à utilização de outras técnicas de natureza água.", "Curto a Longo alcance", "Rank B", null, "水遁・爆水衝波すいとん・ばくすいしょうは", "Suiton: Bakusui Shōha", "Liberação de Água: Onda de Choque Explosiva de Água", null, null, "Estilo Água: Onda de Choque de Água Explosiva");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Onda de Água Selvagem", "A Liberação de Água: Onda de Água Selvagem é uma técnica básica de Liberação de Água que possui muitas variações.", "Curto a Médio alcance", "Rank C", "Dragao → Tigre → Coelho", "水遁・水乱波", "Suiton: Mizurappa", "Liberação de Água: Onda de Água Selvagem", null, "Estilo da Água: Corneta de Água", "Estilo Água: Onda Furiosa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Onda do Espírito da Água", "O usuário aponta sua mão em direção ao alvo, mimetizando uma arma com seus dedos, gerando uma bola de água na ponta de seus dedos, podendo assim atirar em seu oponente. Juntando ambas as mãos e fazendo o gesto, é possível gerar uma bola de água muito maior, bem como dispará-las em rápida sucessão.", "Todos os alcances", null, null, "水遁・水霊波すいとん・すいれいは", "Suiton: Suireiha", "Liberação de Água: Onda do Espírito da Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Ondas de Águas Cortantes", "Depois de amassar chakra no estômago e convertê-lo em água, o usuário expele-o como um fluxo de alta pressão que é poderoso o suficiente para cortar de forma limpa através de várias árvores grossas com facilidade. Quando reencarnado pela primeira vez, Tobirama usou a técnica ao manipular a água ao seu redor, sendo capaz de disparar jatos de alta pressão a partir da água já presente no local, que foi poderosa o suficiente para destruir facilmente rochas.", "Todos os alcances", null, "Manga: Tigre,Anime: Rato → Passaro → Cobra → Juntar as maos", "水遁・水断波", "Suiton: Suidanha", "Liberação de Água: Ondas de Águas Cortantes", null, "Estilo da Água: Jato de Água Cortante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Palma de Onda de Água", "O usuário abre a palma da mão e dispara um jato d'água que envia seu oponente para trás.", "Curto alcance", null, null, "水遁・水掌波", "Suiton: Suishōha", "Liberação de Água: Palma de Onda de Água", "Estilo Água: Onda da Palma de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Palma do Dragão Índigo", "Ao corre na direção de seu oponente colocando as mãos para a frente quando ele entra em contato com o oponente, ele desencadeia um turbilhão em espiral feito de água.", "Curto alcance", null, null, "水遁・青龍掌すいとん・せいりゅうしょう", "Suiton: Seiryūshō", "Liberação de Água: Palma do Dragão Índigo", "Estilo Água: Palma do Dragão Índigo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Peixe Arqueiro", "Com esta técnica, Maroi pode criar um jato de água. Esta técnica também pode ser utilizada para extinguir fogo.", "Curto alcance", null, null, "水遁・歩愴魚すいとん・てっぽううお", "Suiton: Teppōuo", "Liberação de Água: Peixe Arqueiro", null, null, "Estilo Água: Tiro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Projétil da Massa de Água Esmagadora", "Yagura canaliza seu chakra água através do seu bō e cria uma pequena massa de água acima da cabeça de seu oponente. Com a massa controlada pelo bastão ele aumenta-a drasticamente e a faz cair sobre o oponente, afogando-o por algum tempo.", "Curto alcance", null, null, "水遁・塊水崩弾", "Suiton: Kaisui Hōdan", "Liberação de Água: Projétil da Massa de Água Esmagadora", "Estilo Água: Mortalha de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Projétil da Presa de Água", "Uma técnica que causa dano físico ao inimigo com uma massa líquida trazida por debaixo da água. Seu poder de matar e mutilar é aumentado pela adição de um movimento giratório para a água de forma compacta e com pressão. Durante as lutas na água, tirando partido da sua capacidade característica de atacar de qualquer lugar em um raio de 360 graus, em conjunto com outras técnicas de confusão e distração, e assim por diante, esta técnica tem uma ampla variedade de aplicações.", "Curto alcance", "Rank B", "Tigre[2]", "水遁・水牙弾", "Suiton: Suigadan", "Liberação de Água: Projétil da Presa de Água", "Estilo Água: Presa da Bomba de Água", "Estilo da Água: Presa de Água", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Revólver de Água", "Essa técnica é demonstrada como sendo feito tanto por conta própria ou como um konbijutsu bidirecional entre Gamariki e Jiraiya. Através dela, Gamariki pode explodir uma onda de água de alta velocidade da boca com extrema precisão e força. Quando combinado com o chakra de outra pessoa, ela torna-se uma técnica de água colaborada bidirecional que pode cortar o alvo em vários pedaços finamente esculpidos. Após ele aprender essa técnica, Gamatatsu é capaz de combiná-la com o chakra infundido de vento de Naruto para criar o Liberação de Vento: Revólver de Sapo.", "Curto a Médio alcance", null, "Carneiro → Passaro → Javali → Macaco → Carneiro", "水遁・水鉄砲", "Suiton: Mizudeppō", "Liberação de Água: Revólver de Água", "Estilo Água: Pistola de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Ruptura da Luz de Lótus", "O usuário cria gotas de água através de uma fonte externa, que são moldadas em afiadas pétalas de lótus. Já que as pétalas expandem em tamanho e giram ao redor, emitindo energia explosiva em todas as direções, eles são lançadas no oponente. Hisen observou que essa é a sua técnica mais poderosa.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Shuriken de Água", "O usuário cria uma grande quantidade de shuriken, feitas de água, e as lança em direção ao oponente sucessivamente de maneira rápida.", "Curto a Médio alcance", null, null, "水遁・水手裏剣", "Suiton: Mizu Shuriken", "Liberação de Água: Shuriken de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Subida de Água Cortante", "Esta técnica cria um jato de água corrente pressurizado a partir do chão, que é poderoso o suficiente para cortar através de rocha sólida. No entanto, isto pode facilmente ser evitado, saltando para cima. As lâminas de água também podem ser enviadas acima do nível do solo para atingir alvos no ar.", "Curto a Médio alcance", null, "Tigre", "水遁・水上切", "Suiton: Mizu Kamikiri", "Liberação de Água: Subida de Água Cortante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Torrente Dilacerante", "Esta técnica cria uma espiral de água na mão do usuário, que atira-a em alta velocidade contra o inimigo. O montante total de água formado é grande o suficiente para ajudar a extinguir mesmo técnicas de Liberação de Fogo em larga escala. No anime, ela também foi mostrada sendo capaz de ser conjurada a partir da área circundante na forma de uma onda torrencial. Ela pode também ser combinada com a Liberação de Vento: Rasengan para criar a Técnica do Vórtex do Tufão de Água.", "Médio alcance", null, null, "水遁・破奔流", "Suiton: Hahonryū", "Liberação de Água: Corredeiras Destruidoras", null, "Estilo da Água: Torrente Quebrante", "Estilo Água: Torrente Destrutiva");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Transformar-à-Vontade", "Suien tem uma substância líquida sólida, como uma gelatina, que ele faz fluir até o seus adversários para prender e cegá-los ao assumir a forma de um casulo, prendendo-os debaixo da água e sufocando-os no processo.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Trompete de Água", "O usuário lança um jato grande de água da sua boca através de sua mão. Isto dá a aparência de tocar um trompete, daí o nome.", "Médio alcance", null, "Dragao → Tigre → Coelho", "水遁・水喇叭", "Suiton: Mizurappa", "Liberação de Água: Trompete de Água", null, "Estilo da Água: Trombeta de Água", "Estilo Água: Trompete de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Tubarões Infinitos", "Kisame cria uma esfera de água que libera inúmeros tubarões de água que rapidamente atacam o oponente. Se a esfera for liberada subaquaticamente, a resistência dos tubarões é grandemente aumentada e todos eles colidem com o alvo resultando em uma enorme implosão.", "Longo alcance", null, null, "水遁・無限鮫", "Suiton: Mugensame", "Liberação de Água: Tubarões Infinitos", "Estilo Água: Tubarões Infinitos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Tubarão da Onda Pluvial", "Kisame bate a palma de sua mão sobre uma superfície de água, fazendo com que a água se fragmente. Logo, as gotículas tomam forma de inúmeros e pequenos tubarões, que, em seguida, nadam pelo ar ao encontro de seus inimigos. Se os tubarões forem atacados, novamente a água se fragmentará, originando outros de acordo com o número crescente de gotículas.", "Curto a Médio alcance", null, "Carneiro → Selo de Clone → Cao → Selo especifico da técnica → Rato → Carneiro → Selo de Clone → Cão → Selo específico da técnica → Rato → Carneiro", "水遁・雨水鲛波すいとん・あまみずこうは", "Suiton: Amamizukōha", "Liberação de Água: Tubarão da Onda Pluvial", null, null, "Estilo Água: Chuveiro de Quatro Tubarões");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Turbulência de Água", "O usuário atira um fluxo de água de sua boca. Esta técnica parece cansá-lo.", "Médio alcance", null, null, "水遁・水暴走", "Suiton: Mizubōsō", "Liberação de Água: Turbulência de Água", "Estilo Água: Turbulência de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica Envolvente do Grande Redemoinho", "Kisame utiliza uma fonte de água nas proximidades para criar um turbilhão de água gigante em torno de sua espada, Samehada. Kisame então utiliza o turbilhão como uma arma, saltando acima de seu oponente e usando sua espada para quebrar o turbilhão em seus alvos, causando danos enormes.", "Curto a Médio alcance", null, null, "水遁・大渦纏いの術", "Suiton: Ōuzu Matoi no Jutsu", "Liberação de Água: Técnica Envolvente do Grande Redemoinho", "Estilo Água: Grande Apego do Redemoinho", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Bacia da Cachoeira", "Esta é uma técnica que cria uma queda de água, através do desenvolvimento de uma mola de água local sem veios de água e de manipulação da água corrente resultante para formar uma onda. Esta é uma técnica muito conveniente uma vez que pode ser utilizada para a treinamento ou o relaxamento. Por outro lado, uma vez que altera o ambiente em larga escala, também tem o efeito de ocultar a própria localização do inimigo por confundi-los. A largura da fonte de água, em cascata, e bacia pode ser gasto, desde que o usuário continue a enviar chakra.", "Todas os alcances", "Rank C", "Javali → Cao → Carneiro", "水遁・滝壺の術", "Suiton: Takitsubo no Jutsu", "Liberação de Água: Técnica da Bacia da Cachoeira", null, "Estilo Água: Jutsu da Cachoeira", "Estilo Água: Jutsu da Cachoeira");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Chuva Negra", "Esta técnica cria uma névoa negra inflamável que forma uma pequena nuvem. O usuário pode então mover a nuvem acima do seu alvo e dispersá-lo, cobrindo o alvo em óleo inflamável. Esta técnica foi usada pela primeira vez para queimar o barco de Idate Morino, depois que foi aceso com fogo.", "Curto alcance", null, "Carneiro → Cobra → Tigre", "水遁・黒雨の術", "Suiton: Kokū no Jutsu", "Liberação de Água: Técnica da Chuva Negra", null, null, "Estilo Água: Jutsu da Chuva Negra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Esfera de Água do Tubarão Agitado", null, "Curto alcance", null, null, "水遁・水鮫騒玉の術", "Suiton: Suikōsōgyoku no Jutsu", "Liberação de Água: Técnica da Esfera de Água do Tubarão Agitado", "Estilo Água: Esfera do Tubarão Selvagem", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Forja de Água", "Ao reunir água ao redor da espada do usuário, é gerado um fluxo de rodagem rápida, podendo ser lançado em forte onda contra o alvo.", "Curto alcance", null, null, "水遁・水練の術すいとん・すいれんのじゅつ", "Suiton: Suiren no Jutsu", "Liberação de Água: Técnica da Forja de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Grande Cachoeira", "Esta técnica estende água sobre ondas em larga escala e sobe até várias dezenas de metros de altura. Em seguida, ele leva para o chão em uma cascata grande, como uma cachoeira gigantesca. Ao fazê-lo, ele se assemelha a uma onda enorme, com grande poder que pode escavar o solo. O que resta após a utilização dessa técnica é o que lembra nada menos do que a sequência de uma catástrofe natural. Este é um ninjutsu consideravelmente avançado, e ativar esta técnica exige uma boa quantidade de chakra.", "Médio a Longo alcance", "Rank A", "Tigre → Boi → Macaco → Coelho → Carneiro → Javali → Boi → Cavalo → Macaco → Tigre → Cao → Tigre → Cobra → Tigre → Boi → Macaco → Coelho → Passaro", "水遁・大瀑布の術すいとん・だいばくふのじゅつ", "Suiton: Daibakufu no Jutsu", "Liberação de Água: Técnica da Grande Cachoeira", null, "Estilo da Água: Técnica do Vórtex Gigante", "Estilo Água: Jutsu Vórtex Gigante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Lâmina de Água", "Essa é uma técnica de Liberação de Água criada por Rasa, o Quarto Kazekage.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica da Tranca de Água", "Tobirama reúne uma quantidade considerável de água em torno do oponente, que segura-o no lugar, dessa forma, prendendo-o pelos braços e puxando-o para baixo. Logo em seguida, com o inimigo vulnerável, o Hokage pode emendar outro ataque, como, por exemplo, saltar ao ar e atacar o oponente com uma broca de água extremamente poderosa.", "Curto alcance", null, null, "水遁・水錠の術すいとん・すいじょうのじゅつ", "Suiton: Suijō no Jutsu", "Liberação de Água: Técnica da Tranca de Água", "Estilo Água: Jutsu do Bloqueio de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica de Bolhas", "Depois de soprar dezenas de bolhas, o usuário manipula-as para cercar o inimigo em todas as direções. Apenas a menor quantidade de contato físico com o adversário obriga essas bolhas a estourarem, resultando em uma pequena, mas poderosa explosão quando o fazem. O usuário pode então fazer todas detonarem simultaneamente com um estalar de seus dedos.", "Todos os alcances", null, null, "水遁・泡沫の術", "Suiton: Hōmatsu no Jutsu", "Liberação de Água: Técnica de Bolhas", null, "Estilo da Água: Técnica das Bolhas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica de Bolhas — Grande Rio", "Saiken, ou Utakata transformado em sua besta, libera dois tiros de ácido corrosivo e termina com uma saraivada de bolhas explosivas na direção de seu oponente.", "Curto alcance", null, null, "水遁・泡沫の術　大河", "Suiton: Hōmatsu no Jutsu — Taiga", "Liberação de Água: Técnica de Bolhas — Grande Rio", "Estilo Água: Jutsu do Grande Rio de Bolhas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica de Ocultação na Chuva", "O usuário se mistura com a chuva sem ser detectado pelo o inimigo, semelhante à Liberação de Terra: Técnica de Ocultação nas Pedras. Quando usado por Tsurugi, ele pode invocar a chuva em torno dele.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica de Ocultação na Garoa", "O usuário gera uma garoa para ocultar a si mesmo e aos seus companheiros presentes em uma garoa. No entanto, a técnica é inútil em locais com pouca umidade no ar, uma vez que a impede de ter os efeitos naturais da técnica.", null, null, "Selo do Confronto", "水遁・霧雨隠れの術", "Suiton: Kirisamegakure no Jutsu", "Liberação de Água: Técnica de Ocultação na Garoa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica de Ocultação na Água", "O usuário se mistura na água, para não ser detectado pelo inimigo, semelhante à Liberação de Terra: Técnica de Ocultação nas Pedras. Foi mostrado que esta técnica é útil mesmo em água tão rasa quanto uma poça de água, como pode ser visto quando os Irmãos Demônios usaram para emboscar Tazuna e seus guarda-costas.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Espelho de Água", "O usuário cria uma grande forma circular de água, lisa e larga, na frente de seu oponente. Sua superfície age como um espelho, refletindo perfeitamente os alvos que atacam. Logo, então, ele engancha e gira a 90° o 'espelho' com o seu bō, fazendo com que os reflexos emerjam e se materializem a partir do espelho, onde colidem com os inimigos espelhados. Como estas reflexões usam exatamente as mesmas técnicas do inimigo — independentemente do seu tipo — eles contra-atacam seus respectivos alvos completamente, enquanto repelindo-los no processo. No entanto, no momento do impacto, os reflexos se dispersam novamente em água.", "Curto alcance", null, null, "水遁・水鏡の術", "Suiton: Mizukagami no Jutsu", "Liberação de Água: Técnica do Espelho de Água", null, "Estilo da Água: Técnica do Espelho d'Água", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Exército do Demônio de Água", "Esta técnica reúne uma grande quantidade de água para formar uma grande corrente espiral de água. Ele pode ser guiada para atingir um alvo em vários ângulos. Esta técnica foi utilizada por dois desconhecidos shinobi de Konoha para apagar um incêndio na Residência do Hokage.", "Curto à Médio Alcance", null, "Javali → Cao → Cavalo → Tigre", "水遁・水軍水魔の術すいとん・すいぐんすいまのじゅつ", "Suiton: Suigun Suima no Jutsu", "Liberação de Água: Técnica do Exército do Demônio de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Grande Braço de Água", "Ao colocar a Técnica de Hidratação em uso prático, os músculos do braço ficam temporariamente ampliados e reforçados. A umidade é recolhida a partir do corpo inteiro e prensada ​​em todo o braço, como uma bomba instantânea. No entanto, uma vez que é essencial para controlar adequadamente o equilíbrio de umidade no interior do corpo, o grau de dificuldade desta técnica é muito elevado. A técnica dá ao usuário força sobre-humana, capaz de romper os muros de pedra e portas de aço. No anime, Suigetsu pode aplicar esta técnica em sua perna.", null, "Rank B", null, "水遁・豪水腕の術すいとん・ごうすいわんのじゅつ", "Suiton: Gōsuiwan no Jutsu", "Liberação de Água: Grande Braço de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Grande Projétil de Tubarão", "A Liberação de Água: Técnica do Grande Projétil de Tubarão é uma versão mais forte da Liberação de Água: Técnica do Projétil do Tubarão de Água.", "Longo alcance", "Rank A", "Batida → Cobra → Selos Especificos", "水遁・大鮫弾の術すいとん・だいこうだんのじゅつ", "Suiton: Daikōdan no Jutsu", "Liberação de Água: Técnica do Grande Projétil de Tubarão", "Estilo Água! Jutsu da Super Bomba do Tubarão Aquático[2]", "Estilo da Água: Grande Tubarão Explosivo", "Estilo Água: Jutsu da Super Bomba Tubarão");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Projétil de Água", "Esta é uma técnica de Liberação de Água altamente versátil. Depois de amassar chakra no estômago, o utilizador expele uma grande quantidade de água sob a forma de uma corrente forte até o objetivo pretendido.", "Todos os alcances", null, "Tigre → Boi → Tigre → Rato", "水遁・水弾の術", "Suiton: Suidan no Jutsu", "Liberação de Água: Técnica do Projétil de Água", null, "Estilo da Água: Técnica do Jato D'Água", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Projétil do Dragão de Água", "A Liberação de Água: Técnica do Projétil do Dragão de Água molda uma grande quantidade de água em um gigante e poderoso dragão, que atinge o adversário com poder formidável, causando dano físico. O lugar ideal para usá-lo é perto de um corpo de água, mas se a habilidade do usuário permite, é possível usá-lo mesmo em um lugar onde não há nenhuma. A quantidade de água utilizada será em proporção com a habilidade do usuário.", "Todos os alcances", "Rank B", "Boi → Macaco → Lebre → Rato → Javali → Passaro → Boi → Cavalo → Pássaro → Rato → Tigre → Cao → Tigre → Cobra → Boi → Carneiro → Serpente → Javali → Carneiro → Rato → Yang Água → Macaco → Pássaro → Dragão → Pássaro → Boi → Cavalo → Carneiro → Tigre → Cobra → Rato → Macaco → Lebre → Javali → Dragão → Carneiro → Rato → Boi → Macaco → Pássaro → Yang Água → Rato → Javali → Pássaro", "水遁・水龍弾の術すいとん・すいりゅうだんのじゅつ", "Suiton: Suiryūdan no Jutsu", "Liberação de Água: Técnica do Projétil do Dragão de Água", "Estilo Água: Jutsu do Dragão da Água", "Estilo da Água: Ataque do Dragão Marinho", "Estilo Água: Jutsu Dragão de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Projétil do Tubarão de Água", "O usuário molda a água na forma de um grande tubarão e, empurrando a sua mão para a frente, envia-o para o oponente em alta velocidade, resultando em um impacto poderoso. O usuário pode também montar no tubarão dentro de água para aumentar drasticamente a sua velocidade de natação.", "Curto a Médio alcance", "Rank B", "Tigre → Boi → Dragao → Coelho → Cão → Passaro → Rato → Selo de Clonagem → Dragão → Carneiro", "水遁・水鮫弾の術", "Suiton: Suikōdan no Jutsu", "Liberação de Água: Técnica do Projétil do Tubarão de Água", "Estilo Água: Bomba do Tubarão", "Estilo da Água: Técnica da Bomba do Tubarão", "Estilo Água: Jutsu da Bomba do Tubarão de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Tiro do Tubarão de Água", "Kisame bate com sua mão no chão, exala água e liberta vários tubarões de água, que se movimentam rapidamente rente ao solo, um após o outro ou mesmo em conjunto. Quando chegam perto do inimigo, os tubarões voam ferozmente contra ele, causando dano.", "Todos os alcances", null, null, "水遁・水鮫散弾の術", "Suiton: Suikōsandan no Jutsu", "Liberação de Água: Técnica do Tiro do Tubarão de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica do Turbilhão do Dragão de Água", "Gengetsu distrai o oponente e lança um turbilhão de água e envia-o para o alto.", "Curto a Médio alcance", null, null, null, null, null, "Estilo Água: Jutsu do Turbilhão do Dragão de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Técnica dos Projéteis Dispersados", "O usuário envia uma grande rajada de balas de água em seu oponente, entregando vários golpes sequenciais.", "Curto a Médio alcance", null, null, "水遁・散弾の術", "Suiton: Sandan no Jutsu", "Liberação de Água: Técnica dos Projéteis Dispersados", "Estilo Água: Jutsu dos Tiros Dispersos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: 'Onda Selvagem'", "Depois de reunir e converter o chakra em seu estômago, o usuário ataca seu adversário com um jato d'água como uma grande onda. Ele da origem a uma considerável quantidade de água, suficiente para encharcar toda a área.", "Curto a Médio alcance", null, null, "水遁・波濤すいとん・はとう", "Suiton: Hatō", "Liberação de Água: 'Onda Selvagem'", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Água Cortante Contínua", "Depois de amassar chakra no estômago e convertê-lo em água, o usuário expele-o como um fluxo de alta pressão a fim de cortar o oponente mesmo a longas distâncias. Caso a pressão da água não seja o suficiente para cortar o alvo, ela espalha-se normalmente.", "Todos os alcances", null, "Selo de confronto", "水遁・水絶斬", "Suiton: Suizetsusan", "Liberação de Água: Água Cortante Contínua", "Estilo da Água: Corte de Água", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água da Marionete: Dança da Chuva Mística", "O usuário envolve o inimigo entre suas marionetes, que liberam fios de chakra de seu interior para restringir seus movimentos e, para, em seguida, expelir água contra o alvo. Esta técnica foi especialmente eficaz em Gaara, impedindo de manipular sua areia ora lama.", "Médio a Longo alcance", null, null, "傀儡水遁・妖雨の舞", "Kugutsu Suiton: Yōu no Mai", "Liberação de Água da Marionete: Dança da Chuva Mística", null, null, "Marionete do Estilo Água: Dança Mística");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação do Selo de Teletransporte de Quatro Cantos", "Minato faz os selos manuais requeridos da técnica e o selo é desfeito. Esta técnica foi citada como totalmente forte, uma vez que apenas Jiraiya e Minato poderiam realizá-la.", "Curto alcance", null, "Rato → Macaco → Passaro → Cavalo → Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ligamento da Língua Lutadora", "Depois de focar chakra do senjutsu nela, a língua de Shima cresce até o seu limite máximo e é reformulada com um rosto. A língua, então, busca o aroma de seu alvo. Quando o alvo é encontrado, ela enlaça-o para evitar a fuga e libera ácido no corpo do alvo. Depois que o inimigo é encontrado Fukasaku pode usar o Corte da Língua Lutadora nele.", "Todos os alcances", "Rank –", "Carneiro", "舌戦縛ぜっせんばく", "Zessenbaku", "Ligamento da Língua Lutadora", "Língua de Ligação", null, "Jutsu de Detecção de Criatura");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Limbo: Lado da Prisão", "Limbo: Lado da Prisão é uma técnica que usa o poder do Rinnegan.", "Todos os alcances", "Rank –", null, "輪墓・辺獄", "Rinbo: Hengoku", "Roda de Sepultura: Lado da Prisão", null, "Crepúsculo do Limbo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Linha Relâmpago", "Ao concentrar chakra relâmpago em seu chifre, o Kuchiyose Final criar rajadas de relâmpago e atira-as contra seus oponentes, lançando-os longe.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Locomoção Vulcânica", "O golem que Momoshiki Ōtsutsuki cria usando a Liberação de Lava se desfaz para o estado de rocha fundida e se locomove rapidamente pela superfície do solo, se reconstruindo em outro lugar. A técnica pode ser usada tanto para pegar o adversário desprevenido e lhe desferir um ataque surpresa, como também em forma de esquiva ou retirada.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lodo Ácido", "O usuário cospe uma pequena porção de um líquido roxo no alvo, que evapora em forma ácida, quando em contato com o alvo. A técnica é mostrada sendo poderosa o suficiente para incapacitar Kushina Uzumaki, embora ela tenha apenas sido atingida com uma porção pequena do líquido em sua perna.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lonicera Japônica", "Kimimaro cria um pico de osso no joelho e ataca o oponente, depois cria ossos em suas mãos e ataca-o novamente e rapidamente vai para baixo do oponente, criando picos de ossos em suas costas, empalando o oponente.", "Curto alcance", null, null, "スイカズラ", "Suikazura", "Lonicera Japônica", "Madressilva", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lorentz Gun", "O usuário conecta dois pontos distintos ao liberar uma corrente elétrica, criando um campo eletromagnético. Qualquer objeto metálico, como uma kunai, que adentrar o campo irá acelerar rapidamente de um ponto para outro, chegando a ultrapassar a velocidade do som. Isso torna o objeto incrivelmente destrutivo, uma vez que para chegar ao seu destino, uma kunai seria capaz de atravessar árvores em seu caminho e, por fim, destruir um penhasco.", "Longo alcance", null, null, "電磁誘導投擲", "Rōrentsugan", "Disparo por Indução Eletromagnética", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lua Obscura", "Kakashi invoca todos seus oito ninken para segurar o oponente, logo em seguida, ele entrega um corte mortal rapidamente com sua espada.", "Curto alcance", null, null, "朧月おぼろずき", "Oborozuki", null, "Lua Obscura", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lua Remanescente", "Obito inicia desferindo em seu oponente uma série de golpes com suas Esferas da Busca da Verdade, transforma-as em uma lâmina, desfere um rápido corte em seu inimigo e então ele transforma a lâmina em uma esfera, que se torna grande, prende o inimigo e depois explode.", "Curto a Médio alcance", null, null, "残月ざんげつ", "Zangetsu", "Lua Remanescente", "Visibilidade da Lua do Amanhecer", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Luar", "Sasuke ataca com sua espada, apunhala-a no chão, equilibra-se sobre ela e chuta o adversário longe. Seu chute é usado normalmente na parte superior do abdômen até a área do pescoço.", "Curto alcance", null, null, "月影つきかげ", "Tsukikage", "Luar", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lullaby Downtown", "Killer B fica de ponta-cabeça no ar e começa a rodar repetidamente para atacar o inimigo com os pés. Ele ainda pode utilizar uma de suas espadas para um dano mais mortal.", null, null, null, "ララバイダウンタウン", "Rarabaidauntaun", "Cantiga para Descansar em Paz", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Luz Rotatória da Folha", "Rock Lee começa com uma rasteira para lançar o oponente para o alto, gira chutando o oponente no rosto antes de terminar com um poderoso chute para baixo, que irá atirar o oponente contra o chão, emitindo uma feixe de luz pelo caminho.", "Curto alcance", null, null, "木ノ葉旋光", "Konoha Senkō", "Luz Rotatória da Folha", "Luz Espiral da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lótus Frontal", "Esta técnica requer a abertura do primeiro dos Oito Portões, dando ao utilizador cinco vezes a sua força normal. O utilizador lança o oponente para o ar e depois usa a Sombra da Folha Dançante para chegar até as costas do oponente. Uma vez atrás do oponente, o usuário o prende e leva-o girando em alta velocidade de cabeça para baixo em direção ao chão, soltando-o no último minuto. Porque ele usa um dos portões de chakra, o usuário fica extremamente cansado, tornando esta técnica uma 'faca de dois gumes'.", "Curto alcance", "Rank B", null, "表蓮華おもてれんげ", "Omote Renge", "Lótus Frontal", null, "Lótus Exterior", "Lótus Primária");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lótus Frontal Individual", "Uma versão singular de sua técnica parente, Lee em vez disso começa a girar por si mesmo e cria ventos fortes ao redor dele e mergulha para baixo sobre o adversário.", "Curto alcance", null, null, "一人表蓮華ひとりおもてれんげ", "Hitori Omote Renge", "Lótus Frontal Individual", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lótus Frontal: Oito Trigramas Palmas Giratórias do Céu", "Esta técnica combina defesa com uma força de ataque final, para efeitos devastadores. Em primeiro lugar, Neji pula no ar, enquanto Lee envolve-o em suas bandagens. Ao mesmo tempo em que Lee realiza a Lótus Primária, Neji libera chakra de todo o seu corpo. Esta técnica consiste na união das Oito Trigramas Palmas Giratórias do Céu de Neji, junto com a Lótus Frontal de Lee. Os resultados são devastadores.", "Curto alcance", null, null, "表莲华・八卦掌回天おもてれんげ・はっけしょうかいてん", "Omote Renge: Hakkeshō Kaiten", "Lótus Frontal: Oito Trigramas Palmas Giratórias do Céu", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lótus Oculta: Em Direção ao Sol Poente", "Após entrar no estado de Oito Portões, Lee corre em direção ao seu oponente, e em seguida ele salta e libera uma aura amarela fazendo o chão trincar no processo, e em seguida ele agarra seu oponente, e colide em várias formações rochosas, sua velocidade é tão grande que distorce o espaço ao seu redor, e em seguida ele arrasta o corpo de seu oponente vários metros do chão, com seu oponente inconsciente, Lee fica parado e observa o sol se pondo.", "Curto alcance", null, null, null, null, null, "Lótus Oculta: Em Direção ao Sol Poente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lótus Reversa", "Esta técnica é uma versão mais avançada e destrutiva da Lótus Frontal, que requer pelo menos três dos Oito Portões abertos (em todos os casos em que têm sido utilizado, cinco portões são abertos). Uma vez que os portões estão abertos, o usuário joga o alvo no ar e o ataca a uma velocidade sobre-humana antes de batê-lo com um braço e um pé, mandando o alvo direto para o chão. A técnica é geralmente fatal devido à velocidade e ao poder por trás do ataque.", "Curto a Médio alcance", "Rank A", null, "裏蓮華うられんげ", "Ura Renge", "Lótus Reversa", null, null, "Lótus Oculta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina Curva: Espada da Mão Celestial", "Sasuke infunde sua espada com um relâmpago de cor púrpura, aumentando em várias vezes seu poder ofensivo. Com isso ele é capaz de eletrocutar seus oponentes e deixá-los inconscientes, bem como derrotar vários dos dragões de sangue de Chino em rápida sucessão.", "Curto alcance", null, null, "瞬刃・天手刀しゅんじん・てんてがたな", "Shunjin: Tentegatana", "Lâmina Curva: Espada da Mão Celestial", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina Fluida", "Shizune dá um chute de fiação varredura no adversário e, antes de girar de volta para sua posição original, ela corta o adversário com um Bisturi de Chakra.", "Curto alcance", null, null, "流刃", "Ryūjin", "Lâmina Fluida", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina Shuriken Imparável", "O usuário lança a Kubikiribōchō no oponente como uma shuriken, causando grandes danos.", "Longo alcance", null, null, null, null, null, "Lâmina Shuriken Imparável", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina da Chama Uchiha: Possessão de Fogo", "Izuna dispara uma série de bolas de fogo contra o oponente. Em seguida, ele corta o inimigo com sua espada.", "Curto alcance", null, "Tigre", "うちは炎刃・火憑うちはえんじん・ひひょう", "Uchiha Enjin: Hihyō", "Lâmina da Chama Uchiha: Possessão de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina da Luz Branca", "Usando o Sabre de Chakra da Luz Branca, o usuário desfere um poderoso corte diagonal descendente, que deixa um rastro de chakra branco.", "Curto alcance", null, null, "白光刃はっこうじん", "Hakkōjin", "Lâmina da Luz Branca", "Espada da Luz Branca", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina de Areia", "O usuário cria uma grande lâmina de areia, que pode ser atirada ou empunhada a fim de romper as defesas do oponente.", "Curto alcance", null, null, "砂の刃", "Suna no Ha", "Lâmina de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina de Chakra: Linha Reta", "Após infundir chakra em sua faca trincheira e aplicar a transformação da forma para alongar seu alcance, Asuma desfere um corte no pescoço do oponente para decapitá-lo.", "Curto alcance", null, null, "チャクラ刀・真一文字", "Chakura Tō: Maichimonji", "Lâmina de Chakra: Linha Reta", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lâmina de Vento", "Esta técnica é um ataque cortante preciso, onde o usuário emite chakra das pontas de seus dedos e o materializa em uma arma invisível que ataca o inimigo em uma rajada de vento. Se a Técnica da Foice da Doninha pode ser considerada como um enxame de kunai de vento, então esta técnica é semelhante a uma espada longa e inevitável, a qual pode deixar o corpo do alvo mutilado. As lâminas de vento podem ser criadas e lançadas, como projéteis, em rápida sucessão.", "Curto a Médio alcance", "Rank A", null, "風の刃", "Kaze no Yaiba", "Lâmina de Vento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lírio da Aranha Negra", "Depois de prender o inimigo com a Técnica da Costura pelas Sombras, o usuário manipula as sombras para fazer com que os inimigos sejam puxados até um ponto determinado. Esta técnica pode ser usada para botar os inimigos dentro da faixa de alcance da Técnica da Performance de Dança da Mente de Inoichi.", "Todos os alcances", null, "Rato", "黒彼岸花", "Kuro Higanbana", "Lírio da Aranha Negra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lírio de Tigre", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente repetidamente com ataques de sua espada óssea, executa um ataque de sua cauda e depois finaliza seu oponente com um poderoso remate de suas espinhas dorsais.", "Curto alcance", null, null, "鬼百合", "Oniyuri", "Lírio de Tigre", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lírio de Tigre (Ultimate Ninja 3)", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, ataca o oponente repetidamente com ataques de sua espada óssea, executa um ataque de sua cauda e depois finaliza seu oponente com um poderoso remate de suas espinhas dorsais.", "Curto alcance", null, null, "鬼百合", "Oniyuri", "Lírio de Tigre", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lírio de Tigre (Ultimate Ninja Storm)", "Kimimaro, quando em sua forma de selo amaldiçoado nível dois, cria picos ósseos que saem de seus punhos e joga o oponente para cima, chuta o oponente para cima, e finaliza com um ataque de sua broca óssea.", "Curto alcance", null, null, "鬼百合", "Oniyuri", "Lírio de Tigre", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Lápis de Liberação de Relâmpago", "Através de um controle de chakra refinado, o usuário flui chakra de Liberação de Relâmpago em um lápis, aumentando sua agudeza ao ponto dele ser capaz de perfurar em linha reta, um grande tronco de árvore e até mesmo através do solo.", "Curto a Médio alcance", null, null, "雷遁えんぴつらいとんえんぴつ", "Raiton Enpitsu", "Lápis de Liberação de Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Machado", "Usando sua transformação, Jūgo pode formar um machado enorme em seu braço. O machado é forte o suficiente para bloquear espadas reais sem danos.", "Curto alcance", null, null, "鉞", "Masakari", "Machado", "Machado de Batalha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Machado do Julgamento", "Kinshiki materializa um machado de chakra e desfere um corte transversal diagonal em seu oponente, então ele salta e o finaliza com um corte descendente com força o suficiente para demolir o terreno próximo.", "Curto alcance", null, null, null, null, null, "Machado do Julgamento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Malabarista Letal", "O Malabarista Letal é uma técnica de ambidestro utilizada por Isono em uma competição de comida.", "Curto alcance", null, null, "致死ジャグラー", "Chishi Jagurā", "Malabarista Letal", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Maldição Mandala", "Depois de realizar vários selos de mão, o usuário é capaz de realizar uma antiga arte ninja que só é conhecida por algumas gerações do clã Fūma. Arashi era capaz de realizar esta técnica depois de absorver os corpos de Jigumo e Kamikiri. A técnica prende a vítima em uma gaiola de chakra em forma de pirâmide que vai lentamente se contraindo o quanto o usuário mover as mãos para ficarem mais próximas. Qualquer um pego na técnica, é contraído até ser totalmente esmagado.", null, null, "Ativar: Javali → Cao → Rato → Rato → Cobra → Passaro → Cão → Cão → Macaco → Dragão → Javali → Javali → Rato → Javali → Dragão,Disparar: Juntar as Maos,Comprimir: Selo Desconhecido[Nota 1]", "呪縛曼荼羅じゅばくまんだら", "Jubaku Mandara", "Maldição Mandala", null, null, "Arte Ninja Fuuma: Feitiço da Mandala");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Maldição da Marionete", "Hiruko usa essa técnica para assumir o controle de pessoas. Ela pode ser ativada muito tempo após a técnica ser aplicada pela primeira vez. A aparência é um olho vermelho com um outro círculo nele que se assemelha a pupila, com quatro linhas derivadas a partir da linha de cima do olho e duas grandes linhas grossas decorrentes a partir do lado do olho. Este poder parece controlar as mentes de outros como ele apareceu na testa de Kakashi, desapareceu e, em seguida, assumiu o controle sobre seu corpo. Os olhos da pessoa que for pego por esta técnica ficam vermelhos enquanto eles estão sendo controlados.", null, null, null, "傀儡の呪いくぐつののろい", "Kugutsu no Noroi", "Maldição da Marionete", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manipulação da Força de Mil Mãos", "A partir do braço esquerdo da marionete, abra-se um compartimento que libera outros. Estes compartimentos são incorporados com inúmeros selos de invocação, suscitando um grande número de braços de marionetes longos que pode dobrar e acompanhar o adversário, em seguida, para esmagá-lo com sua força devastadora. Essa técnica pode ser impulsionada, tornando-se grande o suficiente para romper o solo ou desintegrar o corpo duma pessoa. Os braços são modificados com lanças, os quais também podem emitir gases venenosos; os fios são ligados com uma kunai-âncora ou regulares. Sasori pôde substituir o braço por uma serra circular.", "Curto a Médio alcance", "Rank C", null, "千手操武せんじゅそうぶ", "Senju Sōbu", "Manipulação da Força de Mil Mãos", null, null, "Manipulação dos Mil Braços");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manipulação da Força de Mil Mãos: Impulso", "Sasori ataca seu oponente usando sua marionete do Terceiro Kazekage, fazendo com que múltiplos braços entrem em erupção a partir dos selos em seu braço esquerdo, dizimando o oponente no meio do ar.", "Curto a Médio alcance", null, null, "千手操武・突", "Senju Sōbu: Totsu", "Manipulação da Força de Mil Mãos: Impulso", "Mil Mãos: Impulso", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manipulação de Cartas", "Uma técnica que emprega o uso de cartas que podem ser manipuladas de acordo com a vontade do usuário. Usando isso, o usuário pode cortar objetos e criar barreiras que são capazes de suportar shuriken.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manipulação do Moinho de Vento de Lâminas Triplas", "O usuário anexa primeiro as cordas previamente na shuriken e na kunai jogadas lançando depois disso, em seguida, uma shuriken é puxada para trás usando uma árvore como um eixo, com base no princípio do Ioiô. A outra shuriken e kunai são iscas, e o verdadeiro ataque da shuriken que é puxado para trás usando a força da kunai lançada. O truque para tornar esta técnica bem sucedida é de alguma forma ser capaz de ler os movimentos do inimigo, que normalmente exigiria o Sharingan.", "Médio alcance", "Rank C", null, "操風車三ノ大刀", "Sōfūshasan no Tachi", "Manipulação do Moinho de Vento de Lâminas Triplas", null, null, "Ataque Triplo Sharingan Moinho de Vento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manipulação do Tempo", "Dependendo de sua emoção, Yota pode criar chuva, neve ou raios, mesmo que seja um dia ensolarado. Quando Yota fica triste começa a chover, quando Yota fica feliz o sol brilha ou começa a nevar, e quando Yota fica com raiva, ele é capaz de convocar um raio roxo.", "Todos os alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Manto da Raposa Demônio", "Em sua forma de quatro caudas, Naruto enterra sua mão no chão criando uma réplica de seu braço que emerge da terra e, em seguida, agarra e bate o inimigo no chão. Naruto então dispara uma rajada pequena, mas violenta, que se expande rapidamente no impacto, indo muito além do inimigo, deixando uma enorme cratera no chão.", "Curto a Médio alcance", null, null, "妖狐の衣", "Yōko no Koromo", "Manto da Raposa Demônio", "Manto da Nove-Caudas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marcação Dinâmica", "Nesta técnica, Akamaru urina no alvo, marcando-o com um forte odor e permite que ele e Kiba o sigam facilmente pelo cheiro. Akamaru é muito hábil nisso, sendo capaz de atingir alvos com muita precisão, e até mesmo vários alvos de uma vez. Este é um pré-requisito para usar a Presa Lobo Presa, com a velocidade da técnica, se torna impossível para o usuário rastrear alvos. A urina pode também ser útil para temporariamente cegar o alvo se atingir os seus olhos. Essa técnica também pode ser feita enquanto Kiba e Akamaru se transformam usando o Transformação Combinada da Besta Humana: Lobo de Duas Cabeças.", "Curto a Médio alcance", null, null, "ダイナミックマーキング", "Dainamikku Mākingu", "Marcação Dinâmica", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marcação de Areia de Ferro", "Shinki usa sua areia de ferro para marcar seu inimigo. Quando este estiver marcado, todos os ataques posteriores serão atraídos para o ponto definido por Shinki, eliminando a possibilidade de falha.", null, null, null, "砂鉄のマーキングさてつのマーキング", "Satetsu no Mākingu", "Marcação de Areia de Ferro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete Armada", "Kankurō manipula a marionete Corvo e faz com que sua cabeça abra e revele uma afiada lâmina-agulha. Com esse membro, ele pode afastá-lo para perfurar um alvo específico, bem como ele pode banhar a lâmina em veneno, tornando o golpe mais nocivo ao sistema imunológico de seu alvo.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete Preparada: Oito Ondas de Agulhas", "Sasori, enquanto dentro de sua marionete Hiruko, faz o boneco cuspir um fluxo rápido de inúmeras agulhas de sua boca, numa forma semelhante a uma metralhadora. Ele também pode incluir agulhas banhadas em veneno em meio às normais.", "Todos os alcances", "Rank S", null, "仕込み傀儡・針々八波", "Shikomi Kugutsu: Shinshin Happa", "Fantoche Preparado: Oito Ondas de Agulhas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete Shinobi dos Quatro Símbolos", "Os membros da Akatsuki recebem um selo especial em forma de humano criado por Nagato que tem uma forma física conhecida como uma marionete, para lutar ou parar um inimigo. Essa marionete vai para um local distante para criar numerosas cópias de si mesmo. A única maneira de parar a técnica é interceptando novas criações dessas marionetes.", null, null, null, "四象偶忍", "Shishō Gūnin", "Marionete Shinobi dos Quatro Símbolos", "Shinobi Vazio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Ataque Rotativo", "Controle de Chakra de Ryūmyaku", null, null, null, "傀儡・回旋撃", "Kugutsu: Kaisengeki", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Ataque de Punição da Primavera", "Usando sua Performance da Marionete: Conquista Habilidosa com um Corpo Humano, Chiyo lança Sakura para o alto. Sakura então usa sua imensa força para atacar o adversário por cima. Esta técnica foi poderosa o suficiente para destruir a defesa final de Sasori, a marionete Hiruko.", "Curto alcance", null, null, "傀儡·春誅撃くぐつ·はるちゅうげき", "Kugutsu: Haruchūgeki", "Marionete: Ataque de Punição da Primavera", "Marionete: Punição do Ataque de Primavera", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Chuva de Verão", "Kankurō utiliza uma de suas marionetes para percorrer o campo de batalha e liberar bombas, que explodem depois de algum tempo ou em contato com o oponente. A fim de aprimorar o alcance e poder destrutivo da técnica, além do Corvo, Kankurō também envia o Formiga Negra para plantar mais bombas.", null, null, null, "傀儡・五月雨", "Kugutsu: Samidare", "Marionete: Chuva de Verão", "Marionete: Chuva de Maio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Dança da Flor de Cerejeira", "Chiyo usa seus Fios de Chakra para controlar Sakura e várias dezenas de Kunai ao mesmo tempo. Sakura corre até o oponente e Chiyo lança as kunai atrás dela, usando Sakura para fazer com que o oponente não veja as kunai. Ao mesmo segundo, Sakura é puxada para trás com o fio, fazendo com que o oponente seja atacado pelas kunai. Então, Sakura pula bem alto e desce preparando o Impacto da Flor de Cerejeira, com Chiyo impulsionando-a com os fios, para Sakura descer mais rápido. Finalmente, Sakura soca o oponente criando uma cratera gigante.", "Curto a Médio alcance", null, null, "傀儡・桜舞", "Kugutsu: Sakura no Mai", "Marionete: Dança da Flor de Cerejeira", "Marionete: Dança da Cerejeira de Flor", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Degeneração", "Kankurō envia rapidamente sua marionete Corvo, que corre em direção ao oponente. A marionete usa suas seis mãos, e em seguida, ataca e esfaqueia o oponente, jogando-o no chão extremamente ferido. A marionete Corvo então, continua seus ataques e bate no oponente, enquanto ainda caído no chão pelo esfaqueamento anterior.", "Curto alcance", null, null, "傀儡・堕羅繰", "Kugutsu: Daraku", "Marionete: Degeneração", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Inesgotáveis Lâminas Explosivas", "Com os quatro braços de Corvo abertos é disparado uma rajada de kunai com papéis explosivos anexados à elas, que caem sobre o adversário e explodem com o impacto.", "Curto a Médio alcance", null, null, "傀儡・無尽爆刃", "Kugutsu: Mujin Bakujin", "Marionete: Inesgotáveis Lâminas Explosivas", "Lâmina de Explosões Inesgotáveis", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Lâmina da Broca Celestial Giratória", "Kankurō faz Karasu brotar vários venenos cobertos com lâminas nos braços e na boca e envia-a girando em direção ao oponente como um tornado, cortando-os várias vezes. Depois de lançar o oponente para o ar, Karasu se explode perto deles. Karasu entretanto sai ileso da explosão.", "Curto alcance", null, null, "傀儡・廻刃天穿", "Kugutsu: Kaijin Tensen", "Marionete: Lâmina da Broca Celestial Giratória", "Girando a Lâmina da Broca do Submundo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionete: Vomitando Balas", "Kankurō desanexa a cabeça de sua marionete, Corvo, assim como o adversário está se aproximando. Sua cabeça, em seguida, projeta um aumento de sua boca, apunhalando o adversário.", "Curto alcance", null, null, "傀儡・噴弾", "Kugutsu: Fundan", "Marionete: Vomitando Balas", "Marionete: Vomitando Tiros", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marionetes Acidentadas", "Desenvolvido por Orochimaru, permite ao usuário absorver parcialmente um cadáver morto recentemente, transformando-o em uma extensão do corpo do usuário. Apesar da absorção permitir ter acesso a técnicas do alvo, o uso das Marionetes Acidentadas pode deformar a mente do usuário e o corpo, fazendo com que o usuário fique altamente instável, com apenas um grande choque o fazendo retomar seus sentidos.", null, null, null, "死者傀儡", "Shisha Kugutsu", "Marionetes Acidentadas", null, null, "Arte Ninja: Bonecos de Cadáveres");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Marretada", "Ao bater sua Kabutowari no chão, Jinin Akebino faz com que a terra entre em erupção debaixo dos pés de seus oponentes. Essa técnica é efetiva para derrubar múltiplos oponentes.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Martelo Sagrado", "Kinshiki desfere vários socos fortalecidos com chakra em seu oponente, então ele manifesta um martelo de chakra e lhe dá uma martelada, atirando-o contra o chão.", "Curto alcance", null, null, null, null, null, "Martelo Sagrado", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Martelo da Super Besta Voadora", "Yamato ataca o oponente com um pilar de madeira, jogando-o para o alvo, onde os falcões de tinta de Sai atacam o oponente e Yamato bate o oponente ao chão com outro pilar de madeira.", null, null, null, "超獣飛翔槌", "Chōjū Hishō Tsui", "Martelo da Super Besta Voadora", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Massa de Água Viscosa", "Nurari extrai o líquido estranho de seu traje especial e forma-o em uma bola oval. Em seguida, ele joga em seu oponente para prendê-lo, neutralizá-lo, ou para fixá-lo para baixo. Ele também pode extraí-lo para formar uma membrana para parar os ataques de projéteis de entrada.", "Curto a Médio alcance", null, null, "粘水塊ねんすいかい", "Nensuikai", "Massa de Água Viscosa", null, null, "Água Pegajosa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Matança Silenciosa", "A Matança Silenciosa, como o nome sugere, é simplesmente um método muito tranquilo de matar um oponente, geralmente por trás com uma lâmina. Além disso, uma vez que o usuário se desloca em silêncio, seus ataques não podem ser defendidos, já que não podem ser previstos. O usuário, muitas vezes, corta a garganta do oponente, impedindo qualquer grito que ele passa dar.", null, null, null, "無音殺人術", "Sairento Kiringu", "Técnica de Assassinato sem Som", null, "Técnica do Assassinato Silencioso", "Assassinato Silencioso");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Medicina Negra", "Esta técnica requer o chakra das serpentes de Yomi para ser realizada. As cobras manipulam alguns equipamentos cirúrgicos do usuário, e executam algum tipo de procedimento cirúrgico que mata a vítima sem deixar marcas.", "Curto alcance", null, null, "暗黒医療", "Ankoku Ijutsu", "Medicina Negra", null, null, "Jutsu de Medicina Negra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Melodia Estridente", "Após se separar de Sakon, Ukon surge em alta velocidade atrás do oponente, executando um rápido e brutal golpe com as duas mãos, que lança o oponente no chão com grande força.", "Curto alcance", null, null, null, null, null, "Melodia Estridente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mente Ampla da Folha", "Guy chuta seu oponente duas vezes — um por cima e outro por baixo, antes de tacá-lo no chão.", "Curto alcance", null, null, "木ノ葉豪放磊落", "Konoha Gōhōrairaku", "Mente Ampla da Folha de Árvore", "Mente Ampla da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mergulho", "Jūgo salta sobre o seu oponente compulsivamente e joga-o no chão, em seguida, com seu braço direito ele golpeia fatalmente seu oponente.", "Curto alcance", null, null, "急降下", "Kyūkōka", "Mergulho", "Voo Picado", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mergulho Gordinho", "Depois de realizar a Técnica do Super Tamanho Múltiplo, Chōji gigante salta no ar e cai sobre o oponente.", "Curto alcance", null, null, "ポッチャリダイブ", "Potchari Daibu", "Mergulho Gordinho", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Meteoro do Instrumento Precioso Mortal", "O Meteoro do Instrumento Precioso Mortal é uma habilidade de Bukijutsu especial de Matsuri com suas maestrias usando o Jōhyō.", "Curto alcance", null, null, "必殺宝具流星", "Hissatsu Hōguryūsei", "Meteoro do Instrumento Precioso Mortal", null, null, "Precioso Instrumento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mil Agulhas Voadoras de Água da Morte", "Usando esta técnica, Haku reúne um pouco de água e produz mil agulhas afiadas e longas. Em seguida, ele as direciona para um alvo específico e em sequência, lança-as em alta velocidade em seu alvo. Antes que possa ser atacado, Haku salta para trás. Apesar das agulhas cercarem o alvo de todos os lados, elas não aparecem na região superior do alvo, disponibilizando uma rota de fuga para o alvo. Haku é capaz de realizar este jutsu com selos de uma mão, permitindo-lhe fixar um braço contra um ataque do oponente, impedindo-o de realizar técnicas no momento de realização.", "Curto alcance", "Rank B", "Selo do Confronto → Fogo → Selo Desconhecido → Selo Desconhecido → Selo Desconhecido → Selo Desconhecido → Selo Desconhecido → Terra → Selo do Confronto", "千殺水翔せんさつすいしょう", "Sensatsu Suishō", "Mil Agulhas Voadoras de Água da Morte", "Mil Agulhas da Morte", null, "Jutsu Secreto: Mil Agulhas Mortais");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mil Anos de Morte", "Uma técnica muito simples, com um nome excessivamente dramático, Mil Anos de Morte, no qual o usuário simplesmente insere os dedos médios (semelhante ao selo de mão do Tigre) no ânus do adversário, causando constipação, dor e/ou constrangimento. Ele não tem nenhum efeito sobre alvos inconscientes. Testemunhas que vêem a técnica reagem estranhamente (Temari fica corada, Pakkun se pergunta se é realmente uma técnica e Gamakichi mostra um olhar de decepção), enquanto aqueles que usaram ou experimentaram a técnica mostram um grande temor em serem atacados com ela.", "Curto alcance", "Rank E", "Tigre", "千年殺し", "Sennen Goroshi", "Mil Anos de Morte", null, null, "Jutsu do Dedo Secreto da Aldeia da Folha: Mil Anos de Morte");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Milhares de Agulhas da Morte", "Kin controla uma vasta quantidade de agulhas de uma só vez utilizando fios, e ataca o inimigo.", "Médio alcance", null, null, null, null, null, "Milhares de Agulhas da Morte", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Minas Terrestres Explosivas", "Depois de criar o dragão C2, Deidara cria muitas minas de argila que o dragão de argila desova através de sua boca e seu parceiro enterra-as no subsolo, permitindo assim que vítimas inocentes andem sobre as minas que explodem em seguida. Estas minas explodem automaticamente ao serem pisadas. Como tal, elas não estão sob o controle de Deidara.", null, null, "Carneiro", "起爆地雷きばくじらい", "Kibaku Jirai", "Minas Explosivas", "Mina Detonadora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mini-Rasenshuriken", "Naruto, enquanto está no Modo de Chakra da Nove Caudas, cria dois pequenos braços de chakra em seu dedo indicador, com os quais ele cria uma miniatura do Rasenshuriken. Embora a shuriken tenha menos poder de ataque do que a original, ainda é muito eficaz, sendo capaz de cortar uma perna dos clones fundidos do Exército de Zetsu Branco com relativa facilidade.", "Todos os alcances", null, null, "ミニ螺旋手裏剣", "Mini-Rasenshuriken", "Mini Shuriken Espiral", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mini Clone do Três-Caudas", "Quando o Três-Caudas devora qualquer coisa, seu corpo produz milhares de clones menores de si mesmo com seu intestino. Esses clones de anticorpos, como atacam e devoram o organismo invasor, supostamente ajudam na digestão e destroem quaisquer perigos potenciais.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mini Rasengan", "O usuário cria uma miniatura do Rasengan após ter perdido muito chakra. O efeito da técnica é desconhecido, pois após utilizá-la Naruto entrou em colapso.", "Curto alcance", null, null, "ちび玉螺旋丸ちびたまらせんがん", "Chibitama Rasengan", "Mini Rasengan", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modificação Física Suave", "Originalmente desenvolvida para atividades de espionagem, esta técnica pode ser feita após uma cirurgia e modificação do corpo real. O usuário se desloca suas articulações, em seguida, controla o seu corpo amolecido com chakra, permitindo-lhes se esticar e torcer qualquer parte de seu corpo em qualquer ângulo, alongando e até mesmo enrolando em torno de seu inimigo. Isto também pode ser utilizado para fazer o corpo do utilizador mais maleável e como de uma cobra. A versão de Orochimaru lhe permite esticar qualquer das partes de seu corpo bem além de seu comprimento normal e lhe permite mover-se na forma de uma cobra.", null, "Rank –", null, "軟の改造", "Nan no Kaizō", "Modificação Física Suave", null, null, null);
