INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo Manto de Vento", "Temari usa a Liberação de Vento para criar uma corrente de ar em volta de si, que é capaz de repelir projéteis e aumenta seus ataques relacionado a vento.", null, null, null, "風の衣モードかぜのころもモード", "Kaze no Koromo Mōdo", "Modo Manto de Vento", "Modo Dança do Leque Celestial[1]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo Médico", "Este modo envolve o usuário em um fluxo de chakra branco e lhes permite curar-se constantemente no meio da batalha, ao mesmo tempo, aumentando a sua força e velocidade. Com o tempo, este modo consome o chakra do usuário até que finalmente desaparece por si só, apesar do usuário ou, mais especificamente, Sakura e Ino, poderem cancelá-lo sempre que desejar. Shizune e Karin, no entanto, não podem.", "Curto a Longo alcance", null, null, "医療モード", "Iryō Mōdo", "Modo Médico", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo Sannin", "Este modo aumenta a todas as estatísticas do usuário. Também aumenta o alcance, poder e velocidade dos ataques do usuário. Enquanto estiver neste modo, o usuário ganha acesso ao seu Jutsu Final de Despertar.", null, null, null, "三忍モード", "Sannin Modo", "Modo Ninja Triplo", "Modo Sannin", null, null);
