INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo Sábio", "O Modo Sábio é um estado que pode ser ativado pela mistura de energia natural com o chakra, criando o chakra do senjutsu. Ele permite aos usuários explorar a força natural do mundo, concedendo novas técnicas para eles e permitindo-lhes poder aplicar o chakra do senjutsu em suas técnicas comuns.", null, "Rank –", null, "仙人モードせんいんモード", "Sennin Mōdo", null, null, "Honorável Modo Sábio", "Modo Sennin");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo Sábio dos Seis Caminhos", "O Modo Sábio dos Seis Caminhos (六道仙人モード, Rikudō Sennin Mōdo) é uma técnica sábia utilizada por Naruto após receber o poder do Sábio dos Seis Caminhos. A aparência deste modo é muito semelhante ao Modo Sábio, exceto pela ausência da típica pigmentação padrão. Como Naruto utiliza este modo combinado com o chakra de Kurama, seus olhos adquirem uma combinação das pupilas característicos dos sapos com as da besta.", null, "Rank –", null, "六道仙人モードりくどうせんいんモード", "Rikudō Sennin Mōdo", "Modo Sábio do Seis Caminhos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo Tsukuyomi", "Essa técnica é uma variação do Tsukuyomi, um genjutsu de alto nível. O usuário transforma o Tsukuyomi em uma barreira que retarda o oponente para baixo. Isso não acontece fisicamente, mas sim mentalmente. Faz o adversário pensar que está sendo retido por uma barreira. Enquanto estiver usando esta técnica, a vitalidade e o chakra de Itachi lentamente abaixam. Se Itachi usa essa técnica quando sua barra de saúde está baixa, ele poderia finalizar-se.", "Curto alcance", null, null, "月読モード", "Tsukuyomi Mōdo", "Modo de Leitor da Lua", "Modo Tsukuyomi", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Modo de Chakra de Liberação de Lava", "O Modo de Chakra de Liberação de Lava é um estado elevado de Rōshi após a manifestação de uma das caudas de Son Goku.", null, null, "Juntar as Maos", "熔遁チャクラモードようとんチャクラモード", "Yōton Chakura Mōdo", "Modo de Chakra de Liberação de Lava", null, null, null);
