INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Moinho de Vento", "Kimimaro cria ossos nas mãos e joga o oponente para cima em movimentos circulares diagonais, aparecendo posteriormente acima do oponente, e girando para baixo como uma broca gigante, ao estender uma mão para cima de sua cabeça e outra para o lado, para rasgar o corpo do oponente.", "Curto alcance", null, null, "風車", "Kazaguruma", "Moinho de Vento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Monitoramento", "O Monitoramento (監視, Kanshi) é uma capacidade possuída pelo Zetsu Negro que permite gravar eventos que ele testemunhou pessoalmente.", null, null, null, "監視", "Kanshi", "Monitoramento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Montanha Flor–Frutífera", "Enquanto em seu estado transformado, o usuário cria um pequeno vulcão que explode violentamente, enviando rocha derretida voando em todas as direções, como uma flor gigante. O calor da lava é o suficiente para queimar uma besta com cauda tão forte como Gyūki.", "Todos os alcances", null, null, "花果山かかざん", "Kakazan", "Montanha Flor–Frutífera", "Montanha Huaguo", "Florescer do Vulcão", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Montanha de Ferro Inclinada", "Depois de criar um clone de insetos, Shino e o clone correm em direção ao adversário e simplesmente o atacam. A força desse ataque é forte o suficiente para enviar o alvo voando longe deles.", "Curto alcance", null, null, "鉄山靠てつざんこう", "Tetsuzankō", "Montanha de Ferro Inclinada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mordida Pegajosa", "Aproveitando-se de seu corpo pegajoso e maleável, Saiken abre sua grande boca e avança no oponente, mordendo e mastiga-o. Mesmo sem dentes, a besta pode seriamente causar danos graves no inimigo a ponto de matá-lo. Além disso, a besta pode aumentar sua grande cabeça para engolir um corpo humano inteiro.", "Curto alcance", null, null, "粘咬", "Nenkō", "Mordida Pegajosa", null, null, null);
