INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Motim Barulhento: Desenho de Imitação da Super Besta da Imagem do Pergaminho", "Naruto e Sai executam uma série de ataques físicos em seu oponente. Naruto executa um chute, e Sai finaliza usando três leões de tinta.", "Curto alcance", null, null, "喧乱・超獣戯画絵巻", "Kenran: Chōjū Giga Emaki", "Motim Barulhento: Desenho de Imitação da Super Besta da Imagem do Pergaminho", "Batalha Deslumbrante: Réplica do Pergaminho da Besta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Motim Encantador do Carro da Folha", "Anko chuta seu oponente para o ar, em seguida chuta novamente mais alto e salta sobre seus ombros, girando-os até o chão.", "Curto alcance", null, null, "木ノ葉甘乱車", "Konoha Kanransha", "Motim Encantador do Carro da Folha de Árvore", "Roda-gigante da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Movimento de Taijutsu em Alta Velocidade", "Um método de taijutsu onde o usuário pode mover-se em altas velocidades que são dificilmente acompanhadas a olho nu, que permite realizar uma série de ataques físicos consecutivos.", null, null, null, "体術運動超高速", "Taijutsu Undō Chō Kōsoku", "Movimento de Técnica Corporal em Alta Velocidade", "Técnica Impulsionante[Nota 1]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mudança de Curso da Cor", "Esta técnica é usada por Fūka para alternar a sua consciência entre os vários corpos que ela tem acesso. Ela precisa fazer isso por causa de seu Shikei Seppun: já que um único corpo não poderia lidar com tanto chakra, ela teve de usar vários para distribuir todo o chakra absorvido durante a sua vida. Desenrolando um pergaminho, ela esconde o seu corpo em um rápido movimento e muda para outro.", "Curto alcance", null, null, "色転進", "Shiki Tenshin", "Mudança da Cor de Curso", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Muito Oculto: Veneno do Inseto Peçonhento", "Torune pousa uma série de golpes no oponente, envenenando-o com seus Rinkaichū.", "Curto alcance", null, null, "極秘伝・毒蟲毒", "Goku Hiden: Dokuchūdoku", "Muito Oculto: Veneno do Inseto Peçonhento", "Jutsu Extremo Secreto: Insetos Venenosos", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Multiplicação Mútua dos Papéis Explosivos", null, "Curto a Médio alcance", "Rank A", "Selo do Confronto", "互乗起爆札", "Gojō Kibaku Fuda", "Multiplicação Mútua dos Papéis Explosivos", "Conjunto de Papéis Bomba", "Papéis Explosivos de Detonação Múltipla", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Muro de Lanças", "Obito reúne as Esferas da Busca da Verdade de suas costas e forma uma parede que dispara uma série de espinhos contra seu oponente.", "Curto a Médio alcance", null, null, "槍衾やりぶすま", "Yaribusuma", "Muro de Lanças", "Parede de Lanças", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Muro de Ventos Rápidos", "Após executar os selos de mão, Ashura cria um poderoso remoinho de vento em torno de si para se defender de ataques inimigos. Com isso ele foi capaz de se defender de uma técnica de relâmpago de Indra. A técnica também pode ser expandida para proteger outras pessoas próximas ao usuário.", "Curto alcance", null, "Boi → Cao → Rato", "風迅壁", "Fūjinheki", "Muro de Ventos Rápidos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplas Shuriken Fantasmas", "Esta técnica é muito semelhante em termos de execução e resultado a Técnica do Clone da Sombra de Shuriken. É um ninjutsu cooperativo onde três usuários, depois de jogar shuriken em seu alvo e formar os selos de mão necessários, podem criar réplicas corporais de shuriken — transformando uma pequena quantidade de shuriken em uma grande quantidade em um instante e com as quais, bombardeiam o inimigo. Ao contrário da Técnica do Clone da Sombra de Shuriken, no entanto, esta técnica não faz uso da Técnica dos Clones das Sombras.", "Curto à Médio Alcance", null, "Cao → Coelho → Macaco → Bater Palmas", "幻影多重手裏剣", "Gen'ei Tajū Shuriken", "Múltiplas Shuriken Fantasmas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Abraços Infinitos", "Esta técnica tem exatamente o mesmo efeito que o Abraço Infinito, mas esta versão é muito maior e mais forte, uma vez que poderia completamente subjugar e incapacitar Naruto Uzumaki, apesar de ele usar o chakra da Kyūbi. O Grupo Magaki coloca seu adversário em uma barreira que empurra-o para baixo, impedindo-os de fugir e drenando seu chakra. Quanto mais esforço colocado na técnica, mais aumenta a sua capacidade de dominar e drenar chakra do alvo.", "Curto à Médio alcance", null, "Tigre + Cao + Cavalo + Dragão + Javali Simultaneamente", "多重無限抱擁たじゅうむげんほうよう", "Tajū Mugen Hōyō", "Múltiplos Abraços Infinitos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Canhões do Lótus Altruísta Conectados", "Enquanto no segundo estado de sua Transformação do Selo Amaldiçoado, Jūgo faz crescer mais dos apêndices reforçados com jato, estendendo-os e usando-os para reunir chakra e liberar uma poderosa explosão de chakra que também abrange uma ampla área. À medida que o chakra é recolhido, ele em seguida, é liberado em grande velocidade, tornando-se impossível escapar da técnica por métodos normais.", "Todos os alcances", null, null, "多蓮不自連砲", "Taren Fujizuho", "Múltiplos Canhões do Lótus Altruísta Conectados", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Clones de Fumaça", "Depois de criar um único Clone de Fumaça como tática para ser destruído, Kigiri certifica-se de que a explosão feita pelo clone espalhe a fumaça por uma área visivelmente grande, possibilitando a criação de novos clones de fumaça em um número maior.", null, null, null, "多重煙分身たじゅうけむりぶんしん", "Tajū Kemuri Bunshin", "Múltiplos Clones de Fumaça", null, null, "Multi Clones de Fumaça");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Punhos Conectados", "Os braços de Ukon brotam em um de Sakon e socam o adversário, triplicando o dano normal. Este ataque é forte o suficiente para quebrar uma rocha sólida.", "Curto alcance", null, null, "多連拳", "Tarenken", "Múltiplos Punhos Conectados", null, "Punhos Múltiplos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Punhos Conectados: Rakshasa", "Sakon e Ukon criam uma distância entre si mesmos e seu oponente com um chute. Eles se dividem e invocam Rashōmon atrás do mesmo, antes ativarem seu selo amaldiçoado e se fundirem. Em seguida, eles acertam vários socos no oponente contra o Rashōmon, até que ele quebre.", "Curto alcance", null, null, "多連拳・羅刹たれんけん・らせつ", "Tarenken: Rasetsu", "Múltiplos Punhos Conectados: Rakshasa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Pés Conectados", "As pernas de Ukon brotam de uma das pernas de Sakon e chutam o adversário, triplicando o dano normal.", "Curto alcance", null, null, "多連脚たれんきゃく", "Tarenkyaku", "Múltiplos Pés Conectados", null, "Chutes Múltiplos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Redemoinhos Conectados", "Sakon e Ukon usam suas pernas para girar com grande força para desviar quaisquer ataques.", "Curto alcance", null, null, "多連旋風", "Tarensenpū", "Múltiplos Redemoinhos Conectados", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Múltiplos Tiros de Lama", "Ampliando sua técnica parente, esta versão permite que o usuário dispare múltiplos tiros contra diversos alvos ao mesmo tempo.", "Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mão Perfurante do Relâmpago Sábio", "Hagoromo converte chakra em eletricidade, podendo desferir um soco poderoso o suficiente para atravessar carne com relativa facilidade e matar o seu alvo. Essa técnica mostrou-se poderosa o suficiente para quebrar uma grande rocha. Seus efeitos são potencializados com o uso da energia natural infundida com seu chakra.", "Curto alcance", null, null, "仙雷の穿手せんらいのせんて", "Senrai no Sente", "Mão Perfurante do Relâmpago Sábio", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mãos de Múltiplas Serpentes Ocultas na Sombra", "Ao aumentar a quantidade de cobras invocadas com o Mãos de Serpente Oculta na Sombra, a diversidade deste ninjutsu é aumentada. O enxame de grandes serpentes aparecem em um instante, cada cobra individualmente fazendo algo intimidante, desviando, ou capturando o inimigo em um ataque de cooperativo. Sem lhes dar tempo para reagir, o inimigo é envolvido pelas serpentes.", "Curto a Médio alcance", "Rank B", null, "潜影多蛇手", "Sen'ei Tajashu", "Mãos de Múltiplas Serpentes Ocultas na Sombra", null, "Técnica das Serpentes Múltiplas das Sombras", "Ataque Múltiplo das Cobras da Sombra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mãos de Serpente Oculta na Sombra", "Esta técnica permite que o usuário ejete cobras de seu pulso ou manga, geralmente após um movimento de pancada. As cobras são usadas ​​principalmente para atacar à distância e, sendo cobras, podem causar várias feridas venenosas no corpo da vítima ou mantê-la no lugar. As cobras também podem abrir a boca para alargar lâminas como a Kusanagi.", "Curto alcance", "Rank C", "Cobra → Tigre", "潜影蛇手", "Sen'ei Jashu", "Mãos de Serpente Oculta na Sombra", null, "Técnica da Serpente das Sombras", "Sombra Combatente da Serpente");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Mãos de Serpente Selvagem Oculta na Sombra", "O usuário conjura uma grande quantidade de cobras que emergem do subsolo para atacar o alvo.", "Curto a Médio alcance", null, null, "潜影乱蛇手せんえいらんじゃしゅ", "Sen'ei Ranjashu", "Mãos de Serpente Selvagem Oculta na Sombra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Abate dos Gêmeos Konjiki", "O Método de Abate dos Gêmeos Konjiki consiste em combinar as kekkei genkai de Liberação de Lava e Aço em uma pseudo-kekkei tōta.", "Longo alcance", null, null, null, null, "Método de Abate dos Gêmeos Konjiki", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Aceleração", "Murasame muda a forma como o seu chakra flui, o que lhe permite aumentar consideravelmente sua velocidade, diminuindo significativamente as oportunidades para seus oponentes reagirem. Ao fluir o chakra através do coração, Murasame pode aumentar o seu batimento cardíaco, aumentando o fluxo de seu chakra ainda mais, incluindo sua velocidade. Em sua velocidade máxima, Murasame também pode confundir seus adversários com as imagens residuais criadas a partir de seu movimento de alta velocidade.", "Todos os alcances", null, "Boi → Carneio → Javali → Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Formação da Barreira", "Uma série de quatro papéis explosivos são colocados em conjunto em torno da fronteira de uma área. Se alguém ou alguma coisa tentar atravessar a fronteira, os selos vão detonar quando o intruso chegar ao centro, matando o alvo preso.", null, "Rank B", null, "結界法陣", "Kekkai Hōjin", "Método da Barreira de Acampamento", "Barreira Perimetral", "Formação de Contenção", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Formação da Barreira — Fórmula Tripla", "Uma versão mais abrangente do Exemplo — Fūinjutsu, Iruka aplica três fórmulas de selamento simultaneamente no chão, uma do lado da outra. Cada uma causa uma explosão se o oponente for pego nelas, explodindo-se depois se nenhum oponente cair nela.", "Curto alcance", null, null, "結界法陣　三連式けっかいほうじん　さんれんしき", "Kekkai Hōjin Sanrenshiki", "Método de Formação da Barreira — Fórmula Tripla", "Camada Tripla da Barreira Perimetral", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Formação da Explosão Intermitente", "Sakon faz um selo circular no chão, com a adição de quatro papéis explosivos ao seu redor, se o adversário se aproximar o suficiente, o selo irá explodir e o oponente ficará coberto por chamas púrpuras.", "Curto alcance", null, null, "爆滅法陣", "Bakumetsu Hōjin", "Método de Formação da Explosão Intermitente", "Formação da Explosão Destruidora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Formação do Selo Vinculador", "Uma matriz de papéis explosivos são colocadas em torno de uma área. Elas explodirão com um selo de mão ou se o adversário tentar se mover. Uma vez que o alvo é incapaz de se mover, um companheiro de equipe, então, é capaz de atacá-lo sem nenhum problema. As etiquetas podem ser desligadas pelo controlador.", "Curto alcance", null, "Macaco → Cavalo → Cao → Carneiro", "封縛法陣", "Fūbaku Hōjin", "Método de Formação do Selo Vinculador", null, null, "Arte Ninja da Barreira: Captura do Selo de Perímetro");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Interseção", "Com o Kōsa Hō (交差法, Kōsa Hō), o utilizador é capaz de interceptar e contra-atacar uma técnica de taijutsu inimiga, elevando o joelho, e conduzindo para baixo do cotovelo ao mesmo tempo, prendendo assim o membro em que o inimigo usou o taijutsu. Isso causa um dano ao membro do inimigo, pois entre os seus membros, o usuário impulsiona o cotovelo para baixo com muita força, efetivamente quebrando o osso. Genshō usou essa técnica enquanto disfarçado de um ninja de Konohagakure, Yagura, para 'quebrar' o Furacão da Folha de Rock Lee interceptando a perna de Lee. O tornozelo de Lee não foi quebrado devido sua proteção de peso no tornozelo, ele foi forçado a parar completamente a sua formação até que o ferimento fosse curado.", "Curto alcance", null, null, "交差法", "Kōsa Hō", "Método de Interseção", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Liberação da Cauda", "Após preparar o terreno para deixar o jinchūriki vulnerável, o usuário pode forçar a liberação da besta com cauda do corpo do hospedeiro. Obito teve que esperar por um longo tempo até que o selo de Kushina fosse fraco o suficiente para usar esta técnica para extrair Kurama.", "Curto alcance", null, null, "解尾法印かいびほういん", "Kaibi Hōin", "Método de Liberação da Cauda", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Liberação do Mal", "O Método de Liberação do Mal é uma contrapartida direta do Método de Selamento do Mal, esta técnica é utilizada para liberar selos induzidos pela técnica em uma pessoa, em vez de isolar. Em comparação com o Método de Selamento do Mal, o Método de Liberação do Mal requer muito menos preparação. Sasuke usou esta técnica para reviver Orochimaru, misturando um pedaço de carne modificado de Kabuto Yakushi para criar um corpo para a consciência de Orochimaru que foi deixada no Selo Amaldiçoado do Céu de Anko Mitarashi.", "Curto alcance", null, "Tigre → Cobra → Cavalo → Carneiro → Tigre → Coelho → Rato", "解邪法印", "Kaija Hōin", "Método de Liberação do Mal", "Maldição: Liberar", "Selo de Cancelamento da Maldição", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Mudança Corporal das Sombras Espelhadas", "Essa técnica é uma das várias outras que foram passadas por gerações no País dos Demônios. Ela inicialmente se estrou como Taruho sendo usuário, para se passar por Shion.", "Curto alcance", null, "Selos de mao especifico da técnica", "影鏡身転の法", "Kage Kagami Shinten no Hō", "Método de Mudança Corporal das Sombras Espelhadas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Respiração dos Sete Paraísos", "Trata-se de um método de respiração envolvendo taijutsu inventado por Shira. Através do acúmulo de grandes quantidades de oxigênio para quadruplicar a capacidade de seus pulmões, Shira adquire uma força equivalente à dos Oito Portões com a diferença da quantidade de estágios. As alterações no corpo são as mesmas também, como a cor de pele escura e olhos completamente brancos. Ativar esta técnica exala uma aura amarela.", null, null, null, "七天呼法", "Shichi Tenkohō", "Método de Respiração dos Sete Paraísos", null, null, "Sete Suspiros Celestiais");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Selamento Escuro", "Este selo é utilizado em conjunção com a Formação dos Quatro Nevoeiros Pretos. Após o Quarteto do Som selar o indivíduo no recipiente, este selo é adicionado como mais uma camada de proteção. Para criar o selo, Sakon escreve o encantamento de sangue no papel de selamento e, em seguida, atribui-o ao recipiente antes de ativá-lo com um selo de mão.", "Curto alcance", "Rank B", null, "封黒法印", "Fūkoku Hōin", "Método de Selamento Escuro", null, "Selo de Contenção da Escuridão", "Arte Ninja: Selo das Trevas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Selamento de Fogo", "Este jutsu é um tipo de fūinjutsu que envolve o selamento dos efeitos de uma técnica dentro de uma localização específica. Por se adaptar a ninjutsu de Fogo, essa técnica requer um usuário bem informado na arte exata das 'Fórmulas de Selamento', assim como em ninjutsu. O espaço de selamento do pergaminho é deixado em branco, para assim a fórmula ser inscrita nele. Ao realizar os selos de mão precisos, um vapor aparece do papel e envolve as chamas. Elas são sugadas até o pergaminho, e a aparição do kanji de selar (封) certifica a conclusão da técnica. Assim que o selamento é realizado, os efeitos da técnica não podem ser soltos novamente enquanto uma técnica que anule o selamento não for aplicada. Essa técnica é efetiva para evitar qualquer dano secundário que uma técnica inimiga perigosa possa causar. Ela foi até mesmo capaz de selar as chamas negras do Amaterasu.", "Curto alcance", "Rank B", "Rato → Passaro → selo especifico → Tigre", "封火法印", "Fūka Hōin", "Método de Selamento de Fogo", null, "Aprisionamento de Fogo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Método de Selamento do Mal", "Essa técnica é usada para suprimir marcas induzidas por uma técnica numa pessoa. Marcas de poder mais fraco podem ser completamente suprimidas por essa técnica, mas marcas de poder mais alto como o Selo Amaldiçoado do Céu de Sasuke, fazem com que o poder do selo se torne dependente da força de vontade do recipiente até certo ponto, especialmente se o selador não é experiente o bastante para fortalecer o selo com seu próprio poder. O selo também pode segurar certas kekkei genkai, tais como a do clã Kurama, assim como Kurenai tentou fazer.", "Curto alcance", "Rank A", "Coelho → Cobra → Cavalo → Carneiro → Passaro → Tigre → Coelho → Cobra → Cavalo → Carneiro → Pássaro → Tigre → Coelho → Cobra → Cavalo → Carneiro → Pássaro → Cao → Rato", "封邪法印ふうじゃほういん", "Fūja Hōin", "Método de Selamento do Mal", null, "Selo de Libertação da Maldição", null);
