INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ninjutsu Secreto Ankorodō: Liberação de Mochi: Bolinhos Infinitos", "O usuário faz com que bolinhos de feijão doce que estão próximos cresçam em tamanhos exorbitantes e numa velocidade absurda. O crescimento constante faz com que seja difícil atacar os bolinhos, uma vez que são muito macios. Apenas Chōji Akimichi, com sua incrível habilidade de comer tudo o que pode, conseguiu parar a técnica.", "Médio alcance", null, "Ativacao: Cobra → Cão → Tigre → Mãos espalmadas para frente,Manter: Rato", "あんころ堂秘伝忍術・餅遁・無限白玉", "Ankorodō Hiden Ninjutsu: Mochiton: Mugen Shiratama", "Técnica Ninja Secreta da Geleia de Feijão Doce: Liberação de Mochi: Bolinhos Infinitos", null, null, null);
