INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Nova Criação: Rasengan do Relâmpago Rápido", "Boruto e Sarada se preparam para atacar, em seguida Sarada atira várias shuriken e Boruto ataca com o Punho Gentil. Logo, Sarada aparece por trás e o joga para o alto com um soco reforçado com chakra. Antes do oponente cair no chão, Boruto e Sarada o finalizam com um Rasengan Desaparecedor e várias kunai infundidas com chakra relâmpago, que culminam em uma explosão de energia.", null, null, null, "新鋭・雷迅螺旋丸しんえい・らいじんらせんがん", "Shin'ei: Raijin Rasengan", "Nova Criação: Esfera Espiral do Relâmpago Rápido", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Nova Técnica Ninja Pervertida", "Uma versão melhorada da Técnica Sensual. Naruto se transforma em uma bela mulher vestida com uma roupa de diaba reveladora, enquanto posa sedutoramente. Naruto descobre que apresentar a mulher vestida com esta roupa é mais eficaz do que apresentar ela totalmente nua. Isso porque, a roupa é cortada e moldada para fazer com que a vítima fique obcecada em querer ver mais do corpo da mulher, combinado com um olhar de 'garota má'.", null, null, null, null, null, "Nova Técnica Ninja Pervertida", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Nove Bestas Mascaradas", "As Nove Bestas Mascaradas (九面獣, Kyūmenjū) são entidades criadas por Menma através do uso do chakra do Nove-Caudas Negro. Cinco dessas bestas são descritas como bestas divinas (神獣, shinjū), enquanto as outras quatro são referidas como sacerdotes divinos (神官, shinkan).", null, "Rank S", "Tigre", "九面獣", "Kumenjū", "Nove Bestas Mascaradas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Novo: Invocação do Impasse de Três-Caminhos", "Naruto, Sasuke e Sakura invocam Gamakichi, Aoda e Katsuyu, respectivamente. Enquanto as invocações atacam, cada um salta da cabeça de sua invocação e acertam um soco em seu alvo. Naruto utilizando um braço de chakra de sua Besta com Cauda, Sasuke com um Susanoo parcialmente manifestado, e Sakura com sua força aprimorada, causando grande dano ao inimigo.", "Curto alcance", null, null, "新・口寄せ三竦みしん・くちよせさんすくみ", "Shin: Kuchiyose Sansukumi", "Novo: Invocação do Impasse de Três-Caminhos", null, null, null);
