INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Névoa Ilusória", "Isobu libera uma névoa criada por seu chakra para colocar intrusos sob uma ilusão. A ilusão atormenta o alvo com eventos do seu passado. Animais e insetos ficam desnorteados ao entrarem em contato com a névoa. A ilusão desaparecerá se a vítima quebrar a ilusão sozinho ou com a ajuda de outro(s).", "Todos os alcances", null, null, null, null, null, null, null, null);
