INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oferecimento de Flores do Funeral", "Ino joga um buquê de flores com um papel explosivo oculto no oponente, que é automaticamente ativado ao entrar em contato com ele. Ino mantém uma distância, para se proteger da explosão, e o inimigo é pego na explosão.", "Curto alcance", null, null, "供花送りくげおくり", "Kuge Okuri", "Oferecimento de Flores do Funeral", "Envio de Flores ao Memorial", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Deuses do Trovão", "Movendo sua mão para baixo, Momoshiki Ōtsutsuki é capaz de desencadear um poderoso relâmpago descendente, capaz de debilitar mesmo o Modo de Chakra da Besta com Cauda de Naruto Uzumaki revestido com o Susanoo de Sasuke Uchiha.", "Longo alcance", null, null, "八雷神", "Hachiraishin", "Oito Deuses do Trovão", "Clamor dos Deuses", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Portões", "Os Oito Portões são oito tenketsu específicos do Sistema de Circulação de Chakra de um indivíduo. Eles limitam o fluxo global de chakra dentro do corpo de uma pessoa. A base para a ideia dos portões de chakra vem de limites do corpo sobre as funções dentro dele. Isso faz o corpo muito mais fraco, mas previne o corpo de esgotar tão cedo. Submetendo-se a treinamento intenso, pode-se aprender como abrir estes portões, permitindo que o usuário ultrapasse seus próprios limites físicos à custa de danos extremos de seu próprio corpo.", null, null, null, "八門はちもん", "Hachimon", "Oito Portões", null, "Oito Portais", "Oito Portões Internos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Torções da Besta com Cauda", "Depois de assumir a sua plena transformação de oito caudas, Killer B gira suas caudas em torno de si, antes de estendê-lo com uma enorme quantidade de força física. Isto faz com que ele gire rapidamente, resultando na geração de tremendas rajadas de vento formando uma área circundante. Esta técnica foi suficientemente forte para nivelar o terreno dentro de uma grande área em volta do Hachibi.", "Todos os alcances", "Rank –", null, "尾獣八巻きびじゅうはちまき", "Bijū Hachimaki", "Oito Torções da Besta de Cauda", null, "Torção da Fera de Oito Caudas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Ataque de Desintegração dos Leões Gêmeos", "Esta técnica é uma versão aperfeiçoada do Passo Gentil dos Punhos de Leões Gêmeos, utilizada por Hinata Hyūga após ganhar o chakra de Hamura Ōtsutsuki, onde devido o ganho de qualidade de chakra, aumenta o poder da técnica consideravelmente.", "Curto alcance", null, null, "八卦双獅子崩撃はっけそうじしほうげき", "Hakke Sōjishi Hōgeki", "Oito Trigramas Ataque de Desintegração dos Leões Gêmeos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Britador da Montanha", "Uma variação mais eficaz do Oito Trigramas Palma de Vácuo. O usuário acerta o alvo e cria uma forte oscilação de chakra, assim ele lança-o a uma incrível extensão e coloca o inimigo com sérios ferimentos.", "Curto alcance", null, null, "八卦破山撃はっけはさんげき", "Hakke Hasangeki", "Oito Trigramas Britador da Montanha", "Oito Trigramas Ataque de Ruptura da Montanha", null, "Oito Trigramas Golpe Hasan");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Cento e Vinte e Oito Palmas", "O Oito Trigramas Cento e Vinte e Oito Palmas é uma técnica do estilo de luta do Punho Gentil. É essencialmente uma versão duplicada do Oito Trigramas Sessenta e Quatro Palmas, realizada no dobro da velocidade. Esta técnica é usada para fechar o fluxo de chakra acertando cento e vinte e oito tenketsu do Sistema de Circulação de Chakra do oponente. Isso elimina sua capacidade de usar chakra por algum tempo e faz com que seja difícil para ele se mover. Alternativamente, ela pode ser usada para atingir um grande número de alvos muito rapidamente.", "Curto alcance", null, null, "八卦百二十八掌はっけひゃくにじゅうはちしょう", "Hakke Hyaku Nijūhachi Shō", "Oito Trigramas Cento e Vinte e Oito Palmas", null, null, "Oito Trigramas Cento e Vinte e Oito Golpes");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Estilo Trezentos e Sessenta e Um", "Esta técnica permite que o usuário ataque todos os 361 pontos de chakra do oponente, paralisando-o com cada golpe e imediatamente matando-o no golpe final. Quando se utiliza esta técnica, a velocidade do utilizador é comparável à da Lótus Reversa.", "Curto alcance", null, null, "八卦三百六十一式", "Hakke Sanbyakurokujū Isshiki", "Oito Trigramas Estilo Trezentos e Sessenta e Um", "Oito Trigramas: Estilo Trezentos e Sessenta e Um", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Palma Aiki", "Hinata levanta os braços defensivamente, sendo um contragolpe. Quando o oponente a acerta, ela agarra o oponente e envia-o voando para longe. Ela, então, os atinge com um ataque poderoso com sua palma.", "Curto alcance", null, null, "八卦合気掌", "Hakke Aikishō", "Oito Trigramas Palma de Espírito Harmonioso", "Oito Trigramas Palma Espírita", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Palma da Parede de Vácuo", null, "Curto a Médio alcance", null, null, "八卦空壁掌はっけくうへきしょう", "Hakke Kūhekishō", "Oito Trigramas Palma da Parede de Vácuo", null, "Golpe da Parede de Vácuo dos Oito Trigramas", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Palma de Rede", "O usuário bate as suas mãos no solo, criando um conjunto de redes que infligem dor nas vítimas e drenam seu chakra.", "Todos os alcances", null, "Tigre → Carneiro → Boi → Passaro", "八卦盤掌はっけばんしょう", "Hakke Banshō", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Palma de Vácuo", "Esta técnica é semelhante, na prática, à Oito Trigramas Palmas Giratórias do Céu, mas tem um alvo específico, em vez de uma área geral. Para realizá-lo, o usuário aponta precisamente para os pontos vitais do inimigo com o Byakugan e libera um impulso com a palma da mão em alta velocidade. A rajada de vácuo compactado usando o Punho Gentil é formada para atacar os órgãos vitais do oponente de uma certa distância, lançando-os para longe com uma força tremenda antes mesmo de perceber que eles foram atingidos.", "Curto a Médio alcance", "Rank –", null, "八卦空掌はっけくうしう", "Hakke Kūshō", "Oito Trigramas Palma de Vácuo", "Oito Trigramas Palma do Céu", "Golpe do Vácuo de Oito Trigramas", "Oito Trigramas Palma Aérea");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Palma de Vácuo do Céu", "Primeiramente, Hinata acerta o inimigo e lança-o no ar, logo o usuário salta até o céu e realiza um incrível Oito Trigramas Palmas Giratórias do Céu, entretanto a esfera maciça aumenta seu tamanho constantemente e assim o inimigo começa a forçar-se contra o solo, assim ele chega a atravessar o solo e sumir no meio da esfera de lâminas. Finalmente, o usuário começa a conter sua rotação, em seguida, ele cai no centro de uma cratera imensa feita com a incrível rotação de lâminas de Chakra.", "Curto a Médio alcance", null, null, "八卦掌空天", "Hakkeshō Kūten", "Oito Trigramas Palma de Vácuo do Céu", "Oito Trigramas Palma do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Palmas Giratórias do Céu", "Um taijutsu secreto que é passado oralmente apenas para os membros da casa principal do clã Hyūga.", "Curto alcance", "Rank –", null, "八卦掌回天はっけしょうかいてん", "Hakkeshō Kaiten", "Oito Trigramas Palmas Giratórias do Céu", "Rotação", "Rotação das Palmas de Oito Trigramas", "Oito Trigramas Palma Rotativa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Quatro Palmas Celestiais", "Neji utiliza o Oito Trigramas Palma de Vácuo três vezes sobre o seu oponente, lançando-o ferozmente à uma rocha gigante, onde em seguida, Neji ataca novamente com mais uma palma de vácuo ainda mais forte, destruindo a rocha gigante em cima do oponente.", "Curto a Médio alcance", null, null, "八卦四天空掌はっけしてんくうそう", "Hakke Shitenkūshō", "Oito Trigramas Quatro Palmas Celestiais", "Oito Trigramas Vento Definitivo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Sessenta e Quatro Palmas", "Os Oito Trigramas Sessenta e Quatro Palmas é um taijutsu exclusivo passado dentro da casa principal dos Hyūga.", "Curto alcance", null, null, "八卦六十四掌はっけろくじゅうよんしょう", "Hakke Rokujūyon Shō", "Oito Trigramas Sessenta e Quatro Palmas", null, "Estilo Jūken: Oito Trigramas Sessenta e Quatro Palmas", "Arte do Punho Suave: Oito Trigramas Sessenta e Quatro Golpes");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Sessenta e Quatro Palmas Espirituais", "Neji usa o Oito Trigramas Sessenta e Quatro Palmas, mais ou menos 8 à 16 ataques, na qual, lança seu oponente para o alto. Hinata então o ataca com a Denominação de Oito Trigramas Onda de Palmas, mas nos dois últimos golpes aparece uma onda gigantesca de chakra, que manda o oponente novamente até Neji, onde por fim, ele ataca seu alvo com a anterior concentração de chakra em seus dedos.", "Curto alcance", null, null, null, null, "Oito Trigramas Sessenta e Quatro Palmas Espirituais", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oito Trigramas Trinta e Duas Palmas", "Esta é uma técnica do estilo de luta do Punho Gentil. É essencialmente uma versão menor do Oito Trigramas Sessenta e Quatro Palmas, assim ela se torna menos eficaz. Essa técnica é feita com o intuito de violar o fluxo de Chakra ao acertar uma meta de trinta e dois Pontos de Chakra do Sistema de Circulação de Chakra do seu inimigo. Isso elimina as chances do alvo em usar ou controlar seu Chakra em algum tempo e faz o inimigo ficar vulnerável e com muita falta de movimentação.", "Curto alcance", null, null, "八卦三十二掌はっけさんじゅうにしょう", "Hakke Sanjūni Shō", "Oito Trigramas Trinta e Duas Palmas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Olho Noturno", "Este é um dōjutsu utilizado por Yome para rastrear os inimigos através da reflexão de minúsculas gotículas de água, após a dilatação das pupilas de seus olhos. De acordo com Sen, uma vez que Yome tenha fixado os olhos, seu alcance visual chega à dezenas de quilômetros. Yome também pode usar essa habilidade para aumentar habilidades de evasão durante um combate.", "Todos os alcances", null, null, "夜目よめ", "Yome", "Olho Noturno", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Olho da Mente de Kagura", "Esta habilidade permite que Karin possa encontrar e sentir indivíduos por meio de sua corrente de chakra, podendo localizá-los em uma área de mais de dez quilômetros. Isso é feito concentrando-se e abrindo o olho da mente, embora Karin o mantenha ativo com frequência. Esta habilidade também permite descobrir quando uma pessoa está mentindo por meio da perturbação do chakra da pessoa que ocorre por causa de emoções desonestas ou detectar se alguém, incluindo ela própria, está em um genjutsu. Além disso, concentrando-se em um chakra em particular, Karin pode perceber a sua localização e movimento com grande detalhe.", "Todos os alcances", "Rank –", null, "神楽心眼", "Kagura Shingan", "Olho da Mente de Kagura", "Kagura: O Olho da Mente", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Olho de Sapo", "Utilizando um sapo olheiro como meio, o usuário compartilha da visão do anfíbio, tornando-se capaz de ver tudo que o animal vê. Muito útil para missões de infiltração, reconhecimento e observação. Ele ainda é capaz de compartilhar essa visão com o óculos de Amado em tempo real.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda de Choque Precoce", "Sakura, usando sua superforça, soca o chão para fazê-lo tremer e, eventualmente, criar uma onda de choque que derruba todos em sua volta.", "Curto a Médio alcance", null, null, "衝波乱れ咲きしょうはみだれさき", "Shōha Midare Saki", "Onda de Choque Precoce", "Onda de Choque da Flor", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda de Choque da Estátua Demoníaca do Caminho Exterior", "Se cercado por inimigos, a Estátua Demoníaca do Caminho Exterior solta uma enorme onda de choque que empurra os inimigos de volta.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda de Corais em Ascensão", "Batendo sua mão ou bastão no chão, Yagura é capaz de criar uma fileira de corais crescentes a partir do solo e, em seguida, atacar o oponente. Para um efeito mais bruto, ele pode aumentar drasticamente um coral na forma de um pilar, atacando o oponente violentamente e jogando-o para cima. Essa versão mais violenta é conhecida como Ataque dos Corais em Ascensão (珊瑚昇撃, Sango Shōgeki).", "Todos os alcances", null, null, "珊瑚漣昇さんごれんしょう", "Sango Renshō", "Onda de Corais em Ascensão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda de Escuridão", "Shinnō pode desencadear uma onda negra generalizada feita a partir de seu chakra escuro no adversário com apenas um movimento de sua mão ou através de sua mente.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda de Palma", "Shizune ataca seu oponente algumas vezes, então ela gira ao redor com seu braço estendido para atingi-los com um Bisturi de Chakra. Ela acaba por bater o seu adversário para o alto com outro Bisturi de Chakra.", "Curto alcance", null, null, "掌波しょうは", "Shōha", "Onda de Palma", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda de Som do Cabelo Uivante", "Ao balançar mechas de seus cabelos, o usuário gera uma onda de som de alta frequência que é capaz de simultaneamente desorientar o oponente por pressionar seu ouvido e também é capaz de cortar o alvo.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Onda do Deserto", "Uma técnica principalmente defensiva que usa areia para criar uma enorme parede de areia que é presumivelmente capaz de proteger de quaisquer ataques frontais. Ela também pode ser usada para separar as pessoas que envolvem-se entre si.", "Curto alcance", null, null, "砂漠波", "Sabakuha", "Onda do Deserto", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ondas Sonoras Decapitadoras", "Usando os tubos de ar implantados em seus braços, Zaku pode controlar a pressão do ar, criando explosões supersônicas de ar. Estas explosões são poderosas o suficiente para destruir uma pedra e podem ser usadas para amolecer o chão.", "Curto alcance", "Rank D", "Cobra → Coelho → Cavalo → Javali", "斬空波", "Zankūha", "Ondas Sonoras Decapitadoras", null, "Onda Sonora Cortante", "Onda de Som Cortante");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ondas de Vento", "O usuário faz ondas na mão e cria múltiplas lâminas de vento, que se combinam para formar uma lâmina, grande e única de ar, que pode ser lançado em direção do oponente. A técnica era poderosa o suficiente para parar os movimentos do Susanoo, mas não foi capaz de romper a sua defesa.", "Curto a Médio alcance", null, null, "風波かざなみ", "Kazanami", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Orbes Explosivos das Chamas Escaldantes do Inferno", "O usuário cria grandes esferas de chakra envoltas em chamas azuis, em seguida, manipula essas esferas, a fim de perseguir o inimigo antes de explodir. Inimigos atingidos pelas explosões de uma dessas esferas têm suas capacidades ofensivas reduzidas por um tempo.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Orbes de Luz", "Esta técnica cria uma quantidade de numerosas pequenas bolas de luz para atacar o alvo. Apenas um único golpe de uma delas pode resultar em sentir uma sensação de queimação.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Orbes de Roda do Nove-Caudas", "O Nove-Caudas cria orbes de chakra preto e vermelho que giram em torno de seu corpo e danifica qualquer um que lhe toca. Os orbes depois se espalham pelo local, enquanto continuam a girar mais rápido até desaparecerem.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Oscilação-Baixa Desajeitada de Gamaken", "Com o oponente no ar, Gamaken saca sua sasumata, prende o inimigo no meio e com uma força brutal prensa-o no solo, destruindo os arredores.", "Curto alcance", null, null, null, null, null, "Oscilação-Baixa Desajeitada de Gamaken", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ossos das Cinzas Mortais", null, "Curto a Médio alcance", "Rank –", null, "共殺の灰骨ともごろしのはいこつ", "Tomogoroshi no Haikotsu", null, null, "Ossos das Cinzas Mortais", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ouro Pegajoso da Aranha", "Kidōmaru usou esta técnica para endurecer o metal dourado que ele lança como o suor. As armas criadas a partir desse processo formam o núcleo de seu estilo de luta. Este metal endurece como aço quando em contato com o ar, por isso, mastigando-o na boca e mudando sua forma Kidōmaru pode criar armas adequadas a cada batalha. Porque instantaneamente endurece, ataques diretos a uma distância são possíveis.", null, null, null, "蜘蛛粘金", "Kumo Nenkin", "Ouro Pegajoso da Aranha", null, "Forja Aracnídea", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ouro do Céu", "Levantando seu lingote gigante para o cima, uma luz dourada se acende no céu e lança vários lingotes contra um grupo de oponente.", "Médio alcance", null, null, null, null, null, "Ouro do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pacote Pequeno", "Konan cria uma tempestade de papel que gira em torno de seu oponente e o corta.", "Curto alcance", null, null, "小包み", "Kodutsumi", "Pacote Pequeno", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Padrão de Selo Amaldiçoado", "Trata-se de marcas naturais espalhadas por todo o corpo de areia de Shukaku que, através do contato físico direto, pode utilizá-lo para imobilizar e selar seus alvos.", "Curto alcance", null, null, "呪印の紋じゅいんのもん", "Juin no Mon", "Padrão de Selo Amaldiçoado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Paladar Pegajoso da Língua Ácida", "Uma pasta ácida altamente concentrada é subitamente liberada a partir da boca. O ácido apresenta tal densidade elevada e concentração que é ainda capaz de diluir pedra, vaporizando-a. Não há movimentos de pré-requisitos para fazer antes da ativação desta técnica. Devido a isso, Katsuyu pode facilmente atacar rapidamente e pegar o inimigo desprevenido com este ataque.", "Todos os alcances", null, null, "舌歯粘酸", "Zesshi Nensan", "Paladar Pegajoso Ácido da Língua", null, "Gosma Ácida", "Limo Ácido");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Coral", "Enquanto em seu estado transformado, Yagura atinge o oponente com um golpe poderoso, fazendo uma grande formação de coral crescer rapidamente a partir do ponto inicial de contato. Isso, então, rapidamente se solidifica e restringe a mobilidade do inimigo, como a substância continua a crescer, eventualmente, cobrindo todo seu corpo.", "Curto alcance", null, null, "珊瑚掌さんごしょう", "Sangoshō", "Palma Coral", null, "Punho de Corais", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Dilacerante da Besta", "A Palma Dilacerante da Besta é uma poderosa técnica de natureza de chakra de vento que pode cortar através de qualquer material. Sora a ativa liberando chakra através de sua mão direita, e, em seguida, uma onda de chakra cortante é liberada, o que parece ser controlada pela vontade e pensamentos de Sora. Ele também é capaz de usar a técnica em rápidos e contínuos disparos, em movimento sucessivo.", "Médio a Longo alcance", null, null, "獣破掌", "Jūha Shō", "Palma Dilacerante da Besta", null, null, "Golpe de Vento da Besta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Inferior", "Esse ataque consiste em um golpe rápido e preciso da palma dos usuários no corpo de um oponente. Como outras técnicas do Punho Gentil, ele envia chakra no alvo para atordoar o inimigo ou, se uma quantidade suficiente de chakra for usada, pode causar graves danos internos em um golpe.", "Curto alcance", null, null, "掌底しょうてい", "Shōtei", "Palma Inferior", null, null, "Ataque da Palma Calcanhar");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Inferior do Portão da Prisão", "A inicia a combinação com uma joelhada, que lança o oponente para cima, rebate com uma cotovelada descendente, que derruba o inimigo contra o solo e finaliza com um ataque ascendente com a palma no adversário, enviando-o muito alto, em direção ao céu.", "Curto alcance", null, null, "獄門掌底", "Gokumon Shōtei", "Palma Inferior do Portão da Prisão", "Ataque da Palma de Prisão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Perfurante de Madeira", "Yamato atinge o oponente com uma série de socos e chutes que levam-no saltando para o ar e libera uma lança de madeira da palma da mão que é direcionada para o oponente e usada para levá-lo ao chão.", "Curto alcance", null, null, null, null, "Palma Perfurante de Madeira", "Palma Perfuradora de Madeira", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Perturbadora", "Sakura carrega chakra em seu punho e soca o seu adversário, o que que impulsa seu alvo, prejudicando o seu sistema nervoso central.", "Curto alcance", null, null, null, null, null, "Palma Perturbadora", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma Vendaval Dilacerante da Besta", "A Palma Vendaval Dilacerante da Besta é uma versão melhorada da Palma Dilacerante da Besta. Ela usa chakra para infundir com e condensar uma quantidade maciça de chakra de vento para formar uma maciça garra demoníaca que se estende desde a mão do usuário para atacar e agarrar um alvo, bem como desviar qualquer arma ou ataque. Dependendo da sua utilização, o utilizador pode manipular o tamanho e comprimento da garra para atacar e defender a qualquer alcance. Além de tornar-se uma arma mortal, a garra que se estende também carrega uma força tremenda que pode devastar tudo em seu caminho, sem afetar o usuário, tornando-o útil para a remoção dos objetos sólidos e pesados.", "Todos os alcances", null, null, "獣破烈風掌", "Jūha Reppū Shō", "Palma Vendaval Dilacerante da Besta", null, null, "Palma da Onda Dilacerante da Besta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Palma da Lâmina de Água", "Tobirama ataca o oponente com uma combinação de taijutsu e Liberação de Água. Ele começa com um simples soco seguido por três chutes e então levanta o oponente para o ar onde com sua maestria em Liberação de Água e uma lâmina de água materializada em suas mãos, ele destrói o oponente jogado ao ar.", "Curto alcance", null, null, "水掌刃すいしょうじん", "Suishōjin", "Palma da Lâmina de Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pampas", "Kimimaro usa sua espada de osso para golpear o oponente com velocidade surpreendente, em diferentes áreas vitais do corpo. Este ataque pode ser usado repetidamente em sucessão para não dar ao oponente a chance de atacar de volta.", "Curto alcance", null, null, "芒", "Susuki", "Pampas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pancada!", "Konohamaru salta sobre eu oponente e acerta um único e poderoso golpe.", "Curto alcance", null, null, "ごっつんこ!", "Gottsunko!", "Pancada!", null, null, null);
