INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Par de Donzelas", "Kankurō invoca a Formiga Negra e faz este esgueirar-se por trás do oponente, imobilizando seus braços. Kankurō depois usa Corvo que solta uma bomba de sua boca e, em seguida, corre em direção ao oponente fazendo com que a bomba exploda.", "Curto alcance", null, null, "一対の処女", "Ittsui no Shojo", "Par de Donzelas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parafusos de Flor", "Sasuke corta o oponente com sua espada, depois disso, ele e Sakura desferem socos ao oponente no ar e, em seguida, chuta-o para o chão.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Paraíso da Terra Pura", "Tsunade soca o chão, criando um tremor que lança seu oponente no ar. Em seguida, ela concentra chakra em seu punho e soca o oponente no estômago com uma força inacreditável, lançando o oponente no ar novamente e causando uma imensa destruição ao redor, devido ao impacto.", "Curto alcance", null, null, "極楽浄土ごくらくじょうど", "Gokuraku Jōdo", "Paraíso da Terra Pura", "Terra Pura do Paraíso", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parcela da Legenda", "Tsunade soca o oponente para Jiraiya, que invoca Gama, como um sapo guerreiro, para atacar o oponente para o chão. Gama então esmaga o oponente ao cair sobre ele.", "Curto alcance", null, null, "伝説の片鱗", "Densetsu no Henrin", "Parcela da Legenda", "Fragmento de uma Lenda", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pardais Sequenciais", "Sai usa o Desenho de Imitação da Super Besta para liberar uma rápida enxurrada de aves de tamanho pequeno. Essa técnica foi forte e rápida o suficiente para colidir com a Senbon de Agulha de Cabelo de Fuguki Suikazan. No anime, Inojin usa essa técnica em menor escala, porém com eficácia suficiente para impedir os movimentos de Chōji ao se defender.", "Curto alcance", null, null, "連燕雀れんえんじゃく", "Ren'enjaku", "Pardais Sequenciais", "Pardais em Série", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parede Dianteira de Areia", "Gaara cria dois pilares sólidos de areia que ele pode usar para atacar seu oponente, comprimindo-o em meio das paredes de areia.", "Curto a Médio alcance", null, null, "砂陣壁", "Sunajin Kabe", "Parede Dianteira de Areia", "Parede de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parede Protetiva de Areia Aérea", "Esta técnica de defesa aérea cria um escudo gigante, suspenso no ar. Composto por uma grande quantidade de areia rica em minerais, dando uma grande resistência a tal. Além disso, a areia tem a sua força defensiva aumentada pelo chakra fluindo através dela. Ele era forte o suficiente para proteger Sunagakure do C3 de Deidara e auxiliar Ōnoki a parar um dos meteoros lançados por Madara.", "Médio a Longo alcance", "Rank –", null, "空砂防壁くうさぼうへき", "Kūsa Bōheki", "Parede Protetiva de Areia Aérea", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parede de Ferro Selada", "Um selo colocado na porta do Templo do Fogo, a fim de se defender contra os intrusos do mundo exterior. As duas imagens colocadas em ambos os lados da porta formam uma barreira. O selo só pode ser cancelado com o chakra Senzoku no Sai. No entanto, como mostrado quando Kakuzu usou a Liberação de Terra: Domu para atacar o portão, um poderoso ataque físico pode destruir o selo.", null, null, null, "封印鉄壁", "Fūin Teppeki", "Parede de Ferro Selada", null, null, "Selo da Parede de Aço");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parede de Liberação de Relâmpago", "Esta técnica é uma exclusividade dos bandidos e shinobi de Yumegakure que tornam-se capazes de criar uma parede de com chakra relâmpago, que repele qualquer um que toca e provoca vários danos à ele, como a eletrocussão.", "Curto alcance", null, null, "雷遁の壁らいとんのへき", "Raiton no Heki", "Parede de Liberação de Relâmpago", "Parede do Estilo Relâmpago", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parede de Prisão Adamantina", "Transformando-se em um bastão adamantino, Enma clona a si mesmo e em seguida prende o inimigo em um lattice. O muro da prisão tem uma incrível resistência e é impossível de se quebrar. Quando usado para defesa, o inimigo não tem chances de atacar.", "Curto alcance", null, null, "金剛牢壁こんごうろうへき", "Kongō Rōheki", "Parede da Prisão Vajra", null, "Prisão de Diamante", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Parede de Quarentena", "Usando seu escudo, Renga cria uma copacta barreira de chakra. É resistente o suficiente para defender uma imensa explosão de bomba.", "Curto a Médio alcance", null, null, "遮断壁", "Shadanheki", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Passo Gentil da Besta com Cauda dos Punhos de Leões Gêmeos", "Naruto e Hinata combinam o Rasengan com o Passo Gentil dos Punhos de Leões Gêmeos para atacar seu oponente e, por fim, Naruto finaliza com uma Bola da Besta com Cauda.", "Curto a Longo alcance", null, null, "柔歩尾獣双獅拳じゅうほびじゅうそうしけん", "Jūho Bijū Sōshiken", "Passo Gentil da Besta com Cauda dos Punhos de Leões Gêmeos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Passo Gentil das Palmas Gêmeas", "O usuário prepara seu Punho Gentil e corre contra seu oponente, acertando-o com dois golpes de palma reforçada, danificando-o e repelindo-o.", "Curto alcance", null, null, "柔歩双掌じゅうほそうしょう", "Jūho Sōshō", "Passo Gentil das Palmas Gêmeas", "Precipitação da Palma Dupla", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Passo Gentil dos Punhos de Leões Gêmeos", "O Passo Gentil dos Punhos de Leões Gêmeos é uma técnica secreta do estilo do Punho Gentil de alto nível, uma técnica somente ministrada na família fundamental do Clã Hyūga.", "Curto alcance", "Rank –", null, "柔歩双獅拳じゅうほそうしけん", "Jūho Sōshiken", "Passo Gentil dos Punhos de Leões Gêmeos", "Passo Gentil: Punho dos Leões Gêmeos", "Técnica do Passo Leve Punho dos Leões Gêmeos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Passo Gentil dos Punhos de Leões Gêmeos Espirais", "Os usuários unem seus chakra e formam uma face de leão gigante com um Rasengan no interior, formando-os onde se encontra as mãos unidas dos usuários. A técnica mostrou-se forte o suficiente para destruir o Tenseigan de Hamura por completo.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pavão da Manhã", "O Pavão da Manhã (朝孔雀 Asa Kujaku) é um taijutsu de morte-certa que nasceu na filosofia de Might Guy. O princípio deste taijutsu é de aumentar rapidamente as habilidades do corpo, abrindo, inclusive, o sexto dos oito portões, e, em seguida, atacar o inimigo com inúmeros socos.", "Curto alcance", "Rank A", null, "朝孔雀あさくじゃく", "Asa Kujaku", "Pavão da Manhã", null, "Pavão do Amanhecer", "Asa Kujaku");
