INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pedregulho de Areia Explosiva", "Gaara condensa sua areia em várias rochas flutuantes. Então, ele pode usá-las como projéteis ou explosivos, criando um campo minado em qualquer lugar em que o inimigo estiver a fim de impedi-lo de escapar.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Peguei Você!", "Konohamaru usa a Técnica da Capa da Invisibilidade para se ocultar do oponente e, quando este o ataca, ele salta no ar, acertando-o com um golpe.", "Curto alcance", null, null, "かかったな!", "Kakattana!", "Peguei Você!", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Peido", "Naruto peida próximo ao inimigo para envenená-lo. Esta habilidade é muito eficaz contra membros do clã Inuzuka, por exemplo, que, devido a seus olfatos aguçados, tornam-se vulneráveis ao odor.", "Curto a Médio alcance", null, "Tigre", "おなら", "Onara", "Peido", "Peido Preso (オナラ縛り, Onara Shibari)", null, null);
