INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pelota de Osso", "Kimimaro dispara de três a cinco pontas afiadas de osso no oponente.", "Curto a Médio alcance", null, null, "骨飛礫", "Hone Tsubute", "Pelota de Osso", "Pelota de Osso Voador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Penas Explosivas", "O pássaro de San libera numerosas penas com grande precisão que explodem de forma semelhante ao papel explosivo.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Penetração das Emoções Ardentes", "Lee abre vários portões de chakra, e depois avança contra o oponente, levantando-o para o céu como um foguete. Depois de atingir uma certa distância, Lee bate atacando-o para o solo.", "Curto alcance", null, null, "突き抜ける熱き思いつきぬけるあつきおもい", "Tsukinukeru Atsuki Omoi", "Penetração das Emoções Ardentes", "Esmagamento de Paixão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Performance Mecânica: Queda da Cortina", "Kankurō usa essa técnica com Karasu e Kuroari. Primeiro, Karasu prende seu oponente e o atira no ar, então Kuroari salta sobre ele, esmagando-o contra o chão.", "Curto alcance", null, null, "カラクリ演技・緞帳降ろし", "Karakuri Engi: Donchō Oroshi", "Performance Mecânica: Queda da Cortina", "Show da Marionete: Queda da Cortina", null, null);
