INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Performance da Marionete: Conquista Habilidosa com um Corpo Humano", "Uma técnica de execução com marionete, onde linhas de chakra ficam ligadas a vários pontos no corpo de uma pessoa, a cabeça, tronco, dois braços e duas pernas. A pessoa que está sendo manipulada ganha a habilidade de usar não só as suas próprias técnicas, mas as habilidades do marionetista também.", "Curto a Médio alcance", "Rank B", null, "操演・人身冴功", "Sōen: Hitomi Gokū", "Performance da Marionete: Conquista Habilidosa com um Corpo Humano", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Performance da Marionete: Decapitação Maquiavélica", "Usando as marionetes Mãe e Pai, Chiyo faz com que elas saquem suas armas — uma espada e um chicote com lâminas, respectivamente — e ataquem o oponente. O primeiro ataque é feito pela marionete Mãe, que corta horizontalmente o oponente, enquanto o segundo ataque é feito pela marionete Pai, que bate seu chicote verticalmente. Para finalizar a sequência, a marionete Mãe posiciona a espada na altura do abdômen e gira verticalmente em torno de si enquanto no ar, causando uma dilaceração sequencial.", "Longo alcance", null, null, "操演・斬奸", "Sōen: Zankan", "Performance da Marionete: Decapitação Maquiavélica", "Performance da Marionete: Corte do Mal", null, null);
