INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poder Adamantino: Acala", "Hashirama chuta o oponente para longe e levanta algumas gavinhas de madeira para golpear o inimigo. Com o oponente deitado no chão, Hashirama reúne uma grande quantidade de chakra em seu punho direito, salta e leva-o no tronco de seu inimigo. O impacto faz com que o chão quebre e uma pequena floresta entre em erupção a partir dele.", "Curto alcance", null, null, "金剛力・不動", "Kongōriki: Fudō", "Poder Vajra: Acala", "Poder Adamantino: Acala", null, null);
