INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poder da Gelel", "Quando Haido se transforma em uma criatura humanoide monstruosa através dos poderes da Pedra de Gelel, ele é capaz de disparar um laser gigante na parte de trás da sua mão direita, onde a pedra está incorporada. A explosão é muito poderosa e pode matar a maioria das pessoas com apenas uma descarga. O laser pode ser ainda mais aumentado para disparar uma explosão muito maior.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);