INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ōdachinagi", null, "Curto alcance", null, null, "大太刀薙ぎおおだちなぎ", "Ōdachinagi", "Rasteira da Grande Espada de Guerra", "Rasteira de Odachi", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Tate Eboshi", "Depois de usar sua Técnica de Hidratação para se fundir com uma fonte de água, Suigetsu pode formar uma onda gigante que se parece com um peixe demoníaco. Suigetsu pode utilizar este forma para lutar contra adversários maiores, ou ele pode usá-la para proteger os aliados de ataques. Apesar de ter sido forte o suficiente para empurrar de volta fisicamente Killer B em seu Modo Bijū, não foi forte o suficiente para suportar a Bola da Besta com Cauda a queima-roupa, que deixou Suigetsu inconsciente, fazendo-o cair em um estado gelatinoso.", "Curto alcance", null, null, "水遁・隋烏帽子すいとん・たてえぼし", "Suiton: Tate Eboshi", "Liberação de Água: Enterro das Crianças do Chapéu de Corvo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Liberação de Água: Tiro da Arma", "O usuário molda o chakra, e converte-o em água e, em seguida, cospe-o para fora, na forma de bolas condensadas. A alta velocidade de ataque que a bola de água possui tem o poder tanto para matar como para dizimar fogo real. Embora esta seja uma técnica de Liberação de Água, que pode ainda ser empregue um lugar onde não há água disponível, que é uma grande vantagem. Também é possível aumentar o poder de destruição das próprias esferas construindo o chakra dentro delas. O número de balas é determinada enquanto molda o chakra. Ao tirar vantagem da gravidade, as balas podem aumentar brutalmente no poder. Com um usuário do tamanho de Gamabunta, as balas podem realmente ser mais como balas de canhão, tornando-as muito mais mortal.", "Todos os alcances", "Rank C", "Tigre", "水遁・鉄砲玉", "Suiton: Teppōdama", "Liberação de Água: Tiro", null, "Estilo da Água: Canhão Aquático", "Estilo Água: Tiro d'água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poder do Hokage", "Tsunade entra no Modo Sannin e soca o abdômen de seu inimigo (alertando-o para se preparar) e em câmera lenta parece que ele ou ela vai ser se partir em dois. Ele é empurrado para trás a uma velocidade tremenda, porém, levando tudo por um campo de árvores até que sejam finalmente parado por uma grande pedra, que faz com que o oponente de Tsunade leve ainda mais danos. Pouco tempo depois, um vulcão entra em erupção no fundo como resultado do poder do soco, atingindo o campo de batalha. Tsunade, endireitando-se para cima, fica se perguntando se ela talvez foi longe demais.", "Curto alcance", null, null, "火影の力", "Hokage no Chikara", "Poder do Hokage", "O Poder da Hokage", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poder do Nove-Caudas", "Kurama é capaz de disparar um grande fluxo de chamas através de sua boca. As chamas tem poder suficiente para quebrar pedras enormes, e até mesmo causar danos ao Chibaku Tensei de Nagato.", "Longo alcance", null, null, "九尾の力", "Kyūbi no Chikara", "Poder do Nove-Caudas", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poder do Rugido Dourado", "Tirando proveito de sua grande força física, Kongō corre em direção ao seu oponente e o acerta com um movimento de laço na área do pescoço, derrubando-o.", "Curto alcance", null, null, "金轟力", "Kin Todoroki-Ryoku", "Poder do Rugido Dourado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poder do Sábio", "Jiraiya estende seu cabelo para atacar seu oponente, de modo a jogá-lo para longe, e, em seguida, ele cospe fogo a fim de incinerá-lo. Atordoado, o oponente é incapaz de acompanhar os movimentos de Jiraiya, que, logo, cria um Rasengan em chamas. Esse Rasengan explode e, quando em contato com o corpo do oponente, gera um pequeno redemoinho de fogo, longe de Jiraiya.", "Curto alcance", null, null, "仙人の力せんにんのちから", "Sennin no Chikara", "Poder do Sábio", "Poder Sábio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poderosa Lança de Pó de Ouro", "Rasa cria uma maciça e poderosa lança em forma de broca usando seu pó de ouro, perfurando o oponente.", "Curto a Médio alcance", null, null, "砂金剛槍さきんごうそう", "Sakin Gōsō", "Poderosa Lança de Pó de Ouro", "Lança de Pó de Ouro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Poeira da Palma Esquerda e Fíbula da Perna Direita", "Sakon e Ukon correm contra o oponente e separam-se para distrair o oponente, passando por ele e mantendo-o no meio. Ao chegar nas costas do oponente, Sakon e Ukon chutam o oponente nas costas, onde é difícil defender.", "Curto alcance", null, null, "掌左の塵と右脚腓骨てのひらひだりのちりとみぎあしひこつ", "Tenohira Hidari no Chiri to Migi Ashi Hikotsu", "Poeira da Palma Esquerda e Fíbula da Perna Direita", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pontapé Flash da Folha", "Rock Lee salta e executa um chute descente com o calcanhar contra o oponente, usando pura força física. Esse golpe é forte o suficiente para destruir rochas.", "Curto alcance", null, null, "木ノ葉閃蹴", "Konoha Senshū", "Pontapé Flash da Folha de Árvore", "Pontapé Flash da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pontapé Giratório dos Clones das Sombras", "Naruto usa seus clones das sombras para desferir uma série de golpes em seu oponente, e terminando com o giro de um de seus clones das sombras a fim de atacar o oponente. Como um método alternativo, o clone é lançado para chutar o oponente.", "Curto alcance", null, null, "影分身回し蹴りかげぶんしんまわしげり", "Kage Bunshin Mawashigeri", "Pontapé Giratório dos Clones das Sombras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pontapé Serial", "O usuário chuta seu oponente várias vezes em rápida sucessão e termina com um chute poderoso que envia o adversário à voo.", "Curto Alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pontapé Voador", "Depois de manifestar as asas de Chōmei, Fū voa no ar e desce, acertando um pontapé no oponente.", "Curto alcance", null, null, "飛翔一脚ひしょうひとあし", "Hishō Hitoashi", "Pontapé Voador", "Pé Único Voador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pontapé do Leão", "O usuário cria uma distração no oponente, seja através de golpes de taijutsu ou com algum parceiro distraindo, e finaliza com um poderoso pontapé em seu oponente, lançando-o longe.", "Curto alcance", null, null, "獅子一脚", "Shishi Hitoashi", "Pontapé do Leão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pontos de Pressão de Dano e Morte", "Guren abre dois Tenketsu; Dano e Morte, para aumentar consideravelmente seu chakra, mudando sua força de vida em chakra, permitindo o uso de técnicas como o Liberação de Cristal: Corda da Glória. A quantidade de chakra liberada é tanta que faz com que a água forme um vórtice em torno dela, semelhante como é causado pela abertura do Keimon dos Oito Portões, também com os olhos momentaneamente completamente brancos, seu chakra é tão denso que se torna visível ao seu redor.", null, null, null, "死傷点穴", "Shishō Tenketsu", "Pontos de Pressão de Dano e Morte", null, null, "Pontos de Chakra: Vida e Morte");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Portal Espaço-Temporal", "São técnicas que permitem aos usuários manipular o espaço-tempo. Ao manipular um ponto específico do espaço, o usuário pode deformar qualquer alvo em um vácuo dimensional e teleportar para outro local instantaneamente.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Possessão da Cobra Branca", "Orochimaru transfere sua consciência para uma pequena cobra branca onde se desliza para dentro do corpo de uma pessoa, permitindo que Orochimaru ganhe o controle de seu corpo e mente. Semelhante à técnica do clã Yamanaka, a Técnica de Troca da Mente do Corpo, o corpo original do usuário fica indefeso depois de tomar o controle do corpo de alguém. Orochimaru usou esta técnica para controlar Kajika e assim testar a extensão dos poderes de Sasuke Uchiha.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa Decapita Presa", "Kiba e Akamaru combinam um em cada ponta para criar uma perfuração gigante que atravessa o oponente, lançando-o no ar, onde Kiba ataca o oponente com um dos pontos de perfuração e através dele, jogando-o de lado.", "Curto alcance", null, null, "牙斬牙", "Gazanga", "Presa Decapita Presa", "Presa Corta Presa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa Giratória de Presa", "O usuário e seu parceiro ninken rolam a uma velocidade feroz em forma de rolo, o que causa muitos danos poderosos quando o contato é feito com o alvo.", "Curto a Médio alcance", null, null, "牙転牙がてんが", "Gatenga", "Presa Giratória de Presa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa Gêmea Celestial", "Kiba e Akamaru saltam e utilizam sua técnica Presa Passando Presa. Os dois então se fundem criando uma versão maior e mais forte da Presa Passando. Eles então, atacam seu oponente com uma força monstruosa, que destrói o solo.", "Todos os alcances", null, null, "天双牙", "Tensōga", "Técnica Secreta da Arte Marcial da Besta Humana: Presa Gêmea Celestial", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa Lobo Presa", "O homem e a besta se transformam em uma única criatura gigantesca com o uso da Transformação Combinada da Besta Humana: Lobo de Duas Cabeças, giram o seu corpo e então atacam o inimigo violentamente. A velocidade da rotação cria um vácuo ao redor do usuário, que danifica o inimigo mesmo sem tocá-lo diretamente. A excessiva velocidade da técnica bloqueia o campo de visão do usuário, sendo que a localização do alvo é feita ao marcar o alvo com antecedência, seguindo-o através do cheiro. No anime, se o usuário é habilidoso o suficiente, isso é desnecessário. O poder da técnica foi tão grande que se mostrou capaz de amassar o Rashōmon. No entanto, ela possui seus malefícios, uma vez que cansa o usuário rapidamente.", "Médio a Longo alcance", "Rank B", null, "牙狼牙がろうが", "Garōga", "Presa Lobo Presa", null, "Trespassar das Presas Lupinas", "Presa do Lobo Sobre Presa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa Passando", "O usuário gira a uma velocidade feroz e pousa muitos ataques poderosos como besta quando o contato é feito com o alvo. O dano que ela pode infligir a um alvo humano pode ser extremamente prejudicial, pois pode facilmente rasgar com galhos grossos de árvores e muro de terra aprimorado com chakra; todos os quais com um raio mais espesso do que o próprio usuário.", "Curto a Médio alcance", "Rank D", null, "通牙つうが", "Tsūga", "Presa Passando", null, "Redemoinho de Presas", "Presa de Tunelamento");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa Passando Presa", "O usuário e seu parceiro ninken rotacionam a uma velocidade feroz e desferem muitos ataques poderosos como bestas quando o contato é feito com o alvo. A força desse ataque é forte o suficiente para perfurar uma pedra. Este ataque também pode ser realizado individualmente.", "Curto alcance", "Rank C", null, "牙通牙がつうが", "Gatsūga", "Presa Passando Presa", "Taijutsu do Homem-Besta: Presa Sobre Presa", "Presa Sobre Presa", "Taijutsu Final do Homem-Besta: Presa Sobre Presa");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presa de Inseto Gigante: Sessenta e Quatro Palmas", "O Time Kurenai se prepara para atacar. Kiba cria um clone da sombra, Shino prepara seus insetos e Hinata concentra chakra em suas mãos. Em seguida, Kiba, seu clone e Akamaru se transformam em um Lobo de Três Cabeças e usam a Cauda Perseguidora de Presa da Presa Giratória de Presa. Hinata se junta ao golpe, deixando-o azul devido a seu chakra, e acerta sessenta e quatro golpes no oponente, aplicando os insetos de Shino. Shino usa um último golpe, que consome o oponente, finalizando com uma grande explosão.", "Curto a Médio alcance", null, null, "大蟲牙・六十四掌だいちゅうが・ろくじゅうよんしょう", "Daichūga: Rokujūyon Shō", "Presa de Inseto Gigante: Sessenta e Quatro Palmas", "Grande Presa de Inseto: 64 Palmas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presas Relâmpago", "Usando suas espadas especiais e posicionando-as uma de cada lado de seu corpo, Ameyuri rapidamente gira liberando um fluxo de chakra relâmpago pelas espadas, sendo capaz de controlar sua extensão.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presas de Raios", "Ao reunir suas espadas gêmeas, Raiga pode enviar uma essência elétrica para as nuvens, permitindo-lhe criar relâmpagos em qualquer local desejado.", "Todos os alcances", null, null, "雷の牙", "Ikazuchi no Kiba", "Presas de Raios", null, null, "Arte Ninja: Presa Relâmpago");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Presas do Rei Macaco", "Hiruzen dá um pontapé no intestino do oponente, então ele amplia o Rei Macaco: Enma na sua forma de bastão. Enma aparece na ponta do bastão, atacando o alvo com suas presas.", "Curto alcance", null, null, "猿王の牙えんおうのきば", "En'ō no Kiba", "Presas do Rei Macaco", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prescrição de Fortalecimento: Injeção de Chakra", "Esta técnica cria cobras negras que forçosamente se conectam com uma pessoa e as injeta chakra medicamente alterado. Este chakra extra permite que o usuário utilize ninjutsu elemental de alto nível. Ele também permite que o usuário use técnicas elementais que ele não seria capaz de dominar normalmente. O chakra não é infinito, e deve ser reposto ao longo do tempo. Além disso, apenas um tipo de chakra elemental pode ser injetado na pessoa de cada vez, como Neji observou que Shizuku foi visto com um elemento completamente diferente do encontro anterior.", "Curto alcance", null, null, "強化処方・チャクラ注入", "Kyōka Shohō: Chakura Chūnyū", "Prescrição de Fortalecimento: Injeção de Chakra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pressurização", "Jūgo, após alterar sua musculatura através de sua transformação, golpeia o oponente sucessivamente.", "Curto alcance", null, null, "加圧", "Kātsu", "Pressurização", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pressão de Pó de Ouro", "Rasa cria um enorme cubo de pó de ouro acima de sua cabeça, leva-o acima do oponente e esmaga-o com o cubo maciço.", "Curto alcance", null, null, "砂金圧搾器さきんあっさくき", "Sakin Assakuki", "Pressão de Pó de Ouro", "Pressão Gigante de Pó de Ouro", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão Celestial da Espada de Fogo", "O usuário acende uma grande chama em sua mão direita, que pouco depois toma a forma de uma espada. Ao atingir o inimigo, ela incendeia o corpo dele por completo. A espada se mostrou capaz de perfurar carne facilmente.", "Curto alcance", null, null, "天牢火劍てんろうかけん", "Tenrō Kaken", "Prisão Celestial da Espada de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão Restritora de Areia", "Uma técnica de captura característica de Gaara. Usando areia comum, um espaço perfeitamente hermético é criado, em que o inimigo é capturado. Uma enorme quantidade de areia envolve completamente o alvo de todas as direções e captura-o. A velocidade, a dureza, e do âmbito da areia dependem da quantidade de chakra vertido sobre ela. Gaara pode ajustar as propriedades da areia para o tamanho e as características do alvo. Não importa se o alvo está no chão ou voando no ar, é quase impossível escapar desta técnica. No entanto, mesmo se alguém escapa com sucesso a partir dela, Gaara pode facilmente usar uma de suas técnicas ofensivas, como o Caixão de Areia, para atacar imediatamente o alvo.", "Todos os alcances", "Rank B", null, "砂縛牢さばくろう", "Sabakurō", "Prisão Restritora de Areia", "Caixão da Tempestade de Areia", null, "Prisão de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão da Boca da Cobra", "O usuário prende o adversário e ele mesmo na boca de uma cobra. O estômago da cobra é muito parecido com elástico, podendo até mesmo resistir a Gamabunta, uma criatura de tamanho similar, sendo invocado dentro dele.", "Todos os alcances", null, null, "蛇口縛り", "Hebi Kuchi Shibari", "Prisão da Boca da Cobra", null, null, "Arte Ninja: Armadilha da Boca de Cobra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão de Arame", "Esta técnica utiliza fios de aço, para formar uma forte cúpula de proteção. Esta cúpula pode repelir a maioria dos ataques básicos de taijutsu. O utilizador pode ainda alterar a forma do fio no interior da cúpula, conforme necessário, transformando-o de uma estrutura defensiva para uma corrente com facilidade.", "Curto alcance", null, "Tigre → Cobra → Javali", "籠の目", "Kago no Me", "Prisão de Arame", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão de Fumaça do Cheiro Destruidor", "Enquanto luta contra um inimigo e caso queira fugir da batalha ou se ocultar, Kigiri é capaz de produzir uma cortina de fumaça extremamente grossa que imediatamente faz o adversário ficar com tosse e ocultar a visão do mesmo, ela também abrange até o cheiro do usuário e seus aliados. Ele então pode atacar a vontade. O utilizador e aqueles que treinaram sobre a técnica podem ver claramente através da cortina de fumaça.", "Todos os alcances", null, null, "滅臭煙牢めっしゅうえんろう", "Messhū Enrō", "Prisão de Fumaça do Cheiro Destruidor", null, null, "Prisão da Fumaça de Fuga");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão do Funeral de Areia", "Gaara usando sua habilidade de manipular a areia, afrouxa o chão abaixo do inimigo e pega-o em um redemoinho de areia. O inimigo engolido afunda até duzentos metros de profundidade, enquanto completamente incapaz de mover um músculo. Em seguida, a areia colada seus membros se aplica pressão sobre todo o seu corpo, por isso, eles não podem sequer se contorcer um dedo. A pressão resultante de ser enterrado em uma profundidade tão extrema certamente mata qualquer ser humano normal, se não de asfixia, então a partir do fato de que todos os seus ossos se quebram.", "Curto a Médio alcance", null, "Carneiro", "獄砂埋葬", "Gokusa Maisō", "Prisão do Funeral de Areia", null, "Funeral na Prisão de Areia", "Funeral da Prisão de Areia");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prisão em Forma de Rede", "Com esta técnica, o usuário pode manipular teias, emitindo teia de sua boca ou mãos e manipulá-la como uma rede, na intenção de prender os adversários.", "Todos os alcances", null, "Javali → Carneiro → Cao → Rato[1] ou (Cão)[2]", "網状牢", "Mōjōrō", "Prisão em Forma de Rede", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Processamento de Areia", "O Processamento de Areia consiste na confecção e manipulação da areia através da infusão de chakra.", null, null, null, "砂処理", "Sunashori", "Processamento de Areia", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Profusão das Cem Flores", "Enquanto estiver usando o poder da Névoa de Veneno Inodoro, Karenbana libera muitas pétalas de flores de seu corpo. Estas pétalas fluem através do ar e um circulam em torno de seu alvo. Karenbana então se move através das flores, fazendo parecer como se ela estivesse dissolvendo em pétalas, usando-as como uma parede de modo que seu alvo não pode ver os ataques recebidos. A fraqueza desta ilusão é que ele só esconde seu visual, quando Naruto liberou uma onda de choque generalizado para bater em suas costas, enquanto Sakura aproveitou ouvindo os passos de Karenbana sobre vidro quebrado para identificar sua localização.", "Curto alcance", null, null, "百花繚乱", "Hyakkaryōran", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projéteis de Perfuração dos Dez Dedos", "Ossos endurecidos dos dedos são atirados no inimigo, com um movimento giratório adicionado às balas esqueléticas. Uma colisão direta escava a pele, carne e osso. Uma vez que os ossos do utilizador podem ser regenerados, a taxa de tiros do arsenal do utilizador é ilimitada. Esta técnica é essencial para Kimimaro para os seus ataques de longa distância. No anime, esse ataque foi mostrado forte o suficiente para facilmente perfurar a armadura de aço usada pelos samurai.", "Curto a Médio alcance", "Rank –", null, "十指穿弾てしせんだん", "Teshi Sendan", "Projéteis de Perfuração dos Dez Dedos", null, "Dez Dedos Perfuradores", "Tiros dos Dedos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil Ascendente Dragoníaco do Avestruz", "Condor dá um chute ascendente no estômago do oponente.", null, null, null, "ダチョウ昇龍弾", "Dachō Shōryūdan", "Projétil Ascendente Dragoníaco do Avestruz", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil Saltitante da Concha Torre", "Transformado como Isobu, Yagura enrola-se em uma bola e salta, permitindo-se ou machucar um adversário, ou mover-se rapidamente ao redor.", "Curto alcance", null, null, "跳弾甲櫓ちょうだんこうろ", "Chōdan Kōro", "Projétil Saltitante da Concha Torre", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil da Fumaça Demoníaca", "Ao usar seu lançador de pulso, Kihō bombardeia o inimigo com uma enxurrada de pequenas granadas de fumaça, explodindo e criando uma espessa nuvem de fumo.", "Todos os alcances", null, null, "魔煙弾", "Maegan", "Projétil da Fumaça Demoníaca", null, null, "Esfera de Fumaça Escura");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil da Queda do Brilho Elevado", "Fū cega seu oponente com Pó de Escama, agarra-o, voa com ele no ar manifestando as asas de Chōmei, e joga-o de volta para o chão.", "Curto alcance", null, null, "翔耀降弾", "Shōyō Kōdan", "Projétil da Queda do Brilho Elevado", "Batida Descendente Brilhante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil de Grande Chama Inigualável", "Sasuke chuta e salta sobre seu oponente, novamente dando-lhe um chute, agora em sua cabeça. Aproveitando a baixa-guarda, chuta-o para o alto e salta para dar um soco que o joga de volta ao chão. Enfim, Sasuke finaliza com um grande projétil de fogo.", "Curto alcance", null, null, "無双豪炎弾むそうごうえんだん", "Musō Gōendan", "Projétil de Grande Chama Inigualável", "Ultra Projétil de Fogo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil de Liberação de Fogo: 'Chamas'", "O Projétil de Liberação de Fogo: 'Chamas' cria uma massa de chamas que pode ser atirada como um projétil ou liberada na forma de um fluxo na direção do oponente.", "Longo alcance", null, "Fogo", "火遁弾・「焔」", "Katon Dan: 'Homura'", "Projétil de Liberação de Fogo: 'Chamas'", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil de Liberação de Relâmpago: Respiração Violenta", "O Projétil de Liberação de Relâmpago: Respiração Violenta cria um relâmpago condensado através do punho do usuário. Ao empurrar o punho para frente, um poderoso raio de longo alcance é desencadeado, capaz de atravessar a cabeça de uma grande cobra. Ele também pode ser conduzido através de uma fonte de água para atingir múltiplos alvos.", "Longo alcance", null, "Relâmpago", "雷遁弾・威吹", "Raiton Dan: Ibuki", "Projétil de Liberação de Relâmpago: Respiração Violenta", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil de Liberação de Vento: Repulsão", "Utilizando o Kote acoplado em seu braço, Katasuke Tōno estende seu braço e cria uma onda de vento capaz de derrubar uma pessoa. No entanto, se o usuário não possuir uma boa base, ele é quem é derrubado.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil de Liberação de Vento: Shuriken", "É uma técnica utilizada por Boruto, que mistura o shurikenjutsu que ele aprendeu com o Sasuke e o conceito de transformação da natureza que ele aprendeu com Konohamaru.", "Longo alcance", null, "Vento", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil de Liberação de Água: Orca", "O Projétil de Liberação de Água: Orca libera uma enchente de água que transborda por todo o campo de batalha, restringindo os movimentos dos oponentes com a intensidade das ondas que a técnica produz. Essa técnica pode ser combinada com uma técnica de relâmpago, a fim de eletrocutar seu alvo.", "Médio alcance", null, "Água", "水遁弾・王琉華", "Suiton Dan: Oruka", "Projétil de Liberação de Água: Orca", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil do Ataque Suicida de Vento Demoníaco", "Naruto cria um clone da sombra que ele agarra e transforma em uma fūma shuriken que ele lança em seu adversário. Após cumprir o seu objetivo a shuriken volta para a sua forma de clone das sombras e ataca o oponente desprevenido.", "Médio a Longo alcance", null, null, "風魔特攻弾", "Fūma Tokkōdan", "Projétil do Ataque Suicida de Vento Demoníaco", "Bomba do Demônio de Vento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil do Clarão da Besta com Cauda", "Naruto, enquanto no Modo de Chakra do Nove-Caudas, realiza com rapidez uma combinação de socos e chutes no oponente, deixando rastros dourados para trás e finalizando com uma rasteira que joga o oponente para cima.", "Curto alcance", null, null, "尾獣閃光弾", "Bijū Senkōdan", "Projétil do Clarão da Besta com Cauda", "Projétil do Flash da Besta com Cauda", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil do Dragão de Água e Relâmpago", "Mei Terumī expele água em forma de um dragão e é assistida por A que adiciona sua Liberação de Relâmpago para o dragão de água. A técnica é capaz de entorpecer o alvo para restringir seus movimentos.", "Longo alcance", null, null, "雷水龍弾らいすいりゅうだん", "Raisuiryūdan", "Projétil do Dragão de Água e Relâmpago", null, "Ataque-Relâmpago do Dragão Marinho", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil do Furacão da Folha", "O usuário, estando no ar, acerta um poderoso chute caído, o qual envia o oponente voando aos ares.", "Curto alcance", null, null, "木ノ葉疾風弾", "Konoha Shippūdan", "Projétil do Furacão da Folha da Árvore", "Projétil do Furacão da Folha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil do Vendaval", "Após ativar o nível dois do Selo Amaldiçoado do Céu, Sasuke pula no ar e começa a girar em direção do inimigo, podendo infringir dano. Para um dano adicional, Sasuke pode usar suas unhas para dilacerar o oponente enquanto gira, bem como alternar com outros golpes provenientes das vantagens concedidas pelo seu selo amaldiçoado.", "Curto alcance", null, null, "烈風弾", "Reppūdan", "Projétil do Vendaval", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Projétil do Óleo de Sapo", "O usuário converte seu chakra em um óleo extremamente pegajoso e cospe isso como uma enorme massa. Quando este óleo atinge o alvo, todo o seu corpo é coberto, e impede o seu movimento. Simplesmente lavar o óleo com água regular não seria fácil. Além disso, se uma técnica de Liberação de Fogo é lançada no alvo após esta técnica ser utilizada, ele estará envolto em chamas em um instante.", "Curto a Médio alcance", "Rank C", null, "蝦蟇油弾", "Gamayudan", "Projétil do Óleo de Sapo", null, "Bola de Óleo do Sapo", "Bomba do Óleo de Sapo");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Proteção Contra Genjutsu", "O usuário pode usar esta técnica para colocar bloqueios mentais na mente do alvo que contém informações valiosas e, que foi capturado e interrogado. As barricadas parecem tradicionais portas estilizadas japonesas de correr. De acordo com Inoichi Yamanaka, é muito eficaz contra leitura mental ou qualquer ataque que venha afetar a mente do indivíduo em si.", null, null, null, "幻術プロテクト", "Genjutsu Purotekuto", "Proteção Contra Técnica de Ilusão", null, null, "Bloqueio de Genjutsu");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Proteção de Oito Trigramas Cento e Vinte e Oito Palmas", "Esta técnica é meramente uma versão incrivelmente melhor da Proteção de Oito Trigramas Sessenta e Quatro Palmas. Entretanto, esta técnica transmite um estouro sensacional e assim consegue estilhar a esfera maciça feita anteriormente e instantaneamente a técnica aumenta seu tamanho e aumenta seu nível letal e seu nível de arruinamento.", "Curto à Médio alcance", null, null, "守護八卦百二十八掌しゅごはっけひゃくにじゅうはちしょう", "Shugohakke Hyaku Nijūhachi Shō", "Proteção de Oito Trigramas Cento e Vinte e Oito Palmas", "Guardiã de Oito Trigramas Cento e Vinte e Oito Palmas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Proteção de Oito Trigramas Sessenta e Quatro Palmas", "Criada por Hinata, esta é uma técnica altamente versátil que proporciona capacidades ofensivas e defensivas em que ela emite um fluxo constante de chakra de suas mãos, formando lâminas muito finas e afiadas. Enquanto estiver usando esta técnica ofensivamente, Hinata molda o chakra em finas e afiadas lâminas flexíveis, permitindo a ela atacar centenas de alvos com extrema precisão e pode cortar alvos menores, como as abelhas, em pedaços. Quando usada de forma defensiva, Hinata cria lâminas muito maiores, fortes e flexíveis em forma de um arco de chakra que se espalham completamente por todo o seu alcance de ataque, criando efetivamente uma barricada entre ela e seus adversários, rejeitando até mesmo o maior dos adversários.", "Curto a Médio alcance", null, null, "守護八卦六十四掌しゅごはっけろくじゅうよんしょう", "Shugohakke Rokujūyon Shō", "Oito Trigramas: Proteção de 64 Golpes", "8 Trigramas 64 Golpes de Defesa", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prática de Caminhada na Superfície da Água", "Este método de treinamento é usado para obter um melhor controle de chakra. Para fazer isso, o utilizador tem de emitir um fluxo constante de chakra a partir do fundo dos seus pés e usando a força repelente para caminhar sobre a superfície da água. Esta técnica é muito mais difícil de dominar que a Prática de Escalada em Árvore, porque a quantidade de chakra que é necessária muda constantemente.", null, null, null, "水面歩行の業", "Suimen Hokō no Gyō", "Prática de Caminhada na Superfície da Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prática de Concentração da Folha", "Este método de treinamento é feito colocando uma folha sobre a testa de uma pessoa e fazendo-a dirigir todo o seu chakra para a folha, usando-a como um ponto focal.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prática de Escalada em Penhasco", "Este é um método de treinamento que envolve o escalamento em um penhasco íngreme ou alto utilizando apenas um braço. Este treinamento se destina a melhorar a força e disciplina do usuário.", null, null, null, "崖登り業がけのぼりぎょう", "Gake Noborigyō", "Prática de Escalada em Penhasco", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prática de Escalada em Árvore", "Prática de Escalada em Árvore é um método de treinamento utilizado para obter mais habilidades com o controle de chakra. Este treinamento envolve focalizar uma quantia fixa de chakra para o fundo do seus pés, e usar isso para subir em uma árvore sem utilizar as mãos. Se o fluxo de chakra é muito fraco, o usuário perderá sua posição na árvore e cairá. Se ele for muito forte, o usuário será empurrado para longe da árvore, fazendo com que a árvore quebre em torno do ponto de contato e o usuário cairá.", null, null, null, "木登り修業", "Kinobori Shūgyō", "Prática de Escalada em Árvore", null, null, "Jutsu de Escalada em Árvore");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Prática de Marionete Humana", "Esta é uma pratica desenvolvida por Sasori, durante seu tempo em Sunagakure, permitindo-lhe criar marionetes a partir de cadáveres humanos. Desde sua morte, no entanto, parece ter caído no esquecimento.", "Curto a Médio alcance", null, null, "人傀儡の業ひとくぐつのぎょう", "Hitokugutsu no Gyō", "Prática de Marionete Humana", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pulando no Buraco de Fogo e Vento", "Tsunade soca o chão, criando um grande terremoto. Como cobertura, Orochimaru invoca três Rashōmon, criando então em uma forma de triângulo, deixando o oponente sem saída. Então, Orochimaru e Jiraiya usam fogo e vento para criar um grande turbilhão de fogo. Tsunade pula nas chamas e, depois de ativar o Selo Yin, dá um soco tão poderoso que quebra os portões na explosão, causando uma grande explosão.", "Curto a Médio alcance", null, null, "火跳風穴かちょうかざあな", "Kachō Kazaana", "Pulando no Buraco de Fogo e Vento", "Buraco de Vento Explosivo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pular por Cima!", "Konohamaru dá um salto para trás enquanto Moegi e Udon seguram o oponente. Em seguida, os dois empurram o oponente para que Konohamaru pule por cima dele, acertando-lhe um chute que o manda longe.", "Curto alcance", null, null, "飛び越えろ!", "Tobikoero!", "Pular por Cima!", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punhalada da Agulha Negra", "Pain corre até seu oponente e apunhala-o com sua Manifestação de Receptores de Chakra, revertendo os seus controles.", "Curto alcance", null, null, "黒針突", "Kokushintō", "Punhalada da Agulha Negra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Adamantino", "O Punho Adamantino (金剛拳, Kongōken) é um estilo de luta caracterizado por um controle de chakra preciso e minucioso. Enquanto alguns podem vê-la simplesmente como força sobre-humana, esta técnica é realmente uma aplicação de ninjutsu médico.", "Curto alcance", null, "Cachorro → Cobra → Rato → Carneiro[7]", "金剛拳こんごうけん", "Kongōken", "Punho Adamantino", "Punho de Ouro Maciço (純金拳, Junkinken)", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Arhat", "O Punho Arhat (羅漢拳, Rakanken) é um estilo de combate que se baseia em simples ataques físicos aumentados com a grande força de Jirōbō.", "Curto alcance", "Rank C", null, "羅漢拳", "Rakanken", "Punho Arhat", null, "Punho do Rakan", "Palma Esmagadora");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Embriagado", "Punho Embriagado é um estilo único de combate, onde, de acordo com Might Guy, 'quanto mais você bebe, mais bêbado que você fica, e mais forte você se torna'. Esta não pode ser aprendida, ensinada, nem refinada através de treinamento e prática. Lutadores do Punho Embriagado não são simplesmente bêbados: eles se tornam potências humanas que atacarão de maneira imprevisível com uma fúria explosiva e sem segundas intenções sobre suas ações. Uma vez bêbado, o usuário perderá sua capacidade de conhecer o amigo do inimigo e atacará quem quer que esteja em sua volta.", "Curto alcance", "Rank C", null, "酔拳", "Suiken", "Punho Embriagado", null, "Punho Bêbado", "Punho Débil");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Emergente do Lobo Canino", "O usuário atinge o oponente com uma série de socos e golpes, terminando com um soco mais poderoso e crescente, levando o oponente para o ar.", "Curto alcance", null, null, "狼牙昇拳ろうがしょうけん", "Rōga Shōken", "Punho Emergente do Lobo Canino", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Enjoado", "Semelhante ao Punho Embriagado, o usuário aproveita o mal estar causado a ele e começa a dar-lhes movimentos com um grau de imprevisibilidade em combate. O usuário também pode vomitar em seu adversário para fins de distração.", "Curto alcance", null, null, "船酔い拳", "Funeyoi Kobushi", "Punho Enjoado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Forte", "O Punho Forte (剛拳, Gōken) é o estilo de luta característico de Might Guy e Rock Lee, cujo objetivo é causar danos externos e quebrar ossos. Posteriormente foi ensinado a Metal Lee por seu pai. É exatamente o oposto do Punho Gentil, que é usado principalmente pelo clã Hyūga. Este estilo de luta envolve esmagar o seu adversário, e geralmente é usado somente por shinobi extremamente poderosos e dominadores em seus atributos físicos.", "Curto alcance", null, null, "剛拳ごうけん", "Gōken", "Punho Forte", null, "Punho Feroz", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Gentil", "O Punho Gentil é uma forma de combate corpo-a-corpo, utilizada principalmente por membros do clã Hyūga.", "Curto alcance", null, null, "柔拳じゅうけん", "Jūken", "Punho Gentil", null, "Punho Leve", "Punho Suave");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Gentil: Agulha do Tenketsu", "Focando num único ponto de chakra, o usuário desfere um golpe com dois dedos no oponente, em uma tentativa de afetar o chakra que está sendo usado e interromper seu fluxo. Esse golpe foi poderoso o suficiente para inutilizar e danificar o braço de Hinata Hyūga durante as preliminares dos Exames Chūnin e para cortar as teias de Kidōmaru. No anime, essa técnica também foi útil para libertar Gaara e Fū das correntes de Hōichi.", "Curto alcance", null, null, "柔拳・点穴針じゅうけん・てんけつしん", "Jūken: Tenketsushin", "Punho Gentil: Agulha dos Pontos de Pressão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Quebra-Gelo", "O Punho Quebra-Gelo (砕氷拳, Saihyōken) é uma forma de combate utilizada por Rahyō, onde ele envolve seu punho com gelo para bloquear ataques de taijutsu que venha receber. Com esta técnica, Rahyō foi capaz de parar com sucesso o Furacão da Folha de Might Guy.", "Curto alcance", null, null, "砕氷拳さいきょうけん", "Saihyōken", "Punho Quebra-Gelo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Silencioso", "O Punho Silencioso (無音の拳, Muon no Ken) é um estilo de luta único de Shira. Ele é capaz de tornar seus movimentos completamente silenciosos envolvendo-se com chakra. Neji Hyūga compara seu uso com as penas de uma coruja, como o manto de chakra faz com que os movimentos de Shira sejam praticamente imprevisíveis.", "Curto alcance", null, null, "無音の拳むおんのけん", "Muon no Ken", "Punho Silencioso", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Sísmico", "Após uma série de socos no oponente, em seguida, o usuário salta para o ar e distribui um soco para baixo, finalizando a técnica.", "Curto alcance", null, null, "空震拳", "Kūshinken", "Punho Sísmico", "Punho do Céu Trêmulo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho Vendaval de Linha Dura do Estilo-Nadeshiko", "Cobrindo a própria mão com chakra de natureza de vento, o usuário ataca o inimigo. A força do ataque é grande o suficiente para produzir uma onda de choque poderosa, que pode causar visíveis fissuras sem contato direto.", "Curto alcance", null, null, "撫子流硬派烈風拳なでしこりゅうこうはれっぷうけん", "Nadeshikoryū Kōha Reppūken", "Punho Vendaval de Linha Dura do Estilo-Nadeshiko", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho de Arenito", "Com o Punho de Arenito (砂岩拳, Saganken) o usuário é capaz acumular areia em seu punho, na qual ele pode moldar para criar ferramentas sólidas, como uma marreta ou um escudo perfeito para um combate de curta distância. Os equipamentos criados são bastante rígidos e de longa duração.", "Curto alcance", null, null, "砂岩拳", "Saganken", "Punho de Arenito", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho de Ferro Negro", "Shinki cria duas mãos massivas compostas de areia de ferro que ele manipula através de campos eletromagnéticos. O usuário pode usá-las em volta se si objetivando proteger-se de ataques, bem como pode usá-las para atacar um alvo a distância.", "Todos os alcances", null, null, "黒鉄乃拳", "Kurogane no Kobushi", "Punho de Ferro Negro", null, null, null);
