INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punho do Renascimento", "Ao reunir chakra escuro em ambas as mãos, o usuário pode desencadear uma onda de choque poderosa e concentrada que, quando atingir uma pessoa de perto, irá causar tremendos danos internos.", "Curto alcance", null, null, "活性拳", "Kasseiken", "Punho do Renascimento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punição Celestial", "Esta é uma técnica na qual o usuário cobre seu corpo com múltiplos papéis explosivos para cometer suicídio. Pode ser usada como uma tática para assassinar o inimigo próximo.", "Curto a Médio alcance", null, null, "天誅てんちゅう", "Tenchū", "Punição Celestial", null, null, "Punição Divina");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Punição Divina", "Trata-se de série de combinações criadas por Hidan para afligir dano a seu oponente, envolvendo socos, pontapés e ataques com a Foice de Lâmina Tripla.", "Curto alcance", null, null, "神罰昇天", "Shinbatsu Shōten", "Punição Divina", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pó de Escama Explosiva", "Usando as asas de Chōmei, Fū ataca seu oponente e voa pelo ar enquanto libera escamas, em seguida, transforma-se totalmente em Chōmei e proporciona um ataque da semelhante à broca em seu oponente. Pouco antes do impacto, Fū encaixa o aparelho bucal de Chōmei, criando uma faísca que inflama o tornado cheio de escama criado de sua rotação, resultando em uma enorme explosão.", "Curto alcance", null, null, "鱗粉爆砕りんぷんばくさい", "Rinpun Bakusai", "Pó de Escama Explosiva", "Explosão da Escama", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pé Dançante", "Após desferir vários socos e chutes no oponente, Naruto concentra chakra em seu punho e acerta outro soco.", "Curto alcance", null, null, "舞踏脚", "Butōkyaku", "Pé Dançante", null, null, null);
