INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pé da Cauda do Dragão", "Zabuza corta o oponente ao meio e depois dá um giro chutando o oponente à distância.", "Curto alcance", null, null, "龍尾脚", "Ryūbikyaku", "Pé da Cauda do Dragão", "Pontapé da Cauda do Dragão", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pé da Dor Celestial", "Esta técnica é simplesmente um pontapé em forma de machado. Tsunade concentra sua incrível força em seu calcanhar e acerta o inimigo. Foi mostrado que, quando ela atinge o solo resulta em uma cratera gigante que pode até matar um adversário preso em sua zona de ataque imediato.", "Curto alcance", "Rank C", null, "痛天脚つうてんきゃく", "Tsūtenkyaku", "Pé da Dor Celestial", "Chute da Dor do Céu", "Golpe do Calcanhar Celestial", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pétalas Dançantes do Jardim de Flores", "Ino desliza em direção ao oponente, com chakra imbuído em seus pés e nas flores roxas ao seu redor, e dá uma joelhada contra o oponente no ar. Ela, então, gira graciosamente e desfere um pontapé reforçado com chakra no rosto do adversário.", "Curto alcance", null, null, "花弁舞う花園", "Hanabiramau Hanazono", "Pétalas Dançantes do Jardim de Flores", "Pétalas Dançantes da Flor do Jardim", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Pássaro Médio Rotativo", "Esta é uma técnica de enganação utilizada por Sasuke Uchiha, para surpreender o adversário.", "Curto alcance", null, null, "媒鳥旋", "Baichōsen", "Pássaro Médio Rotativo", "Rotação do Pássaro Médio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Quarteto do Som: Invocação: Cerco de Formação das Quatro Bestas", "Cada um dos membros do Quarteto do Som invoca suas respectivas invocações para barricar seus oponentes. Enquanto o Triplo Rashōmon faz da evasão difícil, os Doki e o Golem de Prisão de Terra atacam de frente, enquanto Kyodaigumo ataca de cima.", null, null, null, "音隠れ四人衆・口寄せ・四獣包囲の陣おとがくれよんいんしゅう・くちよせ・しじゅうほういのじん", "Otogakure Yoninshū: Kuchiyose: Shijū Hōi no Jin", "Quarteto do Som: Invocação: Cerco de Formação das Quatro Bestas", null, null, "Quatro Ninja do Som: Jutsu de Invocação: Ângulo de Formação das Quatro Bestas");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Quatro Estações Turbulentas", "Itachi e Shisui atacam seu oponente com a Liberação de Fogo: Técnica do Grande Dragão de Fogo. Através do fogo ardente, Itachi lança uma enxurrada de kunai. Depois, Shisui aparece atrás do oponente, agarrando uma das kunai e, em sintonia com Itachi, lança uma onda de ataques com uma kunai em cada mão.", "Curte a Médio alcance", null, null, "乱れ雪月花みだれせつげっか", "Midare Setsugekka", "Turbulenta Neve, Lua e Flores", "Quatro Estações Turbulentas", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Quatro Formações Vermelhas Yang", "Uma formação poderosa de barreira notada sendo usada apenas por aqueles de nível Kage, sendo quatro shinobi de pé em uma formação quadrada, erguendo uma grande barreira que é de cor vermelha. Foi indicada sendo várias vezes mais forte do que a Formação das Quatro Chamas Violeta, e é altamente maleável. Quando agredida pela Bola da Besta com Cauda Instantânea do Dez-Caudas, a barreira simplesmente é distorcida a sua forma, forçando o peso do ataque para cima enquanto ela ficou completamente intacta. Seus usuários podem criar aberturas dentro da barreira para permitir a passagem.", "Longo alcance", "Rank A", "Carneiro + Carneiro + Carneiro + Carneiro simultaneamente", "四赤陽陣", "Shisekiyōjin", "Quatro Formações Vermelhas Yang", null, "Arte Ninja dos Quatro Sóis Vermelhos", "Arte Ninja: Barreira dos Quatro Sóis Escarlate");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Quebra de Chifre", "Kokuō dirige seus chifres diretamente no oponente usando uma poderosa carga. A força do ataque foi tão grande que ele pode enviar uma outra besta com cauda voando a uma distância considerável e causando grandes danos.", "Curto alcance", null, null, "角折り", "Tsunoori", "Investida de Chifres", "Chifre Destruidor", "Quebra-Chifre", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Queda Celestial do Colapso Gêmeo", "Após se separarem, Sakon e Ukon encurralam o oponente. Em seguida, eles agarram o oponente firmemente e em um movimento rápido, eles trocam de lugar para socar o oponente e lançá-lo no ar. Enquanto o oponente está no ar, eles saltam, e o agarram novamente, girando em uma velocidade inexplicável e soltando o oponente no último minuto, fazendo ele cair contra o solo.", "Curto alcance", null, null, "双極落天", "Sōgoku Rakuten", "Queda Celestial do Colapso Gêmeo", "Queda do Colapso Gêmeo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Queda da Aranha Gigante", "Kidōmaru invoca Kyodaigumo em cima de seu oponente, enviando um sinal para que caia em seguida e esmague-o.", "Curto alcance", null, null, "大蜘蛛落とし", "Ōkumo Otoshi", "Queda da Aranha Gigante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Queda da Corrente", "Obito salta no ar, e dá um pontapé e dois socos em seu oponente, terminando com um ataque de suas correntes.", "Curto alcance", null, null, "落鎖", "Rakusa", "Queda da Corrente", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Queda da Flor de Damasco", "Kurenai ataca seu oponente com dois chutes e um golpe de cotovelo, em seguida, o atacando com uma poderosa enxurrada de flores de cerejeira que estouram ao entrar em contato com o corpo do oponente, bombardeando-o em seguida.", "Curto alcance", null, null, "杏咲垂", "Anshōsui", "Queda da Flor de Damasco", "Inclinamento da Flor de Damasco", null, null);
