INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Queda do Impulso Envoltório", "Em sua forma de quatro caudas, Naruto pula no ar e envolve suas caudas em torno de si. Ele, então, cai no chão, girando como um piledriver. Este movimento pode bloquear técnicas de curto alcance.", "Curto alcance", null, null, "降纏突", "Kōten Tsuki", "Queda do Impulso Envoltório", "Impulso Caído", null, null);
