INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Raio", "Killer B salta no ar com suas sete espadas e atinge o oponente enquanto encontra-se revestido por chakra de relâmpago.", "Curto alcance", null, null, "雷斗忍具菩流斗", "Raitoningu Baruto", "Combate da Rosa Fluente de Equipamento Ninja de Combate Relâmpago", "Raio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Raio Divisor do Céu e da Terra", "O usuário acumula chakra de natureza relâmpago em sua mão e dispara uma série de projéteis que, após colidirem contra seu alvo, liberam poderosas descargas elétricas em forma de raios.", "Longo alcance", null, null, "雷分割天地らいぶんかつてんち", "Raibunkatsu Tenchi", "Raio Divisor do Céu e da Terra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Raio Naruto", "Mecha-Naruto trava a mira seus oponentes e dispara feixes de energia de seus olhos neles.", "Longo alcance", null, null, "ナルトビーム", "Naruto Bīmu", "Raio Naruto", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Raio da Lança do Céu do Nove-Caudas", "Após absorver o chakra da Lança do Céu, as habilidades do Clone do Nove-Caudas são ampliadas e o clone pode disparar raios poderosos de sua cabeça semelhante a um dragão. Esses raios eram poderosos o suficiente para cortar uma rocha sólida e atravessá-la, ferindo até mesmo Naruto, mesmo Naruto estando completamente transformado em Kurama.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rajada Súbita da Folha", "Usando ataques simples, mas eficazes, o usuário começa a girar rapidamente sobre seu eixo em apenas uma perna, ganhando balanço com a outra que está erguida, libertando sucessivos chutes rotativos ao inimigo em uma rápida sucessão.", "Curto alcance", null, null, "木ノ葉突風", "Konoha Toppū", "Rajada Súbita da Folha", null, null, "Rajada da Folha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rajada de Meteoro do Dragão", "O usuário salta altamente para bater o seu adversário para baixo com ambos pés. Uma onda de choque poderosa é liberada a partir do local do impacto.", "Curto alcance", null, null, "駝鳥流星爆", "Dachō Ryūseibaku", "Rajada de Meteoro do Dragão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan", "O Rasengan (螺旋丸, Esfera Espiral) é uma técnica poderosa de Rank A inventada pelo Quarto Hokage, que a criou observando a formação da Bola da Besta com Cauda. É a manipulação de chakra puro, em forma de uma esfera espiral de chakra. Minato Namikaze passou três anos desenvolvendo o Rasengan, seu plano era tomar a forma de transformação de chakra para o 'nível mais alto possível', e então combiná-lo com sua afinidade de chakra da natureza, criando uma técnica que não precisava da ajuda de selos de mão e controle. Ele morreu antes que ele pudesse fazer isso, mas mesmo com só a Transformação da Forma o Rasengan ainda é muito útil, poderoso, versátil, e uma difícil técnica de aprender e ensinar.", "Curto alcance", "Rank A", null, "螺旋丸らせんがん", "Rasengan", "Esfera Espiral", null, null, "Rasengan: Esfera Espiral de Chakra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Desaparecedor", "O Rasengan Desaparecedor é uma técnica desenvolvida acidentalmente por Boruto Uzumaki. Ela consiste em uma forma de Rasengan mesclada com a Liberação de Relâmpago.", "Curto ou longo alcance", null, null, "消える螺旋丸きえるらせんがん", "Kieru Rasengan", "Esfera Espiral Desaparecedora", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Duplo", "Com o oponente imobilizado ou distraído, Naruto avança contra ele tenta atingir o Rasengan de um lado, enquanto Jiraiya, ou um clone de Naruto, cria rapidamente outro Rasengan para colidir com o de Naruto no oponente, causando uma explosão de chakra.", "Todos os alcances", null, null, null, null, "Esferas Espirais Duplas", "Rasengan Duplo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Duplo Rápido", "Primeiramente, Jiraiya realiza um único Rasengan, deformando o chão. Em seguida, o Rasengan entra em contato com o oponente, e então ele cria mais um Rasengan, combinando os dois e formando um enorme Rasengan. Devido ao enorme poder, o solo começa a trincar e logo é destruído.", "Curto alcance", null, null, "双迅螺旋丸そうじんらせんがん", "Sōjin Rasengan", "Esfera Espiral Dupla Rápida", "Rasengan Duplo Rápido", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Final Supremo", "Naruto funde seu Rasengan com o de Minato para criar um Rasengan que perfura através do oponente causando grandes danos. O Rasengan Final Supremo tem vários fluxos de chakra em espiral em torno do centro, além de diversos fluxos cobrindo completamente os corpos dos usuários, os quais são capazes de desviar ataques. A esfera em si parece instável, com a forma flutuante descontroladamente ao contrário da forma de esfera sólida de outras técnicas do Rasengan.", null, null, null, "太極螺旋丸", "Taikyoku Rasengan", "Esfera Espiral Final Suprema", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Gelel", "É um Rasengan infundido com a energia da Pedra de Gelel. Ele apenas é visto sendo usado como parte do Rasengan Serial em combinação com um Yōko Rasengan. Em vez de ser criado com a Técnica dos Clones das Sombras, Temujin simplesmente coloca a mão sobre a mão direita de Naruto. Seu poder se presume ser igual ao poder de fogo de um Yōko Rasengan, pois ele pode queimar uma parte do corpo do oponente.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Kagutsuchi", "Naruto e Sasuke formam a Liberação de Calor: Auréola do Furacão de Flecha Negra de Estilo Zero de menor escala e a lançam no oponente, atirando-o para longe. Em seguida, Sasuke aparece atrás do oponente com seu Ame-no-tejikara e lhe dá um chute que o lança para o alto. Logo, Sasuke manifesta um antebraço com seu Susanoo e forma um Takemikazuchi em sua mão, enquanto Naruto acumula chakra em seu punho. Ambos correm em direção ao seu alvo e, antes que ele caia no chão, o acertam com suas respectivas técnicas, causando uma explosão que pode ser vista do espaço.", "Curto alcance", null, null, "螺旋丸加具土命", "Rasengan Kagutsuchi", "Esfera Espiral da Ferramenta Adicionada ao Senhor da Terra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Planetário", null, "Curto alcance", "Rank A", null, "惑星螺旋丸", "Wakusei Rasengan", "Esfera Espiral Planetária", "Rasengan Planetário", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan Serial", "O Rasengan Serial é simplesmente uma versão em que se utiliza as duas mãos, sendo que cada uma contendo um Rasengan normal.", "Curto alcance", "Rank A", null, "螺旋連丸", "Rasenrengan", "Esferas Espirais Seriais", "Barragem Rasengan", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan: Deslocamento Brilhante", "Com o auxilio de Kurama, Naruto é capaz de criar um Rasengan em sua mão e liberar sua energia na forma de um feixe, que ele usa para impulsionar-se para o alto em rápida velocidade ou atingir um inimigo à distância.", "Longo alcance", null, null, "螺旋丸・瞬輝", "Rasengan: Shunki", "Esfera Espiral: Deslocamento Brilhante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan: 'Unissonância'", "Boruto e Kawaki ativam seus selos do Karma em conjunto e, graças à ressonância entre os dois, Boruto é capaz de elevar o poder explosivo de seu Rasengan em várias vezes, o suficiente para destruir parte considerável do corpo de seu oponente.", "Curto alcance", null, null, "螺旋丸・「連奏」", "Rasengan: 'Tsurane'", "Esfera Espiral: 'Unissonância'", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan da Besta com Cauda", "O Rasengan da Besta com Cauda é uma técnica incompleta de Naruto Uzumaki. Ela é semelhante ao Rasengan e à Bola da Besta com Cauda.", null, null, null, "尾獣螺旋丸", "Bijū Rasengan", "Esfera Espiral da Besta com Cauda", null, null, "Rasengan da Besta com Cauda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan da Coragem", "Lembrando das palavras de Jiraiya sobre nunca abandonar o seu caminho ninja, Naruto cria um Rasengan totalmente carregado, mas aumenta sua massa e força bruta. Ele tem mostrado energia suficiente para detonar uma parede inteira de uma fortaleza e até mesmo dizimá-la em questão de minutos. Ao contrário de um Rasengan normal, que desaparece depois de bater sua meta, esta versão não desaparece até que Naruto termine de destruir toda a fortaleza.", "Curto alcance", null, null, "ど根性螺旋丸", "Dokonjō Rasengan", "Esfera Espiral da Coragem", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan da Dança da Nuvem de Areia", "Gaara usa Garoa de Areia para distrair e nocautear o oponente enquanto Naruto finaliza com um combo e o Rasengan, atacando o inimigo no chão.", "Curto alcance", null, null, "砂塵舞う螺旋丸", "Sajinmau Rasengan", "Dança da Esfera Espiral na Nuvem de Areia", "Rotação de Areia do Rasengan", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan da Lua Crescente", "Naruto combina o Rasengan com os raios de uma lua crescente para mergulhá-lo em uma luz branca ofuscante. Isso forma uma lua crescente branca com uma estrela no Rasengan, tornando o ataque maior e mais poderoso.", "Curto alcance", null, null, "三日月螺旋丸", "Mikazuki Rasengan", "Esfera Espiral da Lua Crescente", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan da Raposa Demoníaca", "O Rasengan da Raposa Demoníaca é forma primorada do Rasengan utilizado por Naruto mediante o uso do chakra de Kurama.", "Curto alcance", null, null, "妖狐螺旋丸", "Yōko Rasengan", "Esfera Espiral da Raposa Demoníaca", "Rasengan Vermelho", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan de Sete Cores", "O Rasengan de Sete Cores é uma variação do Rasengan, colorido pela luz refletida do gerador-espelho no País da Neve. Esta coloração fez o chakra utilizado no Rasengan se parecer com um arco-íris. A julgar pela distância que ele faz o oponente voar, pode ser que o chakra multicolorido não altere somente a aparência da técnica, mas de alguma forma aumente a sua potência também.", "Curto alcance", null, null, "七色の螺旋丸", "Nanairo no Rasengan", "Esfera Espiral de Sete Cores", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasengan do Pai com Filho", "O Rasengan do Pai com Filho é um ninjutsu cooperativo pelo qual dois shinobi, na maioria das vezes um usuário e seu filho como o nome sugere, combinam seus Rasengan em uma esfera massiva, que é então empurrada ao seu alvo.", "Curto alcance", null, null, "親子螺旋丸おやこらせんがん", "Oyako Rasengan", "Esfera Espiral do Pai com Filho", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasenshuriken Planetário da Besta com Cauda", "Naruto começa com um soco de vento, mandando o oponente para o ar. Ele, então, se transforma em seu Modo Kurama e prepara um Rasenshuriken de tamanho gigante e uma Bola da Besta com Cauda. Ele joga o Rasenshuriken e dispara quatro Bolas da Besta com Cauda em satélites em volta da shuriken. Explode à medida que atinge o oponente, resultando em um enorme domo de energia azul.", "Longo alcance", null, null, "尾獣惑星螺旋手裏剣", "Bijū Wakusei Rasenshuriken", "Shuriken Espiral Planetária da Besta com Cauda", "Rasen Shuriken Planetário de Bijū", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rasenshuriken da Bola de Besta com Cauda", "O Rasenshuriken da Bola de Besta com Cauda é uma poderosa técnica utilizada por Naruto com a ajuda de Kurama.", "Todos os alcances", "Rank S", null, "尾獣玉螺旋手裏剣びじゅうだまらせんしゅりけん", "Bijūdama Rasenshuriken", "Bola da Besta com Cauda Shuriken Espiral", "Bomba Rasen Shuriken da Bijū", "Esfera Bijū Rasenshuriken", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rato de Fogo", "Yugito libera uma das caudas de Matatabi, bate-a para frente e espalha três ratos de fogo que explodem ao contato. No entanto, se os ratos não chegam até o oponente, eles ficam por um tempo parados no chão até que o inimigo toque-os e eles explodam.", "Curto a Médio alcance", null, null, "火ねずみひねずみ", "Hinezumi", "Rato de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Receptor Negro", "Os receptores negros são ferramentas peculiares utilizadas por usuários do Rinnegan. Eles podem ser utilizados para transmitir chakra através de longas distâncias.", "Todos os alcances", null, null, "黒い受信機", "Kuroi Jushinki", "Receptor Negro", "Agulha Negra (黒針, Kokushin)", null, "Receptores de Chakra");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Recepção da Espada de Vento Cintilante", "Shisui acerta alguns golpes em seu oponente e realiza uma sequência de ataques de fogo com sua tantō, lançando o oponente para cima.", "Curto a Médio alcance", null, null, "瞬風戴刀しゅんふうたいとう", "Shunfū Taitō", "Recepção da Espada de Vento Cintilante", "Ataque da Espada de Ventania", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Redemoinho Violento", "O usuário produz uma poderosa corrente de vento de sua boca. Esta técnica foi poderosa o suficiente para dissipar vários clones das sombras em um instante.", "Todos os alcances", null, null, "烈旋風らっせんぷう", "Ressenpū", "Redemoinho Violento", null, null, "Vendaval do Leque");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Redemoinho da Folha", "Equilibrado de cabeça para baixo, o usuário pode usar as pernas para dissipar possíveis nevoeiros ou atacar o inimigo com os pés em movimento.", null, null, null, "木ノ葉つむじ風このはつむじかぜ", "Konoha Tsumujikaze", "Redemoinho da Folha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Redemoinho de Vento do Avestruz", "Condor usa sua perna para dar um forte chute no estômago do oponente.", "Curto alcance", null, null, "駝鳥旋風撃", "Dachō Senpūgeki", "Redemoinho de Vento do Avestruz", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reencarnação Explosiva Localizada", "A Reencarnação Explosiva Localizada (近傍転生爆, Kinbō Tensei Baku) é uma das poderosas técnicas utilizadas por Toneri Ōtsutsuki.", "Todos os alcances", null, null, "近傍転生爆きんぼうてんせいばく", "Kinbō Tensei Baku", "Reencarnação Explosiva Localizada", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reencarnação da Marionete", "Como último recurso, Toneri, enquanto sem olhos, convoca incontáveis Byakugan do Tenseigan de Hamura destruído e infunde-os em si mesmo. Os Byakugan aparentemente lhe concedem um poder imenso, que é reivindicado a ser capaz de levar o mundo ao seu fim.", "Todos os alcances", null, null, "傀儡転生くぐつてんせい", "Kugutsu Tensei", "Reencarnação da Marionete", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reencarnação da Própria Vida", "A Reencarnação da Própria Vida foi desenvolvida em Sunagakure pela Brigada de Marionetes. O usuário, dando a sua força de vida para o alvo, usa todo seu chakra como um intermediário.", "Curto alcance", "Rank S", null, "己生転生", "Kishō Tensei", "Reencarnação da Própria Vida", null, null, "Jutsu de Reanimação");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reencarnação da Vida do Dragão", "A Reencarnação de Vida do Dragão é a técnica suprema do clã de Ryūzetsu.", "Curto alcance", null, "Passaro → Selo de Confronto", "龍命転生りゅう いのち てんせい", "Ryūmei Tensei", "Reencarnação de Vida do Dragão", null, null, "Transferência de Vida");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reencarnação do Cadáver Vivente", "Esta é uma técnica que foi desenvolvida por Orochimaru, permitindo-lhe transferir sua alma para o corpo de outra pessoa. Se repetido periodicamente, ele concede a imortalidade ao usuário, desde que o usuário possa encontrar novos corpos hospedeiros e mantê-los vivos. Orochimaru só pode executar esta técnica uma vez a cada três anos. Parece também que, quando os três anos estão no término, ele é forçado a trocar para outro hospedeiro, como o anterior começa a enfraquecer e rejeitá-lo. Presumivelmente, se ele encontrar um recipiente adequado, a substituição de hospedeiros não será mais necessária.", null, "Rank S", null, "不屍転生ふしてんせい", "Fushi Tensei", "Ritual de Transferência", null, "Reencarnação em Vida", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Regeneração de Absorção Celular", "Enquanto no seu estado transformado, Jūgo estende uma protuberância em forma de agulha de seu corpo, inserindo-a no seu alvo, sugando rapidamente o seu chakra juntamente de órgãos e/ou carne cuja ele usa para restaurar seu próprio corpo, bem como chakra. Ele usou esta técnica logo após ser deixado à beira da morte pelo Quarto Raikage. Esta técnica também restaurou seu tamanho normal, como ele anteriormente havia reduzido sua carne.", "Curto alcance", null, null, "再胞吸引", "Saibō Kyūin", "Regeneração de Absorção Celular", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Regeneração de Ejeção Celular", "Como originador de Juinjutsu de Orochimaru e possuidor de uma capacidade única de seu clã, Jūgo é capaz de dar sua carne para aqueles que são compatíveis com os referidos selos amaldiçoados, curando assim quaisquer ferimentos que possam ter recebido. Devido a ele estar transferindo suas células para o destinatário, o próprio corpo de Jūgo regride em idade, tornando-se com uma aparência mais jovem.", "Curto alcance", null, null, "再胞排出", "Saibō Haishutsu", "Regeneração de Ejeção Celular", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Regeneração de Vigor", "A Regeneração de Vigor (体力回復, Tairyoku Kaifuku) é uma habilidade única de Karin, que ela herdou de sua mãe. Essa habilidade foi muito cobiçada por Kusagakure, que estava tendo muitas baixas durante as guerras e não tinha meios para curar seus feridos.", "Curto alcance", null, null, "体力回復たいりょくかいふく", "Tairyoku Kaifuku", "Regeneração de Vigor", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Regressão Temporal", "É uma forma de viagem no tempo desempenhada por Urashiki através de seu Rinnegan exclusivo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rei Lótus Kanzeon", "Enquanto estiver usando Yamato como um meio, Tobi cria uma estátua enorme, semelhante a técnica de Hashirama Senju, Arte Sábia da Liberação de Madeira: Várias Milhares de Mãos Verdadeiras. Ashura Ōtsutsuki utilizou essa técnica na luta contra o seu irmão, com a ajuda do chakra de todos os aldeões da vila. Ao contrário da variante muito maior de Hashirama, no entanto, esta criação não utiliza chakra do senjutsu. O portador deste avatar é capaz de utilizar a multiplicidade das mãos para atacar seus oponentes.", "Curto a Médio alcance", null, "Juntar as Maos[2]", "観世音蓮華王", "Kanzeon Renge-Ō", "Rei Lótus Kanzeon", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Relógio da Contagem Regressiva do Armagedom", "Após efetuar um selo de mão, um relógio ultra secreto surge na palma da mão da pessoa. Este relógio foi desenvolvido por Konohagakure apenas para ser usado pelos cinco Kage e seus assistentes em que tinham mais confiança. O relógio serve como uma contagem regressiva para a destruição da Terra, após Toneri ter deslocado a Lua de sua órbita.", "Curto alcance", null, "Selo de Confronto", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Relâmpago (jutsu)", "Uma versão menor da Bola da Besta com Cauda que é infundida com chakra relâmpago que o usuário em forma de besta, ou a própria besta pode usar para atravessar as defesas de seu alvo e causar grandes danos.", "Médio a Longo alcance", null, null, "雷斗刃駆", "Raitoningu", "Condução da Lâmina Relâmpago de Combate", "Corte de Luz", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Relâmpago Negro", "Relâmpago Negro é uma forma única de Liberação de Relâmpago utilizada pelo Terceiro Raikage, que passou para Darui. Aqueles que detêm essa habilidade recebem uma tatuagem estilizada do kanji 'relâmpago' (雷, kaminari) em seus ombros como um traço distintivo que indica sua posse; Darui à sua esquerda e o Raikage à sua direita. Não se sabe muito sobre seu funcionamento, mas como o nome sugere, o relâmpago produzido pelas técnicas desta habilidade é de cor preta.", "Longo alcance", null, null, "黒い雷くろいかみなり", "Kuroi Kaminari", "Relâmpago Negro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Relâmpago Reto", "Enquanto coberto no Modo de Chakra de Liberação de Relâmpago, o usuário corre em direção ao oponente e o soca em sua velocidade máxima.", "Curto alcance", null, null, "雷斗忍遇須吐励刀", "Raitoningu Sutorēto", "Luta Relâmpago Suportando Encontro Obrigatório de Ejeção Incentivando Espada", "Relâmpago Reto", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Repartimento de Chakra", "Esta técnica consiste em criar até cinco jatos de chakra azul-púrpuras, extremamente velozes e que atravessam o solo. Os jatos são difíceis de evitar, entretanto não causam danos fatais a seus alvos. Eles podem, no entanto, selar o chakra daquele que for atingido por certo tempo.", "Médio a longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Replicação da Besta com Cauda", "Gensui cria uma etiqueta de selamento especial que, se colocado em seres humanos, os transforma em falsas réplicas de uma das bestas com cauda. O selo não apenas molda a vítima para a besta desejada, como também concede-lhes as habilidades do originais. A etiqueta também pode replicar o jinchūriki da besta com cauda também.", null, null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Resplendor", "O usuário emite uma corrente de relâmpago para atordoar o inimigo. Esta técnica pode ser usada em conjunto com uma técnica de Liberação de Água para canalizar melhor o relâmpago ou como distração para o oponente evadir e cair numa armadilha.", "Curto alcance", null, null, "残光ざんこう", "Zankō", "Resplendor", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Resplendor Crepuscular", "Sasuke avança em alta velocidade em direção ao seu oponente, então ele desfere um corte transversal com sua Espada de Kusanagi.", "Curto alcance", null, null, "残光", "Zankō", "Resplendor Crepuscular", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Restrição Maldita da Cobra Conjuradora", "O usuário invoca uma ou duas cobras, que rastejam para fora de uma das mangas, e enrola-se ao redor do inimigo para capturá-lo ou imobilizá-lo. Esta é uma grande técnica para capturar alguém vivo, ou para infligir dano por estrangulamento, e se a situação exigir, as cobras podem morder a vítima com seus dentes de veneno. Com a invocação, previamente, o procedimento pode ser reduzido ao usar sangue do selo de contrato em uma invocação, tornando possível usar a técnica imediatamente. Com uma adição da intenção assassina o usuário pode enfraquecer a determinação de seu alvo.", "Curto alcance", "Rank C", null, "蛇睨呪縛じゃげいじゅばく", "Jagei Jubaku", "Restrição Maldita da Cobra Conjuradora", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Restrição da Aranha", "Depois de reunir sua teia aranha em sua boca, Kidōmaru cospe para fora em uma ampla teia padrão. Esta teia pode envolver seus inimigos e amarrá-los para uma superfície próxima. Com o chakra fluindo através da teia é difícil de escapar dela.", "Curto alcance", null, "Tigre", "蜘蛛縛りくもしばり", "Kumo Shibari", "Restrição da Aranha", null, "Teia de Aranha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Restrição de Fios de Aço", "Essa tática engloba a criação de armadilhas utilizando fios de aço.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Restrição de Fios do Projétil Manipulado", "Utilizando um projétil com fios ligados a ele, o usuário pode preparar uma armadilha para seu alvo. Usando shuriken ou kunai para espalhar e colocar os fios ao redor de si ou do alvo, o usuário pode posicioná-los para prender seu alvo, requerendo apenas que provoque uma mudança de posição do conjunta de fios, como cortando um deles, fazendo com que os outros se reorganizem.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reter Água", "Tobirama desfere um soco em seu oponente, e logo após, acompanha com um chute giratório que o levanta no ar, onde então segue com mais três chutes antes de aparecer por cima do oponente, soltando de sua mão uma rajada de água jogando o oponente para o chão.", "Curto alcance", null, null, "抱水ほうすい", "Hōsui", "Reter Água", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Retorno Uchiha", "Ao utilizar o Gunbai Uchiha para bloquear um ataque recebido, o usuário anula os efeitos do ataque convertendo o chakra recebido em transformação da natureza de vento, que é refletida de volta. Como a reflexão de um ataque ocorre de repente e inesperadamente, o ataque refletido pode ser difícil para os adversários escaparem.", "Curto alcance", "Rank B", null, "うちは返しうちはがえし", "Uchihagaeshi", "Retorno do Leque", "Reflexão Uchiha", "Reflexo Uchiha", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Retorno de Vento", "Usando as Espadas Curtas Voadoras Crescentes, o usuário dessa técnica pode facilmente cancelar uma outra técnica da Liberação de Vento e redirecioná-la para o seu usuário na forma de um furacão.", "Curto a Médio alcance", null, null, "風返し", "Kaze Gaeshi", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Retorno do Diabo", "Suigetsu primeiro usa sua Liberação de Água: Técnica do Projétil do Dragão de Água no oponente, danificando-o e deixando-o no ar. Em seguida, ele ataca-o, batendo o adversário no chão com sua Kubikiribōchō.", "Curto alcance", null, null, "悪魔の帰還", "Akuma no Kikan", "Retorno do Diabo", "Retorno do Demônio", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Reversão do Falcão", "Obito salta com as pernas sobre seu oponente, prendendo seus pés nos ombros dele. Em seguida, Obito atira-o para o ar, aparece atrás do oponente e acerta-o com um golpe de duas mãos no estômago.", "Curto alcance", null, null, null, null, "Reversão do Falcão", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Revestimento do Corpo", "Uma habilidade possuída pelo Zetsu Negro, permitindo-lhe revestir um alvo de sua escolha.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Revolta do Mundo do Demônio", "Tayuya manipula seus três Doki com o som de sua flauta. Sua melodia libera espíritos materializados, que tem efetivamente chakra especial selado dentro dos Doki. Os espíritos materializados são compostos quase inteiramente de energia mental, de modo que o chakra fica em um estado instável. Portanto, eles anseiam energia física, de modo que caçam sua presa interminavelmente.", "Curto a Médio alcance", "Rank B", null, "魔境の乱まきょうのらん", "Makyō no Ran", "Revolta do Mundo do Demônio", null, "Revolução na Terra dos Demônios", "Nona Passagem: Revolução do Demônio");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Riacho Azul", "O usuário cria um corpo de água em movimento debaixo dele que se desloca em direção ao oponente, permitindo-o cortar o oponente com uma grande espada diversas vezes ou perfurá-lo.", "Curto alcance", null, null, "青小川あおおがわ", "Ao Ogawa", "Riacho Azul", "Ruptura de Onda[1]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Riacho de Sangue da Chuva de Verão", "Shura usa o interior de seu guarda-chuva, para disparar várias agulhas. Uma vantagem desta técnica é a levitação do usuário e rotação, possibilitando o disparo de mais agulhas, massacrando os alvos numa área considerável.", "Curto a Médio alcance", null, null, "五月雨血河さみだれけっか", "Samidare Kekka", "Riacho de Sangue da Chuva de Verão", null, null, "Arte Ninja: Chuva de Sangue");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ribalta", "Ribalta é uma técnica baseada em um relâmpago que é usado quando todos os quatro usuários têm lugar nas direções cardeais. Eles, então, juntam seu chakra relâmpago na forma de quatro fluxos de conexão que disparam para o céu acima da área de alvo. Isto vai, então, criar uma bola maciça de relâmpago poderoso o suficiente para incinerar tudo dentro da área dos quatro usuários. Ele é considerado como sendo uma das técnicas de Liberação de Relâmpago mais poderosas e destrutivas. Os quatro mais fortes dos Doze Guardiões Ninja eram provavelmente os únicos com a habilidade de usar esta técnica, já que a equipe de Furido se deu ao trabalho de ressuscitá-los com uma técnica de reanimação.", "Todos os alcances", null, null, "雷夢雷人ライムライト", "Raimu Raito", "Sonho Relâmpago de Pessoas Relâmpago", null, null, "Jutsu Ribalta");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rio Lobo", "Depois de se transformar em um lobo de duas cabeças, Kiba e Akamaru saltam em frente e mordem o oponente com as duas cabeças de lobo.", "Curto alcance", null, null, "狼河", "Rōka", "Rio Lobo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Risco da Dança da Lâmina", "Mifune executa um corte rápido em direção ao seu adversário, cortando-o. O adversário só sente o dano quando Mifune guarda sua espada.", "Curto alcance", null, null, "踊刃冒ようじんぼう", "Yōjinbō", "Risco da Dança da Lâmina", "Decapitação de Morador[2]", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rocha do Macaco", "A Rocha do Macaco é uma técnica de Liberação de Lava utilizada por Momoshiki Ōtsutsuki.", "Curto a Médio alcance", null, null, "猿岩さるいわ", "Saruiwa", "Rocha do Macaco", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Roda Dourada da Reencarnação Explosiva", "A Roda Dourada da Reencarnação Explosiva é uma poderosa técnica utilizada por Toneri Ōtsutsuki.", "Todos os alcances", null, null, "金輪転生爆きんりんてんせいばく", "Kinrin Tensei Baku", "Roda Dourada da Reencarnação Explosiva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Roda Prateada da Reencarnação Explosiva", "A Roda Prateada da Reencarnação Explosiva é uma poderosa técnica utilizada por Toneri Ōtsutsuki.", "Todos os alcances", null, null, "銀輪転生爆ぎんりんてんせいばく", "Ginrin Tensei Baku", "Roda Prateada da Reencarnação Explosiva", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Roda de Água", "O usuário utiliza sua maestria em Liberação de Água para canalizar seu chakra através de seu bō e criar uma serra circular composta de água, a fim de cortar o oponente.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rodada da Lua", "Sasuke salta no ar e ataca tanto de forma tradicional quanto de forma giratória o seu oponente com sua espada, usando seguidamente um Chidori, onde atinge-o e derruba-o no chão.", "Curto alcance", null, null, "月輪", "Getsurin", "Rodada da Lua", "Roda da Lua", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rodopio Flamejante", "Utilizando sua Rocha do Macaco, Momoshiki aquece ambos os punhos de sua criatura e comanda-o a girar em torno de seu próprio eixo, destruindo tudo que toca e deixando um rastro de fogo pelo caminho. A técnica mostrou-se forte o suficiente para derrubar o Modo Besta com Cauda de Naruto revestido com a armadura do Susanoo do Sasuke.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rosário de Chakra", "Rosário de Chakra (チャクラの数珠, Chakura no Juzu) é uma técnica do Tenseigan na qual Toneri Ōtsutsuki infunde suas Esferas da Busca da Verdade com o poder do seu Modo de Chakra do Tenseigan.", "Todos os alcances", null, null, "チャクラの数珠ちゃくらのじゅず", "Chakura no Juzu", "Rosário de Chakra", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rotação Destruidora dos Múltiplos Pés Conectados", "Com o seu selo amaldiçoado nível 2 ativado, Sakon rodopia e, entrega uma barragem de pontapés no oponente, fazendo uma grande quantidade de poeira subir. Após o término de golpes sequenciais, Sakon termina lançando o oponente para longe com um pontapé final.", "Curto alcance", null, null, null, null, null, "Rotação Destruidora dos Múltiplos Pés Conectados", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rotação das Lâminas", "Enquanto o oponente paira no ar, Tenten, empunhando duas grandes lâminas, gira verticalmente em torno de seu próprio eixo repetidas vezes, dilacerando o inimigo de forma fatal, lançando o cada vez mais alto.", null, null, null, "廻旋刃", "Kaisenjin", "Rotação das Lâminas", "Lâminas Rotatórias", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rotação do Vento Feroz", "Esta é uma técnica utilizada com as Espadas Curtas Voadoras Crescentes, o que cria uma grande rajada de vento. Quando Seimei uniu as espadas com as outras três armas especiais da Vila Takumi, a rajada de vento foi transformada em um redemoinho muito forte.", "Curto a Médio alcance", null, null, "旋猛風", "Senmōfū", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rugido: Impetuosa Pancada de Gelo", "Uma técnica poderosa usada por Mizore Fuyukuma em que ele navega em seu snowboard em direção ao oponente, depois ele ataca o oponente com o Braço de Cabo Retrátil, batendo-o em um grande pedaço de gelo.", "Curto alcance", null, null, "爆走・豪力氷打ばくそう・ごうりきひょうだ", "Bakusō: Gōriki Hyōda", "Rugido: Impetuosa Pancada de Gelo", "Execução do Rugido: Pancada de Gelo de Grande Força", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rugido do Amor", "Iruka se move até perto do oponente. Então ele esmaga seu punho na cabeça deste, criando um grande choque.", "Curto alcance", null, null, "愛の一喝", "Ai no Ikkatsu", "Rugido do Amor", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Rumo ao Sol Poente", "Rock Lee corre contra o oponente, abre vários portões de chakra e pula contra o oponente, levando-o através de várias montanhas antes de concentrar seu chakra restante para brutalmente levá-lo através de uma parede montanhosa de um vale, finalizando ao pôr do sol.", "Curto alcance", null, null, "あの夕日に向かってあの夕ひにむかって", "Ano Yūhi ni Mukatte", "Rumo ao Sol Poente", "Encarando o Pôr do Sol", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ruptura", "O usuário balança violentamente sua espada a fim de projetar um corte com a intenção de atacar um adversário que se aproxima, liberando uma onda cortante de impacto contra o alvo.", "Curto alcance", null, null, "破断はだん", "Hadan", "Ruptura", null, "Secção", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ruptura da Terra", "Jiraiya golpeia o oponente repetidamente com uma mão enquanto fica pulando em uma perna (o estilo lembra o Punho Embriagado), e em seguida, aparece atrás dele para golpeá-lo com a sua palma da mão, enviando-o para o chão e acabando por bater o calcanhar sobre ele.", "Curto alcance", null, null, "驚天動地", "Kyōtendōchi", "Ruptura da Terra", "Despedaçamento da Terra", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ryūmyaku: Bomba do Dragão de Fogo", "Mukade acumula o chakra de Ryūmyaku na boca de sua marionete e o condensa em bombas que são atiradas sucessivamente em direção ao seu alvo.", "Longo alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ryūmyaku: Corrente do Dragão de Relâmpago", "Mukade flui o chakra de Ryūmyaku através do corpo de sua marionete e o converte em descargas elétricas para atacar e defender simultaneamente.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ryūmyaku: Grande Dragão de Fogo", "Mukade dispara chamas roxas da boca de sua marionete usando o chakra de Ryūmyaku. O ataque deixa um resíduo de chama roxa na área a sua volta.", "Todos os alcances", null, null, "龍脈・豪龍火", "Ryūmyaku: Gōryūka", "Veias de Dragão: Técnica do Grande Dragão de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Ryūmyaku: Super Técnica do Grande Dragão de Fogo", "Usando o chakra de Ryūmyaku, Mukade dispara uma versão maior e mais poderosa do Ryūmyaku: Grande Dragão de Fogo do estômago de seu corpo de marionete. Ele afirma que esta é a sua técnica mais poderosa. Ao contrário de sua versão menor, esta técnica não causa danos simplesmente flamejantes, mas provoca uma grande explosão que pode criar uma enorme cratera no chão, e não deixa as chamas roxas residuais.", "Todos os alcances", null, null, "龍脈・超豪龍火の術", "Ryūmyaku: Chō Gōryūka no Jutsu", "Veias de Dragão: Super Técnica do Grande Dragão de Fogo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sabedoria da Rainha Pavão", "Depois de formar os selos de mão necessários e recitar um encantamento, usando esta técnica, Mukade absorve o selo colocado pelo Quarto Hokage, portanto, ele é capaz de viajar no tempo. As pessoas presas na explosão também são enviadas através do tempo. Especificamente, Mukade viajou de volta por 20 anos, seis anos antes do dia em que o selo foi colocado, embora Naruto Uzumaki e Yamato chegaram seis anos mais tarde.", null, null, "Cavalo → Macaco ou Coelho → Carneiro", "孔雀明王", "Kujaku Myōō", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sabre do Trovão", "Sabre do Trovão (雷光衝破突, Sandā Sāburu) é uma técnica utilizada por Ranke ao extrair energia da Pedra de Gelel.", "Curto alcance", null, null, "雷光衝破突", "Sandā Sāburu", "Colisão Penetrativa de Relâmpago", null, null, null);
