INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Salgueiro (Ultimate Ninja Storm)", "Kimimaro chuta o oponente e executa sucessivos ataques com suas espadas ósseas. Em seguida, ele usa oponente como suporte e pula por cima dele com um mortal, finalizando com um ataque de ponta-cabeça com suas espadas ósseas por trás do oponente.", "Curto alcance", null, null, "柳", "Yanagi", "Salgueiro", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Salgueiro do Estilo Konoha", "O usuário faz movimentos ondulares para trás com sua espada, que, quando visto pelo alvo bloqueia-os em um genjutsu que lhes causa alucinação de múltiplos braços ondulando. O movimento se assemelha ao de uma árvore de salgueiro soprando no vento. Com o inimigo desprevenido, o usuário é livre para cortá-lo.", "Curto alcance", null, null, "木ノ葉流柳", "Konoharyū Yanagi", "Salgueiro do Estilo Folha da Árvore", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Salto das Cinco Montanhas", "Usando a força reforçada, concedida pela sua Armadura de Vapor, Han da um poderoso soco em seu adversário, e enviá-o pro ar. Ele então pula atrás dele e se transforma em Kokuō, ele da uma cabeçada e em seguida esmaga o adversário sob o seu casco tão fortemente que ele rompe o solo e gera uma onda de choque impetuosa.", "Curto alcance", null, null, "五山跳びござんとび", "Gozan Tobi", "Salto das Cinco Montanhas", "Pulo das Cinco Montanhas", null, null);
