INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Seis Caminhos: Kunitsukami", "O Kunitsukami é um modo de chakra despertado por aqueles que possuem o Chakra Sábio do Seis Caminhos em questão. O modo permite que o usuário entre em um auge absoluto, concedendo-lhe até mesmo as Esferas da Busca da Verdade e um espírito de luta destrutivamente comparável ao de um Susanoo de corpo inteiro.", null, null, "Juntar as Maos", "六道・国津守", "Rikudō: Kunitsukami", "Seis Caminhos: Guardião do País", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Seis Caminhos: Shinra Tenkyō", "Tirando proveito de seu poder, Naruto toca em Sasuke e lhe transfere o chakra das bestas com cauda. Com isso, Naruto manifesta seu avatar colossal de Kurama com três cabeças e usa sua poderosa técnica empregando o uso do Rasenshuriken, da mesma forma, Sasuke adota a forma gigantesca do Susanoo reforçado com o chakra das bestas com caudas e usa a Flecha de Indra. Ambos lançam suas técnicas simultaneamente contra seu alvo que, ao colidirem, causam uma poderosa explosão de energia e raios que cobre todo o céu.", "Todos os alcances", null, null, "六道・神羅天叫りくどう・しんらてんきょう", "Rikudō: Shinra Tenkyō", "Seis Caminhos: Grito Celestial do Deus Onipresente", "Seis Caminhos: Grito Todo Poderoso", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Seis Caminhos: Ultra-Grande Bola Rasenshuriken", "Naruto, enquanto em seu modo Kurama de Ashura, cria uma Liberação de Vento: Ultra-Grande Bola Rasenshuriken em seus braços direitos e uma Grande Bola: Rasenshuriken da Besta com Cauda em seus braços esquerdos, ambos infundidos com energia natural. Isso é forte o suficiente para combater a mais forte técnica ofensiva de Sasuke.", "Todos os alcances", null, null, "六道・超大玉螺旋手裏剣りくどう・ちょうおうだまらせんしゅりけん", "Rikudō: Chōōdama Rasenshuriken", "Seis Caminhos: Ultra-Grande Bola Shuriken Espiral", "Seis Caminhos: Rasen Shuriken Gigante", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Seis Caminhos de Pain", "Os Seis Caminhos de Pain (ペイン六道, Pein Rikudō) é uma técnica do Caminho Exterior concebida por Nagato depois de ter sido prejudicado por Hanzō e se tornado magro pelos inúmeros receptores de chakra embutidos em suas costas pela Estátua Demoníaca do Caminho Exterior. Perfurando cadáveres com receptores de chakra, um portador do Rinnegan pode manipular os corpos como se fossem seus próprios.", "Todos os alcances", null, null, "ペイン六道ペインりくどう", "Pein Rikudō", "Seis Caminhos de Pain", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Seis Caminhos de Saṃsāra", "Os Seis Caminhos de Pain atacam simultaneamente o adversário, então o Caminho Asura lança-o no ar e os outros caminhos o empalam com as Lâminas de Rompimento de Chakra.", "Todos os alcances", null, null, "輪廻六道", "Rinne Rikudō", "Seis Caminhos de Saṃsāra", "Seis Caminhos da Reencarnação", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Seis Formações Vermelhas Yang", "Uma barreira extremamente poderosa, do mais alto nível, que não pode ser usado por qualquer outra pessoa, exceto por aqueles abençoados com o Poder do Seis Caminhos. É superior à Quatro Formações Vermelhas Yang, tanto em tamanho, quanto em durabilidade. Aqueles que despertaram o Rinnegan, utilizam os receptores negros e disparam-os para vários locais no mapa, em seguida, o usuário manifesta a barreira colossal.", "Longo alcance", null, "Cobra → Rato → Selo de Técnica Especifica → Rato Invertido", "六赤陽陣むせきようじん", "Musekiyōjin", "Seis Formações Vermelhas Yang", null, "Formação dos Seis Sóis Vermelhos", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selamento Combinado: Supressão Yang do Macaco", "Após plantar etiquetas de selamento na área, os usuários criam uma barreira de selamento que toma a forma de uma mão de macaco transparente, a qual captura o alvo. A técnica requer uma quantidade substancial de chakra para completar o selo, podendo rapidamente cansar o usuário e permitindo que o alvo escape.", null, null, null, "コンボ封印・猿陽封殺", "Combo Fūin: Enyō Fūsatsu", "Selamento Combinado: Supressão Yang do Macaco", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selamento da Besta com Cauda", "Sempre que Naruto Uzumaki entrar em uma forma de cauda, o Colar do Primeiro Hokage ativa essa técnica para contê-lo. Não se sabe se a técnica apenas restringe Naruto até Yamato suprimir a influência da Nove-Caudas ou se suprime a influência por si só já que a Nove-Caudas cancelou a técnica esmagando o colar. Ele também alerta Yamato de quantas caudas Naruto desenvolveu no momento.", null, null, null, "尾獣封印びじゅうふういん", "Bijū Fūin", "Selamento da Besta com Cauda", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selamento do Grande Funeral do Deserto em Camadas", "Utilizando uma grande quantidade de areia, o usuário cobre o inimigo com várias camadas sucessivas do material, antes de comprimi-lo na forma de uma pirâmide enorme que serve para prender o alvo após a sua conclusão. Podem ser inseridas etiquetas de selamento sobre a pirâmide, para aumentar seu poder de selamento, ou Shukaku pode fortalecer o selo com o juinjutsu que cobre seu corpo.", "Todos os alcances", null, null, "砂漠層大葬封印さばくそうたいそうふういん", "Sabaku Sōtaisō Fūin", "Selamento do Grande Funeral do Deserto em Camadas", "Enterro de Areia em Camadas Gigantes", "Grande Pirâmide Mortuária de Areia", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selamento do Poder Budista", "Trata-se de um fūinjutsu que pode ser usado em conjunto com Técnica de Selamento do Poder Budista: Sen no Rikyū para selar uma besta com cauda dentro de uma pessoa, tornando-a um jinchūriki. O selo em si adquire a forma da caligrafia japonesa.", "Curto alcance", null, null, "法力封印", "Hōriki Fūin", "Selamento do Poder Budista", null, null, "Selo Dharma");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Amaldiçoado Animal", "Este é um selo amaldiçoado único que foi aplicado por Orochimaru ainda em seus estágios experimentais e, portanto, estava incompleto e instável.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Amaldiçoado de Genjutsu", "Uma técnica que permite ao usuário manifestar uma marcação espiral em uma pessoa. Ao executá-la, o genjutsu permite que a vítima seja manipulada para acreditar nas informações contadas pelo usuário. A técnica, aparentemente, requer reaplicações regulares, já que gradualmente a marca desaparece, fazendo com que as verdadeiras memórias da vítima voltem a surgir em uma dor de cabeça induzida por enxaqueca.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Amaldiçoado do Clã Iburi", "Esse selo amaldiçoado é aplicado por Orochimaru nos membros do clã Iburi a fim de estabilizar a sua kekkei genkai.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Amaldiçoado do Quarteto do Som", "Cada membro do Quarteto do Som usa um selo amaldiçoado. Seus selos, no entanto, não são tão poderosos quanto os selos do Céu e da Terra, embora tenham resultados finais semelhantes. Como todos os selos amaldiçoados, cada um deles vigorosamente atrai o chakra do usuário, enquanto alimenta de volta com o chakra do senjutsu, dando a cada indivíduo uma transformação única e impulso atributo. No entanto, Sakon comenta que depois de usar o segundo nível de seu selo amaldiçoado e sair dela, eles perdem muita energia.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Aprisionador do Veado e da Borboleta", "Shikamaru imobiliza o oponente com sua sombra enquanto Chōji expande seu corpo para esmagá-lo com um pisão.", "Curto alcance", null, null, "鹿蝶封結かちょうふうけつ", "Kachōfūketsu", "Selo Aprisionador do Veado e da Borboleta", "Kachofuketsu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Consumidor do Demônio Morto", "O Selo Consumidor do Demônio Morto é um selo desenvolvido pelo Clã Uzumaki para invocar o poder do Shinigami.", "Todos os alcances", "Rank S", "Cobra → Javali → Carneiro → Coelho → Cao → Rato → Passaro → Cavalo → Cobra → Juntar as Mãos", "屍鬼封尽", "Shiki Fūjin", "Selo Consumidor do Demônio Morto", null, "Selo do Aprisionamento Demoníaco", "Jutsu de Selamento: Selo do Ceifeiro da Morte");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Consumidor do Demônio Morto: Liberar", "Esta técnica envolve a invocação do Shinigami usando uma máscara do Templo Armazenador de Máscara do Clã Uzumaki. A partir daí, o invocador é possuído pelo Shinigami permitindo-lhe o controle sobre ele, ele pode forçá-lo a cortar o seu próprio estômago resultando na liberação das almas seladas dentro. Devido à posse no entanto, o mesmo dano vai refletir sobre o próprio corpo do invocador.", "Curto alcance", null, null, "屍鬼封尽・解しきふうじん・かい", "Shiki Fūjin: Kai", "Selo Consumidor do Demônio Morto: Liberar", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Gedō: Liberar", "Essa técnica foi criada por Hiruzen na Segunda Guerra Mundial Ninja preocupando-se com os efeitos cruéis da Técnica Proibida: Selo Gedō. Esta técnica pode curar os efeitos do Selo do Caminho Exterior. Tsunade usa essa técnica para curar o Sharingan de Kakashi.", "Curto alcance", null, "Cachorro → Rato → Passaro → Cobra → Javali → Tigre → Dragao → Macaco → Cobra → Boi → Carneiro → Pássaro", "外道の印・解げどうのいん・かい", "Gedō no In: Kai", "Selo Caminho Exterior: Liberar", "Técnica Proibida: Marca do Gedō: Liberar", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Proibido da Maldição Individual", "Este é um selo amaldiçoado, que é colocado diretamente no coração do alvo.", null, null, null, "禁個呪の札きんこじゅのふだ", "Kinkoju no Fuda", "Selo Proibido da Maldição Individual", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Torii", "Quando Naruto adquiriu o Modo de Chakra do Nove-Caudas, a aparência exterior de seu selo anterior se transformou em uma forma de uma das magatamas do Sábio dos Seis Caminhos, junto com um redemoinho que se assemelhava ao símbolo de Uzushiogakure.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo Yin: Liberar", "Quando o usuário do Selo da Força de Uma Centena faz uso dessa técnica, o selo em formato de diamante alastra-se no usuário como 'fitas', e instantaneamente solta a reserva de chakra diretamente no organismo do utilizador. Se houver um restante de chakra ao terminar de fazer uso da reserva de chakra, o utilizador consegue usar o restante na reforma do selo.", null, "Rank S", null, "陰封印・解いんふういん・かい", "Infūin: Kai", "Selo Yin: Liberar", null, "Liberação do Selo Yin", "Selo Reserva: Liberar");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo da Armadura de Ferro", "O Selo da Armadura de Ferro é o fūinjutsu usado por Kumogakure para selar Gyūki em seus respectivos hospedeiros.", null, null, null, "鉄甲封印てっこうふういん", "Tekkō Fūin", "Selo da Armadura de Ferro", "Selo do Escudo de Aço", "Selo de Blindagem de Ferro", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo da Escultura de Dedo", "O usuário concentra chakra em seu dedo e, usando o calor deste, pode aplicar a escrita em tudo o que ele quiser numa superfície. Esta técnica exige, contudo, que ele seja muito delicado com seu controle de chakra. Esta técnica causa um leve desconforto em um alvo vivo e, se ele é de carne, cicatrizará.", null, "Rank C", null, "指刻封印しこくふういん", "Shikoku Fūin", "Selo da Escultura de Dedo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo da Força de Uma Centena", "Um selo com a fama de ser o auge do controle de chakra. Quando armazena-se um imenso valor de chakra durante muito tempo em um único local do corpo — geralmente na testa — o selo então é formado, manifestando-se como uma marcação na forma de um losango. Uma vez formado, o usuário garante um controle de chakra muito alto, conseguindo executar técnicas sem nenhum gasto de energia. Quando libera-se o selo, ele começa a se alastrar no rosto do usuário ou até mesmo no corpo inteiro, e em seguida, o chakra armazenado é liberado, aumentando em muito a força das técnicas do usuário.", null, "Rank S", null, "百豪の印びゃくぎうのいん", "Byakugō no In", "Selo da Força de Uma Centena", null, "Selo das Cem Forças", "Selo Reserva");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Apagar Memória", "Esta técnica elimina certas lembranças de todos aqueles que estão no selo, incluindo a do usuário, com o comando 'destruir' (滅, metsu). No entanto, é possível que o alvo possa, mais uma vez lembrar o que foi apagado como um sonho vago.", "Curto alcance", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Ataúde do Dez-Caudas do Seis Caminhos", "O Selo de Ataúde do Dez-Caudas do Seis Caminhos (六道十尾柩印, Rikudō Jūbi Kyūin) é uma técnica de selamento usada por Obito e Madara Uchiha para selar o Dez-Caudas em si mesmos, tornando-se assim o jinchūriki da besta. Depois que um usuário do Rinnegan executa os selos de mão necessários, enquanto em estreita proximidade com o Dez-Caudas, a besta é rapidamente absorvida para o corpo do usuário.", "Curto alcance", "Rank –", "Carneiro espelhado", "六道十尾柩印りくどうじゅうびきゅういん", "Rikudō Jūbi Kyūin", "Selo de Ataúde do Dez-Caudas do Seis Caminhos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Auto-Amaldiçoamento", "Trata-se de uma técnica similar à Técnica de Erradicação da Língua Amaldiçoada, este selo também pode ser aplicado durante um combate, e pode ser secretamente colocado no corpo do oponente. Quando o usuário o ativa, as marcas do selo amaldiçoado espalham-se pelo corpo do oponente, paralisando-o. No entanto, é possível se livrar do selo através de uma liberação de chakra forte o suficiente.", "Curto alcance", null, null, "自業呪縛の印じごうじゅばくのいん", "Jigō Jubaku no In", "Selo de Auto-Amaldiçoamento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Chakra da Besta com Cauda", "Esta é uma técnica de colaboração em que oito pessoas realizam simultaneamente o selo de mão do Tigre, formando uma grande fórmula de selagem com um jinchūriki em seu centro. A fórmula, então, começa a brilhar e suprimir o chakra da Bijuu.", "Curto Alcance", null, "Tigre", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Contrato", "Quando o usuário executa esta técnica em um invocador, ele remove quaisquer controles do invocador sobre uma criatura invocada. O contato, para a implementação do selo, é necessário. Minato Namikaze usou esta técnica para remover Kurama do controle do Sharingan de Tobi. Ainda assim, mesmo removendo a capacidade do invocador sobre uma criatura, o usuário também não é capaz de controlá-la.", "Curto alcance", null, null, "契約封印けいやくふういん", "Keiyaku Fūin", "Selo de Contrato", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Drenagem de Chakra", "Este é um fūinjutsu projetado para drenar chakra. Ao colocar o selo no alvo, o chakra é drenado e transferido para o usuário. Kabuto utilizou para absorver os restos remanescentes do chakra de Orochimaru que estava dentro do selo amaldiçoado de Anko Mitarashi.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Quatro Símbolos", "Esse é uma técnica de selamento baseada nos fūinjutsu de Uzushiogakure. Essa fórmula de selamento é gravada num corpo humano ou num objeto, e é principalmente usada quando um inimigo gigante ou um espirito do mal precisa ser selado. Para usá-lo, é necessário uma grande habilidade do usuário.", "Curto alcance", "Rank –", null, "四象封印", "Shishō Fūin", "Selo de Quatro Símbolos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Teletransporte de Quatro Cantos", "Se qualquer um passar pela etiqueta com o selo, sapos são invocados para parar os inimigos que desejam passar através do selo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Transcrição: Amaterasu", "Esta é uma técnica utilizável apenas por aqueles que despertaram o Mangekyō Sharingan, é um ninjutsu de selamento da Liberação de Fogo. A técnica sela o fogo de mais alto nível, Amaterasu, no Sharingan de outro ninja, uma vez que o Sharingan ao qual a técnica foi selada vê o alvo especificado, o selo é quebrado e a chama negra que consome até mesmo chamas ataca sua presa.", "Curto alcance", "Rank –", null, "転写封印・天照てんしゃふういん・あまてらす", "Tensha Fūin: Amaterasu", "Selo de Transcrição: Céu Iluminado", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Transcrição: Izanagi", "Esta é uma técnica que sela a técnica Izanagi no Sharingan do alvo. Depois de ser selado dentro da pessoa, é involuntariamente ativado quando certos requisitos, que são pré-determinados pelo usuário, sejam satisfeitos. Madara Uchiha usou isso em seu próprio olho direito, para a técnica ser ativada depois que ele morrer, a fim de reescrever a sua morte em um mero sonho.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo de Transcrição: Kotoamatsukami", "Esta é uma técnica que sela o Kotoamatsukami no Sharingan do alvo. Depois de ser selado dentro da pessoa, ele é involuntariamente ativado quando certos requisitos, que são pré-determinados pelo usuário, sejam satisfeitos.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo do Corte Relâmpago", "Empunhando sua espada infundida com relâmpago, o usuário rapidamente desfere um corte no oponente, selando temporariamente sua capacidade de usar chakra para executar novas técnicas.", "Curto alcance", null, null, "雷切封らいきりふう", "Raikirifū", "Selo do Corte Relâmpago", "Corte do Selamento", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo do Hexagrama de Sensoriamento", "O usuário cria um selo em forma de hexagrama no solo o qual o permite a sensoriar alvos num raio de grande amplitude através da transmissão de ondas pelo selo. Esta técnica também permite identificar a classificação de um ninja.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo do Santuário", "O Selo do Santuário é um selo localizado no País dos Demônios, e contém o corpo do espírito maligno de Mōryō.", null, null, "Hatsu → Jin → Kai → Min → Shin → Gan → Reppyo → Shozen → Mika → Dan → Raku → Sho → Fu → Sai → Dan → Gik → Ragu → Bagi", "社の封印", "Yashiro no Fūin", "Selo do Santuário", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo do Santuário: Liberar", "Depois de realizar os selos de mão (ou melhor, cantar em voz alta como um encantamento), o usuário libera uma onda de choque escuro que destrói os talismãs sobre o Selo do Santuário, e quebra a porta de pedra, libertando a alma aprisionada do Mōryō. Isto pode ser uma variante do Selo do Santuário, ou mesmo a contraparte, uma vez que contém uma porção dos selos de mão que a técnica de selamento faz.", null, null, "Min → Shin → Gan → Reppyo → Shozen → Mika → Dan → Raku → Sho", null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo do Sino Místico de Segurança: Liberar", "Técnica utilizada por Shion, consiste em liberar o poder através do Sino de Segurança Místico, e se transformar. Isto permite que ela envie um enorme raio de luz que pode penetrar a escuridão de Mōryō. Nesta forma, ela teve a capacidade de (tentar) destruir completamente Mōryō junto com ela mesma. No entanto, Shion acabou sendo puxada por Naruto antes que pudesse fazê-lo.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Selo dos Cinco Elementos", "Essa técnica produz um selo poderoso que é usado para bloquear ou atrapalhar o fluxo do chakra num alvo. Assim que o selo é completo, o alvo ficará inconsciente e incapaz de lutar.", "Curto alcance", "Rank A", "Passaro", "五行封印", "Gogyō Fūin", "Selo dos Cinco Elementos", null, null, "Selo de Cinco Pontos");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Senbon de Agulha de Cabelo", "É uma técnica em que o cabelo é enrijecido e disparado em sequência no inimigo como se fosse uma chuva de espinhos, transformando o alvo em uma montanha de agulhas.", "Curto a Médio alcance", "Rank –", null, "毛針千本", "Kebari Senbon", "Senbon de Agulha de Cabelo", null, null, "Arte Ninja: Agulha Senbon");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Senbon de Escama", "Amachi abre os braços e lança suas escamas em alta velocidade no alvo. Esta técnica tinha poder o suficiente para dissipar um clone sombra e jogar Naruto Uzumaki para trás em uma distância considerável. Além disso, ele tinha uma gama de alcance bastante ampla para acabar com seis clones de sombra de uma vez.", "Curto a Médio alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Senjutsu", null, null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sensoriamento Negativo", "É uma técnica sensorial exclusiva para Kurama e seus jinchūriki. Usando esta técnica, o usuário é capaz de detectar as emoções negativas, como ódio, de qualquer indivíduo em torno deles.", "Todos os alcances", null, null, "悪意感知あくいかんち", "Akui Kanchi", "Sensoriamento Negativo", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sensoriamento Triunfante", "Graças aos árduos treinos de concentração, o usuário é capaz de sentir a presença de seus inimigos durante uma batalha ao fechar os olhos, um estilo que Guy chama de 'Olhos do Coração' (心の目, Kokoro no Me). Esta técnica torna-se muito útil quando o usuário tem alguns de seus cinco sentidos básicos afetados, por exemplo enquanto ele estiver em meio de uma cortina de fumaça odorizada.", "Curto alcance", null, null, "ガッツセンサー", "Gattsusensā", "Sensoriamento Triunfante", null, null, "Sensor de Espírito");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sensoriamento de Medo", "Devido à Caixa da Felicidade Suprema, o corpo de Satori pode detectar medo e intenções assassinas, o que faz com que ele consiga instintivamente esquivar de quaisquer ataques de seus inimigos que têm medo inclusos. Esta capacidade, combinada com seus reflexos surpreendentemente ágeis, lhe permitiram esquivar todos os ataques dos 11 de Konoha e Naruto Uzumaki, mesmo enquanto ele estava no Modo Sábio.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sensual: Técnica da Menina com Menina", "A Técnica do Harém de Naruto, personalizada por Konohamaru. É feita utilizando a Técnica dos Clones das Sombras e consecutivamente a Técnica de Transformação, por ter de usar o próprio usuário e o clone transformado em pessoas diferentes. Quando várias meninas diferentes parecem sedutores, as chances de satisfazer as preferências do alvo são elevadas significativamente. Konohamaru inventou uma forma alternativa da técnica para trabalhar em mulheres, Sensual: Técnica do Menino com Menino.", null, "Rank D", null, "おいろけ・女の子どうしの術", "Oiroke: Onnanoko Dōshi no Jutsu", "Sensual: Técnica da Menina com Menina", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sensual: Técnica do Menino com Menino", "Uma técnica que implementa o Sensual: Técnica da Menina com Menina. Uma técnica que confunde e excita as mulheres, transformando-se em dois homens nus. Ao contrário de quando se transformando em meninas, outras coisas são vitais além proporções, como a seleção e combinação dos homens em que se transforma e como eles representam. Ao ser bem familiarizado com a pessoa que a técnica é direcionada para a transformação e adaptação em conformidade, pode-se entregar um impacto ainda maior.", null, "Rank D", null, "おいろけ・男の子どうしの術", "Oiroke: Otokonoko Dōshi no Jutsu", "Sensual: Técnica do Menino com Menino", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sepultura de Areia", "Gaara, manipulando sua areia, cria uma curta linha embaixo de seus pés, que tem um alcance pequeno. Em seguida, ele vibra essa areia e se agacha tocando o chão com sua mão, assim a linha de areia se estende até embaixo dos pés do oponente, vibrando agora em uma potência maior, atingindo o oponente.", "Curto alcance", null, null, null, null, null, "Sepultura de Areia", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sermão", "Iruka acerta o oponente com diversos golpes de kunai e chutes, finalizando com um grito estrondoso que manda o oponente para longe.", "Curto alcance", null, null, "お説教おせっきょう", "Osekkyō", "Sermão", "Reclamação", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Serpente de Aço Líquido", "Depois de usar a Invocação: Serpente de Duas Cabeças, serpentes emergem da boca da cobra invocada, assim entrando em uma ordem contínua do processo na tentativa de capturar seu alvo. Como as cobras continuam a se multiplicar, elas se tornam cada vez menores e diminui o potencial da técnica.", "Curto a Médio", null, null, "蛇鋼網", "Dakōmō", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sete Espadas da Névoa Sangrenta", "Os Sete Espadachins Ninja da Névoa reúnem-se, e uma névoa espessa envolve o campo de batalha. Ameyuri dirige um ataque, entregando um corte horizontal reforçado com eletricidade com as Kiba, seguido por Kisame cortando com a Samehada na direção contrária. Jinin lança o oponente no ar com a parte machado da Kabutowari, onde é encontrado pelo ataque de Jinpachi utilizando a Shibuki, que cria uma explosão. De certa distância, Mangetsu corta o oponente com a forma de espada longa de Hiramekarei, e Kushimaru esfaqueia-o repetidas vezes, amarrando-os no lugar com fios de aço. Finalizando, Zabuza aterra um golpe final com a Kubikiribōchō.", "Curto alcance", null, null, "血霧の七刀", "Chigiri no Shichigatana", "Sete Espadas da Névoa Sangrenta", "Sete Lâminas da Névoa Sangrenta", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sharingan", "O Sharingan (写輪眼; Literalmente significa 'Olho Copiador Giratório'; Significado (Panini) 'Olho Espelhador Giratório') é um dōjutsu kekkei genkai, que aparece em alguns membros do clã Uchiha. Apesar de seus poderes serem vistos pela primeira vez em Kaguya Ōtsutsuki, com o Rinne Sharingan, sua verdadeira forma se manifestou pela primeira vez em Indra Ōtsutsuki, o ancestral mais antigo do clã Uchiha, e mais tarde veio a ser considerado como um dos Três Grandes Dōjutsu (三大瞳術, Sandai Dōjutsu; Literalmente significa 'Três Grandes Técnicas Oculares'), juntamente com o Byakugan e o Rinnegan.", null, null, null, "写輪眼しゃりんがん", "Sharingan", "Olho Copiador Giratório", null, "Olho Espelhador Giratório", "Olho de Sharingan");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shinra Tensei", "Shinra Tensei é a habilidade do Caminho Deva para manipular a força repulsiva (斥力, sekiryoku) à vontade do usuário para empurrar a matéria ou técnicas ninja à distância. Somente os portadores do Rinnegan que dominaram todas as transformações básicas da natureza têm acesso a esta técnica.", "Todos os alcances", "Rank –", "Juntar as Maos[4]", "神羅天征しんらてんせい", "Shinra Tensei", "Subjugação Celestial do Deus Onipresente", null, "Domínio Divino", "Punição Divina");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuradō", "O Caminho Asura (修羅道, Shuradō) concede ao usuário a capacidade de invocar armadura mecanizada, aumentando seu corpo com membros extras e vários armamentos mecânicos. Em vida, Nagato canalizou o Caminho Asura através do corpo de um ninja titereiro.", null, null, null, "修羅道しゅらどう", "Shuradō", "Caminho Asura", null, "Reino da Guerra", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuriken de Areia", "Uma técnica utilizada por Gaara enquanto no processo de transformação para sua forma perfeita. Ele molda a areia que compõe seus braços em incontáveis shuriken e balança os ambos os membros violentamente. Aproveitando-se dessa formidável força centrífuga, as lâminas de areia atiradas tornam-se perigosas armas e, dessa forma, o usuário dispara uma sucessão interrupta de shuriken de areia, que voam selvagemente rasgando tudo em seu caminho.", "Curto a Médio alcance", null, null, "砂手裏剣", "Suna Shuriken", "Shuriken de Areia", "Técnica da Shuriken de Areia (砂手裏剣の術, Suna Shuriken no Jutsu)[2]", "Shuriken da Areia", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuriken de Flor: Flores em Queda e Folhas em Espalhamento", "Esta técnica faz com que as flores pairem no ar antes de endurecer e girarem em altíssima velocidade. As flores vão em direção ao alvo, e por estarem totalmente endurecidas pelo chakra, elas recebem a função de uma shuriken normal, que realiza cortes no corpo do alvo.", "Médio a Longo alcance", null, null, "花手裏剣・飛花落葉はなしゅりけん・ひからくよう", "Hana Shuriken: Hikarakuyō", "Shuriken de Flor: Flores em Queda e Folhas em Espalhamento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuriken de Papel", "Esta é uma técnica que consiste em despejar chakra em um pedaço de papel em uma fração de segundo, endurecendo e dando forma, então ele pode ser usado como uma shuriken. A sua forma é igual à de uma shuriken feita de metal. O utilizador pode aumentar ainda mais a força da shuriken, transformando-a em determinadas formas e ainda manusear as mesmas sem precisar ter contato. O tempo de contato e o esforço colocado no papel fazem a eficiência do corte se elevar ao extremo. Konan é vista usando-as juntamente com a Liberação de Vento: Palma Vendaval de Nagato, agilizando a shuriken e permitindo-lhes penetrar o corpo do inimigo ainda mais.", "Médio a Longo alcance", "Rank D", null, "紙手裏剣かみしゅりけん", "Kami Shuriken", "Shuriken de Papel", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuriken de Telha", "Esta técnica permite ao usuário controlar remotamente, levitar e arremessar telhas girando em uma área próxima. Além de ser capaz de controlar remotamente as telhas, o utilizador pode exercer pressão sobre elas com chakra para torná-las muito mais fortes do que o normal.", "Curto à Médio alcance", null, "Tigre → Dragao → Rato → Cavalo → Tigre", "瓦手裏剣", "Kawara Shuriken", "Shuriken de Telha", null, null, "Arte Ninja: Shuriken de Telha");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shuriken de Todas Direções", "Uma técnica que cerca perfeitamente o inimigo em todas as direções com sua própria 'sombra'. Naruto e seus clones das sombras arremessam várias shuriken de uma só vez. Evitar toda a chuva de shuriken é difícil, mesmo para as pessoas mais ágeis. O único modo de se evitar esse ataque é se o inimigo conseguir focar seus sentidos nas shuriken de cima. Seu próximo passo é atacar o ponto cego, porém, ele dificilmente é criado. À primeira vista, parece ser um movimento desesperado, mas é um ataque inteligente, com uma trama oculta para conduzir o inimigo em uma situação difícil, dando um senso de domínio de Naruto sobre o método de assassinato com os clones das sombras.", "Curto a Médio alcance", "Rank A", null, "四方八方手裏剣", "Shihōhappō Shuriken", "Shuriken Indefensável", null, "Shuriken pra Todo Lado", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shurikenjutsu", null, null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Shurikenjutsu de Relâmpago do Estilo Uchiha: Triplicação", "Uma técnica que emprega conceitos de shurikenjutsu transmitidos dentro do clã Uchiha, com a qual o usuário prepara três shurikens e as infunde com chakra de relâmpago, aprimorando seu poder destrutivo. As shurikens então são lançadas para três direções distintas, acertando múltiplos alvos ou um único alvo em diferentes ângulos.", "Todos os alcances", null, null, "うちは流手裏剣術 「雷」・三連うちはりゅうしゅりけんじゅつ 「いかずち」・さんれん", "Uchiha-Ryū Shurikenjutsu — 'Ikazuchi': Sanren", "Shurikenjutsu de Relâmpago do Estilo Uchiha: Triplicação", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Soco 100% Único", "O usuário canaliza uma grande quantidade de chakra em sua mão e ataca o oponente no estômago.", "Curto alcance", null, null, "100％シングルパンチ", "100-Pāsento Shingurupanchi", "Cem Por Cento Soco Único", "Soco 100% Único", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Soco Naruto", "Mecha-Naruto ataca o oponente com um soco alimentado com um jato.", "Curto alcance", null, null, "ナルトパンチ", "Naruto Panchi", "Soco Naruto", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Soco de 1000 Metros", "O usuário concentra uma grande quantidade de chakra em seu punho e soca o adversário, enviando-o à pelo menos um quilômetro (1.000 metros) de distância.", "Curto alcance", null, null, "1000Mパンチ", "1000M Panchi", "Soco de Mil Metros", "Soco de 1000m", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Soco de 100 Metros", "Uma versão pequena do Soco de 1000 Metros. O usuário concentra seu chakra em seu punho e usa um golpe devastador sobre o adversário, fazendo ele voar para pelo menos 100 metros de distância de onde o usuário se encontrava.", "Curto alcance", null, null, "100mパンチ", "100 Panchi", "Soco de Cem Metros", "Soco de 100m", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Som dos Sinos", "Kin usa essa técnica com os sinos que ela atira junto com suas senbon. O toque do sino produz uma onda sonora especial, que viaja a partir da orelha interna, o que afeta o cérebro do adversário diretamente. Isso faz com que seu alvo tenha alucinações, vendo várias imagens dela, e tornando muito difícil dizer o que é real e o que são ilusões. Além disso, o alvo perde suas habilidades motoras com a exposição continuada ao toque. Esta técnica é normalmente utilizada depois que ela distrai o inimigo com as Agulhas da Sombra.", "Médio alcance", null, null, "鐘の音", "Kane no Oto", "Som dos Sinos", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sombra da Folha Dançante", "Uma técnica onde se segue o adversário ou o movimento do seu corpo, tal como uma folha que dança no ar que é seguida pela sua sombra. A técnica é geralmente iniciada por um chute alto que irá lançar o alvo no ar. É usado principalmente para mudar a posição do oponente em uma posição vulnerável aérea. Este movimento, por si só é inofensivo, mas é utilizável como um trampolim para um grande número de técnicas poderosas, como a Lótus Frontal e o Combo do Leão. Dito isto, também tem um grau bastante elevado de dificuldade.", "Curto alcance", "Rank C", null, "影舞葉かげぶよう", "Kage Buyō", "Sombra da Folha Dançante", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sonchus Asper", "O usuário, enquanto em sua forma de selo amaldiçoado nível dois, dá um pulo estilo mortal e ataca o oponente com sua cauda criada através do selo amaldiçoado.", "Curto alcance", null, null, "オニノゲシ", "Oninogeshi", "Sonchus Asper", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sonho da Folha Carmesim", "Kurenai ataca seu oponente com um ataque combo triplo resumindo em um golpe de cotovelo e dois chutes antes de desaparecer em uma névoa na frente do oponente e, em seguida, aparecer novamente atrás dele em uma névoa rosa golpeando-o com uma kunai por trás, que, em seguida, lança o oponente no ar.", "Curto alcance", null, null, "紅葉夢", "Kōyōmu", "Sonho da Flor Carmesim", "Sonho da Folha Vermelha", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sopro de Vento do Gunbai", "O usuário usa seu gunbai como um leque para criar poderosas rajadas de vento, fortes o bastante para afastar vários inimigos de uma só vez.", "Todos os alcances", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Soulbane", "Soulbane é um genjutsu poderoso conhecido por ser proibido entre os ninjas de Konohagakure.", null, null, null, null, null, null, "Soulbane", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Subida do Pardal", "Sai, enquanto em um curto alcance, libera de um pergaminho pássaros feitos de tinta em tamanho médio, dos quais primeiro levantam o oponente para cima, que está sendo levantado ainda mais a cada segundo pelos mesmos.", "Curto Alcance", null, null, "登燕雀", "Tōenjaku", null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Subida do Pássaro Triturador", "Sasuke atinge o oponente sob o queixo com a bainha da espada. Então ele chuta-o três vezes enviando-o ainda mais para o alto. Como o oponente cai no chão, Sasuke aparece onde ele está prestes a cair e bate-o poderosamente no intestino, levando-o a colidir com o que quer que esteja no caminho.", "Curto a Médio alcance", null, null, "翔け鳥崩し", "Kakedori Kuzushi", "Subida do Pássaro Triturador", "Esforço do Pássaro Triturador", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Substituição Orizuru", "Antes de ser atingido por um ataque, Urashiki ativa seu Rinnegan e troca de lugar com dezenas de orizurus vermelhos, a fim de evitar ser atingido. Após a troca, o chakra do usuário é completamente mascarado enquanto a fuga é realizada.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Substituição de Água", "Quando o usuário é atingido por um ataque esta técnica vai transformá-lo em água para assim evitar levar danos diretos de quaisquer formas de investida.", null, null, null, "水変わり身みずかわりみ", "Mizu Kawarimi", "Substituição de Água", null, null, "Arte Ninja: Técnica da Submissão de Água");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Substituição por Marionete", "A Substituição por Marionete é uma técnica variante da Técnica de Substituição do Corpo que concede ao usuário a capacidade de substituir seu corpo por uma de suas marionetes, e utilizar como um método de distração para a realização de alguma técnica de captura.", null, null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sucessão de Fogo, Água, Terra e Relâmpago", "A Sucessão de Fogo, Água, Terra e Relâmpago trata-se de uma formação de ataques elementais utilizada pela Primeira Divisão para conter Kinkaku.", "Todos os alcances", null, null, "火、水、土と雷の連続ひ,みず,つちとかみなりのれんぞく", "Hi, Mizu, Tsuchi to Kaminari no Renzoku", "Sucessão de Fogo, Água, Terra e Relâmpago", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sucção Esmagadora das Três Jóias", "Esta técnica é uma performance das marionetes 6, 7 e 8 da coleção de Chikamatsu. As três marionetes entram em uma formação triangular e abrindo os mecanismos marcados com 'Buda', 'Dharma' e 'Sangha', respectivamente, eles formam a técnica. Um tornado terrível suga os alvos para o centro da formação. Tudo o que é sugado para o centro é completamente esmagado com uma força tremenda, sendo jogados para fora da formação em seguida. No entanto, quando vácuo é sobrecarregado, os três bonecos se tornam vulneráveis aos ataques.", "Todos os alcances", "Rank B", null, "三宝吸潰さんぼうきゅうかい", "Sanbō Kyūkai", "Sucção das Três Jóias do Esmagamento", null, "O Triturador dos Três Tesouros", "Destruição no Vácuo dos Três Tesouros");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sujeição do Sapo — Técnica de Manipulação das Sombras", "Após infundir chakra na sombra da vítima, o usuário faz o seu próprio corpo ficar o mais plano possível. Então, tornando-se um com a sua sombra, esta técnica estabelece temporariamente o controle sobre a mente e o corpo da vítima. Enquanto a técnica está em efeito, a vítima fica temporariamente atordoada e pode servir como escudo humano. O usuário tem que prender a respiração, mas ele pode falar através da vítima. Assim que a respiração do usuário recomeça, a técnica é anulada e o seu corpo ultra-fino se expande outra vez, retornando a sua espessura original.", "Curto alcance", "Rank B", null, "蝦蟇平　影操りの術がまだいら　かげあやつりのじゅつ", "Gamadaira — Kageayatsuri no Jutsu", "Sujeição do Sapo — Técnica de Manipulação das Sombras", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Sukunahikona", "Ao apontar sua mão, Jigen é capaz de encolher objetos para tamanhos microscópicos e, do mesmo modo, fazê-los retornar ao seu tamanho original de forma instantânea, de modo que olhos normais não podem detectar, fazendo parecer que os objetos surgiram do nada. Esse efeito parece se limitar à objetos inanimados ou ao próprio usuário.", "Curto a Médio alcance", null, null, "少名毘古那スクナヒコナ", "Sukunahikona", "Pequeno Homem de Renome", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Bofetada Abrupta", "Karin dá um tapa em seu oponente a fim de anexar um papel explosivo a seu rosto. Quando o oponente cai longe, o selo é detonado automaticamente.", "Curto alcance", null, null, "激ツンビンタげきつんびんた", "Geki Tsunbinta", "Super Bofetada Abrupta", "Super Tapa Agressivo", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Flash Espiral da Roda da Dança Uivante da Fórmula Participante Estilo Três", "A Super Flash Espiral da Roda da Dança Uivante da Fórmula Participante Estilo Três é uma técnica criada por Minato Namikaze e que emprega o uso de sua Técnica do Deus Voador do Trovão.", "Todos os alcances", null, null, "螺旋閃光超輪舞吼参式", "Rasen Senkō Chō Rinbukō Sanshiki", "Super Flash Espiral da Roda da Dança Uivante da Fórmula Participante Estilo Três", null, "Terceira Forma da Dança do Super-Rugido de Lampejo em Espiral Estágio Três", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Grande Bola Rasenshuriken das Bestas com Cauda", "Naruto entra em seu Modo de Chakra do Nove-Caudas e manifesta o corpo de Kurama em sua forma plena, então ele cria nove Rasenshuriken em suas caudas, cada um infundido com as propriedades elementais das respectivas Bestas com Caudas. Kurama, então, arremessa os Rasenshuriken em direção ao seu oponente, culminando em múltiplas explosões.", "Todos os alcances", null, null, "超尾獣大玉螺旋手裏剣ちょうおけものだいたまらせんしゅりけん", "Chō Bijū Ōdama Rasenshuriken", "Super Grande Bola Rasenshuriken das Bestas com Cauda", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Luz do Pé", "Minato corre em direção ao seu oponente e chuta-o no joelho. Em seguida, ele desliza para trás e chuta novamente o mesmo joelho, de tal maneira que o oponente voa para o ar.", "Curto alcance", null, null, "超光脚", "Chōkōkyaku", "Super Luz do Pé", "Super Raio de Luz do Pontapé", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Mini-Bola da Besta com Cauda", "Enquanto no Modo de Chakra do Nove-Caudas, Naruto reúne e equilibra o chakra positivo preto e negativo branco necessários para formar uma miniatura condensada da Bola da Besta com Cauda. Com o impacto, a bola explode, resultando em um raio de explosão muito considerável. Tal como acontece com o Rasengan, ele pode estender a bola para a frente com o uso de um braço de chakra. Naruto pode usar um clone da sombra para ajudá-lo a formar a bola. Enquanto no Modo Bijū ele pode formá-la por conta própria.", "Todos os alcances", null, null, "超ミニ尾獣玉", "Chō Mini Bijūdama", "Super Mini-Bola da Besta com Cauda", "Super Mini Bomba Bijū", "Super-Mini Bomba Bijū", "Super Mini Bomba da Besta com Cauda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Presa do Lobo Predador", "O homem e seu animal se transformam em um único animal gigantesco de duas cabeças pelo Transformação Combinada da Besta Humana: Lobo de Duas Cabeças, e tal como a sua técnica parente, ele tem sua rotação do corpo em um violento golpe contra o inimigo. O poder é grande o suficiente para romper três portões Rashōmon. Além do contrário de sua técnica parente que não exige primeiro o uso da Marcação Dinâmica.", "Médio a Longo alcance", null, null, "超牙狼牙", "Chō Garōga", "Super Presa Lobo Presa", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Punho do Renascimento", "Esta técnica funciona de forma semelhante ao Rasengan. Ao reunir chakra escuro na mão, o usuário pode criar uma esfera escura, com dois anéis escuros em torno dela. O usuário, então, lavra o orbe para o adversário, resultando no adversário ser atingido com tremenda força quebrando os ossos. De acordo com Shinnō, esta é a técnica suprema derivada do chakra escuro.", "Curto alcance", null, null, "超活性拳", "Chō Kasseiken", "Super Punho do Renascimento", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Rasengan Uzumaki", "O Super Rasengan Uzumaki é um ninjutsu cooperativo usado por Boruto com o auxílio de Naruto.", "Curto alcance", null, null, "超うずまき螺旋丸ちょううずまきらせんがん", "Chō Uzumaki Rasengan", "Super Esfera Espiral Uzumaki", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Rasengan do Chakra de Naruto e Shion", "Este é um Rasengan enorme criado com chakra de Naruto e Shion, apoiado por seu Sino de Segurança Místico. A técnica foi usada para destruir Mōryō.", "Curto alcance", null, null, "ナルトとシオンの超チャクラ螺旋丸", "Naruto to Shion no Chō Chakura Rasengan", "Esfera Espiral do Super Chakra de Naruto e Shion", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Super Tapa", "Depois de utilizar a Técnica do Tamanho Múltiplo para aumentar o seu tamanho, o usuário acumula chakra nas suas mãos, o que provoca marcas especiais que aparecem nas mesmas. O peso e a potência das mãos são aumentadas, devido à maior dimensão, os músculos são ativados com chakra concentrado, e a espessura também é aumentada. A quantidade de chakra é tanta que se torna visível, as palmas do usuário tornam-se como um martelo de ferro. Isso dá a esse ataque o poder de nivelar a terra, causando danos de esmagamento extensivo a qualquer coisa que possa ser capturada abaixo delas. Este ataque é também mostrado ter uma ampla gama, devido ao aumento do tamanho das palmas das mãos.", "Curto a Médio alcance", "Rank B", null, "超張り手ちょうはりて", "Chōharite", "Super Tapa", null, "Super Bofetada", "Britadeira Humana");
