INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Susanoo", "O Susanoo é um humanoide feito de chakra daquele que luta em seu nome. É a terceira habilidade concedida àqueles que despertaram todas as habilidades de ambos os Mangekyō Sharingan.", "Todos os alcances", "Rank –", null, "須佐能乎スサノオ", "Susanoo", "Ele com a habilidade de ajudar por todos os meios", null, "Deus da Tormenta", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Susanoo: Advento do Relâmpago dos Deuses Gêmeos", "Sasuke e Itachi ativam o Corpo Completo — Susanoo. Itachi lança sua Yasaka Magatama em seu seu oponente enquanto Sasuke o corta com sua Espada de Kagutsuchi, como resultado as chamas criam nuvens de relâmpago. Os dois voam para o céu, unindo suas espadas, então Sasuke a infunde com relâmpago e Itachi com chamas negras. Eles finalizam voando para baixo e atacando seu oponente com a espada, causando um impacto tão grande que é capaz de limpar o céu.", "Todos os alcances", null, null, "須佐能乎・双神雷臨", "Susanoo: Sōjin Rairin", "Ele com a habilidade de ajudar por todos os meios: Advento do Relâmpago dos Deuses Gêmeos", null, null, null);
