INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Susanoo: Corte Relâmpago do Kamui", "Após despertar o Mangekyō Sharingan em ambos os olhos, Kakashi manifesta o Corpo Completo — Susanoo e forma o Corte Relâmpago do Kamui imbuído com o Chakra dos Seis Caminhos em sua mão direita. Em seguida, ele descarrega a técnica em seu adversário, culminando em uma explosão de eletricidade.", "Todos os alcances", null, null, "須佐能乎・神威雷切スサノオ・カムイらいきり", "Susanoo: Kamui Raikiri", "Ele tem a capacidade de ajudar por todos os meios: Corte Relâmpago da Autoridade dos Deuses", "Susanoo: Espada Relâmpago Kamui", null, null);
