INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Susanoo: Técnica da Grande Bola de Fogo", "Sasuke Uchiha, usando o Corpo Completo — Susanoo como um meio, dispara uma rajada de bolas de fogo da boca projetada pelo Susanoo. Os Projéteis podem variar em tamanho, podendo se tornar tão grande quanto uma Bijū. Como a técnica não explode no impacto, as bolas de fogo podem ser repelidas com força suficiente. Ao lançar a técnica no céu, pode-se cria nuvens de raios, tornando possível o uso do Kirin.", "Longo Alcance", null, null, null, null, null, null, null, null);
