INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Takemikazuchi", "Takemikazuchi é uma técnica criada por Sasuke Uchiha, que consiste em criar um Chidori em sua mão esquerda e infundi-lo com chamas negras para aumentar o seu poder destrutivo. Ele foi poderoso o suficiente para destruir as estátuas do Vale do Fim ao colidir com o Rasengan de Naruto, além de destruir metade do seu braço.", "Curto alcance", null, null, "建御雷神タケミカヅチ", "Takemikazuchi", "Honorável Deus Criador do Trovão", "Takemikazuchi-no-kami", null, null);
