INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tarja da Barreira de Selamento", "Um Kekkaijutsu que envolve o uso de várias etiquetas de selamento escondidas sob a superfície. Quando alguém pisa na área, ele fica contido por muitos segmentos da fórmula. A força é forte o suficiente para aniquilar completamente uma pessoa reencarnada.", "Curto Alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Teatro do Corpo de Konohamaru", "Konohamaru, Moegi e Udon fazem uma sequência de ataques com socos, tapas e chutes no oponente. Em seguida, os três acertam um último soco simultaneamente no oponente, lançando-o longe com o impacto.", "Curto alcance", null, null, "木ノ葉丸軍団劇場", "Konohamaru Gundam Gekijō", "Teatro do Corpo de Konohamaru", "Teatro do Esquadrão Ninja de Konohamaru", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Teisho", "Trata-se de um golpe semelhante à um soco, porém ele é executado de mão aberta e tem como área de impacto a base da mão. Ele é normalmente utilizado para atingir o rosto, queixo, costelas e plexo solar.", "Curto alcance", null, null, null, null, null, null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Telepatia da Besta com Cauda", "Uma habilidade inata possuída por todas as bestas com cauda, o que permite que elas se comuniquem umas com as outras em uma planície mais profunda de sua mente subconsciente conhecida como mundo mental. Este tipo de comunicação é diferente do utilizado pelos jinchūriki e suas bestas com cauda, já que não há selos ou outras restrições e é acessível para todas as bestas com cauda e seus respectivos jinchūriki. No entanto, para entrar neste mundo é necessário que um jinchūriki e sua besta sejam totalmente ligados. Além disso, é impossível para as influências de fora alcançar este plano, mesmo que as bestas com cauda estejam sendo controladas.", "Todos os alcances", null, null, null, null, null, null, null, "Psique Profunda da Besta com Cauda");
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tempestade da Folha", "Lee se concentra por um momento, e desfere um chute giratório. Lee abrange uma distância curta ao girar no chão, e depois salta enquanto ainda girando, batendo no oponente.", "Curto alcance", null, null, "木ノ葉暴風", "Konoha Bōfū", "Tempestade da Folha", null, null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Temporal do Esmagamento Celestial", "Sarada dá uma voadora no oponente e, rapidamente, move-se para atrás dele agora com o Sharingan ativado, a fim de chutá-lo para o alto. Em seguida, Sarada salta e ataca o oponente com uma combinação única de chute, um ataque com o antebraço e um soco para o atirar brutalmente contra o chão. Em queda, o oponente é surpreendido com uma bola de fogo gigante seguida, entre chamas, por um poderoso soco de chakra e é esmagado contra o solo.", "Todos os alcances", null, null, "夕立天崩しゆうだしあまくずし", "Yūdachi Amakuzushi", "Temporal do Esmagamento Celestial", "Esmagamento da Chuva Súbita", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tendō", "O Caminho Deva (天道, Tendō) concede ao usuário a capacidade de manipular forças atrativas e repulsivas, permitindo mover objetos e pessoas, bem como voar. Estas técnicas de manipulação da gravidade, no entanto, não podem ser realizadas numa sucessão rápida, a quantidade de tempo necessária para recarregar após a utilização varia de acordo com a escala da técnica utilizada, sendo cinco segundos a mínima de base. O Caminho Deva também concede outra habilidade: Chibaku Tensei, que permite ao usuário criar um satélite natural, através de um núcleo gravitacional, como uma esfera negra ou até mesmo uma pessoa.", null, null, null, "天道", "Tendō", "Caminho Deva", null, "Reino do Céu", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tengai Ryūsei", "Utilizando o Poder do Seis Caminhos combinado com o poder do seu Rinnegan, Madara libera vários núcleos gravitacionais que atraem o terreno ao redor para si, criando vários satélites, que são posteriormente jogados contra seus alvos como uma chuva de meteoros.", "Todos os alcances", null, "Juntar as Maos", "天涯流星", "Tengai Ryūsei", "Meteoro do Horizonte", "Meteoro na Linha do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tengai Shinsei", "Madara faz três selos de mão simultaneamente com o seu Susanoo, a fim de extrair meteoritos maciços a partir da atmosfera superior para baixo para um local específico do chão. Eles descem em sucessão ao longo da mesma trajetória, se um desses meteoritos é interrompido, outros colidem com o primeiro como um martelo enorme, quebrando boa parte e garantindo que ele caía em seu destino.", "Longo alcance", null, "Carneiro + Cobra + Passaro Simultaneamente[2]", "天碍震星てんがいしんせい", "Tengai Shinsei", "Obstáculo Celestial da Estrela Trêmula", "Paraíso Escondido", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tenpenchii", "Ao utilizar a sua gigantesca quantidade de chakra para manipular toda a energia natural presente ao seu redor em larga escala, o Dez-Caudas é capaz de criar simultaneamente uma variedade de desastres naturais que assolam a terra, com o próprio agindo como seu epicentro. Os fenômenos envolvidos nesta técnica incluem: terremotos, inundações, tempestades e tornados. Estes eventos simultâneos resultam em uma explosão semelhante a uma explosão nuclear, mesmo formando uma vasta nuvem de cogumelo.", "Todos os alcances", "Rank –", null, "天変地異", "Tenpenchii", "Desastre Natural Sucedido do Céu e Terra", null, "Cataclismo", null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tensō Tsuisei", "Obito joga o oponente no ar durante a utilização de sua Liberação de Madeira: Técnica Cortante. O oponente pousa e Obito envolve-o em uma bola de madeira. Madara, então, chama seu Tengai Shinsei, que cai para baixo sobre o oponente.", "Todos os alcances", null, null, "天挿槌星", "Tensō Tsuisei", "Inserção Celestial do Martelo Estelar", "Estrelas do Céu", null, null);
INSERT INTO `jutsu` (`id`, `name`, `description`, `range`, `rank`, `hand_seals`, `kanji`, `romaji`, `portugues`, `games`, `manga_panini`, `tv_brasileira`) VALUES (null, "Tentáculos de Areia", "Gaara alonga sua areia a partir de sua base flutuante, formando grossos tentáculos que podem ser usados para capturar ou esmagar o inimigo. A velocidade desta técnica é suficiente para pressionar um Ōtsutsuki a se esquivar sem chances de contra-ataque.", "Médio a Longo alcance", null, null, null, null, null, null, null, null);
